INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31207, 'Resource diabetes powder 400 gm', 'ريسورس باودر لمرضى السكر 400 جم', '163', NULL, 'Nutritive powder', 'Nutritive powder formula', 'Nestle', 'Powder', 'بودرة', '400 gm', '1', NULL, 'resource diabetic is a nutritionally balanced diet for oral consumption or hyperglycemia. resource diabetic can be used as the sole source of nutrition or as a supplement. it can also be part of a healthy diabetic meal plan. resource diabetic is lactose f', '7613032991852', NULL, 870, '2022-12-08', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (11631, 'Triactin 2mg/5ml 120ml syrup', 'ترايكتين شراب 120 مل فاتح شهية', '42', '28.5', 'Antihistamine.sedative.first generation', 'Cyproheptadine', 'Kahira', 'Syrup', 'شراب', '2mg', '1', 'فاتح شهية ومضاد للحساسية', NULL, '6221068404591', NULL, 11567, '2025-06-15', 'In the US, prescription cyproheptadine is indicated for the treatment of various allergic symptomatologies - including dermatographia, rhinitis, conjunctivitis, and urticaria - as well as adjunctive therapy in the management of anaphylaxis following treatment with epinephrine.[L32474] In Canada, cyproheptadine is available over-the-counter and is indicated for the treatment of pruritus and for appetite stimulation.[L32519] In Australia, cyproheptadine is additionally indicated for the treatment vascular headaches.[L32479]



Cyproheptadine is also used off-label for the treatment of serotonin syndrome.[A231274]', 'Cyproheptadine appears to exert its antihistamine and antiserotonin effects by competing with free histamine and serotonin for binding at their respective receptors.[L32479] Antagonism of serotonin on the appetite center of the hypothalamus may account for cyproheptadine''s ability to stimulate the appetite.', 'Cyproheptadine has been observed to antagonize several pharmacodynamic effects of serotonin in laboratory animals, including bronchoconstriction and vasodepression, and has demonstrated similar efficacy in antagonizing histamine-mediated effects.[L32479] The reason for its efficacy in preventing anaphylactic shock has not been elucidated, but appears to be related to its anti-serotonergic effects.[L32479]', 'DrugBank', 0, 0, 0);
