INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1704, 'Melatonin-rafada 5 mg 20 odf', 'ميلاتونين رافادا 5مجم 20 فيلم', '93', NULL, 'Sleeping aid antioxidant', 'Melatonine', 'Rafada two rs', 'Unknown', 'غير محدد', '5 mg', '1', NULL, 'melatonin is a potent free radical scavenger naturally produced in the pineal gland and present in high amounts in the gastrointestinal tract. it is involved in many of the body brain and glandular biological functions including regulation of normal sleep', NULL, NULL, 1121, '2024-11-28', 0, NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1705, 'Vita-de-val 10000 iu 30 soft gelatin caps', 'فيتا دي فال 10000 وحدة 30 كبسولة', '165', NULL, 'Dietary supplement', 'Vitamin d3', 'Bee group & pharmatreat', 'Cap', 'كبسولة', '10000 iu', '1', NULL, NULL, NULL, NULL, 1109, '2024-11-28', 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1707, 'Brexin 20mg 10 sachet', 'بركسين 20 مجم اكياس فوارة 10 كيس', '44', '31', 'Nsaid.oxicam', 'Piroxicam', 'El nile. > chiesi italy', 'Sachet', 'أكياس', '20mg', '1', NULL, 'about piroxicam nsaid oxicam derivative analgesic antiinflammatory. mechanism of action of piroxicam drug inhibits the biosynthesis and release of prostaglandins by inhibiting cyclooxygenase enzyme in a reversible manner. in this way it reduces the pg (pr', '6221077100910', NULL, 1102, '2024-12-06', 0, 'For treatment of osteoarthritis and rheumatoid arthritis.', 'The antiinflammatory effect of Piroxicam may result from the reversible inhibition of cyclooxygenase, causing the peripheral inhibition of prostaglandin synthesis. The prostaglandins are produced by an enzyme called Cox-1. Piroxicam blocks the Cox-1 enzyme, resulting into the disruption of production of prostaglandins. Piroxicam also inhibits the migration of leukocytes into sites of inflammation and prevents the formation of thromboxane A2, an aggregating agent, by the platelets.', 'Piroxicam is in a class of drugs called nonsteroidal anti-inflammatory drugs (NSAIDs). Piroxicam works by reducing hormones that cause inflammation and pain in the body. Piroxicam is used to reduce the pain, inflammation, and stiffness caused by rheumatoid arthritis and osteoarthritis.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1709, 'Arthojo 30tabs', 'ارثوجو 30 قرص', '210', NULL, 'Dietary supplement', 'Glucosamine sulphate 2kcl+methyl sulfonyl methane (msm)+rose hips powder extract+chondroitin sulphate+ginger root extract+vitamin d3 as cholecalciferol+l- ascorbic acid+folic acid+cyanocobalamin+zinc gluconate+copper sulphate+manganese sulfate+sodium selenate', 'Bee group & pharmatreat', 'Tab', 'أقراص', NULL, '1', NULL, NULL, NULL, NULL, 1256, '2024-11-28', 0, 'Glucosamine is generally used over the counter in the symptomatic treatment of osteoarthritis and joint pain,[A2352] frequently combined with chondroitin sulfate and/or ibuprofen.[L32699]', 'The mechanism of action of glucosamine in joint health is unclear,[A231894] however there are several possible mechanisms that contribute to its therapeutic effects. Because glucosamine is a precursor for glycosaminoglycans, and glycosaminoglycans are a major component of joint cartilage,[A232289] glucosamine supplements may help to rebuild cartilage and treat the symptoms of arthritis.  Some in vitro studies show evidence that glucosamine reduces inflammation via inhibition of interferon gamma[A12619,A12616,A12621]  and Nuclear factor kappa B subunit 65 (NF-κB p65),[A12616,A232284] improving the symptoms of arthritis and joint pain. Clinical relevance is unknown at this time.', 'The administration of glucosamine, in theory, provides a building block towards the synthesis of glycosaminoglycans, slowing the progression of osteoarthritis and relieving symptoms of joint pain. Studies to this date examining the efficacy of glucosamine sulfate have been inconclusive. Glycosaminoglycans contribute to joint cartilage elasticity, strength, and flexibility.[A232144] A systematic review of various studies and guidelines determined that modest improvements were reported for joint pain and function in patients taking glucosamine. A consistent joint space narrowing was observed, but with an unclear clinical significance.[A231894]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1710, 'Bricanyl 0.25mg 200 dose inhaler', 'بريكانيل 0.25مجم 200 جرعة بخاخ', '12', NULL, 'Bronchodilator.short-acting b2 agonist', 'Terbutaline', 'Cid > astra zeneca', 'Inhaler', 'جهاز استنشاق', '0.25mg', '1', NULL, NULL, '6221043050010', NULL, 999, '2022-12-03', 0, 'Terbutaline is indicated for prevention and reversal of bronchospasm in patients at least 12 years old, with asthma and reversible bronchospasm associated with bronchitis and emphysema.[L32093,L32098]', 'Terbutaline is a selective beta-2 adrenergic receptor agonist.[A230328] Agonism of these receptors in bronchioles activates adenylyl cyclase, increasing intracellular cyclic adenosine monophosphate (cAMP).[A230338] Increased cAMP decreases intracellular calcium, activating protein kinase A, inactivating myosin light-chain kinase, activating myosin light-chain phosphatase, and finally relaxing smooth muscle in the bronchiole.[A230338]', 'Terbutaline is a beta-2 adrenergic receptor agonist indicated to treat reversibly bronchospasm in asthmatic patients with bronchitis and emphysema.[L32093,L32098] It has a short duration as the inhaled form is taken up to three times daily, and the therapeutic window is wide.[L32093,L32098]', 'DrugBank', 0, 0, 0);
