    ) VALUES (4533, 'Ragitovak 4mg/2ml 5 i.m./i.v./inf. amp.', 'راجيتوفاك 4 مجم/2مل 5 امبولات', '82', NULL, 'Antiemetic', 'Ondansetron', 'Hikma specialized pharmaceuticals', 'Amp', 'أمبول', '4mg', '5', NULL, 'In the adult patient population:

