INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4799, 'Sildocare 8 mg 30 caps', 'سيلدوكير 8 مجم 30 كبسولة', '177', '135', 'Urination-difficulty.alpha blocker', 'Silodosin', 'Andalous pharma', 'Cap', 'كبسولة', '8 mg', '3', NULL, NULL, '6223004693106', NULL, 962, '2025-09-12', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Silodosin is a selective antagonist of alpha(α)-1 adrenergic receptors that binds to the α1Asubtype with the highest affinity. α1-adrenergic receptors regulate smooth muscle tone in the bladder neck, prostate, and prostatic urethra: the α1Asubtype accounts for approximately 75% of α1-adrenoceptors in the prostate.[A231159] 

Silodosin was first approved by the FDA in October 2008 [A231159] and it is also approved in Europe and Canada. Silodosin is available as oral capsules with common trade names Rapaflo and Urorec. It is indicated for the symptomatic treatment of benign prostatic hyperplasia in adults.[L32368] Most commonly affecting males over the age of 40 years, benign prostatic hyperplasia is the non-malignant enlargement of the prostate gland, associated with lower urinary tract symptoms that have a negative impact on the quality of life of patients.[A231159] Silodosin works by binding to α1A-adrenoceptors with high affinity and relaxing the lower urinary tract, thereby improving urinary symptoms and alleviating bladder outlet obstruction.[A231229]', '["G04CA04"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4800, 'Silzi oral drops 10 ml', 'سيلزي نقط 10 مل', '120', NULL, 'Vitamin d', 'Cholecalciferol(vitamin d3)', 'Sandoz', 'Drops', 'نقط', '10 ml', '1', NULL, NULL, NULL, NULL, 1002, '2025-06-21', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Vitamin D, in general, is a secosteroid generated in the skin when 7-dehydrocholesterol located there interacts with ultraviolet irradiation - like that commonly found in sunlight [L5689]. Both the endogenous form of vitamin D (that results from 7-dehydrocholesterol transformation), vitamin D3 (cholecalciferol), and the plant-derived form, vitamin D2 (ergocalciferol), are considered the main forms of vitamin d and are found in various types of food for daily intake [L5689]. Structurally, ergocalciferol differs from cholecalciferol in that it possesses a double bond between C22 and C23 and has an additional methyl group at C24 [L5689]. Finally, ergocalciferol is pharmacologically less potent than cholecalciferol, which makes vitamin D3 the preferred agent for medical use [L5689].

Appropriate levels of vitamin D must be upheld in the body in order to maintain calcium and phosphorus levels in a healthy physiologic range to sustain a variety of metabolic functions, transcription regulation, and bone metabolism [A223, L5689, L1782, L5771, F4027, F4042, F4048]. However, studies are also ongoing to determine whether or not cholecalciferol may also play certain roles in cancer, autoimmune disorders, cardiovascular disease, and other medical conditions that may be associated with vitamin D deficiency [L5689].', '["M05BB09", "M05BX53", "M05BB07", "M05BB08", "A11CC55", "M05BB05", "A11CC05", "M05BB03", "M05BB04"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4802, 'Fentanyl- hameln 0.1mg/2ml 1 amp. (net price)', 'فنتانيل - هاملن 0.1مجم/2مل 1 أمبول', '16', '10.5', 'Analgesics.opioid', 'Fentanyl', 'Sunny pharmaceuticals', 'Amp', 'أمبول', '0.1mg', '1', 'مسكن قوي للألم', 'about fentanyl citrate synthetic primary opioid agonist anilidopiperidine derivative narcotic analgesic general anesthetic. mechanism of action of fentanyl citrate this drug primarily acts on the cns. fentanyl is a pure opioid agonist and primarily acts o', NULL, NULL, 11547, '2023-09-14', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Fentanyl, a potent opioid agonist, was developed in the 1950s to fill a need for strong and rapid analgesia.[A179542] Because of these characteristics, fentanyl is commonly used to treat chronic cancer pain or in anesthesia.[Label,L6598,L6601,L6604,L6607,L922,L6610,L6613] Fentanyl is related to other opioids like [morphine] and [oxycodone].

Fentanyl''s high potency has also made it a common adulterant in illicit drugs, especially heroin.[A179542] In 2017, 47600 overdose deaths in the United States involved some opioid (over 2/3 of all overdose deaths).[L6748] Opioid overdoses kill an average of 11 Canadians daily.[L6751]

Fentanyl was FDA approved in 1968.[Label,L6598,L6601,L6604,L6607,L922,L6610,L6613]', '["N01AH01", "N02AB03", "N01AH51"]', '[]');
