INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4946, 'Regenflex bio-plus 75mg/3ml', 'ريجينفليكس بيو بلس 75مجم/3مل', '7,000', NULL, 'Intra articular injection', 'Hyaluronic acid', 'Regenyal laboratories', 'Unknown', 'غير محدد', '75mg', '1', NULL, NULL, NULL, NULL, 978, '2025-06-22', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Hyaluronic acid (HA) is an anionic, nonsulfated glycosaminoglycan found in connective, epithelial, and neural tissues; it was first isolated in 1934. Karl Meyer and John Palmer obtained glycosaminoglycan (GAG) from the bovine eye, giving it the name “hyaluronic acid”. HA is involved in many important physiological processes, including but not limited to wound healing, tissue regeneration, and joint lubrication. It demonstrates unique viscoelasticity, moisturizing, anti-inflammatory qualities, and other important properties that prove beneficial in various clinical applications.[A230128]  

HA is used in drug delivery systems for the treatment of cancer, ophthalmological conditions, joint conditions, and aesthetic imperfections.[A230133] Several preparations of hyaluronic acid have been approved by the FDA and are available in oral, topical, and injectable forms. A popular use of hyaluronic acid in recent years is cosmetic injection due to its ability to minimize the appearance of wrinkles and aging-related skin imperfections.[L32193]', '["S01KA51", "S01KA01", "D03AX05", "M09AX01", "R01AX09"]', '[]');
