INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4967, 'Floxabact 500 mg 10 f.c.tabs.(now levofloxacin-eva)', 'فلوكسابكت 500مجم 10 اقراص', '40', NULL, 'Antibiotic.quinolone', 'Levofloxacin', 'Eva pharma', 'Tab', 'أقراص', '500 mg', '2', 'مضاد حيوي.', NULL, '6223002459797', NULL, 3711, '2022-12-05', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Levofloxacin is a fluoroquinolone antibiotic and the optical S-(-) isomer of racemic [ofloxacin].[A190663] It reportedly carries 8 to 128-fold more activity against both gram-negative and gram-positive bacteria compared to R-(+)-ofloxacin[A190663] and remains stereochemically stable following administration (i.e. it does not invert to the inactive isomer).[L11638] Levofloxacin, along with other quinolones such as [gatifloxacin] and [moxifloxacin], is a member of the third generation of fluoroquinolones, colloquially referred to as the "respiratory quinolones" due to improved activity against gram-positive bacteria commonly implicated in respiratory infections.[A31453,A190756]

Levofloxacin was first approved by the FDA in 1996, and was approved in Canada and several South American countries soon after.[A190663]', '["J01MA12", "A02BD10", "J01RA05", "S01AE05"]', '[]');
