INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17827, 'Dopamine fresenius 200mg/5ml 10amp. concentrate for i.v.inf.', 'دوبامين فريزينيوس 200 مجم / 5 مل 10 امبولات', '46', NULL, 'Adrenergic agonist', 'Dopamine', 'Renaudin laboratories-france > fresenius kabi', 'Amp', 'أمبول', '200mg', '10', NULL, 'about dopamine adrenergic agonist agent phenethylamine derivative inotropic agents neurotransmitter vasopressor. mechanism of action of dopamine the drug exerts it`s pharmacological action by acting as a dopaminergic (both d1&d2) receptor and adrenergic (', NULL, NULL, 1025, '2022-08-01', 'For the correction of hemodynamic imbalances present in the shock syndrome due to myocardial infarction, trauma, endotoxic septicemia, open-heart surgery, renal failure, and chronic cardiac decompensation as in congestive failure', 'Dopamine is a precursor to norepinephrine in noradrenergic nerves and is also a neurotransmitter in certain areas of the central nervous system. Dopamine produces positive chronotropic and inotropic effects on the myocardium, resulting in increased heart rate and cardiac contractility. This is accomplished directly by exerting an agonist action on beta-adrenoceptors and indirectly by causing release of norepinephrine from storage sites in sympathetic nerve endings. In the brain, dopamine acts as an agonist to the five dopamine receptor subtypes (D1, D2, D3, D4, D5).', 'Dopamine is a natural catecholamine formed by the decarboxylation of 3,4-dihydroxyphenylalanine (DOPA). It is a precursor to norepinephrine in noradrenergic nerves and is also a neurotransmitter in certain areas of the central nervous system, especially in the nigrostriatal tract, and in a few peripheral sympathetic nerves. Dopamine produces positive chronotropic and inotropic effects on the myocardium, resulting in increased heart rate and cardiac contractility. This is accomplished directly by exerting an agonist action on beta-adrenoceptors and indirectly by causing release of norepinephrine from storage sites in sympathetic nerve endings.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (809, 'Antizole 100mg/5ml susp. 30ml', 'انتيزول 100مجم/5مل معلق 30 مل', '19', '5.75', 'Anthelmintic', 'Albendazole', 'Alexandria', 'Susp', 'معلق', '100mg', '1', NULL, 'indication: this medication is an anthelmintic prescribed for tapeworm infections hydatid cyst disease cysticercosis or neurocysticercosis capillariasis cutaneous larva migrans giardiasis microsporidiosis including septata intestinalis infection intestina', '6223000171011', NULL, 1365, '2024-10-07', 'For the treatment of parenchymal neurocysticercosis due to active lesions caused by larval forms of the pork tapeworm, <i>Taenia solium</i> and for the treatment of cystic hydatid disease of the liver, lung, and peritoneum, caused by the larval form of the dog tapeworm, <i>Echinococcus granulosus</i>.', 'Albendazole causes degenerative alterations in the tegument and intestinal cells of the worm by diminishing its energy production, ultimately leading to immobilization and death of the parasite. It works by binding to the colchicine-sensitive site of tubulin, thus inhibiting its polymerization or assembly into microtubules. As cytoplasmic microtubules are critical in promoting glucose uptake in larval and adult stages of the susceptible parasites, the glycogen stores of the parasites are depleted. Degenerative changes in the endoplasmic reticulum, the mitochondria of the germinal layer, and the subsequent release of lysosomes result in decreased production of adenosine triphosphate (ATP), which is the energy required for the survival of the helminth. ', 'Albendazole is a broad-spectrum anthelmintic. The principal mode of action for albendazole is by its inhibitory effect on tubulin polymerization which results in the loss of cytoplasmic microtubules.', 'DrugBank', 0, 0, 0);
