INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20860, 'Instantized bcaa 5000 powder unflavored 345 gm', 'انستانتايزد 5000 بودرة 345 جم', '550', NULL, 'Supplement', 'Micronized l-leucine+micronized l-isoleucine+micronized l-valine', 'Optimum nutrition > ramco', 'Powder', 'بودرة', '345 gm', '1', NULL, 'the branched chain amino acids (bcaas) are essential amino acids that can not be produced by your body. they can only be taken in through diet. because the bcaas are very much in demand with all kinds of athletes we have made them available to active adul', NULL, NULL, 714, '2022-07-27', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27890, 'Tamsunorm s.r. 0.4mg 30 capsules', 'تامسونورم اس ار 0.4مجم 30 كبسولة', '60', NULL, 'Urination-difficulty.alpha blocker', 'Tamsulosin', 'Rameda', 'Capsule', 'كبسولة', '0.4mg', '3', 'لصعوبة وتنقيط البول بسبب احتقان البروستاتا -- علاج تضخم البروستاتا الحميد', NULL, '6223003974169', NULL, 7313, '2023-02-03', 'Tamsulosin is indicated for the treatment of signs and symptoms of benign prostatic hyperplasia.[Label]



Tamsulosin is also used off label for the treatment of ureteral stones, prostatitis, and female voiding dysfunction.[A178339,L6259]', 'Tamsulosin is a blocker of alpha-1A and alpha-1D adrenoceptors.[Label,A1078] About 70% of the alpha-1 adrenoceptors in the prostate are of the alpha-1A subtype.[Label] By blocking these adrenoceptors, smooth muscle in the prostate is relaxed and urinary flow is improved.[Label] The blocking of alpha-1D adrenoceptors relaxes the detrusor muscles of the bladder which prevents storage symptoms.[A1078] The specificity of tamsulosin focuses the effects to the target area while minimizing effects in other areas.[Label]', 'Tamsulosin is an alpha adrenoceptor blocker with specificity for the alpha-1A and alpha-1D subtypes, which are more common in the prostate and submaxillary tissue.[A1078] The final subtype, alpha-1B, are most common in the aorta and spleen.[A1078] Tamsulosin binds to alpha-1A receptors 3.9-38 times more selectively than alpha-1B and 3-20 times more selectively than alpha-1D.[A1078] This selectivity allows for a significant effect on urinary flow with a reduced incidence of adverse reactions like orthostatic hypotension.[A1078]', 'DrugBank', 0, 0, 0);
