INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22597, 'Merfo 30 pieces', 'ميرفو 30 قطعة', '45', NULL, 'Supplement', 'Folic acid+ vitamin b12', 'Zakuro > mercy pharma', 'Piece', 'قطعة', NULL, '1', 'مكملات', 'folic acid + vitamin b12 helps preventing any defects in babys brain or spinal cord support buildind rbcs and immune system', NULL, NULL, 909, '2022-05-15', 'Folic acid is indicated for the treatment of folic acid deficiency, megaloblastic anemia, and in anemias of nutritional origins, pregnancy, infancy, or childhood.', 'Folic acid, as it is biochemically inactive, is converted to tetrahydrofolic acid and methyltetrahydrofolate by dihydrofolate reductase (DHFR). These folic acid congeners are transported across cells by receptor-mediated endocytosis where they are needed to maintain normal erythropoiesis, synthesize purine and thymidylate nucleic acids, interconvert amino acids, methylate tRNA, and generate and use formate. Using vitamin B12 as a cofactor, folic acid can normalize high homocysteine levels by remethylation of homocysteine to methionine via methionine synthetase.', 'Folic acid is a water-soluble B-complex vitamin found in foods such as liver, kidney, yeast, and leafy, green vegetables. Also known as folate or Vitamin B9, folic acid is an essential cofactor for enzymes involved in DNA and RNA synthesis. More specifically, folic acid is required by the body for the synthesis of purines, pyrimidines, and methionine before incorporation into DNA or protein. Folic acid is the precursor of tetrahydrofolic acid, which is involved as a cofactor for transformylation reactions in the biosynthesis of purines and thymidylates of nucleic acids. Impairment of thymidylate synthesis in patients with folic acid deficiency is thought to account for the defective deoxyribonucleic acid (DNA) synthesis that leads to megaloblast formation and megaloblastic and macrocytic anemias. Folic acid is particularly important during phases of rapid cell division, such as infancy, pregnancy, and erythropoiesis, and plays a protective factor in the development of cancer. As humans are unable to synthesize folic acid endogenously, diet and supplementation is necessary to prevent deficiencies. In order to function properly within the body, folic acid must first be reduced by the enzyme dihydrofolate reductase (DHFR) into the cofactors dihydrofolate (DHF) and tetrahydrofolate (THF). This important pathway, which is required for de novo synthesis of nucleic acids and amino acids, is disrupted by anti-metabolite therapies such as [DB00563] as they function as DHFR inhibitors to prevent DNA synthesis in rapidly dividing cells, and therefore prevent the formation of DHF and THF. 



In general, folate serum levels below 5 ng/mL indicate folate deficiency, and levels below 2 ng/mL usually result in megaloblastic anemia.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21706, 'Lepsylief 800 mg 20 tabs.', 'ليبسيليف 800مجم 20 قرص', '380', NULL, 'Anti-epileptic', 'Eslicarbazepine', 'Horus for pharmaceutical products', 'Tab', 'أقراص', '800 mg', '2', 'يساعد في حالات الصرع __يقلل التشنجات الناتجة من الصرع __ يقلل نوبات التشنجات', NULL, NULL, NULL, 2434, '2022-04-09', 'Eslicarbazepine acetate is indicated for the treatment of partial-onset seizures in patients 4 years of age and older.[L4565]', 'Eslicarbazepine acetate is converted to the active metabolite eslicarbazepine which carries out its anticonvulsant activity. The exact mechanism of action is unknown, but it is thought to involve the inhibition of voltage-gated sodium channels. In in vitro electrophysiological studies, eslicarbazepine was shown to inhibit repeated neuronal firing by stabilizing the inactivated state of voltage-gated sodium channels and preventing their return to the activated state. In vitro studies also showed eslicarbazepine inhibiting T-type calcium channels, which likely also has a role in anticonvulsant activity. ', 'Eslicarbazepine acetate is associated with a dose- and concentration-dependant increase in heart rate and prolongation of PR interval.', 'DrugBank', 0, 0, 0);
