al gradients.[A190978]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20653, 'Hyseac k 18 - 40 ml', 'هيسياك ك 18 -40 مل', '309', NULL, 'Skin care', NULL, 'Uriage dermatological laboratories > biotech egypt', 'Unknown', 'غير محدد', '40 ml', '1', NULL, 'oily skin. blocked dilated pores. face. properties hyseac k18 is a keratoregulator and unclogs pores effectively to reduce the appearance of new blemishes thanks to a very well tolerated aha (18%)-bha complex. the liquorice extract it contains helps to li', NULL, NULL, 530, '2022-08-05', NULL, NULL, NULL, NULL, 0, 0, 0);
