INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2696, 'Clarikan s.r. 500mg 14 f.c. tab.', 'كلاريكان اس ار 500 مجم 14 قرص', '158', '108', 'Antibiotic.macrolide', 'Clarithromycin', 'Marcyrl co.', 'Tab', 'أقراص', '500mg', '2', 'مضاد حيوي', 'about clarithromycin it is a new macrolide antimicrobial drug.it is the most effective macrolide against haemophilus influenzae chlamydia trachomaitis and chlamydia pneumoniae. mechanism of action of clarithromycin clarithromycin is a member of macrolide', '6223003570651', NULL, 4607, '2025-03-25', 0, 'An alternative medication for the treatment of acute otitis media caused by <i>H. influenzae, M. catarrhalis, or S. pneumoniae</i> in patients with a history of type I penicillin hypersensitivity. Also for the treatment of pharyngitis and tonsillitis caused by susceptible <i>Streptococcus pyogenes</i>, as well as respiratory tract infections including acute maxillary sinusitis, acute bacterial exacerbations of chronic bronchitis, mild to moderate community-acquired pneuomia, Legionnaires'' disease, and pertussis. Other indications include treatment of uncomplicated skin or skin structure infections, helicobacter pylori infection, duodenal ulcer disease, bartonella infections, early Lyme disease, and encephalitis caused by <i>Toxoplasma gondii</i> (in HIV infected patients in conjunction with pyrimethamine). Clarithromycin may also decrease the incidence of cryptosporidiosis, prevent the occurence of α-hemolytic (viridans group) streptococcal endocarditis, as well as serve as a primary prevention for <i>Mycobacterium avium</i> complex (MAC) bacteremia or disseminated infections (in adults, adolescents, and children with advanced HIV infection).



Clarithromycin is indicated in combination with [vonoprazan] and [amoxicillin] as co-packaged triple therapy to treat _Helicobacter pylori_ (_H. pylori_) infection in adults.[L41695]', 'Clarithromycin is first metabolized to 14-OH clarithromycin, which is active and works synergistically with its parent compound. Like other macrolides, it then penetrates bacteria cell wall and reversibly binds to domain V of the 23S ribosomal RNA of the 50S subunit of the bacterial ribosome, blocking translocation of aminoacyl transfer-RNA and polypeptide synthesis. Clarithromycin also inhibits the hepatic microsomal CYP3A4 isoenzyme and P-glycoprotein, an energy-dependent drug efflux pump.', 'Clarithromycin is a macrolide antibiotic whose spectrum of activity includes many gram-positive (<i>Staphylococcus aureus, S. pneumoniae, and S. pyogenes</i>) and gram-negative aerobic bacteria (<i>Haemophilus influenzae, H. parainfluenzae, and Moraxella catarrhalis</i>), many anaerobic bacteria, some mycobacteria, and some other organisms including <i>Mycoplasma, Ureaplasma, Chlamydia, Toxoplasma</i>, and <i>Borrelia</i>. Other aerobic bacteria that clarithromycin has activity against include <i>C. pneumoniae and M. pneumoniae</i>. Clarithromycin has an in-vitro activity that is similar or greater than that of erythromycin against erythromycin-susceptible organisms. Clarithromycin is usually bacteriostatic, but may be bactericidal depending on the organism and the drug concentration.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2697, 'Clarimycin 500 mg 14 f.c. tab.', 'كلاريميسين 500مجم 10 اقراص', '71', '33.6', 'Antibiotic.macrolide', 'Clarithromycin', 'Memphis', 'Tab', 'أقراص', '500 mg', '1', NULL, 'about clarithromycin it is a new macrolide antimicrobial drug.it is the most effective macrolide against haemophilus influenzae chlamydia trachomaitis and chlamydia pneumoniae. mechanism of action of clarithromycin clarithromycin is a member of macrolide', '6221026011700', NULL, 2485, '2023-12-26', 0, 'An alternative medication for the treatment of acute otitis media caused by <i>H. influenzae, M. catarrhalis, or S. pneumoniae</i> in patients with a history of type I penicillin hypersensitivity. Also for the treatment of pharyngitis and tonsillitis caused by susceptible <i>Streptococcus pyogenes</i>, as well as respiratory tract infections including acute maxillary sinusitis, acute bacterial exacerbations of chronic bronchitis, mild to moderate community-acquired pneuomia, Legionnaires'' disease, and pertussis. Other indications include treatment of uncomplicated skin or skin structure infections, helicobacter pylori infection, duodenal ulcer disease, bartonella infections, early Lyme disease, and encephalitis caused by <i>Toxoplasma gondii</i> (in HIV infected patients in conjunction with pyrimethamine). Clarithromycin may also decrease the incidence of cryptosporidiosis, prevent the occurence of α-hemolytic (viridans group) streptococcal endocarditis, as well as serve as a primary prevention for <i>Mycobacterium avium</i> complex (MAC) bacteremia or disseminated infections (in adults, adolescents, and children with advanced HIV infection).



Clarithromycin is indicated in combination with [vonoprazan] and [amoxicillin] as co-packaged triple therapy to treat _Helicobacter pylori_ (_H. pylori_) infection in adults.[L41695]', 'Clarithromycin is first metabolized to 14-OH clarithromycin, which is active and works synergistically with its parent compound. Like other macrolides, it then penetrates bacteria cell wall and reversibly binds to domain V of the 23S ribosomal RNA of the 50S subunit of the bacterial ribosome, blocking translocation of aminoacyl transfer-RNA and polypeptide synthesis. Clarithromycin also inhibits the hepatic microsomal CYP3A4 isoenzyme and P-glycoprotein, an energy-dependent drug efflux pump.', 'Clarithromycin is a macrolide antibiotic whose spectrum of activity includes many gram-positive (<i>Staphylococcus aureus, S. pneumoniae, and S. pyogenes</i>) and gram-negative aerobic bacteria (<i>Haemophilus influenzae, H. parainfluenzae, and Moraxella catarrhalis</i>), many anaerobic bacteria, some mycobacteria, and some other organisms including <i>Mycoplasma, Ureaplasma, Chlamydia, Toxoplasma</i>, and <i>Borrelia</i>. Other aerobic bacteria that clarithromycin has activity against include <i>C. pneumoniae and M. pneumoniae</i>. Clarithromycin has an in-vitro activity that is similar or greater than that of erythromycin against erythromycin-susceptible organisms. Clarithromycin is usually bacteriostatic, but may be bactericidal depending on the organism and the drug concentration.', 'DrugBank', 0, 0, 0);
