INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2897, 'Bonerand 30ml oral drops', 'بون راند نقط بالفم 30 كل', '79', NULL, 'Dietary supplement', 'Vitamin d3', 'Bio brand', 'Drops', 'نقط', '30ml', '1', NULL, NULL, NULL, NULL, 857, '2025-02-11', 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2901, 'Colircusi cicloplejico 1% eye drops 10 ml', 'كولير كوزي سيكلوبلجيكو 1% قطرة عين 10 مل', '19', NULL, 'Mydriatic', 'Cyclopentolate', 'Novartis scientific office', 'Drops', 'نقط', '1%', '1', NULL, NULL, NULL, NULL, 1076, '2022-07-22', 0, 'Used mainly to produce mydriasis and cycloplegia for diagnostic purposes.', 'By blocking muscarinic receptors, cyclopentolate produces dilatation of the pupil (mydriasis) and prevents the eye from accommodating for near vision (cycloplegia).', 'Cyclopentolate is an anti-muscarinic in the same class as atropine and scopolamine. Cyclopentolate blocks the receptors in the muscles of the eye (muscarinic receptors). These receptors are involved controlling the pupil size and the shape of the lens. Cyclopentolate thus induces relaxation of the sphincter of the iris and the ciliary muscles. When applied topically to the eyes, it causes a rapid, intense cycloplegic and mydriatic effect that is maximal in 15 to 60 minutes; recovery usually occurs within 24 hours. The cycloplegic and mydriatic effects are slower in onset and longer in duration in patients who have dark pigmented irises.', 'DrugBank', 0, 0, 0);
