INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6737, 'Moodamash 10 mg 14 f.c. tabs.', 'مودا ماش 10 مجم 14 قرص', '63', NULL, 'Psychiatric.antidepressants', 'Escitalopram', 'Mash premiere', 'Tab', 'أقراص', '10 mg', '1', NULL, NULL, NULL, NULL, 1833, '2022-08-14', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Escitalopram is a selective serotonin re-uptake inhibitor (SSRI) and the S-enantiomer of racemic [citalopram].[A185420] It is used to restore serotonergic function in the treatment of depression and anxiety.[L8513,L8516,L8522] Escitalopram is approximately 150 times more potent than citalopram’s R-enantiomer and is responsible for the vast majority of citalopram’s clinical activity, with some evidence suggesting that the R-enantiomer of racemic citalopram actively dampens the activity of escitalopram rather than existing simply as an inactive enantiomer.[A39738,A185819] Amongst SSRIs, escitalopram exerts the highest degree of selectivity for the serotonin transporter (SERT) relative to other off-targets which may explain its lower rates of adverse effects as compared to other agents in this class.[A185726] Escitalopram also differentiates itself from other SSRIs via allosteric action on its target - this may be the mechanism responsible for its observed superior efficacy and faster onset compared to other SSRIs.[A185825,A185726,A185822]', '["N06AB10"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6738, 'Morfblock 50 mg 20 f.c.tabs.', 'مورفبلوك 50 مجم 20 قرص', '360', NULL, 'Analgesics.narcotic opioid', 'Naltrexone', 'Copad egypt', 'Tab', 'أقراص', '50 mg', '2', NULL, NULL, NULL, NULL, 1222, '2022-08-14', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Derivative of noroxymorphone that is the N-cyclopropylmethyl congener of naloxone. It is a narcotic antagonist that is effective orally, longer lasting and more potent than naloxone, and has been proposed for the treatment of heroin addiction. The FDA has approved naltrexone for the treatment of alcohol dependence.', '["N02AA56", "N07BB04", "A08AA62"]', '[]');
