INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6776, 'Kanzar 25mg 7 f.c.tab.', 'كانزار 25مجم 7 اقراص', '11', NULL, 'Antihypertensive.angiotensin blocker', 'Losartan potassium', 'Hikma pharma', 'Tab', 'أقراص', '25mg', '1', NULL, 'about losartan potassium an angiotensin ii receptor antagonist antihypertensive. mechanism of action of losartan potassium it is a competitive antagonist of angiotensin-2 at at-1 receptor. it decreases peripheral resistance and lowers bp in hypertensive i', NULL, NULL, 819, '2022-10-24', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Losartan is an angiotensin II receptor blocker (ARB) used to treat hypertension.[L7423] Angiotensin-converting enzyme (ACE) inhibitors are used for a similar indication but are associated with a cough.[L7423] When patients with ACE inhibitor associated coughs are switched to ARBs like losartan, they have an incidence of cough similar to placebo or [hydrochlorothiazide].[L7423] Losartan is available as losartan potassium oral tablets as well as a combination tablet of losartan potassium and hydrochlorothiazide.[L7423,L7426] Patients taking losartan should have their renal function and potassium levels monitored.[L7423] Losartan was granted FDA approval on 14 April 1995.[L7423]', '["C09CA01", "C09DA01", "C09DB06"]', '[]');
