INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6792, 'Kast 10mg 10 f.c. tab', 'كاست 10مجم 10 اقراص', '59', '52', 'Asthma.anti-inflammatory.leukotriene antagonist', 'Montelukast', 'Hikma pharma', 'Tab', 'أقراص', '10mg', '1', 'علاج أزمات الصدر وصعوبة وضيق التنفس', NULL, '6221000000867', NULL, 6347, '2025-01-22', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Montelukast was first approved for clinical use by the US FDA in 1998 as Merck''s brand name Singulair.[L6301] The medication is a member of the leukotriene receptor antagonist (LTRA) category of drugs.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] Although capable of demonstrating effectiveness, the use of such LTRAs like montelukast is typically in addition to or complementary with the use of inhaled corticosteroids or other agents in asthma step therapy.[A178645] Regardless, in 2008-2009, there were FDA-led investigations into the possibility of montelukast to elicit neuropsychiatric effects like agitation, hallucinations, suicidal behaviour, and others in individuals who used the medication.[A178651] And although these kinds of effects are currently included in the official prescribing information for montelukast,[L6301,L6304,L6307,L6310,L6325,L6328,L6331] the drug still sees extensive use worldwide via millions of prescriptions annually and has since become available as a generic and as a brand name product.', '["R03DC03", "R03DC53"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6794, 'Kast 5 mg 14 chew. tab.', 'كاست 5مجم 14 قرص مضغ', '53', '42', 'Asthma.anti-inflammatory.leukotriene antagonist', 'Montelukast', 'Hikma pharma', 'Tab', 'أقراص', '5 mg', '2', NULL, 'about montelukast leukotriene(cyslt1) receptor antagonist antiinflammatory antiasthma. mechanism of action of montelukast montelukast is competitively and selectively antagonizes cyslt1 receptor (leukotrienes receptor) mediated bronchoconstriction.montelu', '6221000000874', NULL, 3105, '2023-09-17', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Montelukast was first approved for clinical use by the US FDA in 1998 as Merck''s brand name Singulair.[L6301] The medication is a member of the leukotriene receptor antagonist (LTRA) category of drugs.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] Although capable of demonstrating effectiveness, the use of such LTRAs like montelukast is typically in addition to or complementary with the use of inhaled corticosteroids or other agents in asthma step therapy.[A178645] Regardless, in 2008-2009, there were FDA-led investigations into the possibility of montelukast to elicit neuropsychiatric effects like agitation, hallucinations, suicidal behaviour, and others in individuals who used the medication.[A178651] And although these kinds of effects are currently included in the official prescribing information for montelukast,[L6301,L6304,L6307,L6310,L6325,L6328,L6331] the drug still sees extensive use worldwide via millions of prescriptions annually and has since become available as a generic and as a brand name product.', '["R03DC03", "R03DC53"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6795, 'Katalar 50mg vial 10 ml', 'كاتالار 50مجم فيال 10 مل', '33', NULL, 'Narcotic', 'Ketamine', 'Pfizer', 'Vial', 'فيال', '50mg', '1', NULL, NULL, NULL, NULL, 3493, '2023-01-22', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Ketamine is an NMDA receptor antagonist with a potent anesthetic effect.[A31869] It was developed in 1963 as a replacement for phencyclidine (PCP) by Calvin Stevens at Parke Davis Laboratories. It started being used for veterinary purposes in Belgium and in 1964 was proven that compared to PCP, it produced minor hallucinogenic effects and shorter psychotomimetic effects. It was FDA approved in 1970, and from there, it has been used as an anesthetic for children or patients undergoing minor surgeries but mainly for veterinary purposes.[L1332]', '["N01AX03"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6796, 'Katrex 0.8% syrup 15 ml', 'كاتركس شراب 15 مل', '23', '15', 'Anthelmintic', 'Levamisole', 'Kahira', 'Syrup', 'شراب', '0.8%', '1', NULL, 'about levamisole synthetic imidazothiazole derivative biological response modifier anthelmintic antineoplastic adjunct. mechanism of action of levamisole levamisole is the levo isomer of tetramisole which is used as an anthelminthic agent. it exerts its a', '6221068402962', NULL, 1524, '2025-08-01', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Levamisole is an antihelminthic drug that was commonly used for the treatment of parasitic, viral, and bacterial infections. [A178117] It was manufactured by _Janssen_ and first used in 1969 as an agent to treat worm infestations[A178123] Levamisole was approved by the FDA in 1990 as an adjuvant treatment for colon cancer.[L6232] Prior to this, levamisole was used as an antirheumatic therapy in the 1970s and 1980s for patients with rheumatoid arthritis.[A178123] 

