INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (7171, 'Gamps sleep water 50 ml', 'جامبس سليب ووتر 50 مل', '162', NULL, 'Dietary supplement', 'Melatonin', 'Dopamine pharma', 'Amp', 'أمبول', '50 ml', '1', NULL, NULL, NULL, NULL, 695, '2025-08-25', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Melatonin is a biogenic amine that is found in animals, plants and microbes. Aaron B. Lerner of Yale University is credited for naming the hormone and for defining its chemical structure in 1958.  In mammals, melatonin is produced by the pineal gland. The pineal gland is small endocrine gland, about the size of a rice grain and shaped like a pine cone (hence the name), that is located in the center of the brain (rostro-dorsal to the superior colliculus) but outside the blood-brain barrier. The secretion of melatonin increases in darkness and decreases during exposure to light, thereby regulating the circadian rhythms of several biological functions, including the sleep-wake cycle. In particular, melatonin regulates the sleep-wake cycle by chemically causing drowsiness and lowering the body temperature.  Melatonin is also implicated in the regulation of mood, learning and memory, immune activity, dreaming, fertility and reproduction. Melatonin is also an effective antioxidant.  Most of the actions of melatonin are mediated through the binding and activation of melatonin receptors. Individuals with autism spectrum disorders (ASD) may have lower than normal levels of melatonin. A 2008 study found that unaffected parents of individuals with ASD also have lower melatonin levels, and that the deficits were associated with low activity of the ASMT gene, which encodes the last enzyme of melatonin synthesis. Reduced melatonin production has also been proposed as a likely factor in the significantly higher cancer rates in night workers.', '["N05CH01"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (7172, 'Lee-flox 250mg 5 tab.', 'لي-فلوكس 250مجم 5 اقراص', '86', '59.5', 'Antibiotic.quinolone', 'Levofloxacin', 'Pharopharma', 'Tab', 'أقراص', '250mg', '1', NULL, NULL, '6221094076182', NULL, 967, '2024-11-08', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Levofloxacin is a fluoroquinolone antibiotic and the optical S-(-) isomer of racemic [ofloxacin].[A190663] It reportedly carries 8 to 128-fold more activity against both gram-negative and gram-positive bacteria compared to R-(+)-ofloxacin[A190663] and remains stereochemically stable following administration (i.e. it does not invert to the inactive isomer).[L11638] Levofloxacin, along with other quinolones such as [gatifloxacin] and [moxifloxacin], is a member of the third generation of fluoroquinolones, colloquially referred to as the "respiratory quinolones" due to improved activity against gram-positive bacteria commonly implicated in respiratory infections.[A31453,A190756]

Levofloxacin was first approved by the FDA in 1996, and was approved in Canada and several South American countries soon after.[A190663]', '["J01MA12", "A02BD10", "J01RA05", "S01AE05"]', '[]');
