INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3182, 'Curazole 2% topical cream 15 gm', 'كيورازول كريم 15 جرام', '7', NULL, 'Antifungals.imidazoles', 'Ketoconazole', 'Eva pharma', 'Cream', 'كريم', '2%', '1', NULL, 'about ketoconazole imidazole derivative a broad spectrum antifungal. mechanism of action of ketoconazole ketoconazole is fungicidal or fungistatic depending on concentrations. it inhibits the conversion of lanosterol to 14 demethyl lanosterol by inhibitin', '6223002450060', NULL, 1757, '2023-01-09', 0, 'Ketoconazole is used in the treatment or prevention of fungal infections including blastomycosis, candidiasis, coccidioidomycosis, histoplasmosis, chromomycosis, and paracoccidioidomycosis.[FDA Label] In Europe, it is also used in the treatment of endogenous Cushing''s syndrome.[L7736]', 'Ketoconazole interacts with 14-&alpha;-sterol demethylase, a cytochrome P-450 enzyme necessary for the conversion of lanosterol to ergosterol.[A181802,T116] This results in inhibition of ergosterol synthesis and increased fungal cellular permeability due to reduced amounts of ergosterol present in the fungal cell membrane. This metabolic inhibition also results in accumulation of 14α-methyl-3,6-diol, a toxic metabolite. The increase in membrane fluidity is also thought to produce impairment of membrane-bound enzyme systems as components become less closely packed.', 'Ketoconazole, similarly to other azole antifungals, is a fungistatic agent which causes growth arrest in fungal cells thereby preventing growth and spread of the fungus throughout the body.[T116]', 'DrugBank', 0, 0, 0);
