INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21258, 'Karvex 25mg 30 tab.', 'كارفيكس 25 مجم 30 قرص', '28', NULL, 'Antihypertensive.beta blocker.unselective', 'Carvedilol', 'Sigma', 'Tab', 'أقراص', '25mg', '3', NULL, 'about carvedilol beta blocker with alpha- adrenergic blocking activity antihypertensive and anti angina. mechanism of action of carvedilol carvedilol is an adrenergic antagonist capable of blocking alpha-1 beta-1 and beta-2 receptors. it produces vasodila', NULL, NULL, 753, '2022-07-30', 'Carvedilol is indicated to treat mild to severe heart failure, left ventricular dysfunction after myocardial infarction with ventricular ejection fraction ≤40%, or hypertension.[L7889,L7892]', 'Carvedilol inhibits exercise induce tachycardia through its inhibition of beta adrenoceptors.[A182306] Carvedilol''s action on alpha-1 adrenergic receptors relaxes smooth muscle in vasculature, leading to reduced peripheral vascular resistance and an overall reduction in blood pressure.[A1929,A182306] At higher doses, calcium channel blocking and antioxidant activity can also be seen.[A1929] The antioxidant activity of carvedilol prevents oxidation of low density lipoprotein and its uptake into coronary circulation.[A1929]', 'Carvedilol reduces tachycardia through beta adrenergic antagonism and lowers blood pressure through alpha-1 adrenergic antagonism.[L7889,L7892] It has a long duration of action as it is generally taken once daily and has a broad therapeutic index as patients generally take 10-80mg daily.[L7889,L7892] Patients taking carvedilol should not abruptly stop taking this medication as this may exacerbate coronary artery disease.[L7889,L7892]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13587, 'Agrelocit 50 mg 20 tab.', 'اجريلوسيت 50 مجم 20 قرص لعلاج الاكتئاب والتوتر', '35', NULL, 'Psychiatric.antidepressants', 'Sertraline', 'Memphis > atm pharmaceutical industries', 'Tab', 'أقراص', '50 mg', '2', NULL, 'about sertraline selective serotonin reuptake inhibitor (ssri) antidepressant antidepressant mechanism of action of sertraline sertraline blocks the re-uptake of neurotransmitter serotonin from the synapse in to the presynaptic nerve terminal in the cns.', NULL, NULL, 1013, '2022-07-15', 'Sertraline is indicated for the management of major depressive disorder (MDD), post-traumatic stress disorder (PTSD), obsessive-compulsive disorder (OCD), panic disorder (PD), premenstrual dysphoric disorder (PMDD), and social anxiety disorder (SAD).[L9016] Common off-label uses for sertraline include the prevention of post stroke depression[A187078], generalized anxiety disorder (GAD), fibromyalgia, premature ejaculation, migraine prophylaxis, diabetic neuropathy, and neurocardiogenic syncope.[L5227]

', 'Sertraline selectively inhibits the reuptake of serotonin (5-HT) at the presynaptic neuronal membrane, thereby increasing serotonergic activity. This results in an increased synaptic concentration of serotonin in the CNS, which leads to numerous functional changes associated with enhanced serotonergic neurotransmission.[T28,L9016] These changes are believed to be responsible for the antidepressant action and beneficial effects in obsessive-compulsive (and other anxiety related disorders). It has been hypothesized that obsessive-compulsive disorder, like depression, is also caused by the disregulation of serotonin.[A187165]



In animal studies, chronic administration of sertraline results in down-regulation of brain norepinephrine receptors.[L9016] Sertraline displays affinity for sigma-1 and 2 receptor binding sites[A31941], but binds with stronger affinity to sigma-1 binding sites.[A31940] In vitro, sertraline shows little to no affinity for GABA, dopaminergic, serotonergic (5HT1A, 5HT1B, 5HT2), or benzodiazepine receptors.[L9016] It exerts weak inhibitory actions on the neuronal uptake of norepinephrine and dopamine[A1844] and exhibits no inhibitory effects on the monoamine oxidase enzyme.[L9016]', 'Sertraline improves or relieves the symptoms of depression, OCD, post-traumatic stress disorder, obsessive-compulsive disorder, panic disorder, and premenstrual dysphoric disorder via the inhibition of serotonin reuptake.[A187075,L9016] Clinical studies have shown that it improves cognition in depressed patients.[A1846] It has less sedative, anticholinergic, and cardiovascular effects than the tricyclic antidepressant drugs because it does not exert significant anticholinergic, antihistamine, or adrenergic (alpha1, alpha2, beta) blocking activity.[A1844] The onset of action and beneficial effects are usually noticed after 4-6 weeks, for reasons that are not fully understood and currently under investigation.[A188493,L10677]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24241, 'Ordesca 2.5mg/5ml syp. 120 ml', 'اورديسكا 2.5مجم/5مل شراب 120مل', '9', NULL, 'Antihistamine.antiallergy', 'Desloratadine', 'Rameda', 'Unknown', 'غير محدد', '2.5mg', '1', NULL, 'about desloratadine second generation long-acting tricyclic h1 antagonist antihistamine. mechanism of action of desloratadine this second generation antihistamine has a selective peripheral h1-antagonist action.desloratadine competes with free histamine f', NULL, NULL, 742, '2022-07-24', 'For the relief of symptoms of seasonal allergic rhinitis, perennial (non-seasonal) allergic rhinitis. Desloratidine is also used for the sympomatic treatment of pruritus and urticaria (hives) associated with chronic idiopathic urticaria.', 'Like other H1-blockers, Desloratadine competes with free histamine for binding at H<sub>1</sub>-receptors in the GI tract, uterus, large blood vessels, and bronchial smooth muscle. This blocks the action of endogenous histamine, which subsequently leads to temporary relief of the negative symptoms (eg. nasal congestion, watery eyes) brought on by histamine.', 'Desloratadine is a long-acting second-generation H<sub>1</sub>-receptor antagonist which has a selective and peripheral H1-antagonist action. Histamine is a chemical that causes many of the signs that are part of allergic reactions, such as the swelling of tissues. Histamine is released from histamine-storing cells (mast cells) and attaches to other cells that have receptors for histamine. The attachment of the histamine to the receptors causes the cell to be "activated," releasing other chemicals which produce the effects that we associate with allergies. Desloratadine blocks one type of receptor for histamine (the H1 receptor) and thus prevents activation of cells by histamine. Unlike most other antihistamines, Desloratadine does not enter the brain from the blood and, therefore, does not cause drowsiness.', 'DrugBank', 0, 0, 0);
