INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2584, 'Cidotestone 100mg amp.', 'سيدوتستون 100مجم امبول', '8', NULL, 'Androgen', 'Testosterone enanthate+testosterone propionate', 'Cid', 'Amp', 'أمبول', '100mg', '1', 'تعزيز نمو الاعضاءالتناسلية للذكور -- تعزيز ظهور الصفات الذكورية لديهم', NULL, '6221043012674', NULL, 1899, '2023-02-03', 'Testosterone is indicated to treat primary hypogonadism and hypogonadotropic hypogonadism.[L8983,L8935,L8938,L8986,L8989,L8992,L8995]', 'The androgen receptor exists in the cytoplasm bound to the heat shock proteins HSP90, HSP70, and other chaperones.[A187114] After binding to an androgen, the androgen receptor dissociates from HSP90 and undergoes a conformational change to slow the rate of dissociation from the androgen receptor.[A187114] The androgen-receptor complex is transported into the nucleus where it binds to DNA and recruits other transcriptional regulators to form a pre-initiation complex and eventually induce expression of specific genes.[A187114]



Testosterone and its active metabolite dihydrotestosterone (DHT) antagonize the androgen receptor to develop masculine sex organs including the prostate, seminal vesicles, penis, and scrotum.[A187111,L8983,L8935,L8938,L8986,L8989,L8992,L8995]



Antagonism of the androgen receptor is also responsible for the development of secondary sexual characteristics including facial and body hair, enlargement of the larynx, thickening of the vocal cords, and changes in muscle and fat distribution.[A187111,L8983,L8935,L8938,L8986,L8989,L8992,L8995]', 'Testosterone antagonizes the androgen receptor to induce gene expression that causes the growth and development of masculine sex organs and secondary sexual characteristics.[A187114,L8983,L8935,L8938,L8986,L8989,L8992,L8995] The duration of action of testosterone is variable from patient to patient with a half life of 10-100 minutes.[L8983,L8935,L8938,L8986,L8989,L8992,L8995] The therapeutic index is wide considering the normal testosterone levels in an adult man range from 300-1000ng/dL.[L8983,L8938,L8986,L8989,L8992,L8995] Counsel patients regarding the risk of secondary exposure of testosterone topical products to children.[L8983,L8938,L8986,L8989,L8992]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17834, 'Dopergin 0.2mg 20 scored tab.(n/a)', 'دوبرجين 0.2 مجم 20 قرص', '32', NULL, 'Dopamine agonist', 'Lisuride hydrogen maleate', 'Delpharm lille s.a.s-france > bayer schering', 'Tab', 'أقراص', '0.2mg', '2', NULL, 'about lisuride dopamine agonist semisynthetic ergot derivative mechanism of action of lisuride lisuride is a dopamine and serotonin receptor partial agonist. it has a high affinity for the dopamine d2 d3 and d4 receptors as well as serotonin 5-ht1a[1] and', NULL, NULL, 924, '2022-08-03', 'For the management of Parkinson''s Disease', 'Lisuride is an anti-Parkinson drug chemically related to the dopaminergic ergoline Parkinson''s drugs. Lisuride binds to the 5-HT(1A) and 5-HT(2A/2C) receptors.  It is also thought to bind to the dopamine receptor and to act as a dopamine agonist. Evidence has also emerged that Lisuride also binds to the Histamine H1 receptor. ', 'There is evidence that lisuride lowers prolactin levels and, in low doses, prevents migraine attacks. ', 'DrugBank', 0, 0, 0);
