INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22209, 'Magnabiotic 230mg/5ml pd. for oral susp. 80ml', 'ماجنابيوتيك 230مجم/5مل شراب 80مل', '22', NULL, 'Penicillins.penicillin with b-lactamase inhibitor', 'Amoxicillin+clavulanic acid', 'Sedico', 'Susp', 'معلق', '230mg', '1', NULL, 'amoxicillin is an analog of ampicillin derived from the basic penicillin nucleus 6-aminopenicillanic acid clavulanic acid is produced by the fermentation of streptomyces clavuligerus. it is a beta-lactam structurally related to the penicillins and possess', '6221042492019', NULL, 1239, '2022-07-17', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18221, 'Embanodex 0.5mg/ml syrup 100 ml', 'ايمبانوديكس 0.5مجم/مل شراب 100 مل', '14', NULL, 'Antihistamine.antiallergy', 'Desloratadine', 'Egpi > delta pharma', 'Syrup', 'شراب', '0.5mg', '1', NULL, 'about desloratadine second generation long-acting tricyclic h1 antagonist antihistamine. mechanism of action of desloratadinesthis second generation antihistamine has a selective peripheral h1-antagonist action.desloratadine competes with free histamine f', '6224008722052', NULL, 1032, '2022-07-07', 'For the relief of symptoms of seasonal allergic rhinitis, perennial (non-seasonal) allergic rhinitis. Desloratidine is also used for the sympomatic treatment of pruritus and urticaria (hives) associated with chronic idiopathic urticaria.', 'Like other H1-blockers, Desloratadine competes with free histamine for binding at H<sub>1</sub>-receptors in the GI tract, uterus, large blood vessels, and bronchial smooth muscle. This blocks the action of endogenous histamine, which subsequently leads to temporary relief of the negative symptoms (eg. nasal congestion, watery eyes) brought on by histamine.', 'Desloratadine is a long-acting second-generation H<sub>1</sub>-receptor antagonist which has a selective and peripheral H1-antagonist action. Histamine is a chemical that causes many of the signs that are part of allergic reactions, such as the swelling of tissues. Histamine is released from histamine-storing cells (mast cells) and attaches to other cells that have receptors for histamine. The attachment of the histamine to the receptors causes the cell to be "activated," releasing other chemicals which produce the effects that we associate with allergies. Desloratadine blocks one type of receptor for histamine (the H1 receptor) and thus prevents activation of cells by histamine. Unlike most other antihistamines, Desloratadine does not enter the brain from the blood and, therefore, does not cause drowsiness.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7641, 'Msm 1000 mg 30 tabs', 'ام.اس.ام 100 مجم 30 قرص', '23', NULL, 'Joints care', 'Methyl sulphonyl methane', 'Eva pharma', 'Tab', 'أقراص', '1000 mg', '1', 'يستخدم في حالات التهاب المفاصل', NULL, '6224009763146', NULL, 7813, '2023-02-19', 'For topical use, in combination with 570 to 670 nm wavelength red light illumination, in the treatment of non-hyperkeratotic actinic keratoses of the face and scalp in immunocompetent patients when used in conjunction with lesion preparation (debridement using a sharp dermal curette).', 'Photosensitization following application of methyl aminolevulinate cream occurs through the metabolic conversion of methyl aminolevulinate (prodrug) to photoactive porphyrins (PAP), which accumulates in the skin lesions to which the cream has been applied. When exposed to light of appropriate wavelength and energy, the accumulated photoactive porphyrins produce a photodynamic reaction, resulting in a cytotoxic process dependent upon the simultaneous presence of oxygen. The absorption of light results in an excited state of porphyrin molecules, and subsequent spin transfer from photoactive porphyrins to molecular oxygen generates singlet oxygen, which can further react to form superoxide and hydroxyl radicals.', 'After topical application of methyl aminolevulinate, porphyrins will accumulate intracellularly in the treated skin lesions. The intracellular porphyrins (including PpIX) are photoactive, fluorescing compounds and, upon light activation in the presence of oxygen, singlet oxygen is formed which causes damage to cellular compartments, in particular the mitochondria. Light activation of accumulated porphyrins leads to a photochemical reaction and thereby phototoxicity to the light-exposed target cells.', 'DrugBank', 0, 0, 0);
