INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23111, 'Mupirax 2% oint. 30 gm', 'ميوبيراكس 2% مرهم 30 جم', '118', '77', 'Anti-bacterial.protein synthesis inhibitor', 'Mupirocin', 'Global napi pharmaceuticals', 'Oint', 'مرهم', '2%', '1', NULL, 'about mupirocin a naturally antibiotic isolated from pseudomonas fluorescens antibiotic. mechanism of action of mupirocin mupirocin exerts its action by reversibly binding to isoleucyl transfer rna synthetase and inhibits the enzyme activity. this enzyme', '6223002144907', NULL, 3073, '2024-09-18', 'Indicated for the treatment of impetigo and secondary skin infections, leading to traumatic skin lesions, due to _Staphylococcus aureus_ and _Streptococcus pyogenes_.[L10580] ', 'Mupirocin specifically and reversibly binds to bacterial isoleucyl transfer-RNA (tRNA) synthetase, which is an enzyme that promotes the conversion of isoleucine and tRNA to isoleucyl-tRNA. Inhibition of this enzyme subsequently leads to the inhibition of the bacterial protein and RNA synthesis.[A178531] Mupirocin is bacteriostatic at lower concentrations but it exerts bactericidal effects with prolonged exposure, killing 90-99% of susceptible bacteria over a 24 hour period.[A178552]', 'Mupirocin is reported to be active against susceptible aerobic gram-positive cocci, such as _Staphylococcus aureus_, _Staphylococcus epidermidis_, and other beta-hemolytic streptococci_Streptococcus pyogenes_.[A178594] It mediates its antibacterial activity by inhibiting the bacterial protein synthesis and formation of bacterial proteins essential for survival. The minimum bactericidal concentration (MBC) against relevant pathogens is generally eight-fold to thirty-fold higher than the minimum inhibitory concentration (MIC).[L10580] In one clinical study investigating the therapeutic effectiveness of topical mupirocin in impetigo, the therapeutic response rate was about 94 to 98% after one week following the end of therapy.[L10580] In clinical studies of patients with primary and secondary skin infections, both elimination of the bacterial pathogen and clinical cure or improvement hav been demonstrated in over 90% of patients receiving topical mupirocin.[A178591] Mupirocin resistance as high as 81% has been reported previously.[A178600] Resistance to mupirocin, which occurs more frequently in methicillin-resistant than methicillin-susceptible staphylococci, may occur with the production of a modified isoleucyl-tRNA synthetase, or the acquisition of, by genetic transfer, a plasmid mediating a new isoleucyl-tRNA synthetase.[L10580]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2311, 'Celia expert 1 milk 400 gm', 'مسحوق لبن سيليا 1 400جم', '100', NULL, 'Milk products.first stage (age 0-6 months)', 'Milk formula stage 1', 'Lactalis international > united trading', 'Unknown', 'غير محدد', '400 gm', '1', 'بودرة لبن اطفال مرحلة اةلي من سن شهر الي 6 شهور -- مكمل غذائي -- تقوية المناعة -- امداد جسم الرضيع بالعناصر الغذائية الهامة التي يحتاجها', 'not to replace mother s milk. for infants from birth to 6 months age. to be used within one month from opening.', '3551100569142', NULL, 1245, '2022-12-03', NULL, NULL, NULL, NULL, 0, 0, 0);
