INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (10762, 'Spasmofen 3 amp. for i.m. inj.', 'سبازموفين 3 امبولات للحقن عضل', '54', '39', 'Antispasmodic', 'Hyoscine+ketoprofen', 'Amriya', 'Inj', 'حقن', NULL, '3', 'مضاد للتقلصا ويعمل على تخفيف الألم خاصة ألم التهاب المفاصل.', 'about hyoscine butyl bromide anti-cholinergic a tropane alkaloid antispasmodic in gi and gu spasm. mechanism of action of hyoscine butyl bromide hyoscine butyl bromide is an anticholinergic drug. it exerts its action by inhibiting muscarinic (cholinergic)', '6221075030028', NULL, 11516, '2025-08-04', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7082, 'Lamisil 1% spray 30 ml', 'لاميزيل 1% سبراي 30 مل', '43', NULL, 'Antifungals.allylamines', 'Terbinafine', 'Novartis > glaxo smithkline', 'Spray', 'بخاخ', '1%', '1', NULL, 'about terbinafine a synthetic allylamine derivative antifungal. mechanism of action of terbinafine terbinafine is a fungicidal drug. it interfere the conversion of squalene to ergosterol by inhibiting the enzyme squalene epoxidase.this results in accumula', NULL, NULL, 3104, '2022-10-15', 'Terbinafine hydrochloride is indicated to treat fungal skin and nail infections caused by _Trichophyton_ species, _Microsporum canis_, _Epidermophyton floccosum_,[L9068] and _Tinea_ species.[L9065] Terbinafine hydrochloride also treats yeast infections of the skin caused by _Candida_ species and _Malassezia furfur_.[L9068]', 'Terbinafine inhibits the enzyme squalene monooxygenase (also called squalene epoxidase), preventing the conversion of squalene to 2,3-oxydosqualene, a step in the synthesis of ergosterol.[A1279,A1281,L9068] This inhibition leads to decreased ergosterol, which would normally be incorporated into the cell wall, and accumulation of squalene.[A1281,L9068]



Generation of a large number of squalene containing vesicles in the cytoplasm may leach other lipids away from, and further weaken, the cell wall.[A1281]', 'Terbinafine is an allylamine antifungal that inhibits squalene epoxidase (also known as squalene monooxygenase) to prevent the formation of ergosterol and cause an accumulation of squalene, weakening the cell wall of fungal cells.[A1279,A1281,L9068] Terbinafine distributes into tissues and has a long terminal elimination half life, so the duration of action is long.[A1279] Overdose with terbinafine is rare, even above the therapeutic dose, so the therapeutic index is wide.[L9065,L9068] Patients taking oral terbinafine should have liver function tests performed prior to treatment to reduce the risk of liver injury.[L9065]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27945, 'Taxol 100mg/16.7ml vial', 'تاكسول 100مجم/16.7مل فيال', '1,400', NULL, 'Antineoplastic', 'Paclitaxel', 'Bristol-myers squibb', 'Vial', 'فيال', '100mg', '1', NULL, 'about paclitaxel taxane derivative microtubule inhibitor(mitotic inhibitor) antineoplastic. mechanism of action of paclitaxel drug binds specifically to the beta- tubuline and then enhances the polymerization of tubuline to stable microtubules. it also in', NULL, NULL, 1629, '2022-07-14', 'Used in the treatment of Kaposi''s sarcoma and cancer of the lung, ovarian, and breast. Abraxane® is specfically indicated for the treatment of metastatic breast cancer and locally advanced or metastatic non-small cell lung cancer.', 'Paclitaxel interferes with the normal function of microtubule growth. Whereas drugs like colchicine cause the depolymerization of microtubules in vivo, paclitaxel arrests their function by having the opposite effect; it hyper-stabilizes their structure. This destroys the cell''s ability to use its cytoskeleton in a flexible manner. Specifically, paclitaxel binds to the &beta; subunit of tubulin. Tubulin is the "building block" of mictotubules, and the binding of paclitaxel locks these building blocks in place. The resulting microtubule/paclitaxel complex does not have the ability to disassemble. This adversely affects cell function because the shortening and lengthening of microtubules (termed dynamic instability) is necessary for their function as a transportation highway for the cell. Chromosomes, for example, rely upon this property of microtubules during mitosis. Further research has indicated that paclitaxel induces programmed cell death (apoptosis) in cancer cells by binding to an apoptosis stopping protein called Bcl-2 (B-cell leukemia 2) and thus arresting its function.', 'Paclitaxel is a taxoid antineoplastic agent indicated as first-line and subsequent therapy for the treatment of advanced carcinoma of the ovary, and other various cancers including breast cancer. Paclitaxel is a novel antimicrotubule agent that promotes the assembly of microtubules from tubulin dimers and stabilizes microtubules by preventing depolymerization. This stability results in the inhibition of the normal dynamic reorganization of the microtubule network that is essential for vital interphase and mitotic cellular functions. In addition, paclitaxel induces abnormal arrays or "bundles" of microtubules throughout the cell cycle and multiple asters of microtubules during mitosis.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19216, 'Flumetol s 0.1% sterile eye drops', 'فلوميتول اس 0.1% قطرة معقمة للعين', '4', NULL, 'Glucocorticoid', 'Fluorometholone', 'Kahira', 'Drops', 'نقط', '0.1%', '1', NULL, 'about fluorometholone (eye.prep.) a synthetic glucocorticoid in steroid responsive inflammations of eye( topical anti-inflammatory). mechanism of action of fluorometholone (eye.prep.) the drug exerts its pharmacological action by penetrating and binding t', NULL, NULL, 1079, '2022-07-26', 'For the ophthalmic treatment of corticosteroid-responsive inflammation of the palpebral and bulbar conjunctiva, cornea and anterior segment of the globe.', 'There is no generally accepted explanation for the mechanism of action of ocular corticosteroids. However, corticosteroids are thought to act by the induction of phospholipase A2 inhibitory proteins, collectively called lipocortins. It is postulated that these proteins control the biosynthesis of potent mediators of inflammation such as prostaglandins and leukotrienes by inhibiting the release of their common precursor, arachidonic acid. Arachidonic acid is released from membrane phospholipids by phospholipase A2. Their primary target is the cytosolic glucocorticoid receptor. After binding the receptor the newly formed receptor-ligand complex translocates itself into the cell nucleus, where it binds to many glucocorticoid response elements (GRE) in the promoter region of the target genes. The DNA bound receptor then interacts with basic transcription factors, causing the increase in expression of specific target genes.', 'Corticosteroids such as fluorometholone inhibit the inflammatory response to a variety of inciting agents and probably delay or slow healing. They inhibit the edema, fibrin deposition, capillary dilation, leukocyte migration, capillary proliferation, fibroblast proliferation, deposition of collagen, and scar formation associated with inflammation.', 'DrugBank', 0, 0, 0);
