INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18621, 'Etosid 50mg 4 soft gelatin caps.', 'ايتوسيد 50 مجم 4 كبسولات', '50', NULL, 'Antineoplastic', 'Etoposide', 'Cipla ltd. - india > star international company', 'Cap', 'كبسولة', '50mg', '1', NULL, NULL, NULL, NULL, 1162, '2022-07-21', 'For use in combination with other chemotherapeutic agents in the treatment of refractory testicular tumors and as first line treatment in patients with small cell lung cancer. Also used to treat other malignancies such as lymphoma, non-lymphocytic leukemia, and glioblastoma multiforme.', 'Etoposide inhibits DNA topoisomerase II, thereby inhibiting DNA re-ligation. This causes critical errors in DNA synthesis at the premitotic stage of cell division and can lead to apoptosis of the cancer cell. Etoposide is cell cycle dependent and phase specific, affecting mainly the S and G2 phases of cell division. Inhibition of the topoisomerase II alpha isoform results in the anti-tumour activity of etoposide. The drug is also capable of inhibiting the beta isoform but inhibition of this target is not associated with the anti-tumour activity. It is instead associated with the carcinogenic effect. ', 'Etoposide is an antineoplastic agent and an epipodophyllotoxin (a semisynthetic derivative of the podophyllotoxins). It inhibits DNA topoisomerase II, thereby ultimately inhibiting DNA synthesis. Etoposide is cell cycle dependent and phase specific, affecting mainly the S and G2 phases. Two different dose-dependent responses are seen. At high concentrations (10 &micro;g/mL or more), lysis of cells entering mitosis is observed. At low concentrations (0.3 to 10 &micro;g/mL), cells are inhibited from entering prophase. It does not interfere with microtubular assembly. The predominant macromolecular effect of etoposide appears to be the induction of DNA strand breaks by an interaction with DNA-topoisomerase II or the formation of free radicals.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20817, 'Infinity vitamin c serum 40 ml', 'انفينيتي فيتامين سي سيروم 40مل', '275', NULL, 'Skin care', 'Vitamin c+hyaluronic acid+collagen+vitamin e+tea tree oil+chamomile extract+caffeine+rosa moschata seed oil+papaya extract+niacinamide', 'Egyptian company for cosmetics > infinity pharm', 'Serum', 'سيروم', '40 ml', '1', NULL, 'vitamin c serum is the newest edition to infinity family. vitamin c serum is considered as one in a kind formula due to the combination of 15% vitamin c which doesnï-t irritate the skin because of vitamin e and hyaluronic acid that moisturize nourish and', NULL, NULL, 1047, '2023-01-22', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16355, 'Ciplamide 2 % opth. soln. 5 ml', 'سيبلاميد 2% غسول 5 مل', '28', NULL, 'Antiglaucoma', 'Dorzolamide', 'Cipla ltd. - india > star international company', 'Sol', 'محلول', '2 %', '1', NULL, 'about dorzolamide carbonic anhydrase inhibitor thiophene derivative a sulfonamide antiglucoma. mechanism of action of dorzolamide dorzolamide is a sulfonamide used as topical carbonic anhydrase inhibitor. inhibition of carbonic anhydrase-2 in ciliary proc', NULL, NULL, 777, '2022-07-17', 'Dorzolamide is indicated for the management of elevated intraocular pressure in patients with ocular hypertension or open-angle glaucoma.[L11377] It can also be used in combination with [timolol] for the same indication in patients who are insufficiently responsive to ophthalmic beta-blockers.[L11380]



Its pre-operative use was also investigated to prevent elevated intraocular pressure after neodynium yttrium aluminum garnet laser posterior capsulotomy.[A190183]', 'Elevated intraocular pressure is a characteristic manifestation of ocular hypertension or open-angle glaucoma. The level of intraocular pressure (IOP) is governed by the balance between the production of aqueous humour (by ocular ciliary processes) and its outflow from the anterior segment of the eye via trabecular (conventional) or uveoscleral (unconventional) pathways. When there is an increase in the resistance to the trabecular outflow of aqueous humour, the intraocular pressure is elevated. Subsequently, optic nerve damage can occur from blood flow restrictions and mechanical distortion of ocular structures. Optic nerve damage can further result in optic disc cupping and progressive visual field loss (and blindness in some cases).[A1304] 



Carbonic anhydrase (CA) is a ubiquitous enzyme that catalyzes the reversible hydration of carbon dioxide to bicarbonate ions and dehydration of carbonic acid.[A1304,L11377] In the ocular ciliary processes, the local production of bicarbonate by CAs promotes sodium and fluid transport. CA-II is a key isoenzyme found primarily in red blood cells (RBCs) that regulates aqueous humour production.[A1304] Dorzolamide is a highly specific CA-II inhibitor, where it displays a 4000-fold higher affinity for carbonic anhydrase II than carbonic anhydrase I.[A1304] The inhibition of CA-II in the ciliary process disrupts the formation of bicarbonate ions and reduces sodium and fluid transport, which leads to decreased aqueous humour secretion and reduced intraocular pressure.[A1304,L11377]', 'Dorzolamide is a carbonic anhydrase inhibitor that reduces elevated intraocular pressure in open-angle glaucoma or ocular hypertension. When used in combination with topic beta-adrenergic antagonists, dorzolamide has an additive effect of lowering intraocular pressure. The peak ocular hypotensive effect of dorzolamide is observed at about 2 hours following ophthalmic administration.[A1304]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14915, 'Baraclude 0.5 mg 30 tabs. (net price)', 'باراكلود 0.5 مجم 30 قرص', '3,720', NULL, 'Antiviral.nucleoside', 'Entecavir', 'Bristol-myers squibb', 'Tab', 'أقراص', '0.5 mg', '3', NULL, 'about entecavir nucleoside analog reverse transcriptase inhibitor oral antiviral drug used in the treatment of hepatitis b infection. mechanism of action of entecavir by competing with the natural substrate deoxyguanosine triphosphate entecavir functional', NULL, NULL, 1155, '2022-07-22', 'For the treatment of chronic hepatitis B virus infection in adults with evidence of active viral replication and either evidence of persistent elevations in serum aminotransferases (ALT or AST) or histologically active disease.', 'By competing with the natural substrate deoxyguanosine triphosphate, entecavir functionally inhibits all three activities of the HBV polymerase (reverse transcriptase, rt): (1) base priming, (2) reverse transcription of the negative strand from the pregenomic messenger RNA, and (3) synthesis of the positive strand of HBV DNA. Upon activation by kinases, the drug can be incorporated into the DNA which has the ultimate effect of inhibiting the HBV polymerase activity.', 'Entecavir is a guanosine nucleoside analogue with selective activity against hepatitis B virus (HBV). It is designed to selectively inhibit the Hepatitis B virus, blocking all three steps in the replication process. Entecavir is more efficient than an older Hepatitis B drug, lamivudine.', 'DrugBank', 0, 0, 0);
