INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Sportive 30 caps.', 27423, 'سبورتيف 30 كبسولة', '54', NULL, 'Antirheumatic.osteoarthritis.anabolic agents', 'Antirheumatic.osteoarthritis.anabolic agents', NULL, 'Chondroitin+glucosamine', 'Copad pharma', 'Cap', 'كبسولة', NULL, '3', NULL, NULL, 'about chondroitin sulphate a sulfated glycosaminoglycan (mucopolysaccharides dietary supplement anti-rheumatic and anti-inflammatory. mechanism of action of chondroitin sulphate chondroitin sulfate is part of a large protein molecule (proteoglycan) that g', '6224000394530', 1291, '2022-12-09', NULL, 0, NULL, NULL, NULL, NULL);
