INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Micazole 2% oral gel 40 gm', 8128, 'ميكازول 2% اورال جل 40جم', '29', '18', 'Antifungals.imidazoles', 'Antifungals.imidazoles', NULL, 'Miconazole', 'Eva pharma', 'Gel', 'جل', '2%', '1', NULL, NULL, NULL, '6223002459179', 1448, '2024-09-25', NULL, 0, 'Miconazole is indicated for the local treatment of oropharyngeal candidiasis in adult patients and for the adjunctive treatment of diaper dermatitis complicated by candidiasis in immunocompetent patients aged four weeks and older.[L14021, L14024] Miconazole is available as both a suppository and cream for the treatment of vaginal yeast infections and the relief of associated vulvar itching and irritation.[L14027] Lastly, miconazole cream is effective in treating athlete''s foot (tinea pedis), jock itch (tinea cruris), ringworm infections (tinea corporis),[L14033] pityriasis (formerly tinea) versicolor,[A203633] and cutaneous candidiasis.[A203630]', 'Miconazole is an azole antifungal used to treat a variety of conditions, including those caused by _Candida_ overgrowth. Unique among the azoles, miconazole is thought to act through three main mechanisms.[A203636] The primary mechanism of action is through inhibition of the CYP450 14α-lanosterol demethylase enzyme, which results in altered ergosterol production and impaired cell membrane composition and permeability, which in turn leads to cation, phosphate, and low molecular weight protein leakage.[A203636, A203639]



In addition, miconazole inhibits fungal peroxidase and catalase while not affecting NADH oxidase activity, leading to increased production of reactive oxygen species (ROS).[A203636, A203639, A203642] Increased intracellular ROS leads to downstream pleiotropic effects and eventual apoptosis.[A203636]



Lastly, likely as a result of lanosterol demethylation inhibition, miconazole causes a rise in intracellular levels of farnesol. This molecule participates in quorum sensing in _Candida_, preventing the transition from yeast to mycelial forms and thereby the formation of biofilms, which are more resistant to antibiotics.[A203645, A203648] In addition, farnesol is an inhibitor of drug efflux ABC transporters, namely _Candida_ CaCdr1p and CaCdr2p, which may additionally contribute to increased effectiveness of azole drugs.[A203648]', 'Miconazole is an azole antifungal that functions primarily through inhibition of a specific demethylase within the CYP450 complex.[A203636] As miconazole is typically applied topically and is minimally absorbed into the systemic circulation following application, the majority of patient reactions are limited to hypersensitivity and cases of anaphylaxis.[L14021] Patients using intravaginal miconazole products are advised not to rely on contraceptives to prevent pregnancy and sexually transmitted infections, as well as not to use tampons concurrently.[L14027]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Mumlac ultra nutrition 2 milk 400 gm', 19307, 'ماملاك الترا 2 لبن اطفال 400 جم', '139', '269', 'Infant milk formula', 'Infant milk formula', NULL, 'Infant milk formula', 'Copad egypt', 'Unknown', 'غير محدد', '400 gm', '1', NULL, NULL, NULL, '7613200204135', 647, '2023-09-05', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Nutri start 2 milk 400 gm', 23843, 'نيوتري ستارت 2 لبن اطفال 400 جرام', '140', NULL, 'Milk products.second stage (age 6-12 months)', 'Milk products.second stage (age 6-12 months)', NULL, 'Milk formula stage 2', 'Danone', 'Unknown', 'غير محدد', '400 gm', '1', NULL, NULL, 'not to replace mother s milk. for infants from 6 months to 1 year age. to be used within one month from opening.', NULL, 1271, '2023-02-09', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Dulagen 10 sachets', 17950, 'دولاجين 10 اكياس', '245', '195', 'Antirheumatic.osteoarthritis.anabolic agents', 'Antirheumatic.osteoarthritis.anabolic agents', NULL, 'Gelatin (collagen) hydrolysate+vitamin c', 'Dulex lab', 'Sachet', 'أكياس', NULL, '1', 'كولاجين لبناء الغضاريف وعلاج التهاب المفاصل والخشونة والروماتيزم', NULL, 'for health joints use: 1 sachet in a glass add 100 ml of water stirr well till dissolve then drink daily for 3 months', '6224009413140', 7402, '2025-07-03', NULL, 0, 'Gelatin is used for weight loss and for treating osteoarthritis, rheumatoid arthritis, and brittle bones (osteoporosis). Some people also use it for strengthening bones, joints, and fingernails. Gelatin is also used for improving hair condition and to shorten the recovery after exercise and sports-related injury [L2113]. Gelatin is used in preparations of foods, cosmetics, and medicine [L2113].



Plasma volume expander in hypovolaemic shock [L2115]. Haemostatic [L2115].



Gelatin-based hydrogels are being used in drug delivery and tissue engineering because they are able to promote cell adhesion and proliferation. In addition, these hydrogels can be used as wound dressings because of their attractive fluid absorbance properties. Manufacturing technologies such as ultraviolet stereolithography and two-photon polymerization can be used to prepare structures containing photosensitive gelatin-based hydrogels [L2111].', 'It works as a hemostatic by providing a physical framework within which clotting may occur [L2115].



As a volume expander, gelatin remains in the vascular space. When used in the treatment of hypovolaemia gelatin can produce a significant increase in blood volume, cardiac output, stroke volume, blood pressure, urinary output and oxygen delivery, increasing volume and pressure [L2117].



For intravascular volume expansion, the majority or gelatins produce an effect which is almost equivalent to of which are mild, although severe reactions albumin, with a duration of action of 3 to 4 hours to have been reported [L2118].



Gelatin or collagen chains suspended in solution can be covalently cross-linked to form matrices that are able to swell in the presence of aqueous solutions, forming what are called _gelatin hydrogels_. Hydrogels, characterized by their hydrophilicity and insolubility in water, have the capability of swelling into an equilibrium volume while maintaining their shape. The chemical cross-linkers used may be either small bifunctional molecules or polyfunctional macromolecules, for example, glutaraldehyde [L2122].', 'Gelatin contains collagen, which is one of the materials that make up cartilage and bone [L2113].



In addition to their well-established value as a nutritional protein source, collagen and collagen-derived products may exhibit various potential biological activities on cells and the extracellular matrix through the corresponding food-derived peptides post-ingestion.  This could justify their applications in dietary supplements and pharmaceutical agents [A32426].



Gelatin is a protein that is used as a hemostatic in surgical procedures.  It is also used as a plasma volume expander in hypovolemic shock. Gelatin rods structures may also be used to temporarily block tear outflow in cases of dry eye [L2115].', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Bortezomib-synthon 3.5mg powder in vial for s.c./i.v. inj.', 24477, 'بورتيزوميب سينثون 3.5مجم باودر فيال', '5,703', NULL, 'Antineoplastic', 'Antineoplastic', NULL, 'Bortezomib', 'Aboukir trading', 'Inj', 'حقن', '3.5mg', '1', NULL, NULL, NULL, NULL, 894, '2023-03-07', NULL, 0, 'Bortezomib is indicated for the treatment of adults with multiple myeloma or mantle cell lymphoma.[L44341]', 'The ubiquitin-proteasome pathway is a homeostatic proteolytic pathway for intracellular protein degradation: proteins marked with a poly-ubiquitin chain are degraded to small peptides and free ubiquitin by the proteasome, which is a large multimeric protease.[A18508] Aberrant proteasome-dependent proteolysis, as seen in some malignancies, can lead to uncontrolled cell division, leading to tumorigenesis, cancer growth, and spread.[A18508, A204083] 



Bortezomib is a reversible inhibitor of the 26S proteasome, which is made up of a 20S core complexed with a 19S regulatory complex. Individual β-subunits allow specific catalytic action of the 20S core.[A18509, A204083] In mammalian cells, bortezomib is a potent inhibitor of the proteasome’s chymotryptic-like activity, which is attributed to the β5-subunit of the 20S core particle.[A18509] Bortezomib binds to the active site of the threonine hydroxyl group in the β5-subunit.[A204083] A probing study showed bortezomib also binding to and inhibiting the β1-subunit, which mediates the caspase-like activity of the proteasome, and β1i-subunit, which is an altered subunit that is expressed to form immunoproteasomes in response to cell stress or inflammation.[A18509] By inhibiting the proteasome-mediated degradation of key proteins that promote cell apoptosis,[A18508] bortezomib induces a cell cycle arrest during the G2-M phase.[L14180] It is believed that multiple mechanisms, other than proteasome inhibition, may be involved in the anticancer activity of bortezomib.[A204083] The anticancer activity of bortezomib was largely associated with suppression of the NF-κB signalling pathway, resulting in the downregulation of anti-apoptotic target genes and expression of anti-apoptic proteins. This may be explained by bortezomib preventing uncontrolled degradation of IκB, which is an inhibitory protein of NF-κB. NOXA, which is a pro-apoptotic factor, induced by bortezomib selectively in cancer cells; thus, it is suggested to be another key mechanism of bortezomib.[A204083]', 'Bortezomib works to target the ubiquitin-proteasome pathway, an essential molecular pathway that regulates intracellular concentrations of proteins and promotes protein degradation.[A272] The ubiquitin-proteasome pathway is often dysregulated in pathological conditions, leading to aberrant pathway signalling and the formation of malignant cells. In one study, patient-derived chronic lymphocytic leukemia (CLL) cells contained 3-fold higher levels of chymotrypsin-like proteasome activity than normal lymphocytes.[A272] By reversibly inhibiting proteasome, bortezomib prevents proteasome-mediated proteolysis. Bortezomib exerts a cytotoxic effect on various cancer cell types _in vitro_ and delays tumour growth _in vivo_ in nonclinical tumour models.[L14177] Bortezomib inhibits the proteasome activity in a dose-dependent manner. In one pharmacodynamic study, more than 75% of proteasome inhibition was observed in whole blood samples within one hour after dosing of bortezomib.[A204083]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Paracalcet 30 mg 30 f.c. tab.', 24549, 'باراكالسيت 30 مجم 30 قرص', '804', '585', 'Calcimimetic', 'Calcimimetic', NULL, 'Cinacalcet', 'Global napi pharmaceuticals', 'Tab', 'أقراص', '30 mg', '3', 'علاج فرط نشاط الغدة الجار درقية -- تقليل نسبة الكالسيوم الناتج من مرض سرطان الغدة الجاردرقية', NULL, 'description cinacalcet (inn) is a drug that acts as a calcimimetic (i.e. it mimics the action of calcium on tissues) by allosteric activation of the calcium-sensing receptor that is expressed in various human organ tissues. indication for the treatment of', NULL, 1431, '2025-05-26', NULL, 0, 'For the treatment of secondary hyperparathyroidism in patients with Chronic Kidney Disease who are on hemodialysis or peritoneal dialysis. Also for the treatment of hypercalcemia in patients with parathyroid carcinoma.', 'Cinacalcet directly lowers parathyroid hormone levels by increasing the sensitivity of the calcium sensing receptors to activation by extracellular calcium, resulting in the inhibition of PTH secretion. The reduction in PTH is associated with a concomitant decrease in serum calcium levels.', 'Cinacalcet is a drug that acts as a calcimimetic (i.e. it mimics the action of calcium on tissues). Secondary hyperparathyroidism (HPT) in patients with chronic kidney disease (CKD) is a progressive disease, associated with increases in parathyroid hormone (PTH) levels and derangements in calcium and phosphorus metabolism. Increased PTH stimulates osteoclastic activity resulting in cortical bone resorption and marrow fibrosis. The goals of treatment of secondary hyperparathyroidism are to lower levels of PTH, calcium, and phosphorus in the blood, in order to prevent progressive bone disease and the systemic consequences of disordered mineral metabolism. In CKD patients on dialysis with uncontrolled secondary HPT, reductions in PTH are associated with a favorable impact on bone-specific alkaline phosphatase (BALP), bone turnover and bone fibrosis. Cinacalcet reduces calcium levels by increasing the sensitivity of the calcium sensing receptor to extracellular calcium.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Promotic 30 caps', 6658, 'بروموتيك 30 كبسولة', '140', NULL, 'Git disturbances- antispasmodic-antiflatulents', 'Git disturbances- antispasmodic-antiflatulents', NULL, 'Betaine+alpha amylase+caraway seed+chamomile+fennel+peppermint+bromelain+papain+lipase+ginger root', 'Curelix', 'Cap', 'كبسولة', NULL, '1', NULL, NULL, NULL, NULL, 707, '2025-08-11', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Levectam 1000 mg 20 f.c. tabs.', 21737, 'ليفيكتام 1000مجم 20 قرص', '144', '129', 'Anti-epileptic.pyrrolidine', 'Anti-epileptic.pyrrolidine', NULL, 'Levetiracetam', 'Biopharm egypt > clavita pharma', 'Tab', 'أقراص', '1000 mg', '2', NULL, NULL, NULL, '6224011023085', 2278, '2025-06-19', NULL, 0, 'Levetiracetam is indicated as an adjunctive therapy in the treatment of partial onset seizures in epileptic patients who are one month of age and older. Additionally, it is indicated as an adjunct in the treatment of myoclonic seizures in patients with juvenile myoclonic epilepsy who are 12 years of age and older, and in primary generalized tonic-clonic seizures in patients with idiopathic generalized epilepsy who are 6 years of age and older.[L8606]



Levetiracetam is also available as an orally dissolvable tablet that is indicated as an adjunct in the treatment of partial onset seizures in patients with epilepsy who are 4 years of age and older and weigh more than 20kg.[L8609]', 'The exact mechanism through which levetiracetam exerts its anti-epileptic effects is unclear, but is thought to be unique amongst other anti-epileptic medications. Current knowledge suggests that levetiracetam’s binding to synaptic vesicle protein 2A (SV2A) is a key driver of its action. SV2A is a membrane-bound protein that is found on synaptic vesicles and is ubiquitous throughout the CNS[A12546] - it appears to play a role in vesicle exocytosis[L8606,L8615] and in the modulation of synaptic transmission by increasing the available amount of secretory vesicles available for neurotransmission.[A12552] Stimulation of pre-synaptic SV2A by levetiracetam may inhibit neurotransmitter release,[A12551] but this action does not appear to affect normal neurotransmission. This has led to the suggestion that levetiracetam exclusively modulates the function of SV2A only under pathophysiological conditions.[A12546] Levetiracetam and related analogues showed a correlation between affinity for SV2A and anti-epileptic potency, further suggesting that action at this site contributes to the anti-epileptic activity of the drug.[L8606,L8615]



Levetiracetam has also been shown to indirectly affect GABAergic neurotransmission (despite having no direct effect on GABAergic or glutamatergic receptors) and modulate ionic currents.[A12552] Similarly, levetiracetam has been shown in vitro to inhibit N-type calcium channels.[A16562] How, or even if, these actions are implicated in its anti-epileptic action have yet to be elucidated.', 'Levetiracetam appears to prevent seizure activity via the selective inhibition of hypersynchronized epileptiform burst firing without affecting normal neuronal transmission, though the exact mechanism through which this occurs is unclear.[L8606,L8615] The therapeutic index of levetiracetam is wide,[L8615,A185918] making it relatively unique amongst other anti-epileptic medications.



Anti-epileptic drugs, including levetiracetam, may increase the risk of suicidal ideation or behaviour - patients taking levetiracetam should be monitored for the emergence or worsening of depressive symptoms, suicidal ideation, and behavioural abnormalities.[L8606,L8600,L8615]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Genove hand cream 50ml', 19704, 'جينوفي كريم يد 50 مل', '59', NULL, 'Skin care', 'Skin care', NULL, NULL, 'Laboratorio genove s.a. >', 'Cream', 'كريم', '50ml', '1', NULL, NULL, 'genovï- moisturising hand cream protects nourishes and moisturises dry skin. helps repair cracked dry skins. use: apply as often as needed on hands elbows knees and ankles.', NULL, 809, '2022-07-27', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Je t-aime hair oil 100 ml', 21150, 'جوتيم زيت شعر 100 مل', '74', NULL, 'Hair care', 'Hair care', NULL, 'Snake oil+jojoba oil+lavender oil+olive oil+rosemary oil+peppermint oil+eruca oil+vit.e+paraffin oil', 'Egyptian co. for cosmetics > new pharm', 'Hair oil', 'زيت شعر', '100 ml', '1', NULL, NULL, '** special hair care oil containing effective ingredients ** use: - bath oil: apply oil in warm water about 3 min. then apply on hair and leave it about 3-5 min. then rinse shampoo. - normal: leave oil on your hair about 15-30 min. then rinse shampoo.', NULL, 329, '2022-08-06', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Flamoush cream 50 gm', 19105, 'فلاموش كريم 50 جم', '40', NULL, 'Scar therapy', 'Scar therapy', NULL, 'Cyclopentasiloxane+vitamin e+dc-7040+honey+aloe vera', 'Egyptian company for cosmetics > epsilon health care', 'Cream', 'كريم', '50 gm', '1', 'علاج للندبات.', NULL, NULL, NULL, 1418, '2022-05-15', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Fytosid 100mg/5ml vial', 19524, 'فيتوسيد 100 مجم / 5 مل فيال', '75', '32', 'Antineoplastic', 'Antineoplastic', NULL, 'Etoposide', 'Fresenius kabi > egyptian group for import and export', 'Vial', 'فيال', '100mg', '1', NULL, NULL, NULL, NULL, 712, '2024-11-07', NULL, 0, 'For use in combination with other chemotherapeutic agents in the treatment of refractory testicular tumors and as first line treatment in patients with small cell lung cancer. Also used to treat other malignancies such as lymphoma, non-lymphocytic leukemia, and glioblastoma multiforme.', 'Etoposide inhibits DNA topoisomerase II, thereby inhibiting DNA re-ligation. This causes critical errors in DNA synthesis at the premitotic stage of cell division and can lead to apoptosis of the cancer cell. Etoposide is cell cycle dependent and phase specific, affecting mainly the S and G2 phases of cell division. Inhibition of the topoisomerase II alpha isoform results in the anti-tumour activity of etoposide. The drug is also capable of inhibiting the beta isoform but inhibition of this target is not associated with the anti-tumour activity. It is instead associated with the carcinogenic effect. ', 'Etoposide is an antineoplastic agent and an epipodophyllotoxin (a semisynthetic derivative of the podophyllotoxins). It inhibits DNA topoisomerase II, thereby ultimately inhibiting DNA synthesis. Etoposide is cell cycle dependent and phase specific, affecting mainly the S and G2 phases. Two different dose-dependent responses are seen. At high concentrations (10 &micro;g/mL or more), lysis of cells entering mitosis is observed. At low concentrations (0.3 to 10 &micro;g/mL), cells are inhibited from entering prophase. It does not interfere with microtubular assembly. The predominant macromolecular effect of etoposide appears to be the induction of DNA strand breaks by an interaction with DNA-topoisomerase II or the formation of free radicals.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Rostevo intimate feminine wash 250ml', 33539, 'روستيفو غسول مهبلي للعناية الشخصية للسيدات 250 مل', '50', '50', 'Intimate feminine wash', 'Intimate feminine wash', NULL, 'Chlorhexidine+menthol crystals+chamomile extract+tea tree oil+aloe vera extract+thyme extract+rose oil', 'Larose', 'Unknown', 'غير محدد', '250ml', '1', NULL, NULL, 'rostevo intimate feminine wash 250 ml price 50 le how to use : used without dilution to be used for intimate feminine wash the rinse well with water when needed for external use only', NULL, 568, '2024-10-24', NULL, 0, NULL, NULL, NULL, NULL);
