INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Fusiderm 2% gel 15 gm', 5249, 'فيوسيدرم 2% جل 15 جم', '13', NULL, 'Antibiotic topical', 'Antibiotic topical', NULL, 'Fusidic acid', 'Eva pharma', 'Gel', 'جل', '2%', '1', NULL, NULL, 'about fusidic acid a bacteriostatic steroid antibiotic systemic antibacterial anti-infective. mechanism of action of fusidic acid fusidic acid is a bactericidal or bacteriostatic drug depending upon the concentration. it inhibits the transfer of aminoacid', NULL, 1419, '2022-09-20', NULL, 0, 'For the treatment of bacterial infections.', 'Fusidic acid works by interfering with bacterial protein synthesis, specifically by preventing the translocation of the elongation factor G (EF-G) from the ribosome. It also can inhibit chloramphenicol acetyltransferase enzymes.', 'Fusidic acid is a bacteriostatic antibiotic and helps prevent bacterial growth while the immune system clears the infection.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Longicef 125mg/5ml susp. 60 ml', 7506, 'لونجسيف 125مجم/5مل معلق 60 مل', '4', NULL, 'Antibiotic.cephalosporin.first-generation', 'Antibiotic.cephalosporin.first-generation', NULL, 'Cefadroxil', 'Kahira', 'Susp', 'معلق', '125mg', '1', NULL, NULL, 'about cefadroxil first generation cephalosporin para-hydroxy derivative of cefalexin oral broad-spectrum antibiotic mechanism of action cefadroxil is a 1st generation cephalosporin which exerts its bactericidal action against both gram positive & gram neg', NULL, 965, '2023-04-18', NULL, 0, 'For the treatment of the following infections (skin, UTI, ENT) caused by; <i>S. pneumoniae, H. influenzae, staphylococci, S. pyogenes</i> (group A beta-hemolytic streptococci), <i>E. coli, P. mirabilis, Klebsiella</i> sp, coagulase-negative staphylococci and <i>Streptococcus pyogenes</i>', 'Like all beta-lactam antibiotics, cefadroxil binds to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, causing the inhibition of the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins; it is possible that cefadroxil interferes with an autolysin inhibitor.', 'Cefadroxil, a first-generation cephalosporin antibiotic, is used to treat urinary tract infections, skin and skin structure infections, pharyngitis, and tonsillitis.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Leucovorin calcium 100 mg vial 10 ml', 7221, 'لوكوفورين كالسيوم 100 مجم 10 مل فيال', '70', NULL, NULL, NULL, NULL, 'Leucovoirin+calcium', 'Company', 'Vial', 'فيال', '100 mg', '1', NULL, NULL, NULL, '5015997406617', 5896, '2022-12-06', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Colgimor 10 sachets 12 gm', 16630, 'كلوجيمور 12جم 10 اكياس', '120', NULL, 'Antirheumatic.osteoarthritis.anabolic agents', 'Antirheumatic.osteoarthritis.anabolic agents', NULL, 'Gelatin (collagen) hydrolysate+vitamin c', 'Pharma zad > famila medifood', 'Sachet', 'أكياس', '12 gm', '1', NULL, NULL, 'for healthy joints. orange flavour. use: 1 sachet in a glass add 100 ml of water stirr well till dissolve then drink daily for 3 months', NULL, 1017, '2022-07-07', NULL, 0, 'Gelatin is used for weight loss and for treating osteoarthritis, rheumatoid arthritis, and brittle bones (osteoporosis). Some people also use it for strengthening bones, joints, and fingernails. Gelatin is also used for improving hair condition and to shorten the recovery after exercise and sports-related injury [L2113]. Gelatin is used in preparations of foods, cosmetics, and medicine [L2113].



Plasma volume expander in hypovolaemic shock [L2115]. Haemostatic [L2115].



Gelatin-based hydrogels are being used in drug delivery and tissue engineering because they are able to promote cell adhesion and proliferation. In addition, these hydrogels can be used as wound dressings because of their attractive fluid absorbance properties. Manufacturing technologies such as ultraviolet stereolithography and two-photon polymerization can be used to prepare structures containing photosensitive gelatin-based hydrogels [L2111].', 'It works as a hemostatic by providing a physical framework within which clotting may occur [L2115].



As a volume expander, gelatin remains in the vascular space. When used in the treatment of hypovolaemia gelatin can produce a significant increase in blood volume, cardiac output, stroke volume, blood pressure, urinary output and oxygen delivery, increasing volume and pressure [L2117].



For intravascular volume expansion, the majority or gelatins produce an effect which is almost equivalent to of which are mild, although severe reactions albumin, with a duration of action of 3 to 4 hours to have been reported [L2118].



Gelatin or collagen chains suspended in solution can be covalently cross-linked to form matrices that are able to swell in the presence of aqueous solutions, forming what are called _gelatin hydrogels_. Hydrogels, characterized by their hydrophilicity and insolubility in water, have the capability of swelling into an equilibrium volume while maintaining their shape. The chemical cross-linkers used may be either small bifunctional molecules or polyfunctional macromolecules, for example, glutaraldehyde [L2122].', 'Gelatin contains collagen, which is one of the materials that make up cartilage and bone [L2113].



In addition to their well-established value as a nutritional protein source, collagen and collagen-derived products may exhibit various potential biological activities on cells and the extracellular matrix through the corresponding food-derived peptides post-ingestion.  This could justify their applications in dietary supplements and pharmaceutical agents [A32426].



Gelatin is a protein that is used as a hemostatic in surgical procedures.  It is also used as a plasma volume expander in hypovolemic shock. Gelatin rods structures may also be used to temporarily block tear outflow in cases of dry eye [L2115].', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Merional 75 i.u. vial', 22601, 'ميريونال 75وحدة دولية فيال', '92', NULL, 'Infertility drugs.human menopausal gonadotrophin', 'Infertility drugs.human menopausal gonadotrophin', NULL, 'Human menopausal gonadotrophin (fsh+lh)', 'Ibsa - switzerland > habib scientific office', 'Vial', 'فيال', NULL, '1', 'يستخدم لعلاج العقم.', NULL, 'description menotropins contains follicle stimulating hormone (fsh) and luteinizing hormone (lh) purified from the urine of postmenopausal women. it is used as a fertility medication that is injected either subcutaneously or intramuscularl mechanism of ac', NULL, 4393, '2023-01-23', NULL, 0, 'Human immunoglobulin G is indicated for the following conditions:



### Primary Immunodeficiency

- for the treatment of primary immunodeficiency in adult and pediatric patients[L39920, L39930, L39935, L39940, L39945, L39950, L39955, L39960, L39965, L39975, L39980, L40029, L40034, L40044]

- in combination with [hyaluronidase (human recombinant)] for the treatment of primary immunodeficiency in patients ≥2 years of age.[L42760]



### Immune Thrombocytopenic Purpura (ITP)

- for the treatment of acute or chronic immune thrombocytopenic purpura in adult and pediatric patients[L39940, L39950, L39960, L39965, L39970, L39975, L39980]



### Chronic Inflammatory Demyelinating Polyneuropathy (CIDP)

- for the treatment of CIDP in adult patients[L39955, L39965, L39975, L39980, L40034]

- in combination with hyaluronidase (human recombinant) as a maintenance therapy in adults with chronic inflammatory demyelinating polyneuropathy (CIDP)[L40039]



### Multifocal Motor Neuropathy (MMN)

- for maintenance therapy to improve muscle strength and disability in adult patients with MMN[L39955]



### Prophylaxis of Bacterial Infection

- for the prevention of bacterial infections in patients with hypogammaglobulinemia and/or B-cell chronic lymphocytic leukemia[L39960]



### Coronary Artery Aneurysm Associated With Kawasaki Syndrome

- for the prevention of coronary artery aneurysms in pediatric patients with Kawasaki syndrome[L39960]



### Dermatomyositis

- for the treatment of dermatomyositis in adult patients[L39970]

', 'IVIg interacts with a number of different components of the immune system, including cytokines, complement, Fc receptors and several cell surface immunocompetent molecules. IVIg also impacts different effector cells of the immune system (B and T lymphocytes, dendritic cells, etc.) and regulates a wide range of genes. Its main mechanism of actions are believed to be Fc-dependent and F(ab'')2-dependent. IVIg competitively blocks gamma Fc receptors, preventing the binding and ingestion of phagocytes and suppressing platelet depletion. IVIg contains a number of different antobodies, which prevent infection by attaching to the surface of invading pathogens and aiding in their disposal before they can infect cells. Antibodies remove pathogens via complement activation, agglutination or precipitation, pathogen receptor blocking, macrophage “tagging” or neutralization (via binding) of pathogen toxins. Intact IVIg and F(ab′)2 fragments of IVIg can also neutralize the activity of various autoantibodies. By triggering the production of interleukin-1 receptor antagonist, IVIg modulates of the production of cytokines and cytokine antagonists. It also prevents the generation of the C5b-9 membrane attack complex and subsequent complement-mediated tissue damage by binding active complement components.', 'Used as a replacement therapy in inherited humoral immunodeficiency disorders such as severe combined immunodeficiency syndrome, x-linked agammaglobulinemia, and Wiskott-Aldrich Syndrome. The immunoglobulins target, bind and kill bacterial cells as well as viral particles. IgG is the monomeric immunoglobulin of which there are four subclasses (IgG1, IgG2, IgG3 and IgG4) in differing abundances (66%, 23%, 7% and 4%). IgAs represent about 15% of the immunoglobulins in the blood. These target inhaled or ingested pathogens.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Collagen joint formula 60 caps.', 7130, 'كولاجين جوينت فورميلا 60 كبسولة', '890', NULL, 'Anti-rheumatic.osteoarthritis.anabolic agents', 'Anti-rheumatic.osteoarthritis.anabolic agents', NULL, NULL, 'Stada', 'Cap', 'كبسولة', NULL, '1', NULL, NULL, NULL, NULL, 924, '2025-08-24', NULL, 0, NULL, NULL, NULL, NULL);
