INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ursosernox 500 mg 20 scored f.c. tabs.', 28878, 'اورسوسيرنوكس 500مجم 20 اقراص', '75', NULL, 'Liver support supplements', 'Liver support supplements', NULL, 'Ursodeoxycholic acid', 'Sedico > serio pharm-egypt', 'Tab', 'أقراص', '500 mg', '2', NULL, NULL, NULL, '6225000092105', 870, '2022-12-14', NULL, 0, 'Ursodeoxycholic acid is indicated for the treatment of patients with primary biliary cholangitis.[L44627] 



It is used for the short-term treatment of radiolucent, noncalcified gallbladder stones in patients selected for elective cholecystectomy. It is also used to prevent gallstone formation in obese patients experiencing rapid weight loss.[L44793]', 'Endogenous hydrophobic bile acids such as deoxycholic acid and chenodeoxycholic acid can exert hepatotoxic effects. Ursodeoxycholic acid is a hydrophilic bile acid that mediates its biological effects via several mechanisms. Ursodeoxycholic acid (UDCA) protects hepatocytes and cholangiocytes from bile acid-induced damage, such as reactive oxygen species (ROS)-induced inflammation and mitochondrial dysfunction.[A256267] UDCA was shown to reserve hepatocyte cell structures and stimulate anti-apoptotic pathways.[A256267,A256272,A256458,A256463] It was also shown to prevent the production of ROS by Kupffer cells and resident macrophages in the liver, thus attenuating oxidative stress in the liver.[A256267] 



UDCA can also change the hydrophobicity index of the bile acid pool: following oral administration, UDCA forms a major fraction of the human bile acid pool and competitively displaces the hydrophobic or more toxic bile acids.[A256272,A256267] It increases the absorption of hydrophilic bile acids.[A256267] There are several proposed mechanisms of choleretic actions of UDCA: UDCA increases intracellular calcium levels, stimulating transport proteins and vesicular exocytosis in cholestatic hepatocytes.[A256267] UDCA may also upregulate the expression of membrane transport proteins like the chloride-bicarbonate anion exchanger (AE2),[A256267,A256458] which is involved in biliary secretion and is often observed to be defective in primary biliary cholangitis.[A256448] In rats, UDCA reduced hepatic expression of major histocompatibility complex (MHC) class I antigens, suggesting immunomodulating effects.[A256267] UDCA acts as a partial agonist at the bile acid receptor, also known as the farnesoid X receptor (FXR), and has negligible effects on cholesterol and lipid synthesis.[A256272]', 'Ursodeoxycholic acid (UDCA) is a secondary bile acid with cytoprotectant, immunomodulating, and choleretic effects. It reduces the cholesterol fraction of biliary lipids.[A256267]



UDCA inhibits the absorption of cholesterol in the intestine and the secretion of cholesterol into bile, decreasing biliary cholesterol saturation.[A256267] UDCA increases bile acid flow and promotes the secretion of bile acids.[A256267,A256277,A256463]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Fenistil 0.1% topical gel 20 gm (n/a)', 4789, 'فنيستيل 0.1% جل موضعي 20 جم', '8', NULL, 'H1 antagonist', 'H1 antagonist', NULL, 'Dimetindene maleate', 'Novartis > glaxo smithkline', 'Gel', 'جل', '0.1%', '1', NULL, NULL, NULL, '6223002641048', 2801, '2022-12-30', NULL, 0, 'Indicated as symptomatic treatment of allergic reactions: urticaria, allergies of the upper respiratory tract such as hey fever and perennial rhinitis, food and drug allergies; pruritus of various origins, except pruritus due to cholestasis; insect bites. Dimethindene is also indicated for pruritus in eruptive skin diseases such as chicken-pox. Dimethindene can also be used as an adjuvant in eczema and other pruriginous dermatoses of allergic origin. ', 'Dimethindene is a selective histamine H1 antagonist and binds to the histamine H1 receptor. This blocks the action of endogenous histamine, which subsequently leads to temporary relief of the negative symptoms brought on by histamine.', 'Dimethindene occurs as a racemic mixture. The (S)-(+)-dimethindene is a potent M2-selective muscarinic receptor antagonist (with lower affinity for M1, M3, and M4 muscarinic receptors). The (R)-(-)-enantiomer is the eutomer (responsible for bioactivity) for histamine H1 receptor binding.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Dugoner 20 capsules', 11409, 'دوجونير 20 كبسولة', '280', NULL, 'Neuropathy management', 'Neuropathy management', NULL, 'Alpha lipoic acid 600 mg+benfotiamine 300 mg+methylcobalamin 1000 mcg+riboflavin sodium phosphate 4 mg+pyridoxine hcl 4 mg+folate 200 mcg+vitamin d3 800 i.u+vitamin c 200 mg', 'Duomed pharma', 'Capsule', 'كبسولة', NULL, '2', NULL, NULL, NULL, NULL, 3406, '2024-08-07', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Milnavella 50 mg 30 f.c. tabs.', 22762, 'ميلنافيلا 50 مجم 30 قرص', '111', '75', 'Psychiatric.antidepressants', 'Psychiatric.antidepressants', NULL, 'Milnacipran hydrochloride', 'Mash premiere', 'Tab', 'أقراص', '50 mg', '3', 'مضاد للاكتئاب', NULL, 'milnacipran is a serotonin-norepinephrine reuptake inhibitor (snri). exactly how milnacipran works to treat fibromyalgia is not known. it may work by restoring the balance of certain natural substances in the brain (serotonin and norepinephrine).', NULL, 1595, '2025-08-18', NULL, 0, 'Milnacipran is a selective serotonin and norepinephrine reuptake inhibitor (SNRI) indicated for the management of fibromyalgia in patients that are 18 years old or above [F3925]. 



While milnacipran may be used for the treatment of major depressive disorder (MDD), it is only recommended in adult patients who are 18 years old or above [F3922] due to an increased risk for suicidal ideation, thinking, and behavior in children, adolescents, and young adults taking antidepressants for major depressive disorder (MDD) and other psychiatric disorders. Some regional prescribing information notes that the use of the medication is specifically for the short-term symptomatic relief of MDD [F3919]. Nevertheless, it is important to note that the regulatory approval of and/or indications listed here for milnacipran may or may not exist and/or vary greatly between regions and nations [F3928, F3934].', 'The dual ability for milnacipran to inhibit the reuptake of both serotonin (5HT) and norepinephrine (NE) facilitates its treatment of both fibromyalgia and major depressive disorder (MDD).



In particular, it is generally believed that 5HT and NE participate in the modulation of endogenous analgesic mechanisms by way of the descending inhibitory pain pathways in the brain and spinal cord [A175759, A175843, A175846]. Although the specific mechanism of action remains unclear, some studies have proposed that low levels of 5HT may be associated with increased sensitivity to pain - a condition that could subsequently be improved by milnacipran''s capacity to enhance the presence of 5HT by inhibiting its reuptake via serotonin transporters at synaptic clefts [A175846, F3937, L5659]. Furthermore, in the CNS it is also generally believed that NE released from descending pathways can mitigate pain sensations via eliciting inhibitory effects on alpha-2A-adrenoceptors on central terminals of primary afferent nociceptors, by direct alpha-2-adrenergic action on pain-relay neurons, and by alpha-1-adrenoceptor-mediated activation of inhibitory interneurons [A175843]. Such NE pain mitigation is consequently also enhanced by milnacipran''s ability to enhance the presence of NE by inhibiting its reuptake via norepinephrine transporters at synaptic clefts [F3937].



Concurrently, milnacipran''s capacity to inhibit the reuptake of both 5HT and NE also facilitates its treatment of MDD. Given the monoamine hypothesis'' assertion that decreased 5HT can be associated with anxiety, obsessions, compulsions, and decreased NE can result in lowered alertness, energy, attention, and general interest in life, it is proposed that milnacipran''s basic activities as a serotonin and norepinephrine reuptake inhibitor could assist in treating such symptoms of MDD by increasing the presence of both 5HT and NE in the body by inhibiting their reuptake [A175840]. ', 'When utilized to treat fibromyalgia, the effect of milnacipran on the QTcF interval in patients was measured in a double-blind placebo-and positive-controlled parallel study in 88 healthy subjects using three to six times the recommended therapeutic dose for fibromyalgia at 600 mg/day [F3925]. After baseline and placebo adjustment, the maximum mean QTcF change was 8 ms - an increase that is generally not considered to be clinically significant [F3925].



Conversely, when used for treating major depressive disorder (MDD), non-clinical studies have shown that levomilnacipran binds with high affinity to the norepinephrine (NE) and serotonin (5-HT) transporters (Ki = 71-91 nM and 11 nM respectively at human transporters) [F3919, F3922, F3925]. Levomilnacipran inhibits the uptake of both NE and 5-HT in vitro and in vivo; preferentially inhibiting reuptake of NE over 5-HT by approximately 2-fold [F3919, F3922, F3925]. Levomilnacipran does not directly affect the uptake of dopamine or other neurotransmitters [F3919, F3922, F3925]. Levomilnacipran has no significant affinity for serotonergic (5-HT1-7), α- and β-adrenergic, muscarinic (M1-5), histamine (H1-4), dopamine (D1-5), opiate, benzodiazepine, and γ-aminobutyric acid (GABA) receptors in vitro [F3919, F3922]. Levomilnacipran has no significant affinity for Ca++, K+, Na+, and Cl– channels and does not inhibit the activity of human monoamine oxidases (MAO-A and MAO-B) or acetylcholinesterase [F3919, F3922, F3925].



Moreover, in ECG studies with levomilnacipran used to treat MDD, although no clinically significant changes in QTcF interval (QTcF=QT/RR0.33) were noted, it appears that the agent can cause increases in heart rate and blood pressure [F3919]. In particular, it appears that the maximum therapeutic dose of levomilnacipran at 120 mg/day is capable of causing a maximum mean difference in heart rate from placebo of 20.2 bpm and a mean difference in systolic and diastolic blood pressure from placebo ranging from 3.8 to 7.2 mmHg and 6.1 to 8.1 mmHg, respectively [F3919]. Alternatively, a supratherapeutic dose of 300 mg/day is capable of causing a maximum mean difference in heart rate from placebo of 22.1 bpm and a mean difference in systolic and diastolic blood pressure from placebo ranging from 5.4 to 7.9 mmHg and 7.9 to 10.6 mmHg, respectively [F3919].', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Supraviran 250mg i.v vial', 27726, 'سوبرافيران 250مجم فيال وريدي', '40', NULL, 'Antiviral.nucleoside', 'Antiviral.nucleoside', NULL, 'Acyclovir', 'Grunenthal-germany > al kamal importing & marketing co', 'Vial', 'فيال', '250mg', '1', NULL, NULL, '(indications) : this medication is an antiviral agent prescribed for herpes simplex genital herpes herpes zoster and chickenpox. it slows down the growth and spread of the herpes virus. acyclovir is also sometimes used to treat eczema herpeticum herpes in', NULL, 1076, '2022-07-07', NULL, 0, 'An acyclovir topical cream is indicated to treat recurrent herpes labialis in immunocompetent patients 12 years and older.[L7303] Acyclovir oral tablets, capsules, and suspensions are indicated to treat herpes zoster, genital herpes, and chickenpox.[L7315] An acyclovir topical ointment is indicated to treat initial genital herpes and limited non-life-threatening mucocutaneous herpes simplex in immunocompromised patients.[L7318] An acyclovir cream with hydrocortisone is indicated to treat recurrent herpes labialis, and shortening lesion healing time in patients 6 years and older.[L7321] An acyclovir buccal tablet is indicated for the treatment of recurrent herpes labialis.[L7324] An acyclovir ophthalmic ointment is indicated to treat acute herpetic keratitis.[L7327]', 'Acyclovir is becomes acyclovir monophosphate due to the action of viral thymidine kinase.[A180757] Acyclovir monophosphate is converted to the diphosphate form by guanylate kinase.[A903] Acyclovir diphosphate is converted to acyclovir triphosphate by nucleoside diphosphate kinase, pyruvate kinase, creatine kinase, phosphoglycerate kinase, succinyl-CoA synthetase, phosphoenolpyruvate carboxykinase and adenylosuccinate synthetase.[A903,A180781] Acyclovir triphosphate has higher affinity for viral DNA polymerase than cellular DNA polymerase and incorporates into the DNA where the missing 2'' and 3'' carbons causes DNA chain termination.[A180757] In other cases acyclovir triphosphate competes so strongly for viral DNA polymerase that other bases cannot associate with the enzyme, inactivating it.[A180757]', 'Acyclovir is a deoxynucleoside analog that inhibits the action of viral DNA polymerase and DNA replication of different herpesvirus.[L7303,L7315,L7318,L7321,L7324,L7327] Acyclovir has a wide therapeutic window as overdose is rare in otherwise healthy patients.[L7315]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Utopia neo topical oint. 20 gm', 28884, 'يوتوبيا نيو مرهم موضعي 20 جم', '11', NULL, NULL, NULL, NULL, 'Bacitracin+neomycin+polymyxin+pramoxine', 'Medizen pharmaceutical industries > utopia pharmaceuticals company', 'Oint', 'مرهم', '20 gm', '1', 'مرهم لعلاج الجروح -- مضاد حيوي يعمل علي غلق الشعيرات الدموية بالجرح ليتم التئامه', NULL, 'properties: this cream/ointment are a very effective product containing a good combination of neomycin and polymyxin b sulphate (in addition to bacitracin in this ointment) which has a wide range of antibacterial activity beside pramoxine hci which is an', NULL, 1188, '2022-04-14', NULL, 0, NULL, NULL, NULL, NULL);
