INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Rotaplanin 100 mg vial for i.v/i.m inj.', 31909, 'روتابلانين 100 مجم فيال للحقن', '177', '71', 'Glycopeptide antibiotic', 'Glycopeptide antibiotic', NULL, 'Teicoplanin', 'Rameda > biocapital pharma', 'Inj', 'حقن', '100 mg', '1', NULL, NULL, 'about teicoplanin glycopeptide antibiotic antibiotic similar to vancomycin. mechanism of action of teicoplanin teicoplanin binds to the terminal dipeptide (amino d alanyl d alanine residue) sequence of peptidoglycan and inhibits the association and cross', NULL, 652, '2024-12-15', NULL, 0, 'For the treatment of bacterial infections caused by susceptible microorganisms.', 'Teicoplanin inhibits peptidoglycan polymerization, resulting in inhibition of bacterial cell wall synthesis and cell death.', 'It is a glycopeptide antiobiotic extracted from <i>Actinoplanes teichomyceticus</i>, with a similar spectrum of activity to vancomycin. Its mechanism of action is to inhibit bacterial cell wall synthesis. Oral teicoplanin has been demonstrated to be effective in the treatment of pseudomembranous colitis and Clostridium difficile-associated diarrhoea, with comparable efficacy to vancomycin.', 'DrugBank');
