INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Theragran syrup 90 ml', 11359, 'ثيراجران شراب 90 مل', '8', NULL, 'Multivitamins', 'Multivitamins', NULL, 'Multivitamins+iron', 'Smithkline beecham', 'Syrup', 'شراب', '90 ml', '1', 'مصدر للفيتامينات -- علاج الانيميا -- مصدر للحديد -- مصدر للطاقة', NULL, NULL, NULL, 1538, '2022-08-21', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Budecort 200mcg/dose inhaler', 15551, 'بوديكورت 200 ميكروجرام /جرعة استنشاق', '55', NULL, 'Bronchodilator.anti-inflammatory.glucocorticoid steroid', 'Bronchodilator.anti-inflammatory.glucocorticoid steroid', NULL, 'Budesonide', 'Cipla ltd. - india > star international company', 'Inhaler', 'جهاز استنشاق', '200mcg', '1', NULL, NULL, 'about budesonide tab steroids antiinflammatory drugs. mechanism of action of budesonide tab budesonide controls the rate of protein synthesis depresses the migration of polymorphonuclear leukocytes fibroblasts reverses capillary permeability and lysosomal', '8901117028975', 1461, '2022-07-15', NULL, 0, 'Budesonide extended-release capsules are indicated for the treatment and maintenance of mild to moderate Crohn’s disease.[L10601,L10604] Various inhaled budesonide products are indicated for prophylactic therapy in asthma [L10607,L10613,L10619] and to reduce exacerbations of COPD.[L10619] A budesonide nasal spray is available over the counter for symptoms of hay fever and upper respiratory allergies.[L10616] Extended-release capsules are indicated to induce remission of mild to moderate ulcerative colitis[L10622] and a rectal foam is used for mild to moderate distal ulcerative colitis.[L10625] In addition, a delayed-release capsule formulation of budesonide is indicated to reduce proteinuria in adults with IgA nephropathy at risk of rapid disease progression.[L42515,L49540] Budesonide is indicated to treat eosinophilic esophagitis (EoE): For this indication, it is only approved for use in adults in Europe [L42525] while it is approved for short-term use (12 weeks) in patients 11 years of age and older in the US.[L50161]', 'The short term effects of corticosteroids are decreased vasodilation and permeability of capillaries, as well as decreased leukocyte migration to sites of inflammation.[A187463] Corticosteroids binding to the glucocorticoid receptor mediates changes in gene expression that lead to multiple downstream effects over hours to days.[A187463]



Glucocorticoids inhibit neutrophil apoptosis and demargination; they inhibit phospholipase A2, which decreases the formation of arachidonic acid derivatives; they inhibit NF-Kappa B and other inflammatory transcription factors; they promote anti-inflammatory genes like interleukin-10.[A187463]



Lower doses of corticosteroids provide an anti-inflammatory effect, while higher doses are immunosuppressive.[A187463] High doses of glucocorticoids for an extended period bind to the mineralocorticoid receptor, raising sodium levels and decreasing potassium levels.[A187463]', 'Budesonide is a glucocorticoid used to treat respiratory and digestive conditions by reducing inflammation.[L10601,L10604,L10607,L10613,L10619,L10622,L10625] It has a wide therapeutic index, as dosing varies highly from patient to patient.[L10601,L10604,L10607,L10613,L10619,L10622,L10625] Patients should be counselled regarding the risk of hypercorticism and adrenal axis suppression.[L10601,L10604,L10607,L10613,L10619,L10622,L10625]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Green - alpha 3 miu/ml (1ml) inj.', 20060, 'جرين الفا 3 مليون وحدة دولية / مل (1 مل', '90', NULL, 'Antiviral .interferon alfa', 'Antiviral .interferon alfa', NULL, 'Interferon alfa-2a', 'Koria green cross > eimc', 'Inj', 'حقن', '1ml', '1', NULL, NULL, 'indication for the treatment of chronic hepatitis c hairy cell leukemia aids-related kaposi s sarcoma and chronic myelogenous leukemia. also for the treatment of oral warts arising from hiv infection. mechanism of action interferon alpha binds to type i i', NULL, 747, '2022-07-15', NULL, 0, 'For the treatment of venereal or genital warts caused by the Human Papiloma Virus.', 'Interferon alpha binds to type I interferon receptors (IFNAR1 and IFNAR2c) which, upon dimerization, activate two Jak (Janus kinase) tyrosine kinases (Jak1 and Tyk2). These transphosphorylate themselves and phosphorylate the receptors. The phosphorylated INFAR receptors then bind to Stat1 and Stat2 (signal transducers and activators of transcription)which dimerize and activate multiple (~100) immunomodulatory and antiviral proteins. Interferon alpha binds less stably to type I interferon receptors than interferon beta.', 'Upregulates the expression of MHC I proteins, allowing for increased presentation of peptides derived from viral antigens. This enhances the activation of CD8+ T cells that are the precursors for cytotoxic T lymphocytes (CTLs) and makes the macrophage a better target for CTL-mediated killing. Interferon alpha also induce the synthesis of several key antiviral mediators, including 2''-5'' oligoadenylate synthetase (2''-5'' A synthetase) and protein kinase R.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Vidaza 100mg pd. for i.v. s.c inj', 29286, 'فيدازا 100مجم بودر لعمل محلول حقن وريد/تحت الجلد', '7,500', '4372', 'Myelodysplastic syndromes', 'Myelodysplastic syndromes', NULL, 'Azacitidine', 'Baxter oncology-germany > ramco', 'Inj', 'حقن', '100mg', '1', 'علاج سرطان الدم ونخاع العظام. وهو يعمل عن طريق إبطاء نمو الخلايا السرطانية.', NULL, NULL, '8699538774815', 5405, '2024-11-05', NULL, 0, 'Azacitidine (for subcutaneous or intravenous use) is indicated for the treatment of adult patients with the following French-American-British (FAB) myelodysplastic syndrome (MDS) subtypes: refractory anemia (RA) or refractory anemia with ringed sideroblasts (RARS) (if accompanied by neutropenia or thrombocytopenia or requiring transfusions), refractory anemia with excess blasts (RAEB), refractory anemia with excess blasts in transformation (RAEB-T), and chronic myelomonocytic leukemia (CMMoL). Azacitidine is also indicated for the treatment of pediatric patients aged 1 month and older with newly diagnosed Juvenile Myelomonocytic Leukemia (JMML).[L41910,L46861]



Azacitidine (for oral use) is indicated for continued treatment of adult patients with acute myeloid leukemia (AML) who achieved first complete remission or complete remission with incomplete blood count recovery following intensive induction chemotherapy and are not able to complete intensive curative therapy.[L35335]', 'Azacitidine (5-azacytidine) is a chemical analogue of the cytosine nucleoside present in DNA and RNA.[A1406,A1407] It induces antineoplastic activity by inhibiting DNA methyltransferase at low doses and inducing cytotoxicity by incorporating itself into RNA and DNA at high doses.[A1408,A1409,A1412] 



Covalent binding to DNA methyltransferase results in DNA hypomethylation and prevents DNA synthesis.[A1412] On the other hand, the incorporation of azacitidine into RNA and DNA leads to cytotoxicity as follows: Following cellular uptake, azacitidine is phosphorylated by uridine-cytidine kinase to form 5-azacytidine monophosphate. Afterwards, pyrimidine monophosphate and diphosphate kinases phosphorylate 5-azacytidine monophosphate to form 5-azacytidine diphosphate and triphosphate, respectively. Azacitidine triphosphate is able to incorporate into RNA, disrupting RNA metabolism and protein synthesis. The reduction of azacytidine diphosphate leads to the formation of 5-aza-deoxycytidine diphosphate, which is then phosphorylated to form 5-azadeoxycitidine triphosphate, a compound able to incorporate into DNA and inhibit DNA synthesis.[A1407]



As a ribonucleoside, azacitidine incorporates into RNA to a larger extent than into DNA. Incorporating into RNA leads to the disassembly of polyribosomes, defective methylation and acceptor function of transfer RNA, and the inhibition of protein production, resulting in cell death. During the S-phase of the cell cycle, azacitidine exhibits the highest toxicity; however, the predominant mechanism of cytotoxicity has not been elucidated.[A1407]



The cytotoxic effects of azacitidine cause the death of rapidly dividing cells, including cancer cells that are no longer responsive to normal growth control mechanisms. Non-proliferating cells are relatively insensitive to azacitidine. It is believed that azacitidine exerts its antineoplastic effects through direct cytotoxicity on abnormal hematopoietic cells in the bone marrow.[L46861]', 'The concentration of azacitidine required for maximum inhibition of DNA methylation in vitro does not cause major suppression of DNA synthesis, and hypomethylation may restore normal function to genes critical for differentiation and proliferation.[L46861] Genome-wide DNA methylation levels in bone marrow granulocytes were reduced in patients with juvenile myelomonocytic leukemia after the first treatment cycle of azacitidine (75 mg/m<sup>2</sup> or 2.5 mg/kg), confirming the DNA-hypomethylating activity of azacitidine.[L46861]



The use of azacitidine causes anemia, neutropenia and thrombocytopenia in adult patients with myelodysplastic syndrome and pediatric patients with juvenile myelomonocytic leukemia. Azacitidine may cause renal toxicity, tumor lysis syndrome and embryo-fetal toxicity. It may also lead to the development of hepatotoxicity in patients with severe pre-existing hepatic impairment.[L46861]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Depapalin 100mg/5ml syrup 250 ml', 17266, 'ديبابالين 100 مجم / 5 مل شراب 250 مل', '130', NULL, 'Anti-epileptic.gaba analogs', 'Anti-epileptic.gaba analogs', NULL, 'Pregabalin', 'Dbk pharma > rameda', 'Syrup', 'شراب', '100mg', '1', NULL, NULL, 'about pregabalin a gaba agonist anticonvulsant. mechanism of action of pregabalin pregabalin is a gaba neurotransmitter analog. it produces its action by modulating calcium channels and also reduces the release of neurotransmitters such as glutamate norep', NULL, 1046, '2022-07-21', NULL, 0, 'Pregabalin is indicated for the management of neuropathic pain associated with diabetic peripheral neuropathy, postherpetic neuralgia, fibromyalgia, neuropathic pain associated with spinal cord injury, and as adjunctive therapy for the treatment of partial-onset seizures in patients 1 month of age and older.[L7066]', 'Although the mechanism of action has not been fully elucidated, studies involving structurally related drugs suggest that presynaptic binding of pregabalin to voltage-gated calcium channels is key to the antiseizure and antinociceptive effects observed in animal models.[L7066]  



By binding presynaptically to the alpha2-delta subunit of voltage-gated calcium channels in the central nervous system, pregabalin modulates the release of several excitatory neurotransmitters including glutamate, substance-P, norepinephrine, and calcitonin gene related peptide.[A31163] In addition, pregabalin prevents the alpha2-delta subunit from being trafficked from the dorsal root ganglia to the spinal dorsal horn, which may also contribute to the mechanism of action.[A36628] 



Although pregabalin is a structural derivative of the inhibitory neurotransmitter gamma-aminobutyric acid (GABA), it does not bind directly to GABA or benzodiazepine receptors.[A31165]  ', 'Although the structure of pregabalin is similar to gamma-aminobutyric acid (GABA), it does not bind to GABA receptors.[A31163,A187190] Instead, it binds the alpha2-delta subunit of presynaptic voltage-gated calcium channels in the central nervous system.[A31163,A187190] Pregabalin does not modulate dopamine receptors, serotonin receptors, opiate receptors, sodium channels or cyclooxygenase activity.[A187190] ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Strattera 10mg 28 caps.', 27556, 'ستراتيرا 10مجم 28 كبسولة', '530', NULL, 'Attention-deficit hyperactivity disorder', 'Attention-deficit hyperactivity disorder', NULL, 'Atomoxetine', 'Eli lilly', 'Cap', 'كبسولة', '10mg', '2', NULL, NULL, 'description: atomoxetine is the first non-stimulant drug approved for the treatment of attention-deficit hyperactivity disorder (adhd). dose: dosing of children and adolescents up to 70 kg body weight initial: the recommended dose is 0.5 mg/kg and increas', NULL, 1098, '2022-07-11', NULL, 0, 'Atomoxetine is indicated for the treatment of attention deficit hyperactivity disorder (ADHD) in children and adults.', 'Atomoxetine is known to be a potent and selective inhibitor of the norepinephrine transporter (NET),[A175723] which prevents cellular reuptake of norepinephrine throughout the brain, which is thought to improve the symptoms of ADHD. More recently, positron emission tomography (PET) imaging studies in rhesus monkeys have shown that atomoxetine also binds to the serotonin transporter (SERT),[A178111] and blocks the N-methyl-d-aspartate (NMDA) receptor,[A18263] indicating a role for the glutamatergic system in the pathophysiology of ADHD. ', 'Atomoxetine is a selective norepinephrine (NE) reuptake inhibitor used for the treatment of attention deficit hyperactivity disorder (ADHD). Atomoxetine has been shown to specifically increase norepinephrine and dopamine within the prefrontal cortex, which results in improved ADHD symptoms.[A18262, A18262, F4639]



Due to atomoxetine''s noradrenergic activity, it also has effects on the cardiovascular system such as increased blood pressure and tachycardia.[T244] Sudden deaths, stroke, and myocardial infarction have been reported in patients taking atomoxetine at usual doses for ADHD. Atomoxetine should be used with caution in patients whose underlying medical conditions could be worsened by increases in blood pressure or heart rate such as certain patients with hypertension, tachycardia, or cardiovascular or cerebrovascular disease. It should not be used in patients with severe cardiac or vascular disorders whose condition would be expected to deteriorate if they experienced clinically important increases in blood pressure or heart rate. Although the role of atomoxetine in these cases is unknown, consideration should be given to not treating patients with clinically significant cardiac abnormalities. Patients who develop symptoms such as exertional chest pain, unexplained syncope, or other symptoms suggestive of cardiac disease during atomoxetine treatment should undergo a prompt cardiac evaluation.[F4639]



In general, particular care should be taken in treating ADHD in patients with comorbid bipolar disorder because of concern for possible induction of a mixed/manic episode in patients at risk for bipolar disorder. Treatment emergent psychotic or manic symptoms, e.g., hallucinations, delusional thinking, or mania in children and adolescents without a prior history of psychotic illness or mania can be caused by atomoxetine at usual doses. If such symptoms occur, consideration should be given to a possible causal role of atomoxetine, and discontinuation of treatment should be considered.[F4639]



Atomoxetine capsules increased the risk of suicidal ideation in short-term studies in children and adolescents with Attention-Deficit/Hyperactivity Disorder (ADHD). All pediatric patients being treated with atomoxetine  should be monitored appropriately and observed closely for clinical worsening, suicidality, and unusual changes in behavior, especially during the initial few months of a course of drug therapy, or at times of dose changes, either increases or decreases.[F4639]



Postmarketing reports indicate that atomoxetine can cause severe liver injury. Although no evidence of liver injury was detected in clinical trials of about 6000 patients, there have been rare cases of clinically significant liver injury that were considered probably or possibly related to atomoxetine use in postmarketing experience. Rare cases of liver failure have also been reported, including a case that resulted in a liver transplant. Atomoxetine should be discontinued in patients with jaundice or laboratory evidence of liver injury, and should not be restarted. Laboratory testing to determine liver enzyme levels should be done upon the first symptom or sign of liver dysfunction (e.g., pruritus, dark urine, jaundice, right upper quadrant tenderness, or unexplained “flu like” symptoms).[F4639]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Lovastamed 40mg 30 tab', 7568, 'لوفاستاميد 40مجم 30 قرص', '81', NULL, 'Antihyperlipidimics statins', 'Antihyperlipidimics statins', NULL, 'Lovastatin', 'Mash premiere', 'Tab', 'أقراص', '40mg', '3', 'علاج ارتفاع الدهون الثلاثية في الجسم -- علاج ارتفاع الكولسيترول في الد م -- الوقاية من امراض القلب وارتفاع ضغط الدم', NULL, NULL, '6222001401097', 1069, '2023-04-18', NULL, 0, 'Lovastatin is indicated to reduce the risk of myocardial infarction, unstable angina, and the need for coronary revascularization procedures in individuals without symptomatic cardiovascular disease, average to moderately elevated total-C and LDL-C, and below average HDL-C. It is indicated as an intervention alternative in individuals presenting dyslipidemia at risk of developing atherosclerotic vascular disease. The administration of this agent should be accompanied by the implementation of a fat and cholesterol-restricted diet.[F4661]



Therapy with lipid-altering agents should be a component of multiple risk factor intervention in those individuals at significantly increased risk for atherosclerotic vascular disease due to hypercholesterolemia. Lovastatin is indicated as an adjunct to diet for the reduction of elevated total-C and LDL-C levels in patients with primary hypercholesterolemia (Types IIa and IIb2), when the response to diet restricted in saturated fat and cholesterol and to other nonpharmacological measures alone has been inadequate.[F4661,F4664]



Lovastatin is also indicated to slow the progression of coronary atherosclerosis in patients with coronary heart disease as part of a treatment strategy to lower total-C and LDL-C to target levels.[F4661]



Lovastatin is indicated as an adjunct to diet to reduce total-C, LDL-C and apolipoprotein B levels in adolescent boys and girls with Heterozygous Familial Hypercholesterolemia (HeFH) who are at least one year post-menarche, 10 to 17 years of age, with HeFH if after an adequate trial of diet therapy the following findings are present: LDL-C remains greater than 189 mg/dL or LDL-C remains greater than 160 mg/dL and there is a positive family history of premature cardiovascular disease or two or more other CVD risk factors are present in the adolescent patient.



Before administering lovastatin, it is important to rule out the presence of secondary causes of hypercholesterolemia and a lipid profile should be performed.



Prescribing of statin medications is considered standard practice following any cardiovascular events and for people with a moderate to high risk of development of CVD. Statin-indicated conditions include diabetes mellitus, clinical atherosclerosis (including myocardial infarction, acute coronary syndromes, stable angina, documented coronary artery disease, stroke, trans ischemic attack (TIA), documented carotid disease, peripheral artery disease, and claudication), abdominal aortic aneurysm, chronic kidney disease, and severely elevated LDL-C levels.[A181087, A181406]', 'Lovastatin is a lactone which is readily hydrolyzed _in vivo_ to the corresponding β-hydroxyacid and strong inhibitor of HMG-CoA reductase, a hepatic microsomal enzyme which catalyzes the conversion of HMG-CoA (3-hydroxy-3-methylglutaryl-coenzyme A ) to mevalonate, an early rate-limiting step in cholesterol biosynthesis.[A181421,A174553] At therapeutic lovastatin doses, HMG-CoA reductase is not completely blocked, thereby allowing biologically necessary amounts of mevalonate to be available. Because the conversion of HMG-CoA to mevalonate is an early step in the biosynthetic pathway for cholesterol, therapy with lovastatin would not be expected to cause an accumulation of potentially toxic sterols.[F4664]



Lovastatin acts primarily in the liver, where decreased hepatic cholesterol concentrations stimulate the upregulation of hepatic low density lipoprotein (LDL) receptors which increase hepatic uptake of LDL. Lovastatin also inhibits hepatic synthesis of very low density lipoprotein (VLDL). The overall effect is a decrease in plasma LDL and VLDL and a significant reduction in the risk of development of CVD and all-cause mortality.[A181090,A181093,A181096,A181427,A181475]



A significant effect on LDL-C reduction was seen within 2 weeks of initiation of lovastatin, and the maximum therapeutic response occurred within 4-6 weeks. The response was maintained during continuation of therapy. Single daily doses given in the evening were more effective than the same dose given in the morning, perhaps because cholesterol is synthesized mainly at night. When therapy with lovastatin is stopped, total cholesterol has been shown to return to pre-treatment levels.[F4664]



In vitro and in vivo animal studies also demonstrate that lovastatin exerts vasculoprotective effects independent of its lipid-lowering properties, also known as the pleiotropic effects of statins.[A181424] This includes improvement in endothelial function, enhanced stability of atherosclerotic plaques, reduced oxidative stress and inflammation, and inhibition of the thrombogenic response.



Statins have also been found to bind allosterically to β2 integrin function-associated antigen-1 (LFA-1), which plays an important role in leukocyte trafficking and in T cell activation.[A181559]



Lovastatin has been reported to have beneficial effects on certain cancers. This includes a multi-factorial stress-triggered cell death (apoptosis) and DNA degradation response in breast cancer cells.[A181937] It has also been shown to inhibit histone deacetylase 2 (HDAC2) activity and increase the accumulation of acetylated histone-H3 and the expression of p21(WAF/CIP) in human cancer cells, suggesting that statins might serve as novel HDAC inhibitors for cancer therapy and chemoprevention.[A15235]', 'Lovastatin is an oral antilipemic agent which reversibly inhibits HMG-CoA reductase. It is used to lower total cholesterol, low density lipoprotein-cholesterol (LDL-C), apolipoprotein B (apoB), non-high density lipoprotein-cholesterol (non-HDL-C), and trigleride (TG) plasma concentrations while increasing HDL-C concentrations. High LDL-C, low HDL-C and high TG concentrations in the plasma are associated with increased risk of atherosclerosis and cardiovascular disease. The total cholesterol to HDL-C ratio is a strong predictor of coronary artery disease and high ratios are associated with higher risk of disease. Increased levels of HDL-C are associated with lower cardiovascular risk. By decreasing LDL-C and TG and increasing HDL-C, lovastatin reduces the risk of cardiovascular morbidity and mortality.[A174580,A181087,A181406,A181925]



Elevated cholesterol levels, and in particular, elevated low-density lipoprotein (LDL) levels, are an important risk factor for the development of CVD.[A181087] Use of statins to target and reduce LDL levels has been shown in a number of landmark studies to significantly reduce the risk of development of CVD and all-cause mortality.[A181090,A181093,A181096,A181427,A181475] Statins are considered a cost-effective treatment option for CVD due to their evidence of reducing all-cause mortality including fatal and non-fatal CVD as well as the need for surgical revascularization or angioplasty following a heart attack.[A181087, A181406] Evidence has shown that even for low-risk individuals (with <10% risk of a major vascular event occurring within 5 years) statins cause a 20%-22% relative reduction in major cardiovascular events (heart attack, stroke, coronary revascularization, and coronary death) for every 1 mmol/L reduction in LDL without any significant side effects or risks.[A174580,A181397,A181403] Clinical studies have shown that lovastatin reduces LDL-C and total cholesterol by 25-40%.[A174580] The 50% inhibitory dose is known to be of 46 mcg/kg which is translated into a reduction of approximately 30% of plasma cholesterol.[A174553]



**Myopathy/Rhabdomyolysis**



Lovastatin, like other inhibitors of HMG-CoA reductase, occasionally causes myopathy manifested as muscle pain, tenderness or weakness with creatine kinase (CK) above ten times the upper limit of normal (ULN). Myopathy sometimes takes the form of rhabdomyolysis with or without acute renal failure secondary to myoglobinuria, and rare fatalities have occurred. The risk of myopathy is dose-related and is increased by high levels of HMG-CoA reductase inhibitory activity in plasma. In a clinical study (EXCEL)[A181925] in which patients were carefully monitored and some interacting drugs were excluded, there was one case of myopathy among 4933 patients randomized to lovastatin 20 to 40 mg daily for 48 weeks, and 4 among 1649 patients randomized to 80 mg daily.



Predisposing factors for myopathy include advanced age (≥65 years), female gender, uncontrolled hypothyroidism, and renal impairment. Chinese patients may also be at increased risk for myopathy. In most cases, muscle symptoms and CK increases resolved when treatment was promptly discontinued.



The risk of myopathy during treatment with lovastatin may be increased with concurrent administration of interacting drugs such as [fenofibrate], [niacin], [gemfibrozil], [cyclosporine], and strong inhibitors of the CYP3A4 enzyme.  Cases of myopathy, including rhabdomyolysis, have been reported with HMG-CoA reductase inhibitors coadministered with [colchicine], and caution should therefore be exercised when prescribing these two medications together.[F4661,F4664]



Real-world data from observational studies has suggested that 10-15% of people taking statins may experience muscle aches at some point during treatment.[A182258]



**Liver Dysfunction**



Persistent increases (to more than 3 times the upper limit of normal) in serum transaminases occurred in 1.9% of adult patients who received lovastatin for at least one year in early clinical trials. When the drug was interrupted or discontinued in these patients, the transaminase levels usually fell slowly to pretreatment levels. The increases usually appeared 3 to 12 months after the start of therapy with lovastatin, and were not associated with jaundice or other clinical signs or symptoms.[F4661] In the EXCEL study,[A181925] the incidence of persistent increases in serum transaminases over 48 weeks was 0.1% for placebo, 0.1% at 20 mg/day, 0.9% at 40 mg/day, and 1.5% at 80 mg/day in patients on lovastatin. However, in post-marketing experience with lovastatin, symptomatic liver disease has been reported rarely at all dosages.[F4661]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Linezox 100mg/5ml oral susp. 150 ml', 21851, 'لينزوكس 100مجم/5مل معلق بالفم 150 مل', '72', NULL, 'Antibiotic.oxazolidinone', 'Antibiotic.oxazolidinone', NULL, 'Linezolid', 'El-obour > therapeutica pharma', 'Susp', 'معلق', '100mg', '1', NULL, NULL, 'about linezolid oxazolidinone derivative reversible nonselective monoamine oxidase inhibitor antibiotic. mechanism of action of linezolid linezolid binds to the 23 s fraction of 50 s ribosome and inhibit the formation of 70s initiation complex. indication', NULL, 760, '2022-07-12', NULL, 0, 'Linezolid is indicated in adults and children for the treatment of infections caused by susceptible Gram-positive bacteria, including nosocomial pneumonia, community-acquired pneumonia, skin and skin structure infections, and vancomycin-resistant _Enterococcus faecium_ infections.[L32965] Examples of susceptible bacteria include _Staphylococcus aureus_, _Streptococcus pneumoniae_, _Streptococcus pyogenes_, and _Streptococcus agalactiae_.[L32965]



Linezolid is not indicated for the treatment of Gram-negative infections, nor has it been evaluated for use longer than 28 days.[L32965]', 'Linezolid exerts its antibacterial effects by interfering with bacterial protein translation.[A233035] It binds to a site on the bacterial 23S ribosomal RNA of the 50S subunit and prevents the formation of a functional 70S initiation complex, which is essential for bacterial reproduction, thereby preventing bacteria from dividing.[L32965]



Point mutations in the bacterial 23S rRNA can lead to linezolid resistance, and the development of linezolid-resistant _Enterococcus faecium_ and _Staphylococcus aureus_ have been documented during its clinical use.[L32965] As antimicrobial susceptibility patterns are geographically distinct, local antibiograms should be consulted to ensure adequate coverage of relevant pathogens prior to use.', 'Linezolid is an oxazolidinone antibacterial agent effective against most strains of aerobic Gram-positive bacteria and mycobacteria. It appears to be bacteriostatic against both staphylococci and enterococci and bactericidal against most isolates of streptococci.[L32965] Linezolid has shown some _in vitro_ activity against Gram-negative and anaerobic bacteria but is not considered efficacious against these organisms.[L32965]



Linezolid is a reversible and non-selective inhibitor of monoamine oxidase (MAO) enzymes and can therefore contribute to the development of serotonin syndrome when administered alongside serotonergic agents such as selective serotonin re-uptake inhibitors (SSRIs) or tricyclic antidepressants (TCAs).[L32965] Linezolid should not be used for the treatment of catheter-related bloodstream infections or catheter-site infections, as the risk of therapy appears to outweigh its benefits under these circumstances.[L32965]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Bio balance organic aloe vera shampoo 330 ml', 15208, 'بيو بالانس شامبو الصبار العضوي 180 مل', '265', NULL, 'Hair care', 'Hair care', NULL, NULL, 'Alpaya dermaceuticals > vamer pharma', 'Amp', 'أمبول', '330 ml', '1', NULL, NULL, 'product details organic aloe vera shampoo perfect for dry & brittle hair soothes dry scalp moisturizes and strengths breakage-prone hair even the driest of strands do not stand a chance against bio balance organic aloe vera sulfate-free shampoo that works', NULL, 690, '2022-07-27', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Piroxifar 20mg 10 dispersable tab.', 24961, 'بيروكسيفار 20مجم 10 اقراص', '12', '8.5', 'Nsaid.oxicam', 'Nsaid.oxicam', NULL, 'Piroxicam', 'Pharco', 'Tab', 'أقراص', '20mg', '1', NULL, NULL, 'about piroxicam nsaid oxicam derivative analgesic antiinflammatory. mechanism of action of piroxicam drug inhibits the biosynthesis and release of prostaglandins by inhibiting cyclooxygenase enzyme in a reversible manner. in this way it reduces the pg (pr', '6223000013885', 738, '2025-08-23', NULL, 0, 'For treatment of osteoarthritis and rheumatoid arthritis.', 'The antiinflammatory effect of Piroxicam may result from the reversible inhibition of cyclooxygenase, causing the peripheral inhibition of prostaglandin synthesis. The prostaglandins are produced by an enzyme called Cox-1. Piroxicam blocks the Cox-1 enzyme, resulting into the disruption of production of prostaglandins. Piroxicam also inhibits the migration of leukocytes into sites of inflammation and prevents the formation of thromboxane A2, an aggregating agent, by the platelets.', 'Piroxicam is in a class of drugs called nonsteroidal anti-inflammatory drugs (NSAIDs). Piroxicam works by reducing hormones that cause inflammation and pain in the body. Piroxicam is used to reduce the pain, inflammation, and stiffness caused by rheumatoid arthritis and osteoarthritis.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Xemose cream 200 ml', 29732, 'زيموس كريم 200مل', '399', NULL, 'Skin care', 'Skin care', NULL, NULL, 'Uriage dermatological laboratories > biotech egypt', 'Cream', 'كريم', '200 ml', '1', NULL, NULL, 'xemose universal emollient cream benefits from the last patented discovery of laboratoires dermatologiques dï-uriage the cerasterol - 2f which restores reinforces durably the cutaneous barrier and soothes the irritations of the driest skins. its associati', '3661434000119', 459, '2022-12-14', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Terhexine syrup 120ml', 11296, 'تيرهكسين شراب 120 مل', '20', '12', 'Cough drugs', 'Cough drugs', NULL, 'Bromhexine+guaifenesin+menthol+terbutaline', 'European egyptian pharm. ind.', 'Syrup', 'شراب', '120ml', '1', 'علاج الكحة', NULL, 'indications expectorants are drugs which have been proposed to be useful in loosening and liquefying mucous in soothing irritated bronchial mucosa and in making coughs more productive. azmaril is recommended for clinical relief of cough associated with br', '6221076050179', 2201, '2024-01-21', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Dogmapride fort 200mg 30 tab', 17756, 'دوجمابرايد فورت 200مجم 30 قرص', '24', NULL, 'Psychiatric.antipsychotics', 'Psychiatric.antipsychotics', NULL, 'Sulpiride', 'Amoun', 'Tab', 'أقراص', '200mg', '3', NULL, NULL, NULL, NULL, 892, '2022-07-05', NULL, 0, 'Sulpiride is indicated for the treatment of acute and chronic schizophrenia.[L31933]', 'Sulpiride is a selective dopamine D2 and D3 receptor antagonist.[A229678,A229683,A229688] _In silico_ studies show that sulpiride may interact with the Asp-119 and Phe-417 amino acid residues of these receptors.[A229688] It is estimated that D2 receptors should be 65-80% occupied for optimal treatment and minimal adverse effects.[A229693]', 'Sulpiride is a substituted benzamide derivative and a selective dopamine D2 antagonist indicated to treat acute and chronic schizophrenia.[A229678,A229683,A229688,L31933] It has a short duration of action as it is given twice daily, and a wide therapeutic window as patients have survived single doses as high as 16g.[L31933] Patients should be counselled regarding increased motor agitation, extrapyramidal reactions, and neuroleptic malignant syndrome.[L31933]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Immunizzare 10 caps.', 13150, 'اميونيزار 10 كبسولات', '160', NULL, 'Immunity booster', 'Immunity booster', NULL, 'Epicor+vitamin c+vitamin d+zinc', 'Rx healthcare', 'Cap', 'كبسولة', NULL, '1', NULL, NULL, NULL, NULL, 904, '2022-09-22', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Terbifungin 1% cream 15 gm', 28053, 'تيربيفنجين 1% كريم 15جم', '8', NULL, 'Antifungals.allylamines', 'Antifungals.allylamines', NULL, 'Terbinafine', 'Global napi pharmaceuticals > global pharmaceutical industries', 'Cream', 'كريم', '1%', '1', NULL, NULL, 'about terbinafine a synthetic allylamine derivative antifungal. mechanism of action of terbinafine terbinafine is a fungicidal drug. it interfere the conversion of squalene to ergosterol by inhibiting the enzyme squalene epoxidase.this results in accumula', NULL, 761, '2022-07-25', NULL, 0, 'Terbinafine hydrochloride is indicated to treat fungal skin and nail infections caused by _Trichophyton_ species, _Microsporum canis_, _Epidermophyton floccosum_,[L9068] and _Tinea_ species.[L9065] Terbinafine hydrochloride also treats yeast infections of the skin caused by _Candida_ species and _Malassezia furfur_.[L9068]', 'Terbinafine inhibits the enzyme squalene monooxygenase (also called squalene epoxidase), preventing the conversion of squalene to 2,3-oxydosqualene, a step in the synthesis of ergosterol.[A1279,A1281,L9068] This inhibition leads to decreased ergosterol, which would normally be incorporated into the cell wall, and accumulation of squalene.[A1281,L9068]



Generation of a large number of squalene containing vesicles in the cytoplasm may leach other lipids away from, and further weaken, the cell wall.[A1281]', 'Terbinafine is an allylamine antifungal that inhibits squalene epoxidase (also known as squalene monooxygenase) to prevent the formation of ergosterol and cause an accumulation of squalene, weakening the cell wall of fungal cells.[A1279,A1281,L9068] Terbinafine distributes into tissues and has a long terminal elimination half life, so the duration of action is long.[A1279] Overdose with terbinafine is rare, even above the therapeutic dose, so the therapeutic index is wide.[L9065,L9068] Patients taking oral terbinafine should have liver function tests performed prior to treatment to reduce the risk of liver injury.[L9065]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Awadist plus 20 f.c. tabs.', 565, 'اواديست بلس 20 قرص', '56', '37', 'Cold products', 'Cold products', NULL, 'Paracetamol(acetaminophen)+diphenhydramine', 'High performance pharma (hp pharma)', 'Tab', 'أقراص', NULL, '2', '1- مهديء لنزلات البرد والانفلونزا.', NULL, 'paracetamol+diphenhydramine', '6224011394062', 10165, '2024-08-20', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Femina cream (v-guard) 50 gm', 4780, 'فيمينا كريم 50 جم', '80', '60', 'Vaginal care', 'Vaginal care', NULL, 'Citric acid+thyme oil+tween 20+glycerin+menthol+aloe vera+tea tree oil+chamomile extract+triclosan+m', 'Original group', 'Cream', 'كريم', '50 gm', '1', NULL, NULL, '*1st effective natural vaginal cream *can solve the causes and symptoms of vaginitis', NULL, 1166, '2025-02-10', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Expectasolvin 600 mg 20 ext. rel. tabs.', 20611, 'اكسبكتاسولفين 600مجم 20 قرص', '62', NULL, 'Cough products', 'Cough products', NULL, 'Guaifenesin', 'Viomix', 'Tab', 'أقراص', '600 mg', '2', NULL, NULL, NULL, NULL, 1020, '2024-07-09', NULL, 0, 'Guaifenesin is an expectorant that is indicated for providing temporary symptomatic relief from congested chests and coughs which may be due to a cold, bronchitis, and/or other breathing illnesses [FDA Label, F4510, L6079].', 'Although the exact mechanism of action of guaifenesin may not yet be formally or totally elucidated, it is believed that expectorants like guaifenesin function by increasing mucus secretion [A177661]. Moreover, it is also further proposed that such expectorants may also act as an irritant to gastric vagal receptors, and recruit efferent parasympathetic reflexes that can elicit glandular exocytosis that is comprised of a less viscous mucus mixture [A177661]. Subsequently, these actions may provoke coughing that can ultimately flush difficult to access, congealed mucopurulent material from obstructed small airways to facilitate a temporary improvement for the individual [A177661].



Consequently, while it is generally proposed that guaifenesin functions as an expectorant by helping to loosen phlegm (mucus) and thin bronchial secretions to rid the bronchial passageways of bothersome mucus and make coughs more productive, there has also been research to suggest that guaifenesin possesses and is capable of demonstrating anticonvulsant and muscle relaxant effects to some degree possibly by acting as an NMDA receptor antagonist [A177652].', 'Guaifenesin is categorized as an expectorant that acts by enhancing the output of phlegm (sputum) and bronchial secretions via decreasing the adhesiveness and surface tension of such material [F4516]. Furthermore, guaifenesin elicits an increased flow of less viscous gastric secretions that subsequently promote ciliary action - all actions that ultimately change dry, unproductive coughing to coughs that are more productive and less frequent [F4516]. Essentially, by decreasing the viscosity and adhesiveness of such secretions, guaifenesin enhances the efficacy of mucociliary activity in removing accumulated secretions from the upper and lower airway [F4516].', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Dawavitrol 300.000 i.u / 20.000 i.u / 1 ml sol. for i.m / oral 1 amp', 1950, 'دوافيترول 300.000/20.000 وحدة دولية / 1 مل امبول', '47', '23.5', 'Vitamin a+vitamin d', 'Vitamin a+vitamin d', NULL, 'Vitamin a+cholecalciferol(vitamin d3)', 'Dawaa pharma', 'Amp', 'أمبول', '1 ml', '1', NULL, NULL, NULL, '6225000497740', 1870, '2025-09-22', NULL, 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Fenac 1.5% topical cream 20 gm', 4785, 'فيناك 1.5% كريم 20 جم', '6', NULL, 'Nsaid.acetic acid derivatives', 'Nsaid.acetic acid derivatives', NULL, 'Aceclofenac', 'European egyptian pharm. ind.', 'Cream', 'كريم', '1.5%', '1', NULL, NULL, 'indications for aceclofenac : 1. ankylosing spondylitis 2. osteoarthritis 3. symptomatic treatment of pain and inflammation in post-traumatic pain 4. cervical pain 5. low back pain 6. acute gout half life: the mean plasma elimination half-life is 4 - 4.3', '6221076220060', 969, '2022-10-16', NULL, 0, 'Aceclofenac is indicated for the relief of pain and inflammation in osteoarthritis, rheumatoid arthritis and ankylosing spondylitis. ', 'Through COX-2 inhibition, aceclofenac downregulates the production of various inflammatory mediators including prostaglandin E2 (PGE2), IL-1β, and TNF from the arachidonic acid (AA) pathway. Inhibition of IL-6 is thought to be mediated by diclofenac converted from aceclofenac [A19671]. Suppressed action of inflammatory cytokines decreases the production of reactive oxygen species. Aceclofenac is shown to decreased production of nitrous oxide in human articular chondrocytes [A19667]. In addition, aceclofenac interferes with neutrophil adhesion to endothelium by decreasing the expression of L-selectin (CD62L), which is a cell adhesion molecule expressed on lymphocytes [A19673]. Aceclofenac is proposed to stimulate the synthesis of glycosaminoglycan in human osteoarthritic cartilage which may be mediated through its inhibitory action on IL-1 production and activity [A19666]. The chrondroprotective effects are generated by 4''-hydroxyaceclofenac which suppresses IL-1 mediated production of promatrix metalloproteinase-1 and metalloproteinase-3 and interferes with the release of proteoglycan from chrondrocytes [A19666, A19667, A19672]. ', 'Aceclofenac is a NSAID that inhibits both isoforms of COX enzyme, a key enzyme involved in the inflammatory cascade. COX-1 enzyme is a constitutive enzyme involved in prostacyclin production and protective functions of gastric mucosa whereas COX-2 is an inducible enzyme involved in the production of inflammatory mediators in response to inflammatory stimuli. Aceclofenac displays more selectivity towards COX-2 (IC50 of 0.77uM) than COX-1 (IC50 of >100uM), which promotes its gastric tolerance compared to other NSAIDs. The primary metabolite, 4''-hydroxyaceclofenac, also minimally inhibits COX-2 with IC50 value of 36uM [A19667]. Although the mode of action of aceclofenac is thought to mainly arise from the inhibition of synthesis of prostaglandins (PGE2), aceclofenac also inhibits the production of inflammatory cytokines, interleukins (IL-1β, IL-6), and tumor necrosis factors (TNF) [A19666, A19667]. It is also reported that aceclofenac also affects the cell adhesion molecules from neutrophils [A19673]. Aceclofenac also targets the synthesis of glycosaminoglycan and mediates chrondroprotective effects [A19666]. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Limitless zinc 25 mg 30 tabs.', 21835, 'ليميتلس زنك 25 مجم 30 قرص', '89', '60', 'Zinc', 'Zinc', NULL, 'Zinc gluconate', 'Eva pharma', 'Tab', 'أقراص', '25 mg', '1', 'مكمل غذائي -- مصدر للزنك --مقوي للمناعة', NULL, 'ï- supports a healthy immune system for all family.', NULL, 6257, '2023-08-13', NULL, 0, 'Zinc gluconate is mainly indicated in conditions like zinc deficiency, and can also be administered in adjunctive therapy as an alternative drug of choice in diarrhea [L2088].', 'Although the mechanism of action is not completely known, zinc supplementation may be used to increase immunity against viruses or may interfere with the replication of certain viruses, such as the human papillomavirus (HPV) [L2080].



', 'Zinc is an important mineral found in almost every cell in the human body. It promotes the activity of about 100 enzymes. Zinc deficiency is often associated with an increased risk of infection. When they are used to treat the common cold, zinc supplements may interfere with rhinovirus cleavage or adhesion and may play a role in protecting plasma membranes from microbial toxins and complement [L2086].', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Anthelios xl spf 50+ spray 200 ml', 14220, 'انثيليوس اكس ال سبراي 200مل', '399', NULL, 'Sun block', 'Sun block', NULL, 'Sun protection formula', 'La roche-posay > united company for trading & distribution', 'Spray', 'بخاخ', '200 ml', '1', NULL, NULL, NULL, NULL, 753, '2022-07-28', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Nurtec odt 75 mg 2 oro-disintegrating tabs', 4364, 'نورتيك او دي تي 75مجم 2 قرص تحت اللسان', '1,438', NULL, 'Antimigraine.calcitonin gene-related peptide (cgrp) antagonist', 'Antimigraine.calcitonin gene-related peptide (cgrp) antagonist', NULL, 'Rimegepant', 'Pfizer', 'Tab', 'أقراص', '75 mg', '1', NULL, NULL, NULL, NULL, 1244, '2025-06-17', NULL, 0, 'Rimegepant is indicated for the acute treatment of migraine with or without aura in adults. Rimegepant is also indicated for the prevention of episodic migraine in adults.[L11971]', 'The currently accepted theory of migraine pathophysiology considers dysfunction of the central nervous system, in particular the trigeminal ganglion, to be the root cause behind the condition.[A189207] Activation of the trigeminal ganglion triggers the stimulation of trigeminal afferents that project to the spinal cord and synapse on various pain-sensing intra- and extracranial structures, such as the dura mater. Pain signals are then further transmitted via second-order ascending neurons to the brainstem, hypothalamus, and thalamic nuclei, and from there to several cortical regions (e.g. auditory, visual, motor cortices).[A189207] The trigeminal ganglion appears to amplify and perpetuate the migraine headache pain through the activation of perivascular fibers and the release of molecules involved in pain generation, such as calcitonin gene-related peptide (CGRP).[A189207]



The α-isoform of CGRP, expressed in primary sensory neurons, is a potent vasodilator and has been implicated in migraine pathogenesis - CGRP levels are acutely elevated during migraine attacks, return to normal following treatment with triptan medications, and intravenous infusions of CGRP have been shown to trigger migraine-like headaches in migraine patients. In addition to its vasodilatory properties, CGRP appears to be a pronociceptive factor that modulates neuronal excitability to facilitate pain responses.[A189213]



Rimegepant is an antagonist of the calcitonin gene-related peptide receptor[A189330] - it competes with CGRP for occupancy at these receptors, preventing the actions of CGRP and its ability to amplify and perpetuate migraine headache pain, ultimately terminating the headache.[A189213]', 'Rimegepant helps to abort migraine headaches by preventing the activity of a pronociceptive molecule that has been implicated in migraine pathophysiology.[L11971] It is intended for use as an abortive migraine therapy and therefore has a relatively rapid onset of effect, with most efficacy trials evaluating for effect at the 2 hour mark.[L11971] 



Rimegepant does not require dose adjustment in patients with mild, moderate, or severe renal impairment,[L11971] nor does it require dose adjustment in patients with mild or moderate hepatic impairment. In clinical trials, plasma concentrations of rimegepant were significantly higher in patients with severe (i.e. Child-Pugh C) hepatic impairment - it should therefore be avoided in this population.[L11971] Hypersensitivity reactions have occurred during clinical studies and patients should be made aware of this possibility. Rimegepant should be discontinued immediately if hypersensitivity reaction occurs.[L11971]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Veneer hair shampoo 120 ml', 29095, 'فينير شامبو للشعر 120مل', '33', NULL, 'Hair care', 'Hair care', NULL, 'Selenium sulphide', 'Egyptian company for cosmetics > rand pharm co.', 'Amp', 'أمبول', '120 ml', '1', NULL, NULL, 'anti-dandruff', NULL, 1271, '2022-07-27', NULL, 0, 'For treatment of tinea versicolor, tinea capitis, dandruff and seborrheic dermatitis of the scalp.', 'Topical selenium sulfide may act by an antimitotic action, resulting in a reduction in the turnover of epidermal cells. It also has local irritant, antibacterial, and mild antifungal activity, which may contribute to its effectiveness. An antimitotic mechanism of action is suggested by data showing that selenium sulfide decreases the rate of incorporation of radioactively labeled thymidine into the DNA of dermal epithelial cells. The following organisms are generally considered susceptible to selenium sulfide in vitro: <i>Malassezia furfur</i>, <i>Microsporum sp.</i> including <i>Microsporum audouinii</i> and <i>Microsporum canis</i>, <i>Pityrosporon sp.</i>, <i>Trichophyton sp.</i> including <i>Trichophyton schoenleinii</i> and <i>Trichophyton tonsurans</i>. Selenium sulfide has been shown to be sporicidal to <i>T. tonsurans</i>, the most common etiologic agent of tinea capitis. One in-vitro study demonstrated that 2.5% selenium sulfide was equivalent in sporicidal activity to both 1% and 2% zinc pyrithione.', 'Selenium sulfide is an antifungal agent often used in shampoos for the treatment of dandruff and seborrheic dermatitis. Selenium sulfide is highly active in inhibiting the growth of <i>P. ovale</i>. It is also a proven cytostatic agent, slowing the growth of both hyperproliferative and normal cells in dandruff and seborrheic dermatitis. A 0.6% micronized form of selenium sulfide is also safe and effective for dandruff.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Zapritens 1mg 10 f.c. tab.', 29839, 'زابريتينس 1مجم 10 اقراص', '11', NULL, 'Antihypertensive.ace', 'Antihypertensive.ace', NULL, 'Cilazapril', 'Multi-apex', 'Tab', 'أقراص', '1mg', '1', NULL, NULL, 'description one of the angiotensin-converting enzyme inhibitors (ace inhibitors) used for hypertension. it is a prodrug that is hydrolyzed after absorption to its main metabolite cilazaprilat. indication cilazapril is an ace inhibtor class drug used in th', NULL, 702, '2022-08-10', NULL, 0, 'Cilazapril is an ACE inhibtor class drug used in the treatment of hypertension and heart failure.', 'Cilazapril is a pyridazine ACE inhibitor. It competes with angiotensin I for binding at the angiotensin-converting enzyme, blocking the conversion of angiotensin I to angiotensin II. As angiotensin II is a vasoconstrictor and a negative feedback mediator for renin activity, lower angiotensin II levels results in a decrease in blood pressure, an increase in renin activity, and stimulation of baroreceptor reflex mechanisms. Kininase II, an enzyme which degrades the vasodilator bradykinin, is identical to ACE and may also be inhibited.', 'Cilazapril inhibits the production angiotensin II. By doing so, it decreases sodium and water reabsorption (via aldosterone) and it decreases vasoconstriction. The combined effect of this is a decrease in vascular resistance, and therefore, blood pressure. The absolute bioavailability of cilazaprilat after oral administration of cilazapril is 57% based on urinary recovery data. (The absolute bioavailability of cilazaprilat after oral administration of cilazaprilat is 19%.) Ingestion of food immediately before the administration of cilazapril reduces the average peak plasma concentration of cilazaprilat by 29%, delays the peak by one hour and reduces the bioavailability of cilazaprilat by 14%. These pharmacokinetic changes have little influence on plasma ACE inhibition. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Freshteld oral spray 60 ml', 14337, 'فريشتلد سبراي للفم 60 مل', '70', '59.75', 'Oral care', 'Oral care', NULL, 'Eucalyptus+menthol+propolis+vit.e+hedera helix+chlorhexidine', 'Telda pharma', 'Spray', 'بخاخ', '60 ml', '1', 'سيراى لعلاج حالات تقرحات الفم. التهابات الحلق. رائحة الفم الغير مرغوبة.', NULL, NULL, '6225000497412', 1707, '2024-09-28', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Forticare (cappuccino flavor) susp. 125 ml', 19349, 'فورتيكير (بطعم الكابتشينو) معلق 125 مل', '75', NULL, 'Dietary supplement', 'Dietary supplement', NULL, NULL, 'Nutricia > united company for trading & distribution', 'Cap', 'كبسولة', '125 ml', '1', 'مكمل غذائي', NULL, NULL, NULL, 4758, '2022-12-03', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Anarcol 50mg 30 f.c. tab.', 721, 'اناركول 30 قرص', '540', '285', 'Analgesics.narcotic opioid', 'Analgesics.narcotic opioid', NULL, 'Naltrexone', 'Multi-apex', 'Tab', 'أقراص', '50mg', '3', 'مسكن للألم قوي', NULL, NULL, '6223003200435', 2388, '2023-12-20', NULL, 0, 'Used as an adjunct to a medically supervised behaviour modification program in the maintenance of opiate cessation in individuals who were formerly physically dependent on opiates and who have successfully undergone detoxification. Also used for the management of alcohol dependence in conjunction with a behavioural modification program.', 'Naltrexone is a pure opiate antagonist and has little or no agonist activity. The mechanism of action of naltrexone in alcoholism is not understood; however, involvement of the endogenous opioid system is suggested by preclinical data. Naltrexone is thought to act as a competitive antagonist at mc, κ, and δ receptors in the CNS, with the highest affintiy for the μ receptor. Naltrexone competitively binds to such receptors and may block the effects of endogenous opioids. This leads to the antagonization of most of the subjective and objective effects of opiates, including respiratory depression, miosis, euphoria, and drug craving. The major metabolite of naltrexone, 6-β-naltrexol, is also an opiate antagonist and may contribute to the antagonistic activity of the drug.', 'Naltrexone, a pure opioid antagonist, is a synthetic congener of oxymorphone with no opioid agonist properties. Naltrexone is indicated in the treatment of alcohol dependence and for the blockade of the effects of exogenously administered opioids. It markedly attenuates or completely blocks, reversibly, the subjective effects of intravenously administered opioids. When co-administered with morphine, on a chronic basis, naltrexone blocks the physical dependence to morphine, heroin and other opioids. In subjects physically dependent on opioids, naltrexone will precipitate withdrawal symptomatology.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Enerzair breezhaler 150/50/160 mcg 30 inh. caps. + inhaler', 30479, 'انيرزاير 150/50/160مكجم 30 كبسولة', '758', '606', 'Bronchodilator', 'Bronchodilator', NULL, 'Indacaterol+glycopyrronium+mometasone', 'Novartis', 'Cap', 'كبسولة', '160 mcg', '3', 'موسع للشعب الهوائية يعمل على تحسين اداء الجهاز التنفسى وعلاج ازمات الربو', NULL, 'glycopyrronium is indicated as a long-term once-daily maintenance bronchodilator treatment in patients with chronic obstructive pulmonary disease (copd) including chronic bronchitis and emphysema. not for acute use. indacaterol is a long-acting beta 2 -ag', NULL, 2965, '2024-03-17', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ceforan 500mg vial', 16036, 'سيفوران 500 مجم فيال', '32', '22', 'Antibiotic.cephalosporin.third-generation', 'Antibiotic.cephalosporin.third-generation', NULL, 'Cefotaxime', 'Pharco b', 'Vial', 'فيال', '500mg', '1', NULL, NULL, 'about cefotaxime third generation cephalosporin parenteral antibiotic. mechanism of action of cefotaxime cefotaxime is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & aerobic gram negative organisms by inhi', '6222012202003', 1069, '2025-01-29', NULL, 0, 'Used to treat gonorrhoea, meningitis, and severe infections including infections of the kidney (pyelonephritis) and urinary system. Also used before an operation to prevent infection after surgery.', 'The bactericidal activity of cefotaxime results from the inhibition of cell wall synthesis via affinity for penicillin-binding proteins (PBPs). Cefotaxime shows high affinity for penicillin-binding proteins in the cell wall including PBP Ib and PBP III.', 'Cefotaxime is a third generation intravenous cephalosporin antibiotic. It has broad spectrum activity against Gram positive and Gram negative bacteria. It does not have activity against <i>Pseudomonas aeruginosa</i>. Cefotaxime works by inhibiting bacterial cell wall biosynthesis. A positive feature of cefotaxime is that it display a resistance to penicillinases and is useful to treat infections that are resistant to penicillin derivatives.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Mascglucom 5mg/ml eye drops 5 ml', 22344, 'ماسكجلوكوم قطرة عين 10مل', '14', NULL, 'Antiglaucoma', 'Antiglaucoma', NULL, 'Levobunolol', 'Sigma tec > concord pharmaceutical industries', 'Drops', 'نقط', '5mg', '1', NULL, NULL, NULL, NULL, 744, '2022-08-01', NULL, 0, 'For lowering intraocular pressure (IOP) and may be used in patients with chronic open-angle glaucoma or ocular hypertension.', 'Levobunolol''s mechanism of action in reducing IOP is not clearly defined, but is believed to be due to a reduction of the production of aqueous humor via blockage of endogenous catecholamine-stimulated increases in cyclic adenosine monophosphate (AMP) concentrations within the ciliary processes.', 'Levobunolol is an ophthalmic beta-blocker, equally effective at &beta;(1)- and &beta;(2)-receptor sites. Levobunolol reduces both elevated and normal IOP in patients with or without glaucoma. In patients with elevated IOP, levobunolol reduces mean IOP by approximately 25-40% from baseline. As the drug is a nonselective &beta-adrenergic blocking agent, it can produce both systemic pulmonary and cardiovascular effects following topical application to the eye. These effects include adverse pulmonary effects (eg. bronchoconstriction, increased airway resistance), and a decrease in blood pressure and heart rate. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Amloveran 10/5mg 30 tabs.', 13971, 'املوفيران 5/10 مجم 30 قرص', '111', NULL, 'Antihypertensive.combined ace with calcium channel blocker', 'Antihypertensive.combined ace with calcium channel blocker', NULL, 'Amlodipine + perindopril', 'Eva pharma', 'Tab', 'أقراص', '5mg', '3', NULL, NULL, 'indication for the treatment of mild to moderate essential hypertension mild to moderate congestive heart failure and to reduce the cardiovascular risk of individuals with hypertension or post-myocardial infarction and stable coronary disease. about amlod', NULL, 1022, '2022-07-18', NULL, 0, 'Amlodipine may be used alone or in combination with other antihypertensive and antianginal agents for the treatment of the following conditions [FDA label]:



• Hypertension 



• Coronary artery disease



• Chronic stable angina



• Vasospastic angina (Prinzmetal’s or Variant angina) 



• Angiographically documented coronary artery disease in patients without heart failure or an ejection fraction < 40%', '**Mechanism of action on blood pressure**



Amlodipine is considered a peripheral arterial vasodilator that exerts its action directly on vascular smooth muscle to lead to a reduction in peripheral vascular resistance, causing a decrease in blood pressure. Amlodipine is a dihydropyridine calcium antagonist (calcium ion antagonist or slow-channel blocker) that inhibits the influx of calcium ions into both vascular smooth muscle and cardiac muscle. Experimental studies imply that amlodipine binds to both _dihydropyridine_ and _nondihydropyridine_ binding sites, located on cell membranes. The contraction of cardiac muscle and vascular smooth muscle are dependent on the movement of extracellular calcium ions into these cells by specific ion channels. Amlodipine blocks calcium ion influx across cell membranes with selectivity. A stronger effect of amlodipine is exerted on vascular smooth muscle cells than on cardiac muscle cells [FDA label]. Direct actions of amlodipine on vascular smooth muscle result in reduced blood pressure [F3757]. 



**Mechanism of action in angina**



The exact mechanism by which amlodipine relieves the symptoms of angina have not been fully elucidated to this date, however, the mechanism of action is likely twofold:



Amlodipine has a dilating effect on peripheral arterioles, reducing the total peripheral resistance (afterload) against which the cardiac muscle functions. Since the heart rate remains stable during amlodipine administration, the reduced work of the heart reduces both myocardial energy use and oxygen requirements [F3757].



Dilatation of the main coronary arteries and coronary arterioles, both in healthy and ischemic areas, is another possible mechanism of amlodipine reduction of blood pressure. The dilatation causes an increase in myocardial oxygen delivery in patients experiencing coronary artery spasm (Prinzmetal''s or variant angina) and reduces coronary vasoconstriction caused by smoking [F3757].', '**General pharmacodynamic effects**



Amlodipine has a strong affinity for cell membranes, modulating calcium influx by inhibiting selected membrane calcium channels. This drug''s unique binding properties allow for its long-acting action and less frequent dosing regimen [A573], [FDA label].  



**Hemodynamic effects**



After the administration of therapeutic doses of amlodipine to patients diagnosed with hypertension, amlodipine causes vasodilation, which results in a reduction of supine and standing blood pressure. During these blood pressure reductions, there are no clinically significant changes in heart rate or plasma catecholamine levels with long-term use. Acute intravenous administration of amlodipine reduces arterial blood pressure and increases heart rate in patients with chronic stable angina, however, chronic oral administration of amlodipine in clinical studies did not cause clinically significant alterations in heart rate or blood pressures in patients diagnosed with angina and normal blood pressure. With long-term, once daily oral administration, antihypertensive effectiveness is maintained for at least 24 hours [FDA label]. 



**Electrophysiologic effects**



Amlodipine does not change sinoatrial (SA) nodal function or atrioventricular (AV) conduction in animals or humans. In patients who were diagnosed with chronic stable angina, the intravenous administration of 10 mg of amlodipine did not cause clinically significant alterations A-H and H-V conduction and sinus node recovery time after cardiac pacing. Patients administered amlodipine with concomitant beta-blockers produced similar results. In clinical trials in which amlodipine was given in combination with beta-blockers to patients diagnosed with hypertension or angina, no adverse effects on electrocardiographic parameters were noted. In clinical studies comprised of angina patients alone, amlodipine did not change electrocardiographic intervals or produce high degrees of AV block [FDA label]. 



**Effects on angina**



Amlodipine relieves the symptoms of chest pain associated with angina. In patients diagnosed with angina, daily administration of a single amlodipine dose increases total exercise time, the time to angina onset, and the time to 1 mm ST-segment depression on ECG studies, decreases anginal attack frequency, and decreases the requirement for nitroglycerin tablets [F3757]. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Pentolegic 1% eye drops 30*0.5 ml sdu', 24696, 'بنتولجيك قطرة عين 30 عبوة', '54', NULL, 'Mydriatic', 'Mydriatic', NULL, 'Cyclopentolate', 'Orchidia pharmaceutical industries', 'Pen', 'قلم', '1%', '1', 'معقم للعين -- موسع لحدقة العين قبل العمليات الجراحية والفحص', NULL, 'about cyclopentolate ophthalmic anticholinergic(anti-muscarinic) a mydriatic and cycloplegic agent for diagnostic purposes. mechanism of action of cyclopentolate cyclopentolate is a muscarinic receptor blocker. it nonselectively inhibits the muscarinic ac', '6223005441164', 2088, '2022-05-15', NULL, 0, 'Used mainly to produce mydriasis and cycloplegia for diagnostic purposes.', 'By blocking muscarinic receptors, cyclopentolate produces dilatation of the pupil (mydriasis) and prevents the eye from accommodating for near vision (cycloplegia).', 'Cyclopentolate is an anti-muscarinic in the same class as atropine and scopolamine. Cyclopentolate blocks the receptors in the muscles of the eye (muscarinic receptors). These receptors are involved controlling the pupil size and the shape of the lens. Cyclopentolate thus induces relaxation of the sphincter of the iris and the ciliary muscles. When applied topically to the eyes, it causes a rapid, intense cycloplegic and mydriatic effect that is maximal in 15 to 60 minutes; recovery usually occurs within 24 hours. The cycloplegic and mydriatic effects are slower in onset and longer in duration in patients who have dark pigmented irises.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Corolive cream 40 gm', 30353, 'كوروليف كريم 40 جم', '64', '43', 'Hemorrhoids', 'Hemorrhoids', NULL, 'Thyme extract+tea tree oil+chamomile extract+glyceryl monosterate+lavender oil+rosemary extract+olive oil', 'Italian cosmetics company > core pharm', 'Cream', 'كريم', '40 gm', '1', NULL, NULL, 'corolive cream used for relieve of discomfort of piles', NULL, 1661, '2025-08-13', NULL, 0, NULL, NULL, NULL, NULL);
