INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Nausilex 50mg/2ml i.v./i.m. 3 amps.', 8608, 'نوزيلكس 50مجم/2مل 3 امبولات', '72', '49.5', 'Dopamine antagonist- anti-emetic', 'Dopamine antagonist- anti-emetic', NULL, 'Alizapride', 'Mina pharm', 'Amp', 'أمبول', '50mg', '3', 'مضاد للقيء والترجيع', NULL, NULL, '6222003702383', 1681, '2025-02-19', NULL, 0, 'Alizapride is used in the treatment of nausea and vomiting, including postoperative nausea and vomiting. ', 'The anti-emetic action of Alizapride is due to its antagonist activity at D2 receptors in the chemoreceptor trigger zone (CTZ) in the central nervous system (CNS)—this action prevents nausea and vomiting triggered by most stimuli. Structurally similar to metoclopramide and, therefore, shares similar other atributres related to emesis and prokinetics.', 'Alizapride is a dopamine antagonist.', 'DrugBank');
