INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Cidocetine 250 mg 12 caps.', 2564, 'سيدوستين 250مجم 12 كبسولة', '29', NULL, 'Antibiotic', 'Antibiotic', NULL, 'Chloramphenicol', 'Cid', 'Cap', 'كبسولة', '250 mg', '2', 'مضاد حيوي', NULL, NULL, '6221043010618', 2246, '2022-05-28', NULL, 0, 'Used in treatment of cholera, as it destroys the vibrios and decreases the diarrhea. It is effective against tetracycline-resistant vibrios. It is also used in eye drops or ointment to treat bacterial conjunctivitis.', 'Chloramphenicol is lipid-soluble, allowing it to diffuse through the bacterial cell membrane. It then reversibly binds to the L16 protein of the 50S subunit of bacterial ribosomes, where transfer of amino acids to growing peptide chains is prevented (perhaps by suppression of peptidyl transferase activity), thus inhibiting peptide bond formation and subsequent protein synthesis.', 'Chloramphenicol is a broad-spectrum antibiotic that was derived from the bacterium Streptomyces venezuelae and is now produced synthetically. Chloramphenicol is effective against a wide variety of microorganisms, but due to serious side-effects (e.g., damage to the bone marrow, including aplastic anemia) in humans, it is usually reserved for the treatment of serious and life-threatening infections (e.g., typhoid fever). Chloramphenicol is bacteriostatic but may be bactericidal in high concentrations or when used against highly susceptible organisms. Chloramphenicol stops bacterial growth by binding to the bacterial ribosome (blocking peptidyl transferase) and inhibiting protein synthesis.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Kempoxone 1 gm i.m. vial', 6830, 'كيمبوكسون 1جم فيال عضل', '56', '39', 'Antibiotic.cephalosporin.third-generation', 'Antibiotic.cephalosporin.third-generation', NULL, 'Ceftriaxone', 'Kahira > modern office for import', 'Vial', 'فيال', '1 gm', '1', NULL, NULL, NULL, '6224010511057', 2476, '2024-06-29', NULL, 0, 'Ceftriaxone is used for the treatment of the infections (respiratory, skin, soft tissue, UTI, ENT) caused by susceptible organisms.[L15082] Organisms that are generally susceptible to ceftriaxone include S. pneumoniae, S. pyogenes (group A beta-hemolytic streptococci), coagulase-negative staphylococci, Some Enterobacter spp, H. influenzae, N. gonorrhoeae, P. mirabilis, E. coli, Klebsiella spp, M. catarrhalis, B. burgdorferi, and some oral anaerobes.[A215582] ', 'Ceftriaxone works by inhibiting the mucopeptide synthesis in the bacterial cell wall.[T324,L15082] The beta-lactam moiety of ceftriaxone binds to carboxypeptidases, endopeptidases, and transpeptidases in the bacterial cytoplasmic membrane. These enzymes are involved in cell-wall synthesis and cell division. Binding of ceftriaxone to these enzymes causes the enzyme to lose activity; therefore, the bacteria produce defective cell walls, causing cell death.', 'Ceftriaxone is a cephalosporin/cephamycin beta-lactam antibiotic used in the treatment of bacterial infections caused by susceptible, usually gram-positive, organisms.[L15082] Ceftriaxone has <i>in vitro</i> activity against gram-positive aerobic, gram-negative aerobic, and anaerobic bacteria.[L15087] The bactericidal activity of ceftriaxone results from the inhibition of cell wall synthesis and is mediated through ceftriaxone binding to penicillin-binding proteins (PBPs).[T324] Ceftriaxone is stable against hydrolysis by a variety of beta-lactamases, including penicillinases, and cephalosporinases and extended-spectrum beta-lactamases.[L15082] However, resistance to ceftriaxone usually occurs through beta-lactamase hydrolysis, altered PBPs, or reduced bacterial cell permeability.[T324] Ceftriaxone should not be mixed with or giving in the same IV line as diluents/products containing calcium as they may cause ceftriaxone to precipitate.[L15082] Ceftriaxone use may also cause biliary sludge or gallbladder pseudolithiasis.[L15082,A215602] ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Alexoprine forte 300mg 200 tab.', 305, 'اليكسوبرين فورت 300مجم 200 قرص', '120', '80', 'Nsaid', 'Nsaid', NULL, 'Aluminium aspirin', 'Alexandria', 'Tab', 'أقراص', '300mg', '20', NULL, NULL, NULL, '6223000171639', 1395, '2023-12-28', NULL, 0, NULL, 'Aluminum Acetate is an astringent.  An astrignent is a chemical that tends to shrink or constrict body tissues, usually locally after topical medicinal application.  The shrinkage or constriction is through osmotic flow of water (or other fluids) away from the area where the astringent was applied.  Astringent medicines cause shrinkage of mucous membranes or exposed tissues and are often used internally to check discharge of blood serum or mucous secretions. This can happen with a sore throat, hemorrhages, diarrhea, or with peptic ulcers. Externally applied astringents, which cause mild coagulation of skin proteins, dry, harden, and protect the skin. Acne sufferers are often advised to use astringents if they have oily skin. Astringents also help heal stretch marks and other scars. Mild astringent solutions are used in the relief of such minor skin irritations as those resulting from superficial cuts, allergies, insect bites, or fungal infections such as athlete''s foot. ', NULL, 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Marvy c oral drops 15 ml', 3946, 'مارفي سي نقط فم 15 مل', '40', '30', 'Vitamin c', 'Vitamin c', NULL, 'Vitamin c', 'Marvel', 'Drops', 'نقط', '15 ml', '1', NULL, NULL, NULL, NULL, 1267, '2025-07-22', NULL, 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Inrest massage gel 50 gm', 20855, 'انريست جل مساج 50 جم', '15', NULL, 'Massage', 'Massage', NULL, 'Camphor+eucalyptus+methyl salicylate', 'Egpi > arabian international group (aig)', 'Gel', 'جل', '50 gm', '1', NULL, NULL, NULL, NULL, 539, '2022-07-12', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Gabimash 800 mg 30 tabs.', 5278, 'جابيماش 800مجم 30 قرص', '216', '83', 'Anti-epileptic.gaba analogs', 'Anti-epileptic.gaba analogs', NULL, 'Gabapentin', 'Mash premiere', 'Tab', 'أقراص', '800 mg', '3', 'علاج الصرع -- تقليل التشنجات الناتجة عن الصرع', NULL, NULL, '6222001400328', 15893, '2024-10-19', NULL, 0, 'In the United States, gabapentin is officially indicated for the treatment of postherpetic neuralgia in adults and for the adjunctive treatment of partial-onset seizures, with or without secondary generalization, in patients 3 years of age and older.[L8717] In Europe, gabapentin is indicated for adjunctive therapy in the treatment of partial-onset seizures, with or without secondary generalization, in patients 6 years of age and older and as monotherapy in patients 12 years of age and older. It is also used in adults for the treatment of various types of peripheral neuropathic pain, such as painful diabetic neuropathy.[L8732]', 'The precise mechanism through which gabapentin exerts its therapeutic effects is unclear.[L8717,L8720] The primary mode of action appears to be at the auxillary α2δ-1 subunit of voltage-gated calcium channels (though a low affinity for the α2δ-2 subunit has also been reported).[A186179,A14097,A186277] The major function of these subunits is to facilitate the movement of pore-forming α1 subunits of calcium channels from the endoplasmic reticulum to the cell membrane of pre-synaptic neurons.[A186179] There is evidence that chronic pain states can cause an increase in the expression of α2δ subunits and that these changes correlate with hyperalgesia.[A14097] Gabapentin appears to inhibit the action of α2δ-1 subunits, thus decreasing the density of pre-synaptic voltage-gated calcium channels and subsequent release of excitatory neurotransmitters.[A186179] It is likely that this inhibition is also responsible for the anti-epileptic action of gabapentin.[A186277]



There is some evidence that gabapentin also acts on adenosine receptors[A11009, A186209] and voltage-gated potassium channels,[A186212] though the clinical relevance of its action at these sites is unclear.', 'Gabapentin is an anti-convulsant medication that inhibits the release of excitatory neurotransmitters, allowing for its use against pathologic neurotransmission such as that seen in neuropathic pain and seizure disorders.[L8717,L8732] It has a wide therapeutic index, with doses in excess of 8000 mg/kg failing to cause a fatal reaction in rats.[L8765]



Gabapentin is ineffective in absence seizures and should be used in caution in patients with mixed seizure disorders involving absence seizures. Gabapentin has been associated with drug reaction with eosinophilia and systemic symptoms (DRESS), otherwise known as multi-organ hypersensitivity. This reaction can prove fatal and early symptoms such as fever, lymphadenopathy, and rash should be promptly investigated.[L8717,L8720,L8732]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Pelembap spray', 4621, 'بليمباب سبراي', '40', NULL, 'Nasal spray', 'Nasal spray', NULL, 'Natural tee trea+rosemary+aloevera+spdium chloride', 'Yojeen', 'Spray', 'بخاخ', NULL, '1', NULL, NULL, NULL, NULL, 852, '2025-06-18', NULL, 0, NULL, 'The predominant targets of NK cells are ‘stressed’ cells with downregulated expression of class I MHC (MHC-I), a common mechanism used by malignant or virus-infected cells to evade recognition by the body’s immune system. In response to encountering these cells, NK cells release two types of proteins, perforins and granzyme B, which attack the target cell’s membrane, damage its organelles, and trigger apoptosis. NK Cell immunotherapy is an emerging anti-cancer strategy, as these cells are able to exert effects without activating the graft-versus-host disease cascade. In addition, their broad mechanism allows for generally safe administration of NK cells that are not immunologically matched to a patient. ', NULL, 'DrugBank');
