INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Parox 200mg 10 f.c. tab.', 9656, 'باروكس 200 مجم 10 اقراص', '32', NULL, 'Antibiotic.quinolone', 'Antibiotic.quinolone', NULL, 'Sparfloxacin', 'Jedco int. co. for pharmaceuticals', 'Tab', 'أقراص', '200mg', '1', NULL, NULL, 'about sparfloxacin a third generation fluoroquinolone a synthetic broad-spectrum antibiotic. mechanism of action of sparfloxacin sparfloxacin is a bacteriostatic at low concentration and bactericidal at high concentration. it acts by inhibiting the enzyme', NULL, 943, '2022-12-08', NULL, 0, 'For the treatment of adults with the following infections caused by susceptible strains microorganisms: community-acquired pneumonia (caused by <i>Chlamydia pneumoniae</i>, <i>Haemophilus influenzae</i>, <i>Haemophilus parainfluenzae</i>, <i>Moraxella catarrhalis</i>, <i>Mycoplasma pneumoniae</i>, or <i>Streptococcus pneumoniae</i>) and acute bacterial exacerbations of chronic bronchitis (caused by <i>Chlamydia pneumoniae</i>, <i>Enterobacter cloacae</i>, <i>Haemophilus influenzae</i>, <i>Haemophilus parainfluenzae</i>, <i>Klebsiella pneumoniae</i>, <i>Moraxella catarrhalis</i>, <i>Staphylococcus aureus</i>, or <i>Streptococcus pneumoniae</i>).', 'The bactericidal action of sparfloxacin results from inhibition of the enzymes topoisomerase II (DNA gyrase) and topoisomerase IV, which are required for bacterial DNA replication, transcription, repair, and recombination.', 'Sparfloxacin is a synthetic fluoroquinolone broad-spectrum antimicrobial agent in the same class as ofloxacin and norfloxacin. Sparfloxacin has in vitro activity against a wide range of gram-negative and gram-positive microorganisms. Sparfloxacin exerts its antibacterial activity by inhibiting DNA gyrase, a bacterial topoisomerase. DNA gyrase is an essential enzyme which controls DNA topology and assists in DNA replication, repair, deactivation, and transcription. Quinolones differ in chemical structure and mode of action from (beta)-lactam antibiotics. Quinolones may, therefore, be active against bacteria resistant to (beta)-lactam antibiotics. Although cross-resistance has been observed between sparfloxacin and other fluoroquinolones, some microorganisms resistant to other fluoroquinolones may be susceptible to sparfloxacin. In vitro tests show that the combination of sparfloxacin and rifampin is antagonistic against Staphylococcus aureus.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Syranovo 120 ml syrup', 25636, 'سيرانوفو شراب 120 مل', '55', NULL, 'Cough syrup', 'Cough syrup', NULL, 'Ivy leaf+licorice+thyme', 'Novocare', 'Syrup', 'شراب', '120 ml', '1', NULL, NULL, NULL, NULL, 1109, '2022-09-22', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Pulmicort 100mcg/dose turbuhaler', 25481, 'بلميكورت 100مكجم/جرعة تربوهيلر', '75', NULL, 'Bronchodilator.anti-inflammatory.glucocorticoid steroid', 'Bronchodilator.anti-inflammatory.glucocorticoid steroid', NULL, 'Budesonide', 'Astra zeneca', 'Unknown', 'غير محدد', '100mcg', '1', NULL, NULL, 'about budesonide tab steroids antiinflammatory drugs. mechanism of action of budesonide tab budesonide controls the rate of protein synthesis depresses the migration of polymorphonuclear leukocytes fibroblasts reverses capillary permeability and lysosomal', '7321839710926', 1186, '2022-12-08', NULL, 0, 'Budesonide extended-release capsules are indicated for the treatment and maintenance of mild to moderate Crohn’s disease.[L10601,L10604] Various inhaled budesonide products are indicated for prophylactic therapy in asthma [L10607,L10613,L10619] and to reduce exacerbations of COPD.[L10619] A budesonide nasal spray is available over the counter for symptoms of hay fever and upper respiratory allergies.[L10616] Extended-release capsules are indicated to induce remission of mild to moderate ulcerative colitis[L10622] and a rectal foam is used for mild to moderate distal ulcerative colitis.[L10625] In addition, a delayed-release capsule formulation of budesonide is indicated to reduce proteinuria in adults with IgA nephropathy at risk of rapid disease progression.[L42515,L49540] Budesonide is indicated to treat eosinophilic esophagitis (EoE): For this indication, it is only approved for use in adults in Europe [L42525] while it is approved for short-term use (12 weeks) in patients 11 years of age and older in the US.[L50161]', 'The short term effects of corticosteroids are decreased vasodilation and permeability of capillaries, as well as decreased leukocyte migration to sites of inflammation.[A187463] Corticosteroids binding to the glucocorticoid receptor mediates changes in gene expression that lead to multiple downstream effects over hours to days.[A187463]



Glucocorticoids inhibit neutrophil apoptosis and demargination; they inhibit phospholipase A2, which decreases the formation of arachidonic acid derivatives; they inhibit NF-Kappa B and other inflammatory transcription factors; they promote anti-inflammatory genes like interleukin-10.[A187463]



Lower doses of corticosteroids provide an anti-inflammatory effect, while higher doses are immunosuppressive.[A187463] High doses of glucocorticoids for an extended period bind to the mineralocorticoid receptor, raising sodium levels and decreasing potassium levels.[A187463]', 'Budesonide is a glucocorticoid used to treat respiratory and digestive conditions by reducing inflammation.[L10601,L10604,L10607,L10613,L10619,L10622,L10625] It has a wide therapeutic index, as dosing varies highly from patient to patient.[L10601,L10604,L10607,L10613,L10619,L10622,L10625] Patients should be counselled regarding the risk of hypercorticism and adrenal axis suppression.[L10601,L10604,L10607,L10613,L10619,L10622,L10625]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Tronista 20 tabs', 8051, 'ترونيستا 20 قرص', '260', '160', 'Weight management', 'Weight management', NULL, 'Green coffee+african mango+raspberry ketones+garcinia cambodia', 'Curemedy pharma', 'Tab', 'أقراص', NULL, '2', 'مكمل غذائى غنى بالفيتامينات لتنظيم عمليات الحرق والتحكم بالوزن', NULL, NULL, NULL, 3819, '2025-07-15', NULL, 0, NULL, NULL, NULL, NULL);
