INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Etoricox-hexal 90 mg 30 f.c.tabs.', 32038, 'ايتوريكوكس -هيكسال 90 مجم 30 قرص', '306', '210', 'Nsaid.selective cox-2 inhibitors', 'Nsaid.selective cox-2 inhibitors', NULL, 'Etoricoxib', 'Hexal ag germany > novartis pharma scientific office', 'Tab', 'أقراص', '90 mg', '3', NULL, NULL, 'etoricoxib is indicated for the treatment of rheumatoid arthritis psoriatic arthritis osteoarthritis ankylosing spondylitis chronic low back pain acute pain and gout. like any other selective cox-2 inhibitor (coxib) etoricoxib selectively inhibits isoform', NULL, 10353, '2024-10-26', NULL, 0, 'For the treatment of rheumatoid arthritis, osteoarthritis, ankylosing spondylitis, chronic low back pain, acute pain and gout.', 'Like any other COX-2 selective inhibitor Etoricoxib selectively inhibits isoform 2 of cyclo-oxigenase enzyme (COX-2), preventing production of prostaglandins (PGs) from arachidonic acid.', 'Etoricoxib is a COX-2 selective inhibitor (approximately 106 times more selective for COX-2 inhibition over COX-1).', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Motiprid 1mg/ml susp. 100ml', 23011, 'موتيبريد 1مجم/مل شراب معلق 100مل', '9', NULL, 'Prokinetic agent', 'Prokinetic agent', NULL, 'Cisapride', 'T3a pharma', 'Susp', 'معلق', '1mg', '1', NULL, NULL, NULL, NULL, 761, '2022-07-21', NULL, 0, 'For the symptomatic treatment of adult patients with nocturnal heartburn due to gastroesophageal reflux disease.', 'Cisapride acts through the stimulation of the serotonin 5-HT<sub>4</sub> receptors which increases acetylcholine release in the enteric nervous system (specifically the myenteric plexus). This results in increased tone and amplitude of gastric (especially antral) contractions, relaxation of the pyloric sphincter and the duodenal bulb, and increased peristalsis of the duodenum and jejunum resulting in accelerated gastric emptying and intestinal transit.', 'Cisapride is a parasympathomimetic which acts as a serotonin 5-HT<sub>4</sub> agonist; upon activation of the receptor signaling pathway, cisapride promotes the release of acetylcholine neurotransmitters in the enteric nervous system. Cisapride stimulates motility of the upper gastrointestinal tract without stimulating gastric, biliary, or pancreatic secretions. Cisapride increases the tone and amplitude of gastric (especially antral) contractions, relaxes the pyloric sphincter and the duodenal bulb, and increases peristalsis of the duodenum and jejunum resulting in accelerated gastric emptying and intestinal transit. It increases the resting tone of the lower esophageal sphincter. It has little, if any, effect on the motility of the colon or gallbladder. Cisapride does not induce muscarinic or nicotinic receptor stimulation, nor does it inhibit acetylcholinesterase activity.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Edemex 0.5mg/2ml 3 amps. i.m/i.v', 2409, 'اديميكس 0.5 مجم/2 مل 3 أمبولات', '24', '45', 'Diuretic.loop', 'Diuretic.loop', NULL, 'Bumetanide', 'Memphis', 'Amp', 'أمبول', '0.5mg', '3', NULL, NULL, NULL, NULL, 961, '2025-01-16', NULL, 0, 'For the treatment of edema associated with congestive heart failure, hepatic and renal disease including the nephrotic syndrome.', 'Bumetanide interferes with renal cAMP and/or inhibits the sodium-potassium ATPase pump. Bumetanide appears to block the active reabsorption of chloride and possibly sodium in the ascending loop of Henle, altering electrolyte transfer in the proximal tubule. This results in excretion of sodium, chloride, and water and, hence, diuresis.', 'Bumetanide is a loop diuretic of the sulfamyl category to treat heart failure. It is often used in patients in whom high doses of furosemide are ineffective. There is however no reason not to use bumetanide as a first choice drug. The main difference between the two substances is in bioavailability. Bumetanide has more predictable pharmacokinetic properties as well as clinical effect. In patients with normal renal function, bumetanide is 40 times more effective than furosemide.', 'DrugBank');
