INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4222, 'Mirramorcare hair oil 120 ml', 'ميراموركير 120 مل زيت الشعر', '150', NULL, 'Hair care', 'Olive oil+argan oil+keratin+vitamin c+biotin+vitamin e+retinol', 'Repair pharma', 'Hair oil', 'زيت شعر', '120 ml', '1', NULL, NULL, NULL, NULL, 833, '2025-06-16', 0, 'In a clinical context, olive oil is used as a source of calories and fatty acids in patients requiring parenteral nutrition. A formulation called Clinolipid, comprising olive oil and [soybean oil], is indicated in all age groups, including term and preterm neonates, as a source of calories and essential fatty acids for parenteral nutrition (PN) when oral or enteral nutrition is not possible, insufficient, or contraindicated.[L51013]', 'Fatty acids act as a substrate in energy production through beta-oxidation as well as important components of cell membrane structures and prescursors for bioactive molecules like prostaglandins.', 'Provides a source of calories and fatty acids.[L51013]', 'DrugBank', 0, 0, 0);
