INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4285, 'Epinor 400mg 14 tab.', 'ابينور 400مجم 14 قرص', '35', '21.6', 'Antibiotic.quinolone', 'Norfloxacin', 'Eipico', 'Tab', 'أقراص', '400mg', '2', 'مضاد حيوي.', 'about norfloxacin a synthetic second generation synthetic fluoroquinolone (quinolone) broad spectrum antibiotic. mechanism of action of norfloxacin norfloxacin is a bacteriostatic at low concentration and bactericidal at high concentration. it act by inhi', '6221032112170', NULL, 3698, '2025-08-11', 0, 'For the treatment of urinary tract infection', 'The bactericidal action of Norfloxacin results from inhibition of the enzymes topoisomerase II (DNA gyrase) and topoisomerase IV, which are required for bacterial DNA replication, transcription, repair, and recombination. Norfloxacin is a broad-spectrum antibiotic agent that is shown to be effective against various Gram-positive and Gram-negative bacterial species. The fluorine atom at the 6 position increases potency against gram-negative organisms, and the piperazine moiety at the 7 position is responsible for anti-pseudomonal activity', 'Norfloxacin is a quinolone/fluoroquinolone antibiotic. Norfloxacin is bactericidal and its mode of action depends on blocking of bacterial DNA replication by binding itself to an enzyme called DNA gyrase, which allows the untwisting required to replicate one DNA double helix into two. Notably the drug has 100 times higher affinity for bacterial DNA gyrase than for mammalian.', 'DrugBank', 0, 0, 0);
