INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (8093, 'Loteranol 0.5% eye oint 5 gm', 'لوتيرانول 0.5% مرهم للعين 5 جم', '47', '41', 'Glucocorticoid', 'Loteprednol etabonate', 'Orchidia', 'Oint', 'مرهم', '0.5%', '1', NULL, NULL, '6223005444400', NULL, 1012, '2025-12-13', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Loteprednol Etabonate (LE) is a topical corticoid anti-inflammatory. It is used in ophthalmic solution for the treatment of steroid responsive inflammatory conditions of the eye such as allergic conjunctivitis, uveitis, acne rosacea, superficial punctate keratitis, herpes zoster keratitis, iritis, cyclitis, and selected infective conjunctivitides. As a nasal spray, it can be used for the treatment and management of seasonal allergic rhinitis.

Most prescription LE products, however, tend to be indicated for the treatment of post-operative inflammation and pain following ocular surgery [FDA Label]. A number of such new formulations that have been approved include Kala Pharmaceutical''s Inveltys - the first twice-daily (BID) ocular corticosteroid approved for this indication, designed specifically to enhance patient compliance and simplified dosing compared to all other similar ocular steroids that are dosed four times daily [L4545].

Moreover, LE was purposefully engineered to be a ''soft drug'', one that is designed to be active locally at the site of administration and then rapidly metabolized to inactive components after eliciting its actions at the desired location, thereby subsequently minimizing the chance for adverse effects [A38693].', '[]', '[]');
