INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (8563, 'Napibradine 5mg 30 f.c. tabs.', 'نابيبرادين 5 مجم 30 قرص', '234', '178', 'Anti-ischemic', 'Ivabradine', 'Global napi pharmaceuticals', 'Tab', 'أقراص', '5mg', '3', 'علاج مرضى قصور الدم -- تنظيم ضربات القلب', 'about ivabradine antianginal drug mechanism of action of ivabradine ivabradine is a heart rate lowering agent that works through selective and specific inhibition of the cardiac pacemaker if current. if current controls the spontaneous diastolic depolaris', '6224000821838', NULL, 5105, '2024-08-10', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Ivabradine is a novel heart rate lowering medicine for the symptomatic management of stable angina pectoralis and symptomatic chronic heart failure.  Ivabradine, brand name Corlanor, was approved by the FDA in April 2015 for the treatment of chronic heart failure in patients with an ejection fraction of ≤35%, in sinus rhythm with resting heart rate ≥70 beats per minute, who are not on beta-blockers due to contraindications or already receiving maximum beta-blocker dose. Recently a new indication was added to treat symptomatic heart failure from dilated cardiomyopathy for patients 6 months or more in age[Label]. Ivabradine acts by selectively inhibiting the "funny" channel pacemaker current (If) in the sinoatrial node in a dose-dependent fashion, resulting in a lower heart rate and thus more blood to flow to the myocardium. Although non-dihydropyridine calcium channel blockers and beta blockers also effectively lower heart rate, they exhibit adverse events due to their negative ionotropic effects. Therefore, as ivabradine is designed as a "pure" heart rate-lowering drug by selectively acting on the If channels, it may offer a more favorable side effect profile due to its lower likelihood of causing serious adverse effects.', '["C01EB17", "C07FX05", "C07FX06"]', '[]');
