INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (9268, 'Onsime 1 gm i.m/i.v vial', 'اونسيم 1جم فيال', '31', NULL, 'Antibiotic.cephalosporin.fourth-generation', 'Cefepime', 'Sigma tec', 'Vial', 'فيال', '1 gm', '1', NULL, 'about cefepime fourth generation cephalosporin antibiotic. mechanism of action of cefepime cefepime is a 3rd generation cephalosporin which exerts its bactericidal action by inhibiting bacterial cell wall synthesis. cefepime inhibits transpeptidase & thus', '6221172002775', NULL, 884, '2022-12-07', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Cefepime is a fourth-generation cephalosporin antibiotic developed in 1994. Cefepime is active against Gram-positive and Gram-negative bacteria, and has greater activity against both compared to third-generation antibiotics.[A2457,A249050] Cefepime is normally used to treat severe nosocomial pneumonia and infections caused by multi-resistant microorganisms such as _Pseudomonas aeruginosa_, and is also indicated for the empirical treatment of febrile neutropenia.[A249050] The popularity of its third-generation predecessors, its clinical efficacy, and the high prevalence of multidrug-resistant bacteria might be some of the factors leading to an increase in the use of cefepime. The activity of cefepime against Enterobacteriaceae, _Pseudomonas aeruginosa_, and _Staphylococcus aureus_ is due to its high stability toward beta-lactamases.[A249050] In general, cefepime seems to be well tolerated; however, patients treated with this antibiotic, especially those with renal impairment, may develop neurotoxicity.[A249050,L42095,L42100]', '["J01DE01", "J01RA06"]', '[]');
