INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (9733, 'Pentasa 1 gm 28 supp', 'بنتازا 1 جرام 28 اقماع /لبوس', '702', '434', 'Ulcerative colitis', 'Mesalazine(mesalamine)(5-asa)(5-aminosalicylic acid)', 'Ferring pharmaceuticals > multipharma', 'Pen', 'قلم', '1 gm', '4', '-- التهاب القولون التقرحي -- علاج مرض الكرون -- علاج التهاب القولون شديد التقرح -- تقليل الالتهابات والالام', NULL, '7640128543923', NULL, 10555, '2024-11-03', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'An anti-inflammatory agent, structurally related to the salicylates and non-steroidal anti-inflammatory drugs like acetylsalicylic acid, which is active in inflammatory bowel disease [A174022]. Although demonstrably effective in treating and maintaining remission for ulcerative colitis, mesalazine has historically faced a number of issues regarding its lack of stability as a pharmaceutical agent [A174019]. Throughout the late seventies and the eighties, important research initiatives developed stable mesalazine formulations like the eudragit-S coating of Asacol brand mesalazine and the Pentasa brand''s encapsulation of mesalazine within microgranules [A174019]. In the present day, contemporary research regarding novel methods to stabilize mesalazine continues and interest in the agent''s capacity to decrease inflammatory activity and subsequently potentially reduce the risk of colorectal cancer in conditions like ulcerative colitis is maintained.[A174019,A174022]', '["A07EC02"]', '[]');
