INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (9824, 'Prucasoft 2mg 14 f.c. tab', 'بروكاسوفت 2مجم 14 قرص ملين', '118', '88', 'Laxative', 'Prucalopride', 'Marcyrl', 'Tab', 'أقراص', '2mg', '2', '1- علاج حالات الإمساك المزمن عندما تفشل الملينات العادية في تقديم الراحة الكافية.', NULL, '6223003575359', NULL, 19113, '2024-08-10', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Prucalopride is a dihydrobenzofurancarboxamide derivative from the benzofurane family that selectively stimulates 5-HT4 receptors and thus, it presents enterokinetic properties.[A37348] The high selectivity of prucalopride allowed further development as it prevented the cardiac adverse reactions observed due to non-target effects of precedent therapies.[A40254] Prucalopride was developed by Shire Development LLC and approved for use in Europe in 2009,[A40250] in Canada on December 7, 2011 and by the FDA on December 17, 2018.[L4880]', '["A06AX05"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (9825, 'Regimax 120mg 30 caps', 'ريجيماكس 30 كبسولة', '240', '162', 'Weight loss', 'Orlistat', 'Global napi pharmaceuticals > penta pharma-egypt', 'Cap', 'كبسولة', '120mg', '3', '1- للتخسيس والتنحيف وانقاص الوزن.', NULL, '6224007738016', NULL, 21213, '2024-05-27', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'The global prevalence of obesity is increasing rapidly. Obesity-related complications lead to significant personal and economic burden by reducing quality of life and increasing the cost of healthcare.  In some individuals, diet and exercise are insufficient to maintain weight loss, and pharmacological or surgical intervention is required.[A229928]

Orlistat is a lipase inhibitor used in the treatment of obesity that works by inhibiting fat-metabolizing enzymes. It was approved by the FDA for use in combination with a reduced-calorie diet in 1999.[L11130] This drug is a generally well-tolerated and effective weight-loss aid and is now available in both over-the-counter[L31963] and prescription preparations, depending on the dosage quantity.[L11130]', '["A08AB01"]', '[]');
