INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (10438, 'Sigmazidim 1 gm vial for i.v./i.m. inj.', 'سيجمازيديم 1جم فيال', '41', '15', 'Antibiotic.cephalosporin.third-generation', 'Ceftazidime', 'Pharco b > sigma tec', 'Inj', 'حقن', '1 gm', '1', NULL, NULL, '6221172002447', NULL, 1495, '2024-11-17', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Bacteria possess a cell wall comprising a glycopeptide polymer commonly known as peptidoglycan, which is synthesized and remodelled through the action of a family of enzymes known as "penicillin-binding proteins" (PBPs).[A232920] β-lactam antibiotics, including cephalosporins, are PBP inhibitors that, through inhibition of essential PBPs, result in impaired cell wall homeostasis, loss of cell integrity, and ultimately bacterial cell death.[A232920, A232925, A232930] Ceftazidime is a third-generation cephalosporin with broad-spectrum antibacterial activity, including against some treatment-resistant bacteria such as _Pseudomonas aeruginosa_.[L32935]

Ceftazidime was approved by the FDA on July 19, 1985, and is currently available either alone or in combination with the non-β-lactam β-lactamase inhibitor [avibactam] to treat a variety of bacterial infections.[L32935, L32940]', '["J01DD02"]', '[]');
