INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1280, 'Balsam syrup 120 ml', 'بلسم شراب للكبار 120 مل', '75', '42', 'Anti-cough', 'Guava leaves+tilia flower+fennel oil+honey+thyme', 'Sigma', 'Syrup', 'شراب', '120 ml', '1', 'علاج الكحة الناشفة -- مهدئ للسعال -- تقليل الحساسية', 'help in treatment of cough dose: adults: 10 to 15 ml twice daily', '6221051040072', NULL, 3353, '2024-11-12', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12503, 'Vitamount stress 10 soft gelatin caps', 'فيتاماونت للارهاق 10 كبسولات', '22', NULL, 'Multivitamins', 'Minerals+vitamins', 'Amoun', 'Cap', 'كبسولة', NULL, '1', 'فيتامينات ومعادن للارهاق', NULL, '6221025009784', NULL, 2404, '2022-12-14', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20952, 'Irbetan 150mg 10 tab', 'اربيتان 150 مجم 10 قرص', '14', NULL, 'Antihypertensive.angiotensin blocker', 'Irbesartan', 'Memphis', 'Tab', 'أقراص', '150mg', '1', NULL, 'about irbesartan angiotensin ii receptor blocker a non-peptide compound antihypertensive. mechanism of action of irbesartan it is a competitive antagonist of angiotensin-2 at at-1 receptor. it decreases peripheral resistance and lowers bp in hypertensive', '6221050032047', NULL, 724, '2022-12-06', 'Irbesartan is indicated to treat hypertension and diabetic nephropathy in hypertensive patients with type 2 diabetes, elevated serum creatinine, and proteinuria.[L7456] A combination product with hydrochlorothiazide is indicated for hypertension in patients with uncontrolled hypertension with monotherapy or first line in patients not expected to be well controlled with monotherapy.[L7459]', 'Irbesartan prevents angiotensin II binding to the AT<sub>1</sub> receptor in tissues like vascular smooth muscle and the adrenal gland.[L7456,L7459] Irbesartan and its active metabolite bind the AT<sub>1</sub> receptor with 8500 times more affinity than they bind to the AT<sub>2</sub> receptor.[L7456,L7459] Irbesartan''s prevention of angiotensin II binding causes vascular smooth muscle relaxation and prevents the secretion of aldosterone, lowering blood pressure.[L7456,L7459]



Angiotensin II would otherwise bind to the AT<sub>1</sub> receptor, inducing vasoconstriction and aldosterone secretion, raising blood pressure.[L7456,L7459]', 'Irbesartan is an angiotensin receptor blocker used to treat hypertension and diabetic nephropathy.[L7456,L7459] It has a long duration of action as it is usually taken once daily and a wide therapeutic index as doses may be as low as 150mg daily but doses of 900mg/day were well tolerated in healthy human subjects.[L7456,L7459,A181165]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (33844, 'Vee care 60 caps.', 'في كير 60كبسولة', '490', '440', 'Multivitamin', 'Marine collagen + biotin', 'Veexia pharmaceutical industries', 'Cap', 'كبسولة', NULL, '6', NULL, 'vee care has hydrolyzed marine collagen peptides fortified with biotin vitamins hyaluronic acid and powerful anti-aging herbal extracts to give youthful skin strong nails and shiny strong hair. instructions to use take two (2) capsules daily with a glass', NULL, NULL, 859, '2025-07-29', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4598, 'Ez-care tooth paste 80 gm', 'اية زد كير 120 جم معجون اسنان', '40', '35', 'Tooth paste', 'Chlorhexidine gluconate 0.1%', 'Vita pharm > national company for dental materials (ez-pac)', 'Unknown', 'غير محدد', '80 gm', '1', 'معجون اسنان لنظافة الاسنان واللثة -- تقليل تسوس الاسنان -- الحفاظ علي صحة الاسنان واللثة -- اعطاء رائحة زكية للفم', NULL, '6785556093457', NULL, 2566, '2025-07-25', 'Chlorhexidine is available over-the-counter in various formulations (e.g. solution, sponge, cloth, swab) as a topical antiseptic to sanitize prior to surgeries and/or medical procedures.[L11518,L11521,L11527,L11533] Dental formulations, available by prescription only, include an oral rinse indicated for the treatment of gingivitis[L11512] and a slow-release "chip" which is inserted into periodontal pockets and is indicated for the reduction of pocket depth in adult patients with periodontitis as an adjunct therapy to dental scaling and root planing procedures.[L11536]', 'Chlorhexidine’s broad-spectrum antimicrobial effects are due to its ability to disrupt microbial cell membranes. The positively charged chlorhexidine molecule reacts with negatively charged phosphate groups on microbial cell surfaces - this reaction both destroys the integrity of the cell, allowing leakage of intracellular material, and allows chlorhexidine to enter the cell, causing precipitation of cytoplasmic components and ultimately cell death.[L11536,A190453] The specific means of cell death is dependent on the concentration of chlorhexidine - lower concentrations are bacteriostatic and result in leakage of intracellular substances such as potassium and phosphorous, whereas higher concentrations are bactericidal and cause cytoplasmic precipitation.[A190453]', 'Chlorhexidine is a broad-spectrum antimicrobial with demonstrated activity against both gram-positive and gram-negative bacteria, yeasts, and viruses.[A190417] Antimicrobial activity is dose-dependent - chlorhexidine is bacteriostatic at lower concentrations (0.02%-0.06%) and bactericidal at higher concentrations (>0.12%).[A190417]  Pharmacokinetic studies of oral chlorhexidine rinses indicate that approximately 30% of the active ingredient is retained in the mouth following rinsing, which is subsequently slowly released into oral fluids.[L11512] This ability to adsorb to dentine, shared with tetracycline antibiotics such as [doxycycline], is known as "substantivity" and is the result of chlorhexidine''s positive charge - it is likely that this substantivity plays at least some role in chlorhexidine''s antimicrobial activity, as its persistence on surfaces such as dentine prevent microbial colonization.[A190453]



Dental chlorhexidine rinses may result in staining of oral surfaces, such as teeth. This effect is not ubiquitous and appears to be more significant with extended therapy (i.e. up to 6 months) - nevertheless, patients for whom oral staining is unacceptable should use chlorhexidine rinse with caution and for the shortest effective interval.[L11512] Allergic reactions to chlorhexidine have been associated with the development of anaphylaxis.[A190468]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25815, 'Reiferon 3m.i.u. vial s.c/i.m injection', 'ريافيرون 3م.وحدة امبول', '45', NULL, 'Antiviral .interferon alfa', 'Interferon alfa-2a', 'Mina pharm', 'Injection', 'حقن', NULL, '1', NULL, 'indication for the treatment of chronic hepatitis c hairy cell leukemia aids-related kaposi s sarcoma and chronic myelogenous leukemia. also for the treatment of oral warts arising from hiv infection. mechanism of action interferon alpha binds to type i i', NULL, NULL, 675, '2022-07-27', 'For the treatment of venereal or genital warts caused by the Human Papiloma Virus.', 'Interferon alpha binds to type I interferon receptors (IFNAR1 and IFNAR2c) which, upon dimerization, activate two Jak (Janus kinase) tyrosine kinases (Jak1 and Tyk2). These transphosphorylate themselves and phosphorylate the receptors. The phosphorylated INFAR receptors then bind to Stat1 and Stat2 (signal transducers and activators of transcription)which dimerize and activate multiple (~100) immunomodulatory and antiviral proteins. Interferon alpha binds less stably to type I interferon receptors than interferon beta.', 'Upregulates the expression of MHC I proteins, allowing for increased presentation of peptides derived from viral antigens. This enhances the activation of CD8+ T cells that are the precursors for cytotoxic T lymphocytes (CTLs) and makes the macrophage a better target for CTL-mediated killing. Interferon alpha also induce the synthesis of several key antiviral mediators, including 2''-5'' oligoadenylate synthetase (2''-5'' A synthetase) and protein kinase R.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30224, 'Brano 20 peices', 'برانو 20 قطعة', '85', '65', 'Iron supplement', 'Iron+lactoferrin+zinc+vit.c', 'Brand nutrition > brand pharma', 'Unknown', 'غير محدد', NULL, '1', NULL, 'iron deffecincy- anemia', NULL, NULL, 690, '2025-08-15', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29790, 'Xylotop 4% topical gel 20 gm', 'زيلوتوب 4% جل 20جم', '5', NULL, 'Analgesic.local anesthetic', 'Lidocaine', 'Sigma', 'Gel', 'جل', '4%', '1', NULL, 'indication for production of local or regional anesthesia by infiltration techniques such as percutaneous injection and intravenous regional anesthesia by peripheral nerve block techniques such as brachial plexus and intercostal and by central neural tech', NULL, NULL, 932, '2022-07-14', 'Lidocaine is an anesthetic of the amide group indicated for production of local or regional anesthesia by infiltration techniques such as percutaneous injection and intravenous regional anesthesia by peripheral nerve block techniques such as brachial plexus and intercostal and by central neural techniques such as lumbar and caudal epidural blocks [F4349, L5930].', 'Lidocaine is a local anesthetic of the amide type [F4349, L5930, L5948]. It is used to provide local anesthesia by nerve blockade at various sites in the body [F4349, L5930, L5948]. It does so by stabilizing the neuronal membrane by inhibiting the ionic fluxes required for the initiation and conduction of impulses, thereby effecting local anesthetic action [F4349, L5930, L5948]. In particular, the lidocaine agent acts on sodium ion channels located on the internal surface of nerve cell membranes [F4349, L5930, L5948]. At these channels, neutral uncharged lidocaine molecules diffuse through neural sheaths into the axoplasm where they are subsequently ionized by joining with hydrogen ions [F4349, L5930, L5948]. The resultant lidocaine cations are then capable of reversibly binding the sodium channels from the inside, keeping them locked in an open state that prevents nerve depolarization [F4349, L5930, L5948]. As a result, with sufficient blockage, the membrane of the postsynaptic neuron will ultimately not depolarize and will thus fail to transmit an action potential [F4349, L5930, L5948]. This facilitates an anesthetic effect by not merely preventing pain signals from propagating to the brain but by aborting their generation in the first place [F4349, L5930, L5948].



In addition to blocking conduction in nerve axons in the peripheral nervous system, lidocaine has important effects on the central nervous system and cardiovascular system [F4349, L5930, L5948]. After absorption, lidocaine may cause stimulation of the CNS followed by depression and in the cardiovascular system, it acts primarily on the myocardium where it may produce decreases in electrical excitability, conduction rate, and force of contraction [F4349, L5930, L5948].', 'Excessive blood levels of lidocaine can cause changes in cardiac output, total peripheral resistance, and mean arterial pressure [F4349, L5930]. With central neural blockade these changes may be attributable to the block of autonomic fibers, a direct depressant effect of the local anesthetic agent on various components of the cardiovascular system, and/or the beta-adrenergic receptor stimulating action of epinephrine when present [F4349, L5930]. The net effect is normally a modest hypotension when the recommended dosages are not exceeded [F4349, L5930].



In particular, such cardiac effects are likely associated with the principal effect that lidocaine elicits when it binds and blocks sodium channels, inhibiting the ionic fluxes required for the initiation and conduction of electrical action potential impulses necessary to facilitate muscle contraction [F4349, L5930, L5948]. Subsequently, in cardiac myocytes, lidocaine can potentially block or otherwise slow the rise of cardiac action potentials and their associated cardiac myocyte contractions, resulting in possible effects like hypotension, bradycardia, myocardial depression, cardiac arrhythmias, and perhaps cardiac arrest or circulatory collapse [F4349, L5930, L5948].



Moreover, lidocaine possesses a dissociation constant (pKa) of 7.7 and is considered a weak base [L5948]. As a result, about 25% of lidocaine molecules will be un-ionized and available at the physiological pH of 7.4 to translocate inside nerve cells, which means lidocaine elicits an onset of action more rapidly than other local anesthetics that have higher pKa values [L5948]. This rapid onset of action is demonstrated in about one minute following intravenous injection and fifteen minutes following intramuscular injection [L5930]. The administered lidocaine subsequently spreads rapidly through the surrounding tissues and the anesthetic effect lasts approximately ten to twenty minutes when given intravenously and about sixty to ninety minutes after intramuscular injection [L5930].



Nevertheless, it appears that the efficacy of lidocaine may be minimized in the presence of inflammation [L5948]. This effect could be due to acidosis decreasing the amount of un-ionized lidocaine molecules, a more rapid reduction in lidocaine concentration as a result of increased blood flow, or potentially also because of increased production of inflammatory mediators like peroxynitrite that elicit direct actions on sodium channels [L5948].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2453, 'Movi iron 30 capsule', 'موفي آيرون 30 كبسولة', '138', NULL, 'Iron+ minerals+vitamins', 'Iron bisglycinate+vit c+cupper+zinc+manganese+follic acid+vit b 12+vit b 6', 'Empire pharmaceutical company', 'Capsule', 'كبسولة', NULL, '3', NULL, NULL, NULL, NULL, 945, '2025-01-18', 'For treatment of patients with documented iron deficiency in whom oral administration is unsatisfactory or impossible. Also used to replenish body iron stores in Non-Dialysis Dependent-Chronic Kidney Disease (NDD-CKD) patients receiving or not receiving erythropoietin and in Hemodialysis Dependent (HDD-CKD) and Peritoneal Dialysis Dependent (PDD-CKD) - Chronic Kidney Disease patients receiving an erythropoietin.', 'After iron dextran is injected, the circulating iron dextran is removed from the plasma by cells of the reticuloendothelial system, which split the complex into its components of iron and dextran. The iron is immediately bound to the available protein moieties to form hemosiderin or ferritin, the physiological forms of iron, or to a lesser extent to transferrin. This iron which is subject to physiological control replenishes hemoglobin and depleted iron stores.', 'Iron dextran is a dark brown, slightly viscous sterile liquid complex of ferric hydroxide and dextran for intravenous or intramuscular use. It is for treatment of patients with documented iron deficiency in whom oral administration is unsatisfactory or impossible. Iron is essential to the formation of hemoglobin and to the function and formation of other heme and nonheme compounds. Untreated depletion of iron stores leads to iron-deficient erythropoiesis and, in turn, to iron deficiency anemia.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19152, 'Florax 20 caps', 'فلوراكس 20 كبسولة', '110', '65', 'Multivitamins', 'Vitamin c+vitamin d+zinc', 'Pharmazad > safe life', 'Cap', 'كبسولة', NULL, '2', '- فيتامينات متعددة لتقوية المناعة', 'vitamin c 500 mg-vitamin d 400 i.u.-zinc 25 mg', NULL, NULL, 9583, '2024-06-08', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6292, 'Hydro-safe 10 sachets', 'هيدروسيف 10 اكياس', '40', '20', 'Ors', 'Glucose (dextrose)+potassium chloride+sodium chloride+sodium citrate', 'Sedico', 'Sachet', 'أكياس', NULL, '1', NULL, 'ors solution', '6221042240016', NULL, 5220, '2024-12-07', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (9562, 'Panadol baby infant 100ml', 'بانادول بيبي شراب للرضع 100 مل', '40', NULL, 'Antipyretic', 'Paracetamol(acetaminophen)', 'Alexandria > glaxo smithkline', 'Unknown', 'غير محدد', '100ml', '1', NULL, NULL, '5010214587124', NULL, 1724, '2022-12-07', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16733, 'Conjyclear forte 0.2% eye drops 30 x 0.4 ml sdu', 'كونجيكلير فورت 0.2% قطرة 30*0.4 مل وحدات مفردة', '78', '45', 'Ophthalmic antihistaminic', 'Olopatadine', 'Orchidia pharmaceutical industries', 'Drops', 'نقط', '0.2%', '1', NULL, NULL, '6224000219451', NULL, 2556, '2025-09-05', 'Olopatadine is indicated for the symptomatic treatment of ocular itching associated with allergic conjunctivitis as ophthalmic solution.[L6781]



As a nasal spray, as a monotherapy or in combination with [mometasone furoate], olopatadine is indicated for the symptomatic relief of seasonal allergic rhinitis in patients 12 years of age and older.[L6784,L39845]', 'Histamine is a biogenic vasoactive amine that binds to its receptors, which are G-protein coupled receptors. Signaling through the histamine H1 receptor is thought to primarily promote the activation of inflammatory reactions, such as allergy, asthma, and autoimmune diseases.[A179731] H1 receptor signaling activates the intracellular transcription factors, such as IP3, PLC, PKC, DAG, and intracellular calcium ions, which all work to activate further downstream cascades. Activated downstream cascades lead to the production of cytokines, the release of mast cell inflammatory mediators, synthesis of prostacyclins, activation of platelet factor, as well as the synthesis of nitric oxide, arachidonic acid, and thromboxane, which all contribute to inflammatory reactions.[A179731] 



Olopatadine is an anti-allergic molecule that works via several mechanisms. As a mast cell stabilizer, it stabilizes rodent basophils and human conjunctival mast cells and inhibits the immunologically-stimulated release of histamine.[A179704] Olopatadine acts as an antagonist at the histamine H1 receptors with high selectivity, which is explained by a unique receptor binding pocket that consists of the aspartate residue in the third transmembrane helix and other sites in the H1 receptor.[A1170] Upon binding, olopatadine blocks the H1 receptor signaling pathway, inhibiting the release of inflammatory mediators, such as tryptase, prostaglandin D2, TNF-alpha, as well as pro-inflammatory cytokines.[L6790] It also decreases chemotaxis and inhibits eosinophil activation.[L6781] _In vitro_, olopatadine was shown to inhibit epithelial cell intercellular adhesion molecule-1 (ICAM-1), which promotes the recruitment of migrating pro-inflammatory mediators.[A179704]', 'Inflammatory reactions in response to various stimuli are mediated by endogenous mediators and other pro-inflammatory factors. Histamine receptor activation and mast cell degranulation are primary mechanisms that cause inflammatory reactions such as ocular itching, hyperemia, chemosis, eyelid swelling, and tearing of seasonal allergic conjunctivitis.[A179704] Olopatadine is an anti-allergenic molecule and mast cell stabilizer that inhibits the _in vivo_ type 1 immediate hypersensitivity reaction.[L6790] By blocking the effects of histamine, olopatadine works to reduce the symptoms of allergies and inflammation at various sites of administration, including the eyes and nose. It has shown to exert antihistaminic effects in isolated tissues, animal models, and humans.[L6784] Olopatadine also demonstrated dose-dependent inhibition of immunologically-stimulated release of histamine from rat basophilic leukemia cells and human conjunctival mast cells _in vitro_.[A179704] Olopatadine has a relatively rapid onset of action and prolonged duration, where it was shown to mediate anti-histaminic effects at 5 minutes to 24 hours post-administration.[A179704]



While olopatadine is a non-sedating antihistamine agent, there have been reports of somnolence in some patients taking nasal olopatadine during clinical trials.[L6784] Temporary blurred vision or other visual disturbances were observed following ophthalmic administration. Olopatadine has negligible effects on alpha-adrenergic, dopamine, muscarinic type 1 and 2, and serotonin receptors.[L6790] In clinical trials, there was no evidence of any effect of olopatadine on QT prolongation was observed following intranasal administration.[L6784]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21726, 'Leupro-sandoz 3-month-depot 1 syringe', 'لوبرون ديبوت ساندوز 3 شهور 1 سرنجة', '1,330', NULL, 'Gnrh analogue', 'Leuprorelin', 'Sandoz > novartis', 'Syringe', 'حقنة', NULL, '1', NULL, 'leuprorelin (leuprolide acetate) is a gonadotrophin-releasing hormone (gnrh) analogue used to treat a wide range of sex hormone-related disorders including advanced prostatic cancer endometriosis and precocious puberty. it acts primarily on the anterior p', NULL, NULL, 538, '2022-08-03', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18176, 'Elimbosis 5 mg 30 f.c. tabs', 'اليمبوسيس 5مجم 30 قرص', '269', NULL, 'Anticoagulant', 'Apixaban', 'Debeiky > zeta pharm', 'Tab', 'أقراص', '5 mg', '3', 'مضاد للجلطات', NULL, '6224008407096', NULL, 9611, '2023-05-22', 'Apixaban is indicated for reducing the risk of stroke and systemic embolism in patients who have nonvalvular atrial fibrillation, prophylaxis of deep vein thrombosis(DVT) leading to pulmonary embolism(PE) in patients after a hip or knee replacement surgery, and treatment of DVT and PE to reduce the risk of recurrence[Label,A177565,A6897].', 'Apixaban selectively inhibits factor Xa in its free and bound forms, independant of antithrombin III[Label]. Apixaban also inhibits prothrominase[Label]. These effects prevent the formation of a thrombus[Label].', 'Apixaban selectively inhibits factor Xa in its free and bound forms, independant of antithrombin III[Label]. Apixaban also inhibits prothrominase[Label]. These effects prevent the formation of a thrombus[Label].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20344, 'Ambropro 20 mg 200 lozenges', 'امبرو برو 20 مجم 200 قرص', '640', '420', 'Mucolytic', 'Ambroxol', 'Penta pharma', 'Lozenges', 'استحلاب', '20 mg', '20', NULL, NULL, '6224007738399', NULL, 5727, '2025-06-01', 'Ambroxol is indicated for secretolytic therapy in bronchoplmonary disease with abnormal mucus secretion and transport. It allows the mucus to be more easily cleared and ease a patient''s breathing.', 'Ambroxol is a mucolytic agent. Excessive Nitric oxide (NO) is associated with inflammatory and some other disturbances of airways function. NO enhances the activation of soluble guanylate cyclase and cGMP accumulation. Ambroxol has been shown to inhibit the NO-dependent activation of soluble guanylate cyclase. It is also possible that the inhibition of NO-dependent activation of soluble guanylate cyclase can suppress the excessive mucus secretion, therefore it lowers the phlegm viscosity and improves the mucociliary transport of bronchial secretions.', NULL, 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28232, 'Tobolanza 24 s.g.caps.', 'توبولانزا 24 كبسولة', '74', '49', 'Antispasmodic', 'Alverine citrate+simethicone', 'European egyptian pharm. ind.', 'Cap', 'كبسولة', NULL, '2', 'مضاد للتقلصات', NULL, '6221076110057', NULL, 7256, '2025-09-20', 'Used to relieve cramps or spasms of the stomach and intestines. It is also useful in treating irritable bowel syndrome (IBS) and similar conditions. It can also be used to help relieve period pain. Alverine citrate is also under investigation to increase the cytotoxic effects of the proteasome inhibitor MG132 on breast cancer cells. ', NULL, 'Alverine is a smooth muscle relaxant. Smooth muscle is a type of muscle that is not under voluntary control; it is the muscle present in places such as the gut and uterus. Alverine acts directly on the muscle in the gut, causing it to relax. This prevents the muscle spasms which occur in the gut in conditions such as irritable bowel syndrome and diverticular disease. Diverticular disease is a condition in which small pouches form in the gut lining. These pouches can trap particles of food and become inflamed and painful. In irritable bowel syndrome the normal activity of the gut muscle is lost. The muscle spasms result in symptoms such as heartburn, abdominal pain and bloating, constipation or diarrhoea. By relaxing the gut muscle, alverine citrate relieves the symptoms of this condition. Alverine also relaxes the smooth muscle in the womb (uterus). It is therefore also used to treat painful menstruation, which is caused by muscle spasms in the uterus (dysmenorrhea).', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12419, 'Vitaferrol 30 soft gelatin caps.', 'فيتافيرول 30 كبسولة', '66', '43.5', 'Multivitamins', 'Folic acid+iron+manganese+vitamin b12+vitamin c+zinc', 'Eva pharma', 'Cap', 'كبسولة', NULL, '3', 'مكمل غذائي بالحديد لعلاج الانيميا', NULL, '6223002450879', NULL, 6919, '2024-09-25', 'Folic acid is indicated for the treatment of folic acid deficiency, megaloblastic anemia, and in anemias of nutritional origins, pregnancy, infancy, or childhood.', 'Folic acid, as it is biochemically inactive, is converted to tetrahydrofolic acid and methyltetrahydrofolate by dihydrofolate reductase (DHFR). These folic acid congeners are transported across cells by receptor-mediated endocytosis where they are needed to maintain normal erythropoiesis, synthesize purine and thymidylate nucleic acids, interconvert amino acids, methylate tRNA, and generate and use formate. Using vitamin B12 as a cofactor, folic acid can normalize high homocysteine levels by remethylation of homocysteine to methionine via methionine synthetase.', 'Folic acid is a water-soluble B-complex vitamin found in foods such as liver, kidney, yeast, and leafy, green vegetables. Also known as folate or Vitamin B9, folic acid is an essential cofactor for enzymes involved in DNA and RNA synthesis. More specifically, folic acid is required by the body for the synthesis of purines, pyrimidines, and methionine before incorporation into DNA or protein. Folic acid is the precursor of tetrahydrofolic acid, which is involved as a cofactor for transformylation reactions in the biosynthesis of purines and thymidylates of nucleic acids. Impairment of thymidylate synthesis in patients with folic acid deficiency is thought to account for the defective deoxyribonucleic acid (DNA) synthesis that leads to megaloblast formation and megaloblastic and macrocytic anemias. Folic acid is particularly important during phases of rapid cell division, such as infancy, pregnancy, and erythropoiesis, and plays a protective factor in the development of cancer. As humans are unable to synthesize folic acid endogenously, diet and supplementation is necessary to prevent deficiencies. In order to function properly within the body, folic acid must first be reduced by the enzyme dihydrofolate reductase (DHFR) into the cofactors dihydrofolate (DHF) and tetrahydrofolate (THF). This important pathway, which is required for de novo synthesis of nucleic acids and amino acids, is disrupted by anti-metabolite therapies such as [DB00563] as they function as DHFR inhibitors to prevent DNA synthesis in rapidly dividing cells, and therefore prevent the formation of DHF and THF. 



In general, folate serum levels below 5 ng/mL indicate folate deficiency, and levels below 2 ng/mL usually result in megaloblastic anemia.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16665, 'Captobact 600 gm 10 f.c. tabs.', 'كابتوباكت 600مجم 10 اقراص', '231', '161', 'Antibiotic', 'Linozolid', 'Copad pharma', 'Tab', 'أقراص', '600 gm', '1', 'مضاد حيوى واسع المدى تستخدم لعلاج عدوى المكورات المعوية المقاومة للفانكومايسين، الالتهاب الرئوي، والالتهابات الجلدية المعقدة', NULL, '6223011340178', NULL, 6010, '2024-09-25', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23234, 'Nan lf milk 400 gm', 'نان ال اف لبن اطفال 400 جم', '375', '365', 'Lactose free milk', 'Lactose free milk formula', 'Nestle', 'Unknown', 'غير محدد', '400 gm', '1', NULL, 'used mainly for diarrhea. from birth onwards. use milk within 4 weeks of opening.', '7613035250888', NULL, 1205, '2025-09-01', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13698, 'Blincorest 125 mg 30 chew. tabs.', 'بلنكورست 125مجم 30 قرص مضغ', '69', '43.5', 'Antiflatulent', 'Simethicone', 'P & c labs', 'Tab', 'أقراص', '125 mg', '3', 'مضاد للغازات والانتفاخات', NULL, NULL, NULL, 2006, '2024-07-28', 'Simethicone is indicated for the treatment of bloating, pressure, and cramps caused by gas.[L31533] Simethicone is also used as part of bowel preparation for colonoscopies.[A228343] ', 'Simethicone is a surfactant that decreases the surface tension of gas bubbles in the gastrointestinal tract, more easily allowing gas to exit the body.[A228308]', 'Simethicone decreases the surface tension of gas bubbles in the gastrointestinal tract, facilitating their expulsion.[A228308] It has a short duration of action as it is generally given as needed, and a wide therapeutic index as it is not systemically absorbed.[A228308]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25692, 'Ponvory 20 mg 28 f.c.tabs. (maintenance pack)', 'بونفوري 20مجم 28 قرص', '22,000', NULL, 'Immunosuppressant', 'Ponesimod', 'Janssen cilag', 'Tab', 'أقراص', '20 mg', '4', NULL, NULL, NULL, NULL, 2779, '2022-09-22', 'Ponesimod is indicated to treat adults with relapsing forms of multiple sclerosis, including clinically isolated syndrome, relapsing-remitting disease, and active secondary progressive disease.[L32709]', 'The sphingosine 1-phosphate receptor 1 (S1P1R) is expressed on the surface of lymphocytes and detects sphingosine 1-phosphate (S1P) at nanomolar concentrations.[A231979] S1P is a metabolite of the cell membrane component, sphingomyelin.[A231979] As sphingomyelin degrades, lymphocytes respond to agonism of S1P1R by concentration gradients of S1P.[A231979] Lymphocytes leave the lymphoid organs in response to higher concentrations of S1P in blood and lymph.[A231979] Ponesimod modulates this response by stimulating and internalizing S1P1R on lymphocytes, effectively blinding them to concentration gradients of S1P, reducing the number of lymphocytes in blood.[A231979,L32709] Ponesimod is roughly 650 times more selective for S1P1R than S1P.[A231979]', 'Ponesimod is a sphingosine 1-phosphate receptor 1 modulator indicated to treat adults with relapsing forms of multiple sclerosis.[L32709] It has a long duration of action as it is given once daily.[L32709] Patients should be counselled about the risk of infections, bradyarrhythmia, atrioventricular conduction delays, decreased respiratory function, liver injury, increased blood pressure, cutaneous malignancies, fetal harm, and macular edema.[L32709]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4399, 'Esmorap 20 mg 14 caps.', 'ازموراب 20مجم 14 كبسولة', '76', '51', 'Peptic ulcer.proton pump inhibitor', 'Esomeprazole', 'Aug pharma', 'Cap', 'كبسولة', '20 mg', '2', NULL, 'about esomeprazole a proton pump inhibitor substituted benzimidazole antiulcer antisecretory drug. mechanism of action of esomeprazole esomeprazole is the s enantiomer of omeprazole and is a proton pump inhibitor. it is a prodrug. after administration it', '6223005461018', NULL, 2459, '2024-08-31', 'Esomeprazole is indicated for the treatment of acid-reflux disorders including healing and maintenance of erosive esophagitis, and symptomatic gastroesophageal reflux disease (GERD), peptic ulcer disease, H. pylori eradication, prevention of gastrointestinal bleeds with NSAID use, and for the long-term treatment of pathological hypersecretory conditions including Zollinger-Ellison Syndrome. ', 'Esomeprazole exerts its stomach acid-suppressing effects by preventing the final step in gastric acid production by covalently binding to sulfhydryl groups of cysteines found on the (H+, K+)-ATPase enzyme at the secretory surface of gastric parietal cells. This effect leads to inhibition of both basal and stimulated gastric acid secretion, irrespective of the stimulus. As the binding of esomeprazole to the (H+, K+)-ATPase enzyme is irreversible and new enzyme needs to be expressed in order to resume acid secretion, esomeprazole''s duration of antisecretory effect that persists longer than 24 hours.[FDA Label]

', 'Esomeprazole is a compound that inhibits gastric acid secretion and is indicated in the treatment of gastroesophageal reflux disease (GERD), the healing of erosive esophagitis, and <i>H. pylori</i> eradication to reduce the risk of duodenal ulcer recurrence. Esomeprazole belongs to a new class of antisecretory compounds, the substituted benzimidazoles, that do not exhibit anticholinergic or H2 histamine antagonistic properties, but that suppress gastric acid secretion by specific inhibition of the H<sup>+</sup>/K<sup>+</sup> ATPase at the secretory surface of the gastric parietal cell. By doing so, it inhibits acid secretion into the gsatric lumen. This effect is dose-related and leads to inhibition of both basal and stimulated acid secretion irrespective of the stimulus.



Esomeprazole is the s-isomer of [DB00338], which is a racemate of the S- and R-enantiomer. Esomeprazole has been shown to inhibit acid secretion to a similar extent as [DB00338], without any significant differences between the two compounds _in vitro_. 



PPIs such as esomeprazole have also been shown to inhibit the activity of dimethylarginine dimethylaminohydrolase (DDAH), an enzyme necessary for cardiovascular health. DDAH inhibition causes a consequent accumulation of the nitric oxide synthase inhibitor asymmetric dimethylarginie (ADMA), which is thought to cause the association of PPIs with increased risk of cardiovascular events in patients with unstable coronary syndromes.[A177577, A177580]



Due to their good safety profile and as several PPIs are available over the counter without a prescription, their current use in North America is widespread. Long term use of PPIs such as esomeprazole has been associated with possible adverse effects, however, including increased susceptibility to bacterial infections (including gastrointestinal _C. difficile_), reduced absorption of micronutrients including iron and B12, and an increased risk of developing hypomagnesemia and hypocalcemia which may contribute to osteoporosis and bone fractures later in life.[A177571]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23444, 'Neulastim 6 mg / 0.6 ml 1 prefilled syringe', 'نيولاستيم 6 مجم / 0.6 مل سرنجة معبأة', '8,130', '4900', 'Colony stimulating factor', 'Pegfilgrastim', 'Amgen > multipharma', 'Syringe', 'حقنة', '6 mg', '1', 'علاج مرض حمي نقص الخلايا المتعادلة الناتج عن العلاج الكميائي', NULL, NULL, NULL, 1293, '2025-08-23', 'Pegfilgrastim is indicated to decrease the incidence of infection, as manifested by febrile neutropenia, in patients with non­ myeloid malignancies receiving myelosuppressive anti-cancer drugs associated with a clinically significant incidence of febrile neutropenia.[L44221]



It is also indicated to increase survival in patients acutely exposed to myelosuppressive doses of radiation (Hematopoietic Subsyndrome of Acute Radiation Syndrome).[L44221]', 'Neutrophils are short-lived immune cells that are highly susceptible to cell death following myelosuppressive chemotherapy. This marked reduction in neutrophil numbers during chemotherapy increases the risk of hospitalization, infection, and infection-related mortality. It also directly impacts the clinical outcome of patients if cases of febrile neutropenia requires dose reductions or schedule delay of chemotherapy, thus reducing the clinical efficacy of chemotherapy and patient benefit from receiving appropriate treatment.[A187631] 



G-CSF is an endogenous haematopoietic growth factor that stimulates granulopoietic cells of the neutrophil lineage. Pegfilgrastim mimics its biological actions and binds to the same G-CSF receptor expressed on cells of myeloid lineage, such as granulocytic precursors and mature neutrophils.[A29] Upon binding of the ligand, G-CSF receptor undergoes a conformational change and activates several downstream signalling pathways including JAK/STAT, PI3K/AKT and MAPK/ERK.[A187868] These pathways work to increase proliferation and differentiation of granulocyte progenitor cells, induce maturation of the progenitor cells, and enhance survival and function of mature neutrophils.[A29]', 'Pegfilgrastim is a recombinant human granulocyte colony-stimulating factor (G-CSF) that promotes the production, proliferation, and maturation of neutrophils, which are white blood cells involved in both innate and adaptive immune responses.[A187631,A248855] The safety and efficacy of pegfilgrastim in reducing the risk of febrile neutropenia and infections have been demonstrated in various tumor types and settings.[A187631] 



During chemotherapy-induced neutropenia, the clearance of pegfilgrastim is significantly reduced and the concentration of pegfilgrastim is sustained until the onset of neutrophil recovery.[A29] Serum concentrations of pegfilgrastim decline as the neutrophil count increases as neutrophil and neutrophil precursors are involved in cell-mediated clearance of the drug.[A187601] Due the addition of polyethylene glycol group to its structure, Pegfilgrastim is a long-acting form of filgrastim with an extended serum half-life and reduced renal clearance.[A187607] Although it is more slowly absorbed than filgrastim, self-regulation of pegfilgrastim is more efficient and the drug effects are maintained during one chemotherapy cycle (2-3 weeks).[A29]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1382, 'Icoface 60 gm sunscreen cream', 'ايكوفيس صنسكرين كريم 60 جم', '297', NULL, 'Sunscreen', 'Zinc oxide+hyaluronic acid+collagen vitamin c', 'Iconic pharmaceutical', 'Cream', 'كريم', '60 gm', '1', NULL, NULL, NULL, NULL, 898, '2022-08-19', 'Zinc can be used for the treatment and prevention of zinc deficiency/its consequences, including stunted growth and acute diarrhea in children, and slowed wound healing. It is also utilized for boosting the immune system, treating the common cold and recurrent ear infections, as well as preventing lower respiratory tract infections [L2172].  



', '**Zinc has three primary biological roles**: _catalytic_, _structural_, and _regulatory_. The catalytic and structural role of zinc is well established, and there are various noteworthy reviews on these functions. For example, zinc is a structural constituent in numerous proteins, inclusive of growth factors, cytokines, receptors, enzymes, and transcription factors for different cellular signaling pathways. It is implicated in numerous cellular processes as a cofactor for approximately 3000 human proteins including enzymes, nuclear factors, and hormones [L2096].



Zinc promotes resistance to epithelial apoptosis through cell protection (cytoprotection) against reactive oxygen species and bacterial toxins, likely through the antioxidant activity of the cysteine-rich metallothioneins [A32419].



In HL-60 cells (promyelocytic leukemia cell line), zinc enhances the up-regulation of A20 mRNA, which, via TRAF pathway, decreases NF-kappaB activation, leading to decreased gene expression and generation of tumor necrosis factor-alpha (TNF-alpha), IL-1beta, and IL-8 [A32418].



There are several mechanisms of action of zinc on acute diarrhea. Various mechanisms are specific to the gastrointestinal system: zinc restores mucosal barrier integrity and enterocyte brush-border enzyme activity, it promotes the production of antibodies and circulating lymphocytes against intestinal pathogens, and has a direct effect on ion channels, acting as a potassium channel blocker of adenosine 3-5-cyclic monophosphate-mediated chlorine secretion. Cochrane researchers examined the evidence available up to 30 September 2016 [L2106].



Zinc deficiency in humans decreases the activity of serum _thymulin_ (a hormone of the thymus), which is necessary for the maturation of T-helper cells. T-helper 1 (Th(1)) cytokines are decreased but T-helper 2 (Th(2)) cytokines are not affected by zinc deficiency in humans [A32417].



The change of _Th(1)_ to _Th(2)_ function leads to cell-mediated immune dysfunction. Because IL-2 production (Th(1) cytokine) is decreased, this causes decreased activity of natural-killer-cell (NK cell) and T cytolytic cells, normally involved in killing viruses, bacteria, and malignant cells [A32424]. 



In humans, zinc deficiency may lead to the generation of new CD4+ T cells, produced in the thymus. In cell culture studies (HUT-78, a Th(0) human malignant lymphoblastoid cell line), as a result of zinc deficiency, nuclear factor-kappaB (NF-kappaB) activation, phosphorylation of IkappaB, and binding of NF-kappaB to DNA are decreased and this results in decreased Th(1) cytokine production [A32417].



In another study, zinc supplementation in human subjects suppressed the gene expression and production of pro-inflammatory cytokines and decreased oxidative stress markers [A32424]. In HL-60 cells (a human pro-myelocytic leukemia cell line), zinc deficiency increased the levels of TNF-alpha, IL-1beta, and IL-8 cytokines and mRNA. In such cells, zinc was found to induce A20, a zinc finger protein that inhibited NF-kappaB activation by the tumor necrosis factor receptor-associated factor pathway. This process decreased gene expression of pro-inflammatory cytokines and oxidative stress markers [A32417].



The exact mechanism of zinc in acne treatment is poorly understood. However, zinc is considered to act directly on microbial inflammatory equilibrium and facilitate antibiotic absorption when used in combination with other agents. Topical zinc alone as well as in combination with other agents may be efficacious because of its anti-inflammatory activity and ability to reduce P. acnes bacteria by the inhibition of P. acnes lipases and free fatty acid levels [L2102].', 'Zinc is involved in various aspects of cellular metabolism. It has been estimated that approximately 10% of human proteins may bind zinc, in addition to hundreds of proteins that transport and traffic zinc. It is required for the catalytic activity of more than 200 enzymes, and it plays a role in immune function wound healing, protein synthesis, DNA synthesis, and cell division. Zinc is an essential element for a proper sense of taste and smell and supports normal growth and development during pregnancy, childhood, and adolescence. It is thought to have antioxidant properties, which may be protective against accelerated aging and helps to speed up the healing process after an injury; however, studies differ as to its effectiveness. Zinc ions are effective antimicrobial agents even if administered in low concentrations [L2211].



Studies on oral zinc for specific conditions shows the following evidence in various conditions [L2095]:



**Colds:** Evidence suggests that if zinc lozenges or syrup are taken within 24 hours after cold symptoms start, the supplement may shorten the length of colds. The use intranasal zinc has been associated with the loss of the sense of smell, in some cases long-term or permanently [L2095].



**Wound healing:** Patients with skin ulcers and decreased levels of zinc may benefit from oral zinc supplements [L2095]. 



**Diahrrea**: Oral zinc supplements can reduce the symptoms of diarrhea in children with low levels of zinc, especially in cases of malnutrition [L2095].

', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28422, 'Treczimus 0.03% topical oint. 20 gm', 'تريكزيمس مرهم 0.03% 20 جرام', '114', '78', 'Immunosuppressants.psoriasis', 'Tacrolimus', 'Marcyrl co.', 'Oint', 'مرهم', '0.03%', '1', 'علاج حالات الصدفية.', 'this form of tacrolimus is used on the skin to treat a skin condition called eczema (atopic dermatitis) in patients who have not responded well to (or should not use) other eczema medications. eczema is an allergic-type condition that causes red irritated', '6223003571559', NULL, 12781, '2024-11-16', 'Immediate-release formulations of tacrolimus are indicated for the prophylaxis of organ rejection in adult and pediatric patients receiving allogeneic liver, kidney, heart, or lung transplants, in combination with other immunosuppressants.[L8162] Extended-release formulations of tacrolimus are indicated for the prophylaxis of organ rejection in adult and pediatric patients receiving kidney transplants, in combination with other immunosuppressants,[L20034,L41005] and may be used in patients converted from immediate-release formulations.[L41005]



Topical tacrolimus ointment is indicated as second-line therapy for short-term and non-continuous treatment of moderate-to-severe atopic dermatitis in non-immunocompromised adults and children who have failed to respond adequately to other topical treatments or for whom alternative treatments are not advisable.[L3935] Both available strengths are indicated in adult patients, while only the lower strength (0.03%) formulation is indicated in pediatric patients between 2 and 15 years of age.[L3935]', 'The mechanism of action of tacrolimus in atopic dermatitis is not known. While the following have been observed, the clinical significance of these observations in atopic dermatitis is not known. It has been demonstrated that tacrolimus inhibits T-lymphocyte activation by first binding to an intracellular protein, FKBP-12. A complex of tacrolimus-FKBP-12, calcium, calmodulin, and calcineurin is then formed and the phosphatase activity of calcineurin is inhibited. This prevents the dephosphorylation and translocation of nuclear factor of activated T-cells (NF-AT), a nuclear component thought to initiate gene transcription for the formation of lymphokines. Tacrolimus also inhibits the transcription for genes which encode IL-3, IL-4, IL-5, GM-CSF, and TNF-, all of which are involved in the early stages of T-cell activation. Additionally, tacrolimus has been shown to inhibit the release of pre-formed mediators from skin mast cells and basophils, and to downregulate the expression of FceRI on Langerhans cells.', 'Tacrolimus acts by reducing peptidyl-prolyl isomerase activity by binding to the immunophilin FKBP-12 (FK506 binding protein) creating a new complex. This inhibits both T-lymphocyte signal transduction and IL-2 transcription. Tacrolimus has similar activity to cyclosporine but rates of rejection are lower with tacrolimus. Tacrolimus has also been shown to be effective in the topical treatment of eczema, particularly atopic eczema. It suppresses inflammation in a similar way to steroids, but is not as powerful. An important dermatological advantage of tacrolimus is that it can be used directly on the face; topical steroids cannot be used on the face, as they thin the skin dramatically there. On other parts of the body, topical steroid are generally a better treatment.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1785, 'Mounjelly iron & vitamin c 60 gummies', 'مونجيلي حديد فيتامين سي 60 قطعة', '250', NULL, 'Vitamins and minerals', '2.5 mg iron+12 mg vitamin c', 'Mountain health', 'Unknown', 'غير محدد', NULL, '1', NULL, NULL, NULL, NULL, 962, '2024-12-14', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17890, 'Doxycost 50mg 30 tabs.', 'دوكسي كوست 50مجم 30 قرص', '51', '31.5', 'Tetracycline antibiotic', 'Doxycycline', 'Penta pharma-egypt', 'Tab', 'أقراص', '50mg', '3', NULL, 'about doxycycline tetracycline derivative antibiotic. mechanism of action of doxycycline doxycycline is a broad spectrum antibiotic which acts against both gram positive and gram negative organisms. it exerts its bacteriostatic action by accumulating insi', '6224007738351', NULL, 1772, '2024-06-29', 'Doxycycline is indicated for the treatment of various infections by gram-positive and gram-negative bacteria, aerobes and anaerobes, as well other types of bacteria, including:



- Early Lyme disease (as evidenced by erythema migraines) due to _Borrelia burgdorferi_ in adults and pediatric patients 8 years of age and older weighing 45 kg and above [L42875]

- Rickettsial infections,[L42870] such as Rocky Mountain spotted fever, typhus fever and the typhus group, Q fever, rickettsialpox, and tick fevers [L42880]

- Sexually transmitted infections [L42870]

- Respiratory tract infections [L42870] caused by _Mycoplasma pneumoniae_ and _Haemophilus influenzae_ [L42880]

- Specific bacterial infections [L42870] after indicative bacteriologic testing. These include infections caused by _Escherichia coli_, _Enterobacter aerogenes_, _Shigella_ species, _Acinetobacter_ species, and _Klebsiella_ species [L42880]

- Ophthalmic infections,[L42870] such as inclusion conjunctivitis caused by _Chlamydia trachomatis_ [L42880]

- Anthrax, including inhalational anthrax (post-exposure) [L42870]

- Alternative treatment for selected infections when [penicillin] is contraindicated [L42870]

- Adjunctive therapy in acute intestinal amebiasis and severe acne [L42870, L42880]

- Lymphogranuloma venereum caused by _Chlamydia trachomatis_ [L42880]

- Psittacosis (ornithosis) caused by _Chlamydophila psittaci_ [L42880]

- Trachoma caused by _Chlamydia trachomatis_, although the infectious agent is not always eliminated, as judged by immunofluorescence [L42880]

- Uncomplicated urethral, endocervical, or rectal infections in adults caused by _Chlamydia trachomatis_ [L42880]

- Nongonococcal urethritis caused by _Ureaplasma urealyticum_ [L42880]

- Relapsing fever due to _Borrelia recurrentis_ [L42880]

- Prophylaxis of malaria due to _Plasmodium falciparum_ in short-term travelers (<4 months) to areas with chloroquine and/or pyrimethamine-sulfadoxine resistant strains [L42870, L42880]



It is also used to treat infections caused by the following gram-negative microorganisms:



- Chancroid caused by _Haemophilus ducreyi_ [L42880]

- Plague due to _Yersinia pestis_ [L42880]

- Tularemia due to _Francisella tularensis_ [L42880]

- Cholera caused by _Vibrio cholerae_ [L42880]

- Campylobacter fetus infections caused by _Campylobacter fetus_ [L42880]

- Brucellosis due to _Brucella_ species (in conjunction with [streptomycin]) [L42880]

- Bartonellosis due to _Bartonella bacilliformis_ [L42880]

- Granuloma inguinale caused by _Klebsiella granulomatis_ [L42880]', 'Protein synthesis is essential for survival and functioning of cells, including bacteria.[A251725] Doxycycline inhibits bacterial protein synthesis by allosterically binding to the 30S prokaryotic ribosomal subunit.[A251730, L42870] The drug blocks the association charged aminoacyl-tRNA (aa-tRNA) with the ribosomal A site, which is the acceptor site on the mRNA-ribosome complex. Doxycycline ultimately impedes the elongation phase of protein synthesis and halts the production of essential proteins for bacterial survival and functioning.[A251725, A251730]



Doxycycline mediates anti-inflammatory actions by preventing calcium-dependent microtubular assembly and lymphocytic proliferation, thereby inhibiting leukocyte movement during inflammation.[A251730] It also inhibits nitric oxide synthase, which is an enzyme that produces nitric oxide, an inflammatory signaling molecule.[A251730]', 'Doxycycline and other tetracyclines are mainly bacteriostatic and are thought to exert antimicrobial effects by the inhibition of protein synthesis. They suppress the growth of bacteria or keep them in the stationary phase of growth.[A174025] Tetracyclines have antimicrobial spectrum of activity against a variety of gram-positive and gram-negative microorganisms.[L42870] Cross-resistance of these microorganisms to tetracyclines is a common occurrence.[L42870] 



As it is a highly lipophilic drug, doxycycline crosses multiple membranes of target molecules.[A251730] Doxycycline shows favorable intra-cellular penetration, with bacteriostatic activity against a wide range of bacteria.[A174028] Doxycycline also exhibits antiparasitic properties [A372, A373, A174025] and anti-inflammatory actions.[A174031, A251730] Its anti-inflammatory effects were investigated in various inflammatory skin conditions, such as bullous dermatoses [A251730] and rosacea.[A174031, A251730]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (11032, 'Carishema 60 gm massage gel', 'كاريشيما مساج جل 60 جم', '40', NULL, 'Massage gel', 'Camphor oil + eucalyptus oil + menthol crystal + phenoxy ethanol + vitamin e', 'P pharm', 'Gel', 'جل', '60 gm', '1', NULL, NULL, NULL, NULL, 933, '2022-08-13', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12079, 'Vantomor 50 mg 20 dispersible tab', 'فانتومور 50 مجم 20 اقراص', '50', '34', 'NSAIDS acetic acid derivatives', 'Diclofenac sodium', 'Spimco misr >hygint pharmaceuticals egypt', 'Tab', 'أقراص', '50 mg', '2', 'مسكن عام للالام', NULL, '6225000047747', NULL, 2278, '2024-11-02', 'Diclofenac is indicated for use in the treatment of pain and inflammation from varying sources including inflammatory conditions such as osteoarthritis, rheumatoid arthritis, and ankylosing spondylitis, as well as injury-related inflammation due to surgery and physical trauma. It is often used in combination with [misoprostol] as a gastro-protective agent in patients with high risk of developing NSAID-induced ulcers.', 'Diclofenac inhibits cyclooxygenase-1 and -2, the enzymes responsible for production of prostaglandin (PG) G<sub>2</sub> which is the precursor to other PGs.[label,T116] These molecules have broad activity in pain and inflammation and the inhibition of their production is the common mechanism linking each effect of diclofenac.



PGE<sub>2</sub> is the primary PG involved in modulation of nociception.[A179023] It mediates peripheral sensitization through a variety of effects.[T116,A179023] PGE<sub>2</sub> activates the G<sub>q</sub>-coupled EP<sub>1</sub> receptor leading to increased activity of the inositol trisphosphate/phospholipase C pathway. Activation of this pathway releases intracellular stores of calcium which directly reduces action potential threshold and activates protein kinase C (PKC) which contributes to several indirect mechanisms. PGE<sub>2</sub> also activates the EP<sub>4</sub> receptor, coupled to G<sub>s</sub>, which activates the adenylyl cyclase/protein kinase A (AC/PKA) signaling pathway. PKA and PKC both contribute to the potentiation of transient receptor potential cation channel subfamily V member 1 (TRPV1) potentiation, which increases sensitivity to heat stimuli. They also activate tetrodotoxin-resistant sodium channels and inhibit inward potassium currents. PKA further contributes to the activation of the P2X3 purine receptor and sensitization of T-type calcium channels. The activation and sensitization of depolarizing ion channels and inhibition of inward potassium currents serve to reduce the intensity of stimulus necessary to generate action potentials in nociceptive sensory afferents. PGE<sub>2</sub> act via EP<sub>3</sub> to increase sensitivity to bradykinin and via EP<sub>2</sub> to further increase heat sensitivity. Central sensitization occurs in the dorsal horn of the spinal cord and is mediated by the EP<sub>2</sub> receptor which couples to G<sub>s</sub>. Pre-synaptically, this receptor increases the release of pro-nociceptive neurotransmitters glutamate, CGRP, and substance P. Post-synaptically it increases the activity of AMPA and NMDA receptors and produces inhibition of inhibitory glycinergic neurons. Together these lead to a reduced threshold of activating, allowing low intensity stimuli to generate pain signals. PGI<sub>2</sub> is known to play a role via its G<sub>s</sub>-coupled IP receptor although the magnitude of its contribution varies. It has been proposed to be of greater importance in painful inflammatory conditions such as arthritis. By limiting sensitization, both peripheral and central, via these pathways NSAIDs can effectively reduce inflammatory pain.



PGI<sub>2</sub> and PGE<sub>2</sub> contribute to acute inflammation via their IP and EP<sub>2</sub> receptors.[T116,A179044] Similarly to β adrenergic receptors these are G<sub>s</sub>-coupled and mediate vasodilation through the AC/PKA pathway. PGE<sub>2</sub> also contributes by increasing leukocyte adhesion to the endothelium and attracts the cells to the site of injury.[T116] PGD<sub>2</sub> plays a role in the activation of endothelial cell release of cytokines through its DP<sub>1</sub> receptor.[A179044] PGI<sub>2</sub> and PGE<sub>2</sub> modulate T-helper cell activation and differentiation through IP, EP<sub>2</sub>, and EP<sub>4</sub> receptors which is believed to be an important activity in the pathology of arthritic conditions. By limiting the production of these PGs at the site of injury, NSAIDs can reduce inflammation.



PGE<sub>2</sub> can cross the blood-brain barrier and act on excitatory G<sub>q</sub> EP<sub>3</sub> receptors on thermoregulatory neurons in the hypothalamus.[T116] This activation triggers an increase in heat-generation and a reduction in heat-loss to produce a fever. NSAIDs prevent the generation of PGE<sub>2</sub> thereby reducing the activity of these neurons.

', 'Diclofenac reduces inflammation and by extension reduces nociceptive pain and combats fever.[label,T116] It also increases the risk of developing a gastrointestinal ulcer by inhibiting the production of protective mucus in the stomach.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (32253, 'Moventor 20 tabs.', 'موفينتور 20 قرص', '390', '360', 'Anti rheumatic-osteoarthritis-anabolic agent', 'Undenatured collagen type ii-hyaluronic acid -boron', 'Copad pharma', 'Tab', 'أقراص', NULL, '2', NULL, NULL, '6223011340116', NULL, 28575, '2024-02-28', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (33412, 'Polivy 140 mg pd. for inf. vial', 'بوليفي 140 مجم بي دي فيال', '202,000', '149690', 'Antineoplastic', 'Polatuzumab', 'Roche', 'Vial', 'فيال', '140 mg', '1', NULL, 'polivy in combination with bendamustine and a rituximab product is indicated for the treatment of adult patients with relapsed or refractory diffuse large b-cell lymphoma (dlbcl) not otherwise specified after at least 2 prior therapies.', NULL, NULL, 1139, '2024-10-06', 'Polatuzumab vedotin is used in combination with [bendamustine] and [rituximab] to treat adult patients with relapsed or refractory diffuse large B-cell lymphoma, not otherwise specified, after at least two prior therapies.[L6658] In Canada, this indication is approved for patients who are not eligible for autologous stem cell transplant and have received at least one prior therapy.[L44141]



Polatuzumab vedotin is also used in combination with [rituximab], [cyclophosphamide], [doxorubicin], and [prednisone] (R-CHP) to treat adult patients with previously untreated large B-cell lymphoma (LBCL), including diffuse large B-cell lymphoma (DLBCL) not otherwise specified (NOS), high-grade B-cell lymphoma,[L44141, L46013] Epstein-Barr virus-positive (EBV+) DLBCL NOS, and T-cell/histiocyte rich LBCL.[L44141]', 'Polatuzumab vedotin is an antibody-drug conjugate consisting of a CD79b-directed antibody, a microtubule-disrupting agent called monomethyl auristatin E (MMAE), and a cleavable linker that holds the components together.[L6658] CD79 is a heterodimer composed of CD79a and CD79b. Responsible for signal transduction, CD79 forms a complex with the B cell receptor (BCR) and is almost exclusively expressed on B cells, including malignant B cells.[A254936,A254951] Most importantly, CD79b gained increasing attention as a promising therapeutic target as it plays an essential role in BCR expression, transport, and functions such as B cell proliferation and differentiation.[A254951]



Once the antibody component binds to CD79b, polatuzumab vedotin is internalized, and lysosomal proteases cleave the linker to release MMAE in the cell. MMAE is a microtubule-disrupting anti-mitotic agent that exerts cytotoxic effects against malignant B cells. It binds to microtubules, inhibits mitosis by interfering with tubulin and tubulin polymerization, and induces apoptosis in dividing B cells.[A254936,L6658,L44141]', 'Polatuzumab vedotin is an anti-cancer agent that works to cause apoptosis in malignant B cells.[L6658] _In vitro_, it exerted cytotoxic effects on most diffuse large B-cell lymphoma (DLBCL) cell lines: this effect was consistent across cell lines, regardless of the cell-of-origin subtypes and whether they harboured mutations in the _CD79B_ gene or not.[A254936] In mouse xenograft models, polatuzumab vedotin caused apoptosis and reduced proliferation of mature CD79b+ B-cell NHL cell lines.[A254936]



Polatuzumab vedotin can cause immunosuppression, including neutropenia and thrombocytopenia.[A179380,L6658]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25737, 'Rawdament i.v inf. 250 ml', 'راودامنت امبول 250مل', '5', NULL, 'Sterile solution', 'Glucose (dextrose)+sodium chloride+potassium chloride', 'Al mottahedoon pharma', 'Unknown', 'غير محدد', '250 ml', '1', NULL, NULL, NULL, NULL, 539, '2022-07-27', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26210, 'Rivaxel 6mg 7 caps.', 'ريفاكسيل 6مجم 7 كبسولات', '30', NULL, 'Anticholinesterase', 'Rivastigmine', 'Hikma pharma', 'Cap', 'كبسولة', '6mg', '1', NULL, 'about rivastigmine a central reversible non competitive anticholinesterase antidementia. mechanism of action of rivastigmine alzheimer`s disease may result from a deficiency in neurotransmitters used by nerves in the brain to communicate with one another.', NULL, NULL, 718, '2022-07-29', 'For the treatment of mild to moderate dementia associated with Parkinson''s disease or of the Alzheimer''s type.', 'Rivastigmine is a carbamate derivative that is structurally related to physostigmine, but not to donepezil and tacrine. The precise mechanism of rivastigmine has not been fully determined, but it is suggested that rivastigmine binds reversibly with and inactivates chlolinesterase (eg. acetylcholinesterase, butyrylcholinesterase), preventing the hydrolysis of acetycholine, and thus leading to an increased concentration of acetylcholine at cholinergic synapses. The anticholinesterase activity of rivastigmine is relatively specific for brain acetylcholinesterase and butyrylcholinesterase compared with those in peripheral tissues.', 'Rivastigmine is a parasympathomimetic and a reversible cholinesterase inhibitor. An early pathophysiological feature of Alzheimer''s disease that is associated with memory loss and cognitive deficits is a deficiency of acetylcholine as a result of selective loss of cholinergic neurons in the cerebral cortex, nucleus basalis, and hippocampus. Tacrine is postulated to exert its therapeutic effect by enhancing cholinergic function. While the precise mechanism of rivastigmine''s action is unknown, it is postulated to exert its therapeutic effect by enhancing cholinergic function. This is accomplished by increasing the concentration of acetylcholine through reversible inhibition of its hydrolysis by cholinesterase. If this proposed mechanism is correct, rivastigmine''s effect may lessen as the disease progresses and fewer cholinergic neurons remain functionally intact.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7494, 'Lohydran 10 packets', 'لوهيدران محلول معالجة الجفاف 10 اكياس', '32', '22', 'Ors', 'Glucose (dextrose)+potassium chloride+sodium chloride+sodium citrate', 'Cid', 'Unknown', 'غير محدد', NULL, '10', 'محلول معالجة الجفاف', NULL, '6221043011431', NULL, 4594, '2025-05-18', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29429, 'Viscoplus matrix 2.5% (75mg/3ml) intra-articular prefilled syringe', 'فيسكوبلس ماتريكس 2.5% حقن معبأة للحقن بالمفصل', '5,500', '3500', 'Analgesic anti-inflammatory.eye surgery.tissue lubricant', 'Sodium hyaluronate', 'Biomedical b. baumann gmbh > pharmacon', 'Syringe', 'حقنة', '2.5%', '1', 'مسكن ومضاد التهاب وملين لانسجة المفاصل', 'about sodium hyaluronate a visco-elastic polymer(glycosaminoglycan) a tissue lubricant(prevent vaginal dryness) in osteoarthritis. mechanism of action of sodium hyaluronate sodium hyaluronate is a sterile nonpyrogenic highly purified noninflammatory high', NULL, NULL, 20552, '2024-02-14', 'For the treatment of small uncomplicated varicose veins of the lower extremities that show simple dilation with competent valves.', 'Sodium tetradecyl sulfate is a potent toxin for endothelial cells in that brief exposure to even low concentrations are effective in stripping endothelium over a considerable distance and exposing highly thrombogenic endothelium in the process. Diluted sodium tetradecyl sulfate is also able to induce a hypercoagulable state, possibly by selective inhibition of protein C, and can also promote platelet aggregation.', 'Sodium tetradecyl sulfate is an anionic surfactant which occurs as a white, waxy solid. It is used as a sclerosing agent in sclerotherapy. Sclerotherapy is an injection of a sclerosing agent directly through the skin into a lesion and is used primarily for slow-flow vascular anomalies, particularly for venous malformation and lymphatic malformation. Intravenous injection causes intima inflammation and thrombus formation. This usually occludes the injected vein. Subsequent formation of fibrous tissue results in partial or complete vein obliteration that may or may not be permanent.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4278, 'Epilat 10mg 30 soft gelatin caps.', 'ابيلات 10مجم 30 كبسولة', '18', '11.5', 'Antihypertensive.selective calcium channel blocker', 'Nifedipine', 'Eipico', 'Cap', 'كبسولة', '10mg', '1', NULL, NULL, '6221032141163', NULL, 1877, '2024-11-22', 'Nifedipine capsules are indicated to treat vasospastic angina and chronic stable angina.[L11383] Extended release tablets are indicated to treat vasospastic angina, chronic stable angina, and hypertension.[L11389,L1245]', 'Nifedipine blocks voltage gated L-type calcium channels in vascular smooth muscle and myocardial cells.[A190204] This blockage prevents the entry of calcium ions into cells during depolarization, reducing peripheral arterial vascular resistance and dilating coronary arteries.[A190204] These actions reduce blood pressure and increase the supply of oxygen to the heart, alleviating angina.[A190204]', 'Nifedipine is an inhibitor of L-type voltage gated calcium channels that reduces blood pressure and increases oxygen supply to the heart.[A190204] Immediate release nifedipine''s duration of action requires dosing 3 times daily.[L11383] Nifedipine dosing is generally 10-120mg daily.[L11383] Patients should be counselled regarding the risk of excessive hypotension, angina, and myocardial infarction.[L11383]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30766, 'Kolagra 10% urea foot cream 50 ml', 'كولاجرا 10% كريم قدم 50 مل', '164', '159', 'Skin care.moisturizing', 'Lactic acid+urea+vitamin e', 'Egyptian company for cosmetics > bio stream', 'Cream', 'كريم', '10%', '1', NULL, NULL, '6224001714443', NULL, 1074, '2025-05-18', 'For use as an alkalinizing agent.', 'Lactate ions are metabolized ultimately to carbon dioxide and water, which requires the consumption of hydrogen cations.', 'Lactic acid produces a metabolic alkalinizing effect.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28243, 'Tobrex 0.3% eye oint. 3.5 gm', 'توبركس 0.3% مرهم للعين 3.5جم', '19', '15.6', 'Aminoglycoside antibiotic', 'Tobramycin', 'Alcon > united company for trading & distribution', 'Oint', 'مرهم', '0.3%', '1', NULL, 'about tobramycin aminoglycoside derivative antibiotic. mechanism of action of tobramycin tobramycin exerts its bactericidal action against gram- negative organisms & some of gram positive organisms by inhibiting bacterial protein synthesis. the process in', '5413895042348', NULL, 1127, '2025-08-16', 'Inhaled tobramycin is indicated for the management of cystic fibrosis patients with _Pseudomonas aeruginosa_, but is not recommended in patients under six years of age, those with forced expiratory volume in 1 second (FEV<sub>1</sub>) <25 or >80% predicted, or in those with _Burkholderia cepacia_.[L32739,L45364]



Tobramycin applied topically to the eyes is indicated for the treatment of external eye (and adjoining structure) infections by susceptible bacteria.[L32744] 



Tobramycin injection is indicated in adult and pediatric patients for the treatment of serious bacterial infections, including septicemia (caused by _P. aeruginosa_, _Escherichia coli_, and _Klebsiella_ spp.), lower respiratory tract infections (caused by _P. aeruginosa_, _Klebsiella_ spp., _Enterobacter_ spp., _Serratia_ spp., _E. coli_, and _Staphylococcus aureus_, both penicillinase and non-penicillinase-producing strains), serious central-nervous-system infections (meningitis, caused by susceptible organisms), intra-abdominal infections including peritonitis (caused by _E. coli_, _Klebsiella_ spp., and _Enterobacter_ spp.), skin, bone, and skin structure infections (caused by _P. aeruginosa_, _Proteus_ spp., _E. coli_, _Klebsiella_ spp., _Enterobacter_ spp., _Serratia_ spp. and _S. aureus_), and complicated and recurrent urinary tract infections (caused by _P. aeruginosa_, _Proteus_ spp., _E. coli_, _Klebsiella_ spp., _Enterobacter_ spp., _Serratia_ spp., _S. aureus_, _Providencia_ spp., and _Citrobacter_ spp.).[L32749] Aminoglycosides, including tobramycin, should generally not be used in uncomplicated urinary tract infections or staphylococcal infections unless less toxic antibiotics cannot be used and the bacteria in question are known to be sensitive to aminoglycosides.[L32749,L45359]



As with all antibiotics, tobramycin use should be limited to cases where bacterial infections are known or strongly suspected to be caused by sensitive organisms, and the possible emergence of resistance should be monitored closely.[L32739,L32744,L32749]', 'Tobramycin is a 4,6-disubstituted 2-deoxystreptamine (DOS) ring-containing aminoglycoside antibiotic with activity against various Gram-negative and some Gram-positive bacteria.[L32739, L32744, L32749] The mechanism of action of tobramycin has not been unambiguously elucidated, and some insights into its mechanism rely on results using similar aminoglycosides. In general, like other aminoglycosides, tobramycin is bactericidal and exhibits both immediate and delayed killing, which are attributed to different mechanisms, as outlined below.[A232294, A232299]



Aminoglycosides are polycationic at physiological pH, such that they readily bind to bacterial membranes ("ionic binding"); this includes binding to lipopolysaccharide and phospholipids within the outer membrane of Gram-negative bacteria and to teichoic acid and phospholipids within the cell membrane of Gram-positive bacteria. This binding displaces divalent cations and increases membrane permeability, which allows aminoglycoside entry.[A232294, A232304, A232309, A232314] Additional aminoglycoside entry ("energy-dependent phase I") into the cytoplasm requires the proton-motive force, allowing access of the aminoglycoside to its primary intracellular target of the bacterial 30S ribosome.[A232294, A232314] Mistranslated proteins produced as a result of aminoglycoside binding to the ribosome (see below) integrate into and disrupt the cell membrane, which allows more of the aminoglycoside into the cell ("energy-dependent phase II").[A232294, A232314, A232319] Hence, tobramycin and other aminoglycosides have both immediate bactericidal effects through membrane disruption and delayed bactericidal effects through impaired protein synthesis; observed experimental data and mathematical modelling support this two-mechanism model.[A232294, A232299]



Inhibition of protein synthesis was the first recognized effect of aminoglycoside antibiotics. Structural and cell biological studies suggest that aminoglycosides bind to the 16S rRNA in helix 44 (h44), near the A site of the 30S ribosomal subunit, altering interactions between h44 and h45. This binding also displaces two important residues, A1492 and A1493, from h44, mimicking normal conformational changes that occur with successful codon-anticodon pairing in the A site.[A232324, A232329] Overall, aminoglycoside binding has several negative effects, including inhibiting translation initiation and elongation and ribosome recycling.[A232294, A232334, A232339] Recent evidence suggests that the latter effect is due to a cryptic second binding site situated in h69 of the 23S rRNA of the 50S ribosomal subunit.[A232329, A232339] Also, by stabilizing a conformation that mimics correct codon-anticodon pairing, aminoglycosides promote error-prone translation;[A232344] mistranslated proteins can incorporate into the cell membrane, inducing the damage discussed above.[A232294, A232319]



Although direct mutation of the 16S rRNA is a rare resistance mechanism, due to the gene being present in numerous copies, posttranscriptional 16S rRNA modification by 16S rRNA methyltransferases (16S-RMTases) at the N7 position of G1405 or the N1 position of A1408 are common resistance mechanisms in aminoglycoside-resistant bacteria.[A232294, A232349] These mutants also further support the proposed mechanism of action of aminoglycosides. Direct modification of the aminoglycoside itself through acetylation, adenylation, and phosphorylation by aminoglycoside-modifying enzymes (AMEs) are also commonly encountered resistance mutations.[A232294, A232349] Finally, due to the requirement for active transport of aminoglycosides across bacterial membranes, they are not active against obligately anaerobic bacteria.[A232294]', 'Tobramycin is an aminoglycoside antibiotic derived from the actinomycete _Streptomyces tenebrarius_.[L32749] It has a broad spectrum of activity against Gram-negative bacteria, including _Enterobacteriaceae_, _Escherichia coli_, _Klebsiella pneumoniae_, _Morganella morganii_, _Moraxella lacunata_, _Proteus_ spp., _Haemophilus_ spp., _Acinetobacter_ spp., _Neisseria_ spp., and, importantly, _Pseudomonas aeruginosa_. Aminoglycosides also generally retain activity against the biothreat agents _Yersinia pestis_ and _Francisella tularensis_. In addition, aminoglycosides are active against some Gram-positive bacteria such as _Staphylococcus_ spp., including methicillin-resistant (MRSA) and vancomycin-resistant strains, _Streptococcus_ spp., and _Mycobacterium_ spp.[A232294, L32744]



Like other aminoglycosides, tobramycin is taken up and retained by proximal tubule and cochlear cells in the kidney and ear, respectively, and hence carries a risk of nephrotoxicity and ototoxicity.[A232294, L32749] There is also a risk of neuromuscular block, which may be more pronounced in patients with preexisting neuromuscular disorders such as myasthenia gravis or Parkinson''s disease.[A232294, L32739, L32749] Aminoglycosides can cross the placenta, resulting in total, irreversible, bilateral congenital deafness in babies born to mothers who were administered an aminoglycoside during pregnancy.[L32739, L32749] Due to the low systemic absorption of inhaled and topical tobramycin formulations, these effects are more pronounced with injected tobramycin than with other formulations.[L32739, L32744, L32749] However, all formulations carry a risk of hypersensitivity reactions, including potentially fatal cutaneous reactions such as Stevens-Johnson syndrome and toxic epidermal necrolysis.[L32739, L32744, L32749]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1713, 'Ferroflav 120ml syrup', 'فيروفلاف 120 مل شراب', '89', NULL, 'Dietary supplement', 'Ferric pyrophosphate+vitamin b1+vitamin b6+vitamin b12', 'Bee group & pharmatreat', 'Syrup', 'شراب', '120ml', '1', NULL, NULL, NULL, NULL, 1039, '2024-11-28', 'Ferric carboxymaltose is an iron replacement product indicated for the treatment of iron deficiency anemia in patients ≥1 year of age who have an intolerance to, or unsatisfactory response from, oral iron therapy,[L49379] and in adult patients who have non-dialysis-dependent chronic kidney disease.[L49379] It is also indicated to treat iron deficiency and improve exercise capacity in adult patients with NYHA class II or III heart failure.[L49379]', 'Ferric carboxymaltose is a colloidal iron (III) hydroxide in complex with carboxymaltose, a carbohydrate polymer that releases iron.[L49379]', 'When measured using positron emission tomography (PET), the red cell uptake of 59-Fe and 52-Fe from INJECTAFER ranged from 61% to 99%.[L49379] In patients with iron deficiency, the red cell uptake ranged from 91% to 99%.[L49379] In patients with renal anemia, the red cell uptake ranged from 61% to 84%.[L49379]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12420, 'Vitaferrol elixir 120 ml', 'فيتافيرول اليكسر 120 مل', '9', NULL, 'Multivitamins', 'Iron+nicotinamide+panthenol+vitamin b1+vitamin b2+vitamin b6+vitamin b12', 'Eva pharma', 'Unknown', 'غير محدد', '120 ml', '1', NULL, NULL, '6223002451821', NULL, 1295, '2022-12-14', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23303, 'Natuferon 2.5m.i.u. i.m.vial', 'ناتوفيرون 2.5مليون وحدة دولية فيال عضل', '150', NULL, 'Antiviral .interferon alfa', 'Interferon alfa', 'Otsuka', 'Vial', 'فيال', NULL, '1', NULL, 'mechanism of action interferon alpha binds to type i interferon receptors (ifnar1 and ifnar2c) which upon dimerization activate two jak (janus kinase) tyrosine kinases (jak1 and tyk2). these transphosphorylate themselves and phosphorylate the receptors. t', NULL, NULL, 860, '2022-06-18', 'For the treatment of venereal or genital warts caused by the Human Papiloma Virus.', 'Interferon alpha binds to type I interferon receptors (IFNAR1 and IFNAR2c) which, upon dimerization, activate two Jak (Janus kinase) tyrosine kinases (Jak1 and Tyk2). These transphosphorylate themselves and phosphorylate the receptors. The phosphorylated INFAR receptors then bind to Stat1 and Stat2 (signal transducers and activators of transcription)which dimerize and activate multiple (~100) immunomodulatory and antiviral proteins. Interferon alpha binds less stably to type I interferon receptors than interferon beta.', 'Upregulates the expression of MHC I proteins, allowing for increased presentation of peptides derived from viral antigens. This enhances the activation of CD8+ T cells that are the precursors for cytotoxic T lymphocytes (CTLs) and makes the macrophage a better target for CTL-mediated killing. Interferon alpha also induce the synthesis of several key antiviral mediators, including 2''-5'' oligoadenylate synthetase (2''-5'' A synthetase) and protein kinase R.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17501, 'Dextrose 5% (el nile) i.v. inf. 500 ml', 'دكستروز 5% النيل محلول وريدي زجاجة 500 مل', '11', NULL, 'Sterile solution', 'Glucose (dextrose)', 'El nile', 'Unknown', 'غير محدد', '5%', '1', NULL, NULL, NULL, NULL, 773, '2022-07-15', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18178, 'Elimolivan 5/160mg 28 f.c. tabs.', 'اليموليفان 5/160مجم 28 قرص', '136', '103', 'Anti-hypertensive. combined angiotensin blocker with calcium channel blocker', 'Amlodipine+valsartan', 'Sigma > mash premiere', 'Tab', 'أقراص', '160mg', '2', 'لعلاج ضغط الدم المرتفع', 'valsartan is a nonpeptide orally active and specific angiotensin ii antagonist acting on the at1 receptor subtype. (amlodipine and valsartan) is indicated for the treatment of hypertension to lower blood pressure. lowering blood pressure reduces the risk', NULL, NULL, 4365, '2024-09-26', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1722, 'Centella moist 50 gm cream', 'سنتيلا مويست كريم 50 جم', '200', NULL, 'Cream', 'Centella+panthenol+ceramide+vite+vita cream', 'Sun shine pharmaceuticals', 'Cream', 'كريم', '50 gm', '1', NULL, NULL, NULL, NULL, 905, '2024-11-29', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23864, 'Nuxe body-contouring serum 150 ml', 'نوكس سيرم 150مل', '439', NULL, 'Skin care', NULL, 'Nuxe laboratory > biotech egypt', 'Serum', 'سيروم', '150 ml', '1', NULL, 'this highly concentrated body lift cellulite serum formulated with plant botanical caffeine plant extracts and cocoa polyphenols helps to reduce the appearance of embedded cellulite and contributes to firm the skin. skin is visibly smoother from the first', NULL, NULL, 823, '2022-08-03', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16557, 'Closol 10mg/ml topical spray 50 ml', 'كلوسول 10مجم/مل سبراي 50 مل', '44', '28', 'Antifungals.imidazoles', 'Clotrimazole', 'European egyptian pharm. ind.', 'Sol', 'محلول', '10mg', '1', 'مضاد فطريات', NULL, '6221076124030', NULL, 12088, '2024-07-11', '**Topical preparations**



Clotrimazole topical cream is indicated for the topical treatment of the following dermal infections [F3088], [F3121]:



Tinea pedis, tinea cruris, and tinea corporis due to _Trichophyton rubrum_, _Trichophyton mentagrophytes_, _Epidermophyton floccosum_



Candidiasis due to _Candida albicans_



Tinea versicolor due to _Malassezia furfur_



Diaper rash infected by _Candida albicans_





In some preparations, clotrimazole may be combined with betamethasone dipropionate, a corticosteroid [F3121]. 



**Oral preparations**



The oral troche preparation is indicated for the local treatment of oropharyngeal candidiasis [FDA label].  It is also indicated as a prophylactic drug to reduce the incidence of oropharyngeal candidiasis in patients immunocompromised by conditions such as chemotherapy, radiotherapy, or steroid therapy utilized in the treatment of leukemia, solid tumors, or renal transplantation [FDA label]. Troche preparations are not indicated for the treatment of any systemic mycoses [FDA label].', 'Clotrimazole acts primarily by damaging the permeability barrier in the cell membrane of fungi. Clotrimazole causes inhibition of ergosterol biosynthesis, an essential constituent of fungal cell membranes. If ergosterol synthesis is either completely or partially inhibited, the cell is no longer able to construct an intact and functional cell membrane [F3088],[F3100]. Because ergosterol directly promotes the growth of fungal cells in a hormone‐like fashion, rapid onset of the above events leads to dose-dependent inhibition of fungal growth [A174094].



Though decreased ergosterol, due to the inhibition of lanosterol 14-demethylase (also known as _CYP51_) [A174094] is accepted to be primarily responsible for the antimycotic properties of clotrimazole, this drug also shows other pharmacological effects. These include the inhibition of sarcoplasmic reticulum Ca2+‐ATPase,  depletion of intracellular calcium, and blocking of calcium‐dependent potassium channels and voltage‐dependent calcium channels [A174094]. The action of clotrimazole on these targets accounts for other effects of this drug that are separate from its antimycotic activities [A174094].', 'Clotrimazole is a broad-spectrum antifungal agent that inhibits the growth of pathogenic yeasts by changing the permeability of cell membranes. The action of clotrimazole is fungistatic at concentrations of drug up to 20 mcg/mL and may be fungicidal _in vitro_ against Candida albicans and other species of the genus Candida at higher concentrations [FDA label]. Unfortunately, resistance to clotrimazole, which was rare in the past, is now common in various patient populations [A174094]. 



Clotrimazole is generally considered to be a fungistatic, and not a fungicidal drug, although this contrast is not absolute, as clotrimazole shows fungicidal properties at higher concentrations [A174094].



', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6942, 'Kinoprid 5mg/ml 5 amp.', 'كينوبريد 5مجم/مل 5 امبولات', '46', NULL, 'Antiparkinson agents. muscarinic antagonist', 'Biperidene', 'Delta pharma', 'Amp', 'أمبول', '5mg', '5', NULL, NULL, '6221163005051', NULL, 1186, '2023-04-19', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26581, 'Scalex hair tonic lotion 6*30 ml', 'سكاليكس لوسيون للشعر 30 مل', '445', NULL, 'Hair care', NULL, 'El-helou perfumes & cosmetics > oshba', 'Lotion', 'لوشن', '30 ml', '1', 'تحسين الدورة الدموية لفروة الراس -- علاج الصلع الوراثي', NULL, NULL, NULL, 1550, '2022-05-29', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14656, 'Avanjoy 200 mg 4 tabs.', 'افانجوي 200 مجم 4 اقراص', '43', NULL, 'Tonic for men', 'Avanafil', 'Eva pharma', 'Tab', 'أقراص', '200 mg', '1', NULL, 'avanafil is a pde5 inhibitor approved for erectile dysfunction by the fda on april 27 2012 and by ema on june 21 2013. avanafil is known by the trademark names stendra and spedra and was developed by vivus inc. avanafil acts by inhibiting a specific phosp', NULL, NULL, 1378, '2022-07-24', 'Avanafil is indicated for the treatment of erectile dysfunction.[L32058]', 'Avanafil inhibits the cGMP-specific phosphodiesterase type 5 (PDE5) which is responsible for the degradation of cGMP in the corpus cavernosum located around the penis. Sexual arousal results in the local release of nitric oxide, which in turn stimulates the enzyme guanylate cyclase to produce cGMP. Elevated levels of cGMP result in local smooth muscle relaxation and increased blood flow to the penis (i.e. an erection).[L32058,L32113]



As PDE5 inhibitors like avanafil require the endogenous release of nitric oxide in order to exert their pharmacologic effect, they have no effect on the user in the absence of sexual stimulation/arousal.[L32058,L32113]', 'Avanafil is a strong competitive inhibitor of phosphodiesterase 5 (PDE5) with a demonstrated _in vitro_ IC<sub>50</sub> of 5.2 nM.[L32113] Its inhibitory effects on PDE5 are 100-fold more potent than on PDE6 and >1000-fold more potent than on other PDE enzymes,[L32058] meaning it is less likely to cause visual disturbances and cardiovascular adverse effects when compared with less selective PDE5 inhibitors such as [sildenafil] and [vardenafil].[L32113] It has a relatively quick onset of action allowing for administration as early as 15 minutes prior to sexual activity.[L32058]



PDE5 inhibitors like avanafil can cause significant drug interactions when administered alongside certain antihypertensive agents (e.g. alpha blockers, substantial amounts of alcohol).[L32058] PDE5 inhibitors have also been associated with the development of non-arteritic anterior ischemic optic neuropathy (NAION), a rare condition that typically presents as sudden loss of vision in one or both eyes and appears to be more common in patients with a "crowded" optic disc. Patients presenting with any degree of vision loss should immediately discontinue use of all PDE5 inhibitors and seek medical attention.[L32058] In some jurisdictions, a history of NAION or other degenerative retinal disorders is considered a contraindication to avanafil therapy.[L32233]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1046, 'Depofort b12 1mg/ml 5 amp.', 'ديبوفورت ب12 - 5 أمبولات', '55', '35', 'Vitamin b', 'Hydroxocobalamin', 'Pharco', 'Amp', 'أمبول', '1mg', '5', 'آلام العمود الفقري.', 'description injectable form of vitamin b 12 that has been used therapeutically to treat vitamin b 12 deficiency. mechanism of action vitamin b12 exists in four major forms referred to collectively as cobalamins; deoxyadenosylcobalamin methylcobalamin hydr', '6221151015338', NULL, 9435, '2024-11-26', 'For treatment of pernicious anemia and the prevention and treatment of vitamin B12 deficiency arising from alcoholism, malabsorption, tapeworm infestation, celiac, hyperthyroidism, hepatic-biliary tract disease, persistent diarrhea, ileal resection, pancreatic cancer, renal disease, prolonged stress, vegan diets, macrobiotic diets or other restrictive diets. Also for the treatment of known or suspected cyanide poisoning.', 'Vitamin B12 exists in four major forms referred to collectively as cobalamins; deoxyadenosylcobalamin, methylcobalamin, hydroxocobalamin, and cyanocobalamin. Two of these, methylcobalamin and 5-deoxyadenosyl cobalamin, are primarily used by the body. Methionine synthase needs methylcobalamin as a cofactor. This enzyme is involved in the conversion of the amino acid homocysteine into methionine. Methionine in turn is required for DNA methylation. 5-Deoxyadenosyl cobalamin is a cofactor needed by the enzyme that converts L-methylmalonyl-CoA to succinyl-CoA. This conversion is an important step in the extraction of energy from proteins and fats. Furthermore, succinyl CoA is necessary for the production of hemoglobin, the substances that carries oxygen in red blood cells.', 'Hydroxocobalamin is a synthetic, injectable form of Vitamin B12. Hydroxocobalamin is actually a precursor of two cofactors or vitamins (Vitamin B12 and Methylcobalamin) which are involved in various biological systems in man. Vitamin B12 is required for the conversion of methylmalonate to succinate. Deficiency of this enzyme could therefore interfere with the production of lipoprotein in myelin sheath tissue and so give rise to neurological lesions. The second cofactor, Methylcobalamin, is necessary for the conversion of homocysteine to methionine which is essential for the metabolism of folic acid. Deficiency of tetrahydrafolate leads to reduced synthesis of thymidylate resulting in reduced synthesis of DNA which is essential for cell maturation. Vitamin B12 is also concerned in the maintenance of sulphydryl groups in reduced form, deficiency leading to decreased amounts of reduced SH content of erythrocytes and liver cells. Overall, vitamin B12 acts as a coenzyme for various metabolic functions, including fat and carbohydrate metabolism and protein synthesis. It is necessary for growth, cell replication, hematopoiesis, and nucleoprotein as well as myelin synthesis. This is largely due to its effects on metabolism of methionine folic acid, and malonic acid.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5588, 'Calvigo 20 tablet', 'كالفيجو 20 قرص', '72', NULL, 'Multivitamin', 'Calcium+magnesium+zinc and vitamin d3', 'Vigor med pharama', 'Tablet', 'أقراص', NULL, '2', NULL, NULL, NULL, NULL, 792, '2025-06-30', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31883, 'Tulango-vit syrup 120 ml', 'تولانجو-فيت شراب 120 مل', '95', '75', 'Multivitamin', 'Royal jelly+vitamin b complex+vitamin c+vitamin d3+vitamin e+vitamin a+folic acid+biotin', 'Praxo pharm', 'Syrup', 'شراب', '120 ml', '1', NULL, 'uses: 1-supports immune system and increase body resistance against infection. 2- strengthens bodies and improves activities 3-improves appetite. 4-increases mental health and cognitive function. dose : -from 5-12 years/ one teaspoon 5ml/day. -for teenage', '6224001594083', NULL, 1367, '2024-03-22', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16111, 'Cephadrol 250 mg/5 ml susp. 60 ml', 'سيفادرول 250مجم/5مل معلق 60 مل', '9', NULL, 'Antibiotic.cephalosporin.first-generation', 'Cefadroxil', 'Arab drug company.', 'Susp', 'معلق', '250 mg', '1', NULL, 'about cefadroxil first generation cephalosporin para-hydroxy derivative of cefalexin oral broad-spectrum antibiotic mechanism of action cefadroxil is a 1st generation cephalosporin which exerts its bactericidal action against both gram positive & gram neg', NULL, NULL, 790, '2023-04-27', 'For the treatment of the following infections (skin, UTI, ENT) caused by; <i>S. pneumoniae, H. influenzae, staphylococci, S. pyogenes</i> (group A beta-hemolytic streptococci), <i>E. coli, P. mirabilis, Klebsiella</i> sp, coagulase-negative staphylococci and <i>Streptococcus pyogenes</i>', 'Like all beta-lactam antibiotics, cefadroxil binds to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, causing the inhibition of the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins; it is possible that cefadroxil interferes with an autolysin inhibitor.', 'Cefadroxil, a first-generation cephalosporin antibiotic, is used to treat urinary tract infections, skin and skin structure infections, pharyngitis, and tonsillitis.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26198, 'Rivamep 4.5 mg 20 caps.', 'ريفاميب 4.5مجم 20 كبسولات', '58', NULL, 'Anticholinesterase', 'Rivastigmine', 'Mepaco', 'Cap', 'كبسولة', '4.5 mg', '2', NULL, 'about rivastigmine a central reversible non competitive anticholinesterase antidementia. mechanism of action of rivastigmine alzheimer`s disease may result from a deficiency in neurotransmitters used by nerves in the brain to communicate with one another.', NULL, NULL, 853, '2022-07-27', 'For the treatment of mild to moderate dementia associated with Parkinson''s disease or of the Alzheimer''s type.', 'Rivastigmine is a carbamate derivative that is structurally related to physostigmine, but not to donepezil and tacrine. The precise mechanism of rivastigmine has not been fully determined, but it is suggested that rivastigmine binds reversibly with and inactivates chlolinesterase (eg. acetylcholinesterase, butyrylcholinesterase), preventing the hydrolysis of acetycholine, and thus leading to an increased concentration of acetylcholine at cholinergic synapses. The anticholinesterase activity of rivastigmine is relatively specific for brain acetylcholinesterase and butyrylcholinesterase compared with those in peripheral tissues.', 'Rivastigmine is a parasympathomimetic and a reversible cholinesterase inhibitor. An early pathophysiological feature of Alzheimer''s disease that is associated with memory loss and cognitive deficits is a deficiency of acetylcholine as a result of selective loss of cholinergic neurons in the cerebral cortex, nucleus basalis, and hippocampus. Tacrine is postulated to exert its therapeutic effect by enhancing cholinergic function. While the precise mechanism of rivastigmine''s action is unknown, it is postulated to exert its therapeutic effect by enhancing cholinergic function. This is accomplished by increasing the concentration of acetylcholine through reversible inhibition of its hydrolysis by cholinesterase. If this proposed mechanism is correct, rivastigmine''s effect may lessen as the disease progresses and fewer cholinergic neurons remain functionally intact.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2606, 'Rotamind 30 soft gelatin capsules', 'روتاميند 30 كبسولة جيلاتينية طرية', '210', NULL, 'Supports a normal function of the brain vision and heart', 'Fish oil 1000 mg+d-alpha tocopherol acetate ( vitamin e acetate )', 'Rotabiogen', 'Capsule', 'كبسولة', NULL, '3', NULL, NULL, NULL, NULL, 688, '2025-01-23', 'Under FDA approval, fish oil pharmaceuticals are typically products consisting of a combination of the omega-3-fatty acids eicosapentaenoic acid (EPA) and docosahexaenoic acid (DHA) and are indicated primarily as an adjunct to diet to reduce triglyceride levels in adult patients with severe (>=500 mg/dL) hypertriglyceridemia [FDA Label, F36].



Under EMA approval, such fish oil pharmaceuticals comprised of virtually the same fish and fish oil derived omega-3-fatty acids EPA and DHA are indicated specifically for (a) adjuvant treatment in secondary prevention after myocardial infarction, in addition to other standard therapy (ie. statins, antiplatelet medicinal products, beta blockers, ACE inhibitors), and (b) as a supplement to diet when dietary measures alone are insufficient to produce an adequate response, particularly with type IV hypertriglyceridemia in monotherapy or type IIb/III in combination with statins, when control of triglycerides is insufficient [L2661]. In addition, prescribing information for EMA approved fish oil pharmaceuticals are also indicated as an adjunct to diet to reduce very high (>=500 mg/dL) triglyceride levels in adult patients, much like similar FDA approved indications [F37, FDA Label].', 'The specific mechanism of action by which the fish oil EPA and DHA acids are capable of reducing serum triglyceride levels is not yet fully understood [FDA Label, A32933]. Nevertheless, it is proposed that such omega-3-fatty acids may not be the preferred substrates of the enzyme diacylglycerol O-acyltransferase that participates in the generation of triglycerides; that  they might interact with nuclear transcription factors that manage lipogenesis; or that their presence and increase in levels can cause cellular metabolism to subsequently shift toward a decrease in triglyceride synthesis and an increase in fatty acid oxidation [A32933]. Moreover, the EPA and DHA acids are also believed to be able to promote apolipoprotein B degradation in the liver through the stimulation of an autophagic process [A32933]. It may also be possible that these fish oil acids can accelerate the clearance of very-low-density lipoprotein (VLDL) particles and chylomicron [A32933]. The combination of all these actions results in fewer VLDL particles being assembled and secreted, which is of considerable importance as VLDL particles are the major endogenous source of triglycerides [A32933].



Moreover, new paradigms of how inflammation is contained and dissipated involve various newly discovered chemical mediators, resolvins, and protectins [A32933]. Such agents are believed to be directly involved in blocking neutrophil migration, infiltration, recruitment, as well as blocking T-cell migration and promoting T-cell apoptosis [A32933]. Additionally, such protectins can also reduce tumor necrosis factor and interferon secretion [A32933]. Of particular importance, however, is the fact that protectins and resolvins are exclusively derived from omega-3-fatty acids and that EPA is the substrate of the resolvins family and DHA can be converted to both resolvins and protectins [A32933]. It is believed that these effects of such fish oil acids underlie the actions that fish oil have demonstrated on eliciting stability for vulnerable inflammatory plaques [A32933].



Finally, fish oil acids have demonstrated certain direct electrophysiological effects on the myocardium [A32933]. In animal studies, it was shown that the ventricular fibrillation threshold could be increased in both animals fed or infused with omega-3-fatty acids [A32933]. Further studies subsequently revealed that such fatty acids could reduce both sodium currents and L-type calcium currents on a cellular and ion channel level [A32933]. It is consequently hypothesized that during ischemia, a reduction in the sodium ion current protects hyperexcitable tissue, and a reduction in the calcium ion current could reduce arrhythmogenic depolarizing currents - and that perhaps the use of EPA and DHA fish oil acids could facilitate such activity [A32933]. For the time being, however, omega-3-fatty acids in pharmaceutical supplement form have not been shown to elicit such protection against heart conditions [L2662].', 'In general, the only practical method of obtaining and increasing the levels of eicosapentaenoic acid (EPA) and docosahexaenoic acid (DHA) fish oil omega-3-fatty acids in the body is to consume them directly from foods and/or dietary supplements [L784]. Having EPA and DHA is important because they facilitate numerous important functions in the human body. As an example, such supplementation of fish oil EPA and DHA demonstrates a legitimate ability to decrease triglyceride levels as their presence in the body can act as poor substrates for the enzymes that are ordinarily responsible for triglyceride synthesis and also inhibit the esterification of other fatty acids, among other mechanisms [FDA Label, L2661].



Moreover, such fish oil acids can become important components of the phospholipids that form the structures of cell membranes [L784]. Specifically, DHA is particularly high in the retina, brain, and sperm [L784]. Additionally, these acids also provide energy for the body and are used to form eicosanoids - signaling molecules that have similar chemical structures to the fish oil fatty acids from which they are derived [L784]. Furthermore, such eicosanoids possess wide-ranging functions in the cardiovascular, pulmonary, immune, and endocrine systems [L784].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31085, 'Pediamil 1 milk 350 gm', 'بيدياميل 1 لبن 350 جم', '349', '329', 'Milk products.first stage (age 0-6 months)', 'Milk formula stage 1', 'Hochdorf nutricare co. > liptis', 'Unknown', 'غير محدد', '350 gm', '1', NULL, 'not to replace mother s milk. for infants from 0-6 months age. to be used within one month from opening. liptomil plus contains 1. prebiotics. 2.nucleotides. 3.antioxidants. 4.essential fatty acids. 5. choline & taurine. 6. vitamins minerals and trace ele', NULL, NULL, 2784, '2025-11-24', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17314, 'Lucente lumini foam cleanser 200 ml', 'لوسينت لوميني فوم كلينزر 200 مل', '270', NULL, 'Skin care', 'D-panthenol+pentavitin+nanoactive salicylic acid+liquorice extract+arbutin+ascorbyl palmitate+glutathione', 'Macro group pharmaceuticals', 'Cleanser', 'منظف', '200 ml', '1', NULL, NULL, NULL, NULL, 673, '2022-08-12', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27753, 'Svr sebiaclear creme spf50 - 50 ml', 'اس في ار سيبياكلير كريم بمعامل حماية 50- 50مل', '299', NULL, 'Skin care', NULL, 'Svr laboratoire dermatologique > biotech egypt', 'Unknown', 'غير محدد', '50 ml', '1', NULL, 'high sun protection mattifying anti-blemishes sebiaclear crï-me spf50 is the high sun protection skincare treatment for combination to oily skin prone to blemishes. anti acne it moisturizes and mattifies all day long. use before exposing the skin to sunli', NULL, NULL, 609, '2022-08-04', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24435, 'Pacify massage cream 50 gram', 'باسيفاي كريم مساج 50 جم', '75', '42', 'Massage cream', 'Eucalyptus oil+ menthol+camphor oil+ glycerin', 'Italian co. for cosemetics > core pharm', 'Cream', 'كريم', NULL, '1', 'كريم مساج', NULL, NULL, NULL, 1510, '2025-08-23', 'As an active agent, eucalyptus oil has been indicated for relief of the symptoms of catarrhal colds, and/or the relief of the symptoms of minor muscular sprains and cramps [L1857].', 'The general consensus is that the exact mechanism of action of eucalyptus oil is largely unknown at this time but comprises various hypotheses from various studies.



Cineol containing preparations of eucalyptus oil may contain up to 80% (or more) 1,8-cineole [A32243] and is one of the most common types of eucalyptus oil formulations used. As an active agent indicated for relieving certain cold symptoms and/or certain muscular sprains and cramps, it is believed that eucalyptus oil may possess some antimicrobial and anti-inflammatory activities.



Some in vitro studies of human blood monocytes suggest a dose-dependent effect of eucalyptus oil to elicit significant inhibition of multiple cytokines, perhaps in the treatment of airway inflammation [A32244, A32245]. Moreover, other studies in animal models discuss the possibility of eucalyptus oil demonstrating anti-inflammatory and anti-nociceptive effects that potentially account for inhibiting the formation of prostaglandins and cytokines by stimulated monocytes in vitro [A32246, A32247].



Furthermore, additional studies have observed eucalyptus oil anti-viral activity against herpes simplex virus (HSV-1, HSV-2) in cell cultures as well as the demonstration of broad antimicrobial activity of eucalyptus medicinal plant extracts against Alicyclobacillus acidoterretris, Bacillus cereus, E. coli, Enterococcus faecalis, MRSA, Propionibacterium acnes, S. aureus, fungus including C. albicans isolates, Trichophyton mentagrophytes, and other Gram-positive bacteria. Specific activity against periodontopathic bacteria, such as Porphyromonas gingivalis, Actinobacillus actinomycetemcomitans, Fusobacterium nucleatum, Streptococcus mutans, and Streptococcus sobrinus has also been observed [A32250, A32251, A32252, A32253, A32254].', 'Lipophilic monoterpene formulations of eucalyptus oil appear to be readily absorbed orally, with a primarily oxidative metabolism that might necessitate induction of the cytochrome P450 enzyme system and subsequent urinary excretion [A32236]. Gastrointestinal absorption of eucalyptus appears to be rapid and may be enhanced by the intake of lipids and milk. 1,8-cineole (which makes up to as much as 90% of most commonly used cineole-based eucalyptus oils) [A32227] has also been found in vitro and in animals to possess cytochrome P450 inducing activity [A32237, A32238, A32239].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3760, 'Swalena 50 mg moisturizing emulgel', 'سوالينا جل مرطب للبشرة 50 جم', '199', '150', 'For acne and whitining agent', 'Anthemis nobilis flower extract+leptospermum petersonii oil+glycyrrhiza glabra+alpha arbutin+kojic acid+tranexamic acid+lactic acid+salicylic acid+aloe vera+panthenol -', 'Swa pharma', 'Gel', 'جل', '50 mg', '1', NULL, NULL, NULL, NULL, 985, '2025-07-22', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28531, 'Trisolvin 10 soft gelatin caps.', 'تراي سولفين 10 كبسولات', '5', NULL, 'Mucolytic', 'Ambroxol+guaifenesin+theophylline', 'Glaxo smithkline', 'Cap', 'كبسولة', NULL, '1', NULL, '(indications) : this medication is a mucolytic agent prescribed for various respiratory diseases such as emphysema with bronchitis pneumoconiosis chronic inflammatory pulmonary conditions tracheobronchitis (respiratory tract inflammation) bronchiectasis b', '6221045001669', NULL, 1104, '2022-07-15', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29353, 'Villapurg 67% syrup 200 ml', 'فيلابورج 67% شراب 200مل', '18', NULL, 'Laxative', 'Lactulose', 'Al esraa pharmaceutical optima', 'Syrup', 'شراب', '67%', '1', NULL, 'about lactulose a synthetic disaccharide osmotic laxative ammonium detoxicant. mechanism of action of lactulose laxative: lactulose is a synthetic disaccharide of galactose and fructose that resist intestinal disaccharidase activity. the sugars are joined', NULL, NULL, 874, '2022-07-09', 'Lactulose is indicated for use as a laxative in the treatment of chronic constipation in adults and geriatric patients.[FDA Label,L6199,L6202]



Additionally, lactulose is also employed as an adjunct to protein restriction and supportive therapy for the prevention and treatment of portal-systemic encephalopathy (PSE), including both the hepatic pre-coma and coma variations.[FDA Label,L6199,L6202] In particular, lactulose solution has been effective at managing PSE resulting from surgical portacaval shunts or from chronic hepatic diseases like cirrhosis.[L6199]



Moreover, there have also been studies demonstrating the capacity for lactulose to minimize the formation of gallstones and even some investigations regarding the experimental use of the agent in developing novel anticancer agents owing to its ability to bind galactin carbohydrates involved in various tumor progressions [L6202].', 'Lactulose is a synthetic disaccharide derivative of lactose that consists of one molecule of galactose and one molecule of fructose.[FDA Label,L6199,L6202] Saccharolytic bacteria present in the large intestine subsequently break the substance down into organic acids like lactic acid and small amounts of formic and acetic acids.[FDA Label,L6199,L6202] Such resultant volatile fatty acid metabolites, in combination with hydrogen and methane that is also generated consequently enhance intraluminal gas formation, peristaltic gut motility, and elicit an osmotic effect that facilitates an increase in the water content of stool as well as associated stool softening.[FDA Label,L6199,L6202] All of these actions ultimately assist in facilitating and increasing the frequency of bowel movements in patients experiencing constipation, although it may take 24 to 48 hours after using the medication for this laxative effect to become evident.[FDA Label,L6199,L6202]



At the same time, the formation of such acids via the metabolism of lactulose by colonic bacteria also acidifies the contents of the colon, thereby contributing to the treatment of portal-systemic encephalopathy (PSE).[FDA Label,L6199,L6202] As one of the principal features of PSE involves the accumulation of nitrogenous waste products like ammonia in the systemic circulation, a state in which the colonic contents become more acidic than blood allows ammonia in the circulation to diffuse into the colon.[FDA Label,L6199,L6202]. Furthermore, ammonia that diffuses into the acidic colon is ionized to ammonium ions that are incapable of being absorbed back into the blood.[FDA Label,L6199,L6202] These effects, combined with the laxative action of lactulose facilitates the excretion of excess ammonia.[FDA Label,L6199,L6202] And finally, it is also believed that an acidic colonic environment results in the elimination of urease-producing bacteria that contribute to the formation of ammonia while surviving colonic bacteria use up any trapped ammonia in the colon as a source of nitrogen for protein synthesis.[L6202]', 'Lactulose formulations are most commonly administered via the oral route or the rectal route.[L6202] Consequently, because the substance experiences minimal absorption by the gut it typically remains localized in the gastrointestinal tract environment and ultimately demonstrates almost all of its pharmacologic effects within the gut.[FDA Label,L6199,L6202] In particular, as lactulose elicits its laxative effects in enhancing stool amounts and softening stool, such biochemical and physiologic activities can cause increased bowel sounds (borborygmi), a feeling of bloatedness, belching, frequent flatus, and diarrhea.[FDA Label,L6199,L6202]   ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26607, 'Schistocide 600mg/5ml susp. 15ml', 'شيستوسيد 600مجم /5مل معلق 15مل', '7', NULL, 'Anthelmintic', 'Praziquantel', 'Delta pharma', 'Susp', 'معلق', '600mg', '1', 'طارد للديدان', 'about praziquantel anthelmintic a trematodicide. mechanism of action of praziquantel it is a broad spectrum anthelminthic against trematodes & cestodes and their larval forms. the drug exerts its action by producing strong muscular contractions which lead', NULL, NULL, 1004, '2022-07-07', 'Praziquantel is indicated in patients aged 1 year and older for the treatment of the schistosomiasis due to all species of Schistosoma (for example, Schistosoma mekongi, Schistosoma japonicum, Schistosoma mansoni and Schistosoma hematobium) and clonorchiasis and opisthorchiasis due to the liver flukes, Clonorchis sinensis/Opisthorchis viverrini (approval of this indication was based on studies in which the two species were not differentiated).[L50021]', 'Although the exact mechanism of action is unknown, praziquantel was hypothesized to target the β subunits of voltage-gated Ca<sub>2+</sub> channels, particularly in Schistosoma mansoni and Schistosoma japonicum, due to the lack of two conserved serine residues in these subunits. This is supported by the finding that co-administration of calcium channel blockers like nicarpidine and nifedipine renders 50% of Schistosoma mansoni resistant to praziquantel.[L50021,A1664]



Increased exposure of antigens on the worm surface was also observed, but little research has been done to elucidate on the mechanism of action.[A1664]', 'In vitro studies on trematodes and cestodes have shown that praziquantel induces a rapid contraction of schistosomas by a specific effect on the permeability of the cell membrane. The drug further causes vacuolization and disintegration of the schistosome tegument. The effect is more marked on adult worms compared to young worms. An increased Ca2<sup>+</sup>-influx may play an important role.[L40268]



Secondary effects are inhibition of glucose uptake, lowering of glycogen levels and stimulation of lactate release. The action of praziquantel is specific to trematodes and cestodes; nematodes (including filariae) are not affected.[L40268]



Praziquantel is active against schistosoma (for example, Schistosoma mekongi, Schistosoma japonicum, Schistosoma mansoni and Schistosoma hematobium), and infections due to the liver flukes, Clonorchis sinensis/Opisthorchis viverrini. Published in vitro data have shown a potential lack of efficacy of praziquantel against migrating schistosomulae.[L50021]



An interesting quirk of praziquantel is that it is relatively ineffective against juvenile schistosomes. While initially effective, effectiveness against schistosomes decreases until it reaches a minimum at 3-4 weeks. Effectiveness then increases again until it is once again fully effective at 6-7 weeks.[A1664]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (8169, 'Migracid 20 tab.', 'ميجراسيد 20 قرص', '5', NULL, 'Antiemetic', 'Metoclopramide+paracetamol(acetaminophen)', 'Cid', 'Tab', 'أقراص', NULL, '2', NULL, 'about metoclopramide it is a dopamine antagonist - antiemetic gastroprokinetic agent. mechanism of action of metoclopramide metoclopramide acts through both serotonergic and dopaminergic receptors. metoclopramide binds to serotonin (5 ht3 &5ht4) and dopam', '6221043011455', NULL, 1619, '2022-12-06', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17634, 'Diety 50 tabs', 'دايتي 50 قرص', '4', NULL, 'Sweetener', 'Aspartame', 'Eva pharma', 'Tab', 'أقراص', NULL, '1', NULL, NULL, '6223002454303', NULL, 837, '2022-12-04', 'Used as a diet supplement and sugar substitute.', '180 to 200 times sweeter than sucrose, it is metabolized as a protein and its subsequent amino-acids used up in there respective mechanisms.', 'Aspartame (L-alpha-aspartyl-L-phenylalanine methyl ester) is a low-calorie sweetener used to sweeten a wide variety of low- and reduced-calorie foods and beverages, including low-calorie tabletop sweeteners. Aspartame is composed of two amino acids, aspartic acid and phenylalanine, as the methyl ester. Aspartic acid and phenylalanine are also found naturally in protein containing foods, including meats, grains and dairy products. Methyl esters are also found naturally in many foods such as fruits and vegetable and their juices. Upon digestion, aspartame breaks down into three components (aspartic acid, phenylalanine and methanol), which are then absorbed into the blood and used in normal body processes. Neither aspartame nor its components accumulates in the body. These components are used in the body in the same ways as when they are derived from common foods.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23527, 'New stracyl 20 sachets', 'نيو ستراسيل 20 كيس', '45', NULL, 'Weight loss', 'Psyllium husks powder+glucose (dextrose)+carboxy methyl cellulose+sucralose+orange flavour', 'Nutrixia > trustmar pharma', 'Sachet', 'أكياس', NULL, '1', NULL, 'soluble fibre that is used in weight loss', NULL, NULL, 1826, '2022-07-28', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28672, 'Ultra vitamin c 500 mg 10 tabs.', 'الترا فيتامين سي 500مجم 10 قرص', '20', NULL, 'Vitamin c', 'Vitamin c', 'Vitabiotics', 'Tab', 'أقراص', '500 mg', '1', NULL, 'about vitamin c water soluble vitamin antioxidant. mechanism of action of vitamin c vitamin c exerts it`s action by influencing the biologic oxidations and reductions used in cellular respirations. it directly stimulates collagen synthesis and maintains i', '6224001078309', NULL, 1453, '2022-08-14', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13157, 'Tigint plus 10 sachets', 'تايجينت بلس 10 اكياس', '290', '260', 'Anti-rheumatic.osteoarthritis.anabolic', 'Gelatin (collagen) hydrolysate+lactoferrin', 'Hygint', 'Sachet', 'أكياس', NULL, '1', 'خشونة المفاصل . مضاد للروماتيزم والتهاب المفاصل', 'for health joints', '6224008622161', NULL, 5681, '2025-01-29', 'Gelatin is used for weight loss and for treating osteoarthritis, rheumatoid arthritis, and brittle bones (osteoporosis). Some people also use it for strengthening bones, joints, and fingernails. Gelatin is also used for improving hair condition and to shorten the recovery after exercise and sports-related injury [L2113]. Gelatin is used in preparations of foods, cosmetics, and medicine [L2113].



Plasma volume expander in hypovolaemic shock [L2115]. Haemostatic [L2115].



Gelatin-based hydrogels are being used in drug delivery and tissue engineering because they are able to promote cell adhesion and proliferation. In addition, these hydrogels can be used as wound dressings because of their attractive fluid absorbance properties. Manufacturing technologies such as ultraviolet stereolithography and two-photon polymerization can be used to prepare structures containing photosensitive gelatin-based hydrogels [L2111].', 'It works as a hemostatic by providing a physical framework within which clotting may occur [L2115].



As a volume expander, gelatin remains in the vascular space. When used in the treatment of hypovolaemia gelatin can produce a significant increase in blood volume, cardiac output, stroke volume, blood pressure, urinary output and oxygen delivery, increasing volume and pressure [L2117].



For intravascular volume expansion, the majority or gelatins produce an effect which is almost equivalent to of which are mild, although severe reactions albumin, with a duration of action of 3 to 4 hours to have been reported [L2118].



Gelatin or collagen chains suspended in solution can be covalently cross-linked to form matrices that are able to swell in the presence of aqueous solutions, forming what are called _gelatin hydrogels_. Hydrogels, characterized by their hydrophilicity and insolubility in water, have the capability of swelling into an equilibrium volume while maintaining their shape. The chemical cross-linkers used may be either small bifunctional molecules or polyfunctional macromolecules, for example, glutaraldehyde [L2122].', 'Gelatin contains collagen, which is one of the materials that make up cartilage and bone [L2113].



In addition to their well-established value as a nutritional protein source, collagen and collagen-derived products may exhibit various potential biological activities on cells and the extracellular matrix through the corresponding food-derived peptides post-ingestion.  This could justify their applications in dietary supplements and pharmaceutical agents [A32426].



Gelatin is a protein that is used as a hemostatic in surgical procedures.  It is also used as a plasma volume expander in hypovolemic shock. Gelatin rods structures may also be used to temporarily block tear outflow in cases of dry eye [L2115].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17967, 'Durafos 1 amp. 2ml im or intra articular', 'ديورافوس امبول 2 مل حقن عضل او للحقن في المفصل', '22', '16', 'Glucocorticoid', 'Betamethasone dipropionate+betamethasone sodium phosphate', 'Sigma tec > eva pharma', 'Amp', 'أمبول', '2ml', '1', 'مضاد التهاب -- حالات الخشونة والتهاب المفاصل -- حالات الحساسية المختلفة كحساسية الصدر او الجلد', 'about betamethasone dipropionate a synthetic glucocorticoid anti-inflammatory. indications for betamethasone dipropionate 1.eczema 2.psoriasis 3.contact dermatitis 4.anal and vulval pruritis 5.inflammation of the skin contra-indications of betamethasone d', '6223004510335', NULL, 3908, '2024-12-01', 'As a member of the corticosteroid family, betamethasone is indicated for the treatment of several inflammatory conditions. As topical monotherapy, betamethasone is indicated to relieve pruritic and inflammatory symptoms of corticosteroid-responsive-dermatoses.[L11991,L10782,L11997] Betamethasone can be used topically in combination with a vitamin D analog such as calcipotriene to treat plaque psoriasis.[L12000] The corticosteroid is also available as an injectable suspension and can be used to manage a range of inflammatory conditions including endocrine disorders, gastrointestinal disorders, and rheumatic disorders among other conditions.[L11994]

', 'Glucocorticoids inhibit neutrophil apoptosis and demargination, and inhibit NF-Kappa B and other inflammatory transcription factors.[A187463] They also inhibit phospholipase A2, leading to decreased formation of arachidonic acid derivatives.[A187463] In addition, glucocorticoids promote anti-inflammatory genes like interleukin-10.[A187463]



Corticosteroids like betamethasone can act through nongenomic and genomic pathways.[A31458] The genomic pathway is slower and occurs when glucocorticoids activate glucocorticoid receptors and initiate downstream effects that promote transcription of anti-inflammatory genes including phosphoenolpyruvate carboxykinase (PEPCK), IL-1-receptor antagonist, and tyrosine amino transferase (TAT).[A31458] On the other hand, the nongenomic pathway is able to elicit a quicker response by modulating T-cell, platelet and monocyte activity through the use of existing membrane-bound receptors and second messengers.[A31458]', 'Corticosteroids bind to the glucocorticoid receptor inhibiting pro-inflammatory signals, while promoting anti-inflammatory signals.[A187463] Corticosteroids have a wide therapeutic window as patients may require doses that are multiples of what the body naturally produces.[A187463] Patients who require long-term treatment with a corticosteroid should be counselled regarding the risk of hypothalamic-pituitary-adrenal axis suppression and increased susceptibility to infections.[A187463]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5251, 'Mr. bear omega syrup 400 ml', 'مستر.بير اوميجا شراب 400 مل', '1,400', NULL, 'Multivitamins', 'Multivitamins', 'Aug pharma', 'Syrup', 'شراب', '400 ml', '1', NULL, NULL, '6223005464248', NULL, 1311, '2024-07-08', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3935, 'Rotamag bisglycinate 30 h.g. caps.', 'روتاماج بيس جليسينات 30 كبسولة', '210', NULL, 'Magnesium', 'Magnesium', 'Rotabiogen', 'Cap', 'كبسولة', NULL, '1', NULL, NULL, '6224011378604', NULL, 722, '2022-08-27', 'Used for immediate control of life-threatening convulsions in the treatment of severe toxemias (pre-eclampsia and eclampsia) of pregnancy and in the treatment of acute nephritis in children. Also indicated for replacement therapy in magnesium deficiency, especially in acute hypomagnesemia accompanied by signs of tetany similar to those of hypocalcemia. Also used in uterine tetany as a myometriat relaxant.', 'Magnesium is the second most plentiful cation of the intracellular fluids. It is essential for the activity of many enzyme systems and plays an important role with regard to neurochemical transmission and muscular excitability. Magnesium sulfate reduces striated muscle contractions and blocks peripheral neuromuscular transmission by reducing acetylcholine release at the myoneural junction. Additionally, Magnesium inhibits Ca<sup>2+</sup> influx through dihydropyridine-sensitive, voltage-dependent channels. This accounts for much of its relaxant action on vascular smooth muscle.', 'Magnesium sulfate is a small colorless crystal used as an anticonvulsant, a cathartic, and an electrolyte replenisher in the treatment of pre-eclampsia and eclampsia. It causes direct inhibition of action potentials in myometrial muscle cells. Excitation and contraction are uncoupled, which decreases the frequency and force of contractions. Magnesium sulfate is gaining popularity as an initial treatment in the management of various dysrhythmias, particularly torsades de pointes, and dyrhythmias secondary to TCA overdose or digitalis toxicity.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16472, 'Cleanol facial cleanser 250 ml', 'كلينول غسول للوجة 250 مل', '120', '100', 'Skin care', 'Panthenol+aloe vera+tea tree oil+triclosan+bee propolis+salicylic acid+allantoin+citric acid+chamomi', 'Hi-care > avano pharma', 'Cleanser', 'منظف', '250 ml', '1', NULL, 'a rich formula soap free non-irritant and ph balanced cleanser', '6224000201081', NULL, 1512, '2025-06-29', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1043, 'Astin 10mg 10 tab.', 'استين 10مجم 10 اقراص', '23', '14.4', 'Antihistamine.antiallergy', 'Ebastine', 'Future pharmaceutical industries (fpi)', 'Tab', 'أقراص', '10mg', '1', NULL, NULL, '6223004190162', NULL, 1469, '2025-11-03', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6524, 'Marketova carebaby cream 50gm', 'ماركيتوفا كيربيبي كريم 50 جم', '45', NULL, 'Diaper rash area', 'Zinc oxide+chamomile+alovera+olea oil+titanium dioxide+liquorice', 'Marketova pharma health care', 'Cream', 'كريم', '50gm', '1', NULL, NULL, NULL, NULL, 768, '2025-08-08', 'Zinc can be used for the treatment and prevention of zinc deficiency/its consequences, including stunted growth and acute diarrhea in children, and slowed wound healing. It is also utilized for boosting the immune system, treating the common cold and recurrent ear infections, as well as preventing lower respiratory tract infections [L2172].  



', '**Zinc has three primary biological roles**: _catalytic_, _structural_, and _regulatory_. The catalytic and structural role of zinc is well established, and there are various noteworthy reviews on these functions. For example, zinc is a structural constituent in numerous proteins, inclusive of growth factors, cytokines, receptors, enzymes, and transcription factors for different cellular signaling pathways. It is implicated in numerous cellular processes as a cofactor for approximately 3000 human proteins including enzymes, nuclear factors, and hormones [L2096].



Zinc promotes resistance to epithelial apoptosis through cell protection (cytoprotection) against reactive oxygen species and bacterial toxins, likely through the antioxidant activity of the cysteine-rich metallothioneins [A32419].



In HL-60 cells (promyelocytic leukemia cell line), zinc enhances the up-regulation of A20 mRNA, which, via TRAF pathway, decreases NF-kappaB activation, leading to decreased gene expression and generation of tumor necrosis factor-alpha (TNF-alpha), IL-1beta, and IL-8 [A32418].



There are several mechanisms of action of zinc on acute diarrhea. Various mechanisms are specific to the gastrointestinal system: zinc restores mucosal barrier integrity and enterocyte brush-border enzyme activity, it promotes the production of antibodies and circulating lymphocytes against intestinal pathogens, and has a direct effect on ion channels, acting as a potassium channel blocker of adenosine 3-5-cyclic monophosphate-mediated chlorine secretion. Cochrane researchers examined the evidence available up to 30 September 2016 [L2106].



Zinc deficiency in humans decreases the activity of serum _thymulin_ (a hormone of the thymus), which is necessary for the maturation of T-helper cells. T-helper 1 (Th(1)) cytokines are decreased but T-helper 2 (Th(2)) cytokines are not affected by zinc deficiency in humans [A32417].



The change of _Th(1)_ to _Th(2)_ function leads to cell-mediated immune dysfunction. Because IL-2 production (Th(1) cytokine) is decreased, this causes decreased activity of natural-killer-cell (NK cell) and T cytolytic cells, normally involved in killing viruses, bacteria, and malignant cells [A32424]. 



In humans, zinc deficiency may lead to the generation of new CD4+ T cells, produced in the thymus. In cell culture studies (HUT-78, a Th(0) human malignant lymphoblastoid cell line), as a result of zinc deficiency, nuclear factor-kappaB (NF-kappaB) activation, phosphorylation of IkappaB, and binding of NF-kappaB to DNA are decreased and this results in decreased Th(1) cytokine production [A32417].



In another study, zinc supplementation in human subjects suppressed the gene expression and production of pro-inflammatory cytokines and decreased oxidative stress markers [A32424]. In HL-60 cells (a human pro-myelocytic leukemia cell line), zinc deficiency increased the levels of TNF-alpha, IL-1beta, and IL-8 cytokines and mRNA. In such cells, zinc was found to induce A20, a zinc finger protein that inhibited NF-kappaB activation by the tumor necrosis factor receptor-associated factor pathway. This process decreased gene expression of pro-inflammatory cytokines and oxidative stress markers [A32417].



The exact mechanism of zinc in acne treatment is poorly understood. However, zinc is considered to act directly on microbial inflammatory equilibrium and facilitate antibiotic absorption when used in combination with other agents. Topical zinc alone as well as in combination with other agents may be efficacious because of its anti-inflammatory activity and ability to reduce P. acnes bacteria by the inhibition of P. acnes lipases and free fatty acid levels [L2102].', 'Zinc is involved in various aspects of cellular metabolism. It has been estimated that approximately 10% of human proteins may bind zinc, in addition to hundreds of proteins that transport and traffic zinc. It is required for the catalytic activity of more than 200 enzymes, and it plays a role in immune function wound healing, protein synthesis, DNA synthesis, and cell division. Zinc is an essential element for a proper sense of taste and smell and supports normal growth and development during pregnancy, childhood, and adolescence. It is thought to have antioxidant properties, which may be protective against accelerated aging and helps to speed up the healing process after an injury; however, studies differ as to its effectiveness. Zinc ions are effective antimicrobial agents even if administered in low concentrations [L2211].



Studies on oral zinc for specific conditions shows the following evidence in various conditions [L2095]:



**Colds:** Evidence suggests that if zinc lozenges or syrup are taken within 24 hours after cold symptoms start, the supplement may shorten the length of colds. The use intranasal zinc has been associated with the loss of the sense of smell, in some cases long-term or permanently [L2095].



**Wound healing:** Patients with skin ulcers and decreased levels of zinc may benefit from oral zinc supplements [L2095]. 



**Diahrrea**: Oral zinc supplements can reduce the symptoms of diarrhea in children with low levels of zinc, especially in cases of malnutrition [L2095].

', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24299, 'Orphamol 20 tab.', 'اورفامول 20 اقراص', '38', '5.25', 'Cns.analgesic.skeletal muscle relaxant.antimuscarinic', 'Orphenadrin+paracetamol(acetaminophen)', 'Rameda', 'Tab', 'أقراص', NULL, '2', NULL, 'about orphenadrine anticholinergic(antimuscarinic) ethanolamine h1 antagonist anti-parkinson`s agent centrally acting skeletal muscle relaxent. mechanism of action of orphenadrine the exact mechanism of action is not known. a lesion in the nigra striatum', '6223003970239', NULL, 1154, '2025-05-02', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26202, 'Rivarospire 2.5 mg 20 f.c. tab.', 'ريفاروسبير 2.5مجم 20 قرص', '66', NULL, 'Antiplatlet.direct factor xa inhibitor', 'Rivaroxaban', 'Sedico > inspire pharmaceutical company', 'Tab', 'أقراص', '2.5 mg', '2', 'مضاد للتجلط', NULL, '6224000866907', NULL, 6044, '2023-02-03', 'Rivaroxaban is indicated for the prevention of venous thromboembolic events (VTE) in patients who have undergone total hips replacements and total knee replacement surgery; prevention of stroke and systemic embolism in patients with nonvalvular atrial fibrillation; treatment of deep vein thrombosis (DVT) and pulmonary embolism (PE); to reduce risk of recurrent DVT and/or PE. Rivaroxaban is also indicated, in combination with aspirin, for reducing the risk of major cardiovascular events in patients with chronic coronary artery disease or peripheral artery disease. Its use is also not recommended in those with severe renal impairment (<30mL/min).[L12819]



Rivaroxaban is also indicated for the treatment and prevention of VTE in pediatric patients (from birth to 18 years of age) and for thromboprophylaxis in pediatric patients ≥2 years old with congenital heart disease following the Fontan procedure.[L12819]

', 'Rivaroxaban competitively inhibits free and clot bound factor Xa. Factor Xa is needed to activate prothrombin (factor II) to thrombin (factor IIa). Thrombin is a serine protease that is required to activate fibrinogen to fibrin, which is the loose meshwork that completes the clotting process. Since one molecule of factor Xa can generate more than 1000 molecules of thrombin, selective inhibitors of factor Xa are profoundly useful in terminating the amplification of thrombin generation. The action of rivaroxaban is irreversible.

 

', 'Rivaroxaban is an anticoagulant which binds directly to factor Xa. Thereafter, it effectively blocks the amplification of the coagulation cascade, preventing the formation of thrombus. Rivaroxaban is a unqiue anticoagulant for two reasons. First of all, it is does not involve antithrombin III (ATIII) to exert its anticoagulant effects. Secondly, it is an oral agent whereas the widely used unfractionated heparin and low molecular weight heparins are for parenteral use only. Although the activated partial thromboplastin time (aPTT) and HepTest (a test developed to assay low molecular weight heparins) are prolonged in a dose-dependant manner, neither test is recommended for the assessment of the pharmacodynamic effects of rivaroxaban. Anti-Xa activity and inhibition of anti-Xa activity monitoring is also not recommended despite being influenced by rivaroxaban.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5646, 'Terbinasil 250 mg 7 tab.', 'تيربيناسيل 250مجم 7 اقراص', '68', NULL, 'Antifungals.allylamines', 'Terbinafine', 'Jamjoom pharmaceuticals-k.s.a', 'Tab', 'أقراص', '250 mg', '1', NULL, NULL, NULL, NULL, 778, '2025-07-01', 'Terbinafine hydrochloride is indicated to treat fungal skin and nail infections caused by _Trichophyton_ species, _Microsporum canis_, _Epidermophyton floccosum_,[L9068] and _Tinea_ species.[L9065] Terbinafine hydrochloride also treats yeast infections of the skin caused by _Candida_ species and _Malassezia furfur_.[L9068]', 'Terbinafine inhibits the enzyme squalene monooxygenase (also called squalene epoxidase), preventing the conversion of squalene to 2,3-oxydosqualene, a step in the synthesis of ergosterol.[A1279,A1281,L9068] This inhibition leads to decreased ergosterol, which would normally be incorporated into the cell wall, and accumulation of squalene.[A1281,L9068]



Generation of a large number of squalene containing vesicles in the cytoplasm may leach other lipids away from, and further weaken, the cell wall.[A1281]', 'Terbinafine is an allylamine antifungal that inhibits squalene epoxidase (also known as squalene monooxygenase) to prevent the formation of ergosterol and cause an accumulation of squalene, weakening the cell wall of fungal cells.[A1279,A1281,L9068] Terbinafine distributes into tissues and has a long terminal elimination half life, so the duration of action is long.[A1279] Overdose with terbinafine is rare, even above the therapeutic dose, so the therapeutic index is wide.[L9065,L9068] Patients taking oral terbinafine should have liver function tests performed prior to treatment to reduce the risk of liver injury.[L9065]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2702, 'Claritine 10mg 20 tab.', 'كلاريتين 10مجم 20 قرص', '110', '72', 'Antihistamine.antiallergy', 'Loratadine', 'Sedico > bayer healthcare', 'Tab', 'أقراص', '10mg', '2', 'حساسية والتهاب الجيوب الأنفية.', 'about loratadine second generation h1 antagonist antihistamine. mechanism of action of loratadine loratadine blocks one type of receptor for histamine (the h1 receptor) and thus prevents activation (activation releasing other chemicals which produce the s', '6221042134025', NULL, 26247, '2024-06-25', 'Loratadine is a 2nd generation antihistamine and is used to manage symptoms of allergic rhinitis, wheal formation, urticaria, and other allergic dermatologic conditions.[A176435][A176438][L8486] ', 'Histamine release is a key mediator in allergic rhinitis and urticaria.[A185312][A176441][A175060] As a result, loratadine exerts it''s effect by targeting H1 histamine receptors.



Loratadine binds to H1 histamine receptors found on the surface of epithelial cells, endothelial cells, eosinophils, neutrophils, airway cells, and vascular smooth muscle cells among others.[A176441] H1 histamine receptors fall under the wider umbrella of G-protein coupled receptors, and exist in a state of equilibrium between the active and inactive forms.[A176441][A175060] Histamine binding to the H1-receptor facilitates cross linking between transmembrane domains III and V, stabilizing the active form of the receptor.[A176441][A175060] On the other hand, antihistamines bind to a different site on the H1 receptor favouring the inactive form.[A176441][A175060]



Hence, loratadine can more accurately be classified as an "inverse agonist" as opposed to a "histamine antagonist", and can prevent or reduce the severity of histamine mediated symptoms.[A176441][A175060][L8486]    ', 'Like other 2nd generation antihistamines, loratadine is selective for peripheral H1 receptors.[A5506] Loratadine does not penetrate effectively into the central nervous system and has poor affinity for CNS H1-receptors.[A5506] These qualities result in a lack of CNS depressant effects such as drowsiness, sedation, and impaired psychomotor function.[A5506]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28955, 'Vamid 10mg 10 tab.', 'فاميد 10مجم 10 اقراص', '40', NULL, 'Immunosuppressive agent.antirheumatic antiviral agent.pyrimidine synthesis inhibitor', 'Leflunomide', 'Hikma pharma', 'Tab', 'أقراص', '10mg', '1', NULL, NULL, NULL, NULL, 853, '2022-09-10', 'For the management of the signs and symptoms of active rheumatoid arthritis (RA) to improve physical function and to slow the progression of structural damage associated with the disease. Has also been used for the prevention of acute and chronic rejection in recipients of solid organ trasnplants and is designated by the FDA as an orphan drug for this use.', 'Leflunomide is a prodrug that is rapidly and almost completely metabolized following oral administration to its pharmacologically active metabolite, A77 1726. This metabolite is responsible for essentially all of the drug''s activity in-vivo. The mechanism of action of leflunomide has not been fully determined, but appears to primarily involve regulation of autoimmune lymphocytes. It has been suggested that leflunomide exerts its immunomodulating effects by preventing the expansion of activated autoimmune lymphocytes via interferences with cell cycle progression. In-vitro data indicates that leflunomide interferes with cell cycle progression by inhibiting dihydroorotate dehydrogenase (a mitochondrial enzyme involved in de novo pyrimidine ribonucleotide uridine monophosphate (rUMP)synthesis) and has antiproliferative activity. Human dihydroorotate dehydrogenase consists of 2 domains: an α/β-barrel domain containing the active site and an α-helical domain that forms a tunnel leading to the active site. A77 1726 binds to the hydrophobic tunnel at a site near the flavin mononucleotide. Inhibition of dihydroorotate dehydrogenase by A77 1726 prevents production of rUMP by the de novo pathway; such inhibition leads to decreased rUMP levels, decreased DNA and RNA synthesis, inhibition of cell proliferation, and G1 cell cycle arrest. It is through this action that leflunomide inhibits autoimmune T-cell proliferation and production of autoantibodies by B cells. Since salvage pathways are expected to sustain cells arrested in the G1 phase, the activity of leflunomide is cytostatic rather than cytotoxic. Other effects that result from reduced rUMP levels include interference with adhesion of activated lymphocytes to the synovial vascular endothelial cells, and increased synthesis of immunosuppressive cytokines such as transforming growth factor-β (TGF-β). Leflunomide is also a tyrosine kinase inhibitor. Tyrosine kinases activate signalling pathways leading to DNA repair, apoptosis and cell proliferation. Inhibition of tyrosine kinases can help to treating cancer by preventing repair of tumor cells. ', 'Leflunomide is a pyrimidine synthesis inhibitor indicated in adults for the treatment of active rheumatoid arthritis (RA). RA is an auto-immune disease characterized by high T-cell activity. T cells have two pathways to synthesize pyrimidines: the salvage pathways and the de novo synthesis. At rest, T lymphocytes meet their metabolic requirements by the salvage pathway. Activated lymphocytes need to expand their pyrimidine pool 7- to 8-fold, while the purine pool is expanded only 2- to 3-fold. To meet the need for more pyrimidines, activated T cells use the de novo pathway for pyrimidine synthesis. Therefore, activated T cells, which are dependent on de novo pyrimidine synthesis, will be more affected by leflunomide''s inhibition of dihydroorotate dehydrogenase than other cell types that use the salvage pathway of pyrimidine synthesis.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26410, 'Rytmonorm 70mg/20ml 5 amp.', 'ريتمونورم 70مجم/20مل 5 امبول', '80', NULL, 'Antiarrhythmias.class ic', 'Propafenone', 'Abbott laboratories > al kamal importing & marketing co', 'Amp', 'أمبول', '70mg', '5', NULL, 'about propafenone membrane stabilizing agent antiarrhythmic(class ic). indications for propafenone arrhythmia typical dosage for propafenone adults: 150mg 3 times daily and if sufficient response is not obtained dose can be increased to 225mg 3 times dail', '8002660037640', NULL, 745, '2022-08-01', 'Used to prolong the time to recurrence of paroxysmal atrial fibrillation/flutter (PAF) associated with disabling symptoms in patients without structural heart disease. Also used for the treatment of life-threatening documented ventricular arrhythmias, such as sustained ventricular tachycardia.', 'The electrophysiological effect of propafenone manifests itself in a reduction of upstroke velocity (Phase 0) of the monophasic action potential. In Purkinje fibers, and to a lesser extent myocardial fibers, propafenone reduces the fast inward current carried by sodium ions, which is responsible for the drugs antiarrhythmic actions. Diastolic excitability threshold is increased and effective refractory period prolonged. Propafenone reduces spontaneous automaticity and depresses triggered activity. At very high concentrations in vitro, propafenone can inhibit the slow inward current carried by calcium but this calcium antagonist effect probably does not contribute to antiarrhythmic efficacy.', 'Propafenone is a Class 1C antiarrhythmic drug with local anesthetic effects, and a direct stabilizing action on myocardial membranes. It is used in the treatment of atrial and ventricular arrhythmias. It acts by inhibiting sodium channels to restrict the entry of sodium into cardiac cells resulting in reduced excitation. Propafenone has local anesthetic activity approximately equal to procaine.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19092, 'Flamigel hydroactive colloid gel 50 gm', 'فلاميجل جل 50 جم', '135', NULL, 'Healing topical', 'Acid colloidal hydrocolloid+arginin+macrogol+branch chained fatty acid+methyl&propyl p-hydroxybenzoa', 'Flen pharma > eimc', 'Gel', 'جل', '50 gm', '1', NULL, 'wound treatment .... for: superficial burns and minor wounds (e.g. grazes cuts)', NULL, NULL, 1243, '2022-07-12', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25027, 'Polar frost cold roll-on 75 ml', 'بولار فروست كولد رول 75مل', '125', NULL, 'Analgesic', 'Menthol', 'Niva medical > al kamal importing & marketing co', 'Unknown', 'غير محدد', '75 ml', '1', NULL, 'polar frost cold gel can be used by muscular disorders and injuries like: strains sprains and ruptures stressed muscles and tension chronic neck- shoulder and low back pains and aches work and occupation related muscular disorders cramps and spasms bruise', NULL, NULL, 793, '2022-07-18', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4918, 'Flamotal 400 mg 30 f.c.tabs.', 'فلاموتال 400مجم 30 قرص', '47', NULL, 'Nsaid.propionic acid derivatives', 'Ibuprofen', 'Global napi pharmaceuticals > future pharmaceutical industries (fpi)', 'Tab', 'أقراص', '400 mg', '3', 'مسكن ومضاد للإلتهابات -- خفض الحرارة -- الصداع', NULL, '6223004190001', NULL, 2026, '2022-05-14', 'Ibuprofen is the most commonly used and prescribed NSAID. It is a very common over-the-counter medication widely used as an analgesic, anti-inflammatory and antipyretic.[A39096]



The use of ibuprofen and its enantiomer [DB09213] in a racemic mix is common for the management of mild to moderate pain related to dysmenorrhea, headache, migraine, postoperative dental pain, spondylitis, osteoarthritis, rheumatoid arthritis, and soft tissue disorder.[A39097]



Due to its activity against prostaglandin and thromboxane synthesis, ibuprofen has been attributed to alteration of platelet function and prolongation of gestation and labour.[A39092]



As ibuprofen is a widely used medication, the main therapeutic indications are:



* Patent Ductus Arteriosus - it is a neonatal condition wherein the ductus arteriosus (blood vessel that connects the main pulmonary artery to the proximal descending aorta) fails to close after birth causing severe risk of heart failure. The prostaglandin inhibition of ibuprofen has been studied for the treatment of this condition as it is known that prostaglandin E2 is responsible for keeping the ductus arteriosus open.[A39100]



* Rheumatoid- and osteo-arthritis - ibuprofen is very commonly used in the symptomatic treatment of inflammatory, musculoskeletal and rheumatic disorders.[A39176]



* Cystic fibrosis - the use of high dosages of ibuprofen has been proven to decrease inflammation and decreasing polymorphonuclear cell influx in the lungs.[A39177]



* Orthostatic hypotension - ibuprofen can induce sodium retention and antagonize the effect of diuretics which has been reported to be beneficial for patients with severe orthostatic hypotension.[A1651]



* Dental pain - ibuprofen is used to manage acute and chronic orofacial pain.[A10901]



* Pain - ibuprofen is widely used to reduce minor aches and pains as well as to reduce fever and manage dysmenorrhea. It is very commonly used for the relief of acute indications such as fever and tension headaches.[A39092] It is also used to manage mild to moderate pain and moderate to severe pain as an adjunct to opioid analgesics.[L40208]



* Investigational uses - efforts have been put into developing ibuprofen for the prophylaxis of Alzheimer''s disease, Parkinson disease, and breast cancer.[A39092]', 'The exact mechanism of action of ibuprofen is unknown. However, ibuprofen is considered an NSAID and thus it is a non-selective inhibitor of cyclooxygenase, which is an enzyme involved in prostaglandin (mediators of pain and fever) and thromboxane (stimulators of blood clotting) synthesis via the arachidonic acid pathway.[L4614]



Ibuprofen is a non-selective COX inhibitor and hence, it inhibits the activity of both COX-1 and COX-2. The inhibition of COX-2 activity decreases the synthesis of prostaglandins involved in mediating inflammation, pain, fever, and swelling while the inhibition of COX-1 is thought to cause some of the side effects of ibuprofen including GI ulceration.[A39195]', 'Ibuprofen has multiple actions in different inflammatory pathways involved in acute and chronic inflammation. The main effects reported in ibuprofen are related to the control of pain, fever and acute inflammation by the inhibition of the synthesis of prostanoids by COX-1 and COX-2. Pain relief is attributed to peripheral affected regions and central nervous system effects in the pain transmission mediated by the dorsal horn and higher spinothalamic tract. Some reports have tried to link the pain regulation with a possible enhancement on the synthesis of endogenous cannabinoids and action on the NMDA receptors. The effect on pain has been shown to be related to the cortically evoked potentials.[A39190]



The antipyretic effect is reported to be linked to the effect on the prostanoid synthesis due to the fact that the prostanoids are the main signaling mediator of pyresis in the hypothalamic-preoptic region.[A39190]



The use of ibuprofen in dental procedures is attributed to the local inhibition of prostanoid production as well as to anti-oedemic activity and an increase of plasma beta-endorphins. Some reports have suggested a rapid local reduction of the expression of COX-2 in dental pulp derived by the administration of ibuprofen.[A39190]



The administration of ibuprofen in patients with rheumatic diseases has shown to control joint symptoms.[A39092] 



Ibuprofen is largely used in OTC products such as an agent for the management of dysmenorrhea which has been proven to reduce the amount of menstrual prostanoids and to produce a reduction in the uterine hypercontractility.[A39181] As well, it has been reported to reduce significantly the fever and the pain caused by migraines.[A39182, A39183] This effect is thought to be related to the effect on platelet activation and thromboxane A2 production which produces local vascular effects in the affected regions. This effect is viable as ibuprofen can enter in the central nervous system.[A39190]



In the investigational uses of ibuprofen, it has been reported to reduce neurodegeneration when given in low doses over a long time.[A39184] On the other hand, its use in Parkinson disease is related to the importance of inflammation and oxidative stress in the pathology of this condition.[A39185] The use of ibuprofen for breast cancer is related to a study that shows a decrease of 50% in the rate of breast cancer.[A39186]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23943, 'Octotramadol 50mg 12caps.', 'اوكتوترامادول 50مجم 12 كبسولة', '13', NULL, 'Analgesics.narcotic opioid', 'Tramadol', 'October pharma', 'Cap', 'كبسولة', '50mg', '1', NULL, NULL, NULL, NULL, 967, '2022-07-23', 'Tramadol is approved for the management of moderate to severe pain in adults.[L9257,F4679]



Tramadol is also used off-label in the treatment of premature ejaculation.[A173986]', 'Tramadol is a centrally acting μ-opioid receptor agonist and SNRI (serotonin/norepinephrine reuptake-inhibitor) that is structurally related to [codeine] and [morphine]. Tramadol binds weakly to κ- and δ-opioid receptors and to the μ-opioid receptor with 6000-fold less affinity than morphine.[A182300]



Tramadol exists as a racemic mixture consisting of two pharmacologically active enantiomers that both contribute to its analgesic property through different mechanisms: (+)-tramadol and its primary metabolite (+)-O-desmethyl-tramadol (M1) are agonists of the μ opioid receptor while (+)-tramadol inhibits serotonin reuptake and (-)-tramadol inhibits norepinephrine reuptake. These pathways are complementary and synergistic, improving tramadol''s ability to modulate the perception of and response to pain.[A4269] 



In animal models, M1 is up to 6 times more potent than tramadol in producing analgesia and 200 times more potent in μ-opioid binding.[L9257] 



Tramadol has also been shown to affect a number of pain modulators including alpha2-adrenoreceptors, neurokinin 1 receptors, the voltage-gated sodium channel type II alpha subunit[A183728], transient receptor potential cation channel subfamily V member 1 (TRPV1 - also known as the capsaicin receptor)[A183734], muscarinic receptors (M1 and M3), N-methyl-D-aspartate receptor (also known as the NMDA receptor or glutamate receptor)[A17158], Adenosine A1 receptors[A183761], and nicotinic acetylcholine receptor.[A17159] 



In addition to the above neuronal targets, tramadol has a number of effects on inflammatory and immune mediators involved in the pain response. This includes inhibitory effects on cytokines, prostaglandin E2 (PGE2), nuclear factor-κB, and glial cells as well as a change in the polarization state of M1 macrophages.[A182300]', 'Tramadol modulates the descending pain pathways within the central nervous system through the binding of parent and M1 metabolite to μ-opioid receptors and the weak inhibition of the reuptake of norepinephrine and serotonin.[A173983,A173980] 



Apart from analgesia, tramadol may produce a constellation of symptoms (including dizziness, somnolence, nausea, constipation, sweating and pruritus) similar to that of other opioids. 



**Central Nervous System**



In contrast to [morphine], tramadol has not been shown to cause histamine release. At therapeutic doses, tramadol has no effect on heart rate, left-ventricular function or cardiac index. Orthostatic hypotension has been observed.[L9257]



Tramadol produces respiratory depression by direct action on brain stem respiratory centres. The respiratory depression involves both a reduction in the responsiveness of the brain stem centres to increases in CO2 tension and to electrical stimulation. 



Tramadol depresses the cough reflex by a direct effect on the cough centre in the medulla. Antitussive effects may occur with doses lower than those usually required for analgesia.



Tramadol causes miosis, even in total darkness. Pinpoint pupils are a sign of opioid overdose but

are not pathognomonic (e.g., pontine lesions of hemorrhagic or ischemic origin may produce

similar findings). Marked mydriasis rather than miosis may be seen with hypoxia in the setting of

oxycodone overdose.[F4679]



Seizures have been reported in patients receiving tramadol within the recommended dosage range. Spontaneous post-marketing reports indicate that seizure risk is increased with doses of tramadol above the recommended range. Risk of convulsions may also increase in patients with epilepsy, those with a history of seizures

or in patients with a recognized risk for seizure (such as head trauma, metabolic disorders,

alcohol and drug withdrawal, CNS infections), or with concomitant use of other drugs known to reduce the seizure threshold.[F4679]



Tramadol can cause a rare but potentially life-threatening condition resulting from concomitant administration of serotonergic drugs (e.g., anti-depressants, migraine medications). Treatment with the serotoninergic drug should be discontinued if such events (characterized by clusters of symptoms such as hyperthermia, rigidity, myoclonus, autonomic instability with possible rapid fluctuations of vital signs, mental status changes including confusion, irritability, extreme agitation progressing to delirium and coma) occur and supportive symptomatic treatment should be initiated. Tramadol should not be used in combination with MAO inhibitors or serotonin-precursors (such as L-tryptophan, oxitriptan) and should be used with caution in combination with other serotonergic drugs (triptans, certain tricyclic antidepressants, lithium, St. John’s Wort) due to the risk of serotonin syndrome.[F4679]



**Gastrointestinal Tract and Other Smooth Muscle**



Tramadol causes a reduction in motility associated with an increase in smooth muscle tone in the antrum of the stomach and duodenum. Digestion of food in the small intestine is delayed and propulsive contractions are decreased. Propulsive peristaltic waves in the colon are decreased, while tone may be increased to the point of spasm resulting in constipation. Other opioid-induced effects may include a reduction in gastric, biliary and pancreatic secretions, spasm of the sphincter of Oddi, and transient elevations in serum amylase.[F4679]



**Endocrine System**



Opioids may influence the hypothalamic-pituitary-adrenal or -gonadal axes. Some changes that can be seen include an increase in serum prolactin and decreases in plasma cortisol and testosterone. Clinical signs and symptoms may be manifest from these hormonal changes.[F4679]



Hyponatremia has been reported very rarely with the use of tramadol, usually in patients with predisposing risk factors, such as elderly patients and/or patients using concomitant medications that may cause hyponatremia (e.g., antidepressants, benzodiazepines, diuretics). In some reports, hyponatremia appeared to be the result of the syndrome of inappropriate antidiuretic hormone secretion (SIADH) and resolved with discontinuation of tramadol and appropriate treatment (e.g., fluid restriction). During tramadol treatment, monitoring for signs and symptoms of hyponatremia is recommended for patients with predisposing risk factors.[F4679]



**Cardiovascular**



Tramadol administration may result in severe hypotension in patients whose ability to maintain adequate blood pressure is compromised by reduced blood volume, or concurrent administration of drugs such as phenothiazines and other tranquillizers, sedative/hypnotics, tricyclic antidepressants or general anesthetics. These patients should be monitored for signs of hypotension after initiating or titrating the dose of tramadol.[F4679]



**QTc-Interval Prolongation**



The maximum placebo-adjusted mean change from baseline in the QTcF interval was 5.5 ms in the 400 mg/day treatment arm and 6.5 ms in the 600 mg/day mg treatment arm, both occurring at the 8h time point. Both treatment groups were within the 10 ms threshold for QT prolongation. Post-marketing experience with the use of tramadol containing products included rare reports of QT prolongation reported with an overdose. Particular care should be exercised when administering tramadol to patients who are suspected to be at an increased risk of experiencing torsade de pointes during treatment with a QTc-prolonging drug.[F4679]



**Abuse and Misuse**



Like all opioids, tramadol has the potential for abuse and misuse, which can lead to overdose and death. Therefore, tramadol should be prescribed and handled with caution.[F4679]



**Dependence/Tolerance**



Physical dependence and tolerance reflect the neuroadaptation of the opioid receptors to chronic exposure to an opioid and are separate and distinct from abuse and addiction. Tolerance, as well as physical dependence, may develop upon repeated administration of opioids, and are not by themselves evidence of an addictive disorder or abuse. Patients on prolonged therapy should be tapered gradually from the drug if it is no longer required for pain control. Withdrawal symptoms may occur following abrupt discontinuation of therapy or upon administration of an opioid antagonist. Some of the symptoms that may be associated with abrupt withdrawal of an opioid analgesic include body aches, diarrhea, gooseflesh, loss of appetite, nausea, nervousness or restlessness, anxiety, runny nose, sneezing, tremors or shivering, stomach cramps, tachycardia, trouble with sleeping, unusual increase in sweating, palpitations, unexplained fever, weakness and yawning.[F4679]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5576, 'Globacand 16mg 28 f.c. tab.', 'جلوباكاند 16مجم 28 قرص', '40', NULL, 'Antihypertensive.arbs', 'Candesartan cilexetil', 'Global napi pharmaceuticals', 'Tab', 'أقراص', '16mg', '2', NULL, 'about candesartan cilexetil angiotensin || antagonist antihypertensive agent mechanism of action of candesartan cilexetil it is a antagonist of angiotensin 2 at at-1 receptor.it decreases peripheral resistance and lowers bp in hypertensive individuals. it', '6223002140770', NULL, 967, '2022-12-30', 'May be used as a first line agent to treat uncomplicated hypertension, isolated systolic hypertension and left ventricular hypertrophy. May be used as a first line agent to delay progression of diabetic nephropathy. Candesartan may be also used as a second line agent in the treatment of congestive heart failure, systolic dysfunction, myocardial infarction and coronary artery disease in those intolerant of ACE inhibitors.', 'Candesartan selectively blocks the binding of angiotensin II to AT1 in many tissues including vascular smooth muscle and the adrenal glands. This inhibits the AT1-mediated vasoconstrictive and aldosterone-secreting effects of angiotensin II and results in an overall decrease in blood pressure. Candesartan is greater than 10,000 times more selective for AT1 than AT2. Inhibition of aldosterone secretion may increase sodium and water excretion while decreasing potassium excretion.', 'Candesartan cilexetil is an ARB prodrug that is rapidly converted to candesartan, its active metabolite, during absorption from the gastrointestinal tract. Candesartan confers blood pressure lowering effects by antagonizing the hypertensive effects of angiotensin II via the RAAS. RAAS is a homeostatic mechanism for regulating hemodynamics, water and electrolyte balance. During sympathetic stimulation or when renal blood pressure or blood flow is reduced, renin is released from granular cells of the juxtaglomerular apparatus in the kidneys. Renin cleaves circulating angiotensinogen to angiotensin I, which is cleaved by angiotensin converting enzyme (ACE) to angiotensin II. Angiotensin II increases blood pressure by increasing total peripheral resistance, increasing sodium and water reabsorption in the kidneys via aldosterone secretion, and altering cardiovascular structure. Angiotensin II binds to two receptors: type-1 angiotensin II receptor (AT1) and type-2 angiotensin II receptor (AT2). AT1 is a G-protein coupled receptor (GPCR) that mediates the vasoconstrictive and aldosterone-secreting effects of angiotensin II. Studies performed in recent years suggest that AT2 antagonizes AT1-mediated effects and directly affects long-term blood pressure control by inducing vasorelaxation and increasing urinary sodium excretion. Angiotensin receptor blockers (ARBs) are non-peptide competitive inhibitors of AT1. ARBs block the ability of angiotensin II to stimulate pressor and cell proliferative effects. Unlike ACE inhibitors, ARBs do not affect bradykinin-induced vasodilation. The overall effect of ARBs is a decrease in blood pressure.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21841, 'Lincocin 600mg/2ml i.m./i.v. 3 amp', 'لينكوسين 600مجم /2مل عضل/وريد 3 امبول', '24', NULL, 'Antibiotic.macrolide', 'Lincomycin', 'Memphis > pfizer', 'Amp', 'أمبول', '600mg', '3', NULL, 'about lincomycin it is a macrolide. mechanism of action of lincomycin it binds to the 50s sub unit of bacterial ribosome and inhibits translocation.ie: they interfere with the transfer of the newly formed peptide chain from the a site to the p site and fa', '6221050010496', NULL, 806, '2022-07-07', 'Lincomycin is indicated for the treatment of serious bacterial infections by susceptible strains of streptococci, pneumococci, and staphylococci in patients who are allergic to penicillins or for situations in which a penicillin is deemed inappropriate. As with all antibacterial agents, lincomycin should only be used to treat infections proven or strongly suspected to be caused by susceptible bacteria.[L33214]', 'Lincomycin contains the unusual amino acid moiety propyl hygric acid linked to the sugar moiety α-methylthiolincosamine (α-MTL) that, like other lincosamides, functions as a structural analogue of the 3'' end of L-Pro-Met-tRNA and deacylated-tRNA to interact with the 23S rRNA of the 50S bacterial ribosomal subunit.[A190657, A190621] Detailed investigations into the mechanism of the related lincosamide [clindamycin] suggested a two-phase binding, instantaneously to the A-site with a shift in equilibrium towards the P-site over several seconds.[A233794, A233799, A233804] This shift appears to be due to rotation of the propyl hygric acid moiety, while the α-MTL remains relatively stationary.[A233804, A233819] Recent crystal structures of lincomycin in complex with the 50S ribosomal subunit of _Staphylococcus aureus_ show that the α-MTL moiety forms hydrogen bonds with C2611, A2058, G2505, A2059, and G2503 of the 23S rRNA while the propyl hygric acid moiety interacts only through van der Waals contacts, suggesting it may be free to rotate similar to [clindamycin].[A233819] This mechanism is supported by the observation that the most common resistance mechanism, which also affects macrolides and streptogramin B (MSLB resistance) involves methylation of A2058; other resistance mechanisms similarly target residues such as A2058, A2059, and C2611.[A190621, A233819]', 'Lincomycin is a lincosamide antibiotic derived as a natural fermentation product from _Streptomyces lincolnensis_. Like [clindamycin], lincomycin is active against Gram-positive cocci and bacilli as well as Gram-negative cocci and some other organisms such as _Haemophilus_ spp. It is also effective against anaerobic bacteria, though in this regard [clindamycin] is generally more potent.[A190657, A190621] Prescribing information highlights that the range of clinically confirmed effectiveness is largely limited to _Staphylococcus_ spp. and _Streptococcus_ spp., with additional activity noted _in vitro_.[L33214]



Lincomycin should be used with caution due to its association with severe cutaneous hypersensitivity reactions such as Stevens-Johnson syndrome, toxic epidermal necrolysis, erythema multiforme, and acute generalized exanthematous pustulosis, and its potential to precipitate _Clostridium difficile_ associated diarrhea (CDAD), which may lead to fatal colitis. Special care should therefore be exercised when used in elderly patients, individuals with a history of gastrointestinal disease, and those with a history of asthma or significant allergies. Lincomycin for injection may contain benzyl alcohol as a preservative, which has been associated with gasping syndrome in pediatric patients. The serum half-life is extended in patients with hepatic/renal impairment and may require dose adjustments and additional monitoring. Like all antibiotics, lincomycin use may cause overgrowth of non-susceptible organisms, which should be considered.[L33214]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19241, 'Flustat 20 tab.', 'فلوستات 20 قرص', '34', '15', 'Cold drugs', 'Chlorpheniramine+paracetamol(acetaminophen)+pseudoephedrine', 'Hi-pharm', 'Tab', 'أقراص', NULL, '1', NULL, NULL, '6224001050176', NULL, 1981, '2025-03-12', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (32175, 'Z-gest 20 caps', 'زي-جيست 20 كبسولة', '100', '70', 'Digestive enzymes', 'Papain+bromelain', 'Organix > pivot', 'Cap', 'كبسولة', NULL, '2', 'مكمل غذائى يساعد على تخفيف عسر الهضم والمغص والغازات والانتفاخات', NULL, NULL, NULL, 5755, '2024-08-22', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22135, 'Lutone 25mg/ml i.m. 3 amp.', 'لوتون 25 مجم / 1 مل 3 امبول', '30', '19.5', 'Progesterone supplement', 'Progesterone', 'Misr', 'Amp', 'أمبول', '25mg', '3', 'علاج هرموني', 'indication for progesterone supplementation or replacement as part of an assisted reproductive technology (art) treatment for infertile women with progesterone deficiency and for the treatment of secondary amenorrhea. also used for the reduction of the in', '6221035016116', NULL, 1382, '2024-10-29', '**Gelatinized capsules**



The gelatinized capsules are indicated for use in the prevention of endometrial hyperplasia in non-hysterectomized postmenopausal women who are receiving conjugated estrogens tablets. They are also indicated for use in secondary amenorrhea [FDA label]. 



**Vaginal gel**



Progesterone gel (8%) is indicated as progesterone supplementation or replacement as part of an Assisted Reproductive Technology (“ART”) treatment for infertile women with progesterone deficiency.  The lower concentration progesterone gel (4%) is used in the treatment of secondary amenorrhea, with the use of the 8% concentration if there is no therapeutic response to the 4% gel [F3898].



**Vaginal insert**



This form is indicated to support embryo implantation and early pregnancy by supplementation of corpus luteal function as part of an Assisted Reproductive Technology (ART) treatment program for infertile women [F3901].



**Injection (intramuscular)**



This drug is indicated in amenorrhea and abnormal uterine bleeding due to hormonal imbalance in the absence of organic pathology, such as submucous fibroids or uterine cancer [F3907]. 



**Tablets, contraceptive**



The tablet form of progesterone in contraceptive formulations is indicated for the prevention of pregnancy [F3904]. ', 'Progesterone binds and activates its nuclear receptor, _PR_, which plays an important part in the signaling of stimuli that maintain the endometrium during its preparation for pregnancy.



Progesterone receptor (PR) is a member of the nuclear/steroid hormone receptor (SHR) family of ligand-dependent transcription factors that is expressed primarily in female reproductive tissue as well as the central nervous system. As a result of its binding its associated steroid hormone, progesterone, the progesterone receptor (PR) modulates the expression of genes that regulate the development, differentiation, and proliferation of target tissues [A175666]. In humans, PR is found to be highly expressed in the stromal (connective tissue) cells during the secretory phase and during pregnancy [A175651]. 



Progesterone may prevent pregnancy by changing the consistency of cervical mucus to be unfavorable for sperm penetration, and by inhibiting follicle-stimulating hormone (FSH), which normally causes ovulation.  With perfect use, the first-year failure rate for progestin-only oral contraceptives is approximately 0.5%. The typical failure rate, however, is estimated to be approximately 5%, due to late or missed pills [F3904].', 'Progesterone, depending on concentration and dosage form, and timing of exposure may have several pharmacodynamic effects.  These actions, according, to various preparations, are listed below:



General effects



Progesterone is the main hormone of the corpus luteum and the placenta. It acts on the uterus by changing the proliferative phase to the secretory phase of the endometrium (inner mucous lining of the uterus).   This hormone, stimulated by a hormone called _luteinizing hormone_ (LH) is the main hormone during the secretory phase to prepare the corpus luteum and the endometrium for implantation of a fertilized ovum. As the luteal phase concludes, the progesterone hormone sends negative feedback to the anterior pituitary gland in the brain to decrease FSH (follicle stimulating hormone) and LH (luteinizing hormone) levels. This prevents ovulation and maturation of oocytes (immature egg cells). The endometrium then prepares for pregnancy by increasing its vascularity (blood vessels) and stimulating mucous secretion. This process occurs by progesterone stimulating the endometrium to decrease endometrial proliferation, leading to a decreased uterine lining thickness, developing more complex uterine glands, collecting energy in the form of glycogen, and providing more uterine blood vessel surface area suitable for supporting a growing embryo. As opposed to cervical mucous changes observed during the proliferative phase and ovulation, progesterone decreases and thickens the cervical mucus, rendering it less elastic. This change occurs because the fertilization time period has passed, and a specific consistency of mucous amenable to sperm entry is no longer required [T490].



**Gelatinized capsules**



Progesterone capsules are an oral dosage form of micronized progesterone which, chemically identical to progesterone of ovarian origin. Progesterone capsules have all the properties of endogenous progesterone with induction of a secretory phase endometrium with gestagenic, antiestrogenic, slightly antiandrogenic and anti-aldosterone effects [F3913]. Progesterone opposes the effects of estrogen on the uterus, and is beneficial in women with unopposed estrogen exposure, which carries an increased risk of malignancy [F3913].





**Vaginal gel and vaginal insert**



 The gel preparation mimics the effects of naturally occurring progesterone. In the presence of adequate levels of estrogen, progesterone converts a proliferative endometrium into secretory endometrium. This means that the endometrium changes from a growing and thickening stage into a subsequent preparation stage for pregnancy, which involves further preparatory changes. Progesterone is necessary for the development of decidual tissue (specialized tissue amenable to supporting a possible pregnancy). Progesterone is required to increase endometrial receptivity for the implantation of a fertilized embryo. Once an embryo is implanted, progesterone helps to maintain the pregnancy [F3898]. 





**Injection (intramuscular)**



 Intramuscularly injected progesterone increases serum progesterone and aids in the prevention of endometrial tissue overgrowth due to unopposed estrogen (which leads to abnormal uterine bleeding and sometimes uterine cancer) [L5638], [F3916]. In the absence or deficiency of progesterone, the endometrium continually proliferates, eventually outgrowing its limited blood supply, shedding incompletely, and leading to abnormal and/or profuse bleeding as well as malignancy [L5638].



**Tablets, contraceptive**



Progesterone-only contraceptive tablets prevent conception by suppressing ovulation in about half of users, causing a thickening of cervical mucus to inhibit sperm movement, lowering the midcycle LH and FSH hormone peaks, slowing the movement of the ovum through the fallopian tubes, and causing secretory changes in the endometrium as described above [F3904].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4618, 'Faktu oint. 30 gm', 'فاكتو مرهم 30 جم', '76', '53', 'Haemorrhoids', 'Cinchocaine+policresulen', 'Marcyrl co. > aug pharma', 'Oint', 'مرهم', '30 gm', '1', NULL, 'about cinchocaine an amide derivative most potent and toxic long-acting local anesthetics a local anaesthetic mechanism of action of cinchocaine cinchocaine is a local anesthetic. it works by blocking the pathway of pain signals along nerves.', '6223005461056', NULL, 6320, '2024-07-28', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27597, 'Sucra sweet 50 tab sugar', 'سوكرا سويت 50 اقراص', '15', NULL, 'Sweetener', 'Sucralose+lactose+croscarmellose+l-leucine', 'Amriya > safe pharma', 'Tab', 'أقراص', NULL, '1', NULL, NULL, NULL, NULL, 703, '2022-07-18', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4940, 'Nolaver anti-scar gel 25 gm', 'نولافير للندبات انتي سكار جل 25 جرام', '260', '195', 'Anti-scar', 'Silicone+onion ext.', 'Egyptian company for cosmetics', 'Gel', 'جل', '25 gm', '1', 'جل لعلاج الندبات -- علاج الحروق واثارها -- تفتيح الجلد', NULL, '6223012630995', NULL, 1005, '2025-07-29', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29419, 'Viscera 50mg 10 tab.', 'فيسرا 50مجم 10 اقراص', '3', NULL, 'Antispasmodic', 'Tiemonium methylsulphate', 'Amoun', 'Tab', 'أقراص', '50mg', '1', NULL, 'tiemonium methylsulfate - treatment of acute intense or refractory painful syndromes with a spasmodic component. the use of noramidopyrine is only justified when its activity confers a real advantage over other analgesics. indications: smooth muscle spasm', '6221025019806', NULL, 1139, '2022-07-30', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14903, 'Bambedil 1mg/ml syrup 120ml', 'بامبيديل 1مجم/مل شراب 120 مل', '32', '22', 'Bronchodilator.long acting beta 2 agonist', 'Bambuterol', 'Western pharmaceuticals industries', 'Syrup', 'شراب', '1mg', '1', NULL, 'about bambuterol long acting beta 2-adrenoceptor agonist anti asthmatic-bronchodilators. mechanism of action of bambuterol this prodrug of the 2-agonist terbutaline undergoes oxidation and hydrolysis in the body and converted into terbutaline. this terbut', '6224007308240', NULL, 1643, '2024-12-01', 'For the prevention and reversal of bronchospasm in patients 12 years of age and older with asthma and reversible bronchospasm associated with bronchitis and emphysema.', 'The pharmacologic effects of bambuterol are at least in part attributable to stimulation through beta-adrenergic receptors (beta 2 receptors) of intracellular adenyl cyclase, the enzyme that catalyzes the conversion of adenosine triphosphate (ATP) to cyclic AMP. Increased cyclic AMP levels are associated with relaxation of bronchial smooth muscle and inhibition of release of mediators of immediate hypersensitivity from cells, especially from mast cells.', 'Bambuterol is a long acting beta2-adrenoceptor agonist used in the treatment of asthma. It is a prodrug of terbutaline. Bambuterol causes smooth muscle relaxation, resulting in dilation of bronchial passages.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6719, 'Fatroxim 200 mg 30 f.c. tab.', 'فاتروكسيم 200مجم 30 قرص', '243', NULL, 'Travelers diarrhea', 'Rifaximin', 'Atm pharmaceutical industries', 'Tab', 'أقراص', '200 mg', '3', NULL, NULL, NULL, NULL, 808, '2025-08-12', 'Rifaximin has multiple indications by the FDA: for the treatment of patients (≥12 years of age) with traveller''s diarrhea caused by noninvasive strains of Escherichia coli; for the reduction of overt hepatic encephalopathy recurrence in patients ≥18 years of age; and in May 2015 it was approved for irritable bowel syndrome with diarrhea (IBS-D) treatment in adult men and women. ', 'Rifaximin acts by inhibiting RNA synthesis in susceptible bacteria by binding to the beta-subunit of bacterial deoxyribonucleic acid (DNA)-dependent ribonucleic acid (RNA) polymerase enzyme. This binding blocks translocation, which stops transcription.', 'Rifaximin is a structural analog of rifampin and a non-systemic, gastrointestinal site-specific antibiotic. This non-systemic property of the drug is due to the addition of a pyridoimidazole ring, which renders it non-absorbable. Rifaximin acts by inhibiting bacterial ribonucleic acid (RNA) synthesis and contributes to restore intestinal microflora imbalance. Other studies have also shown rifaximin to be an pregnane X receptor (PXR) activator. As PXR is responsible for inhibiting the proinflammatory transcription factor NF-kappa B (NF-κB) and is inhibited in inflammatory bowel disease (IBD), rifaximin was proven to be effective for the treatment of IBS-D.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18556, 'Esmopump 40mg 14 f.c. tabs.', 'ايزموبمب 40 مجم 14 قرص', '172', '119', 'Peptic ulcer.proton pump inhibitor', 'Esomeprazole', 'Organopharma', 'Tab', 'أقراص', '40mg', '2', 'علاج عسر الهضم والتهاب وارتجاع المريء والحموضة المعوية والقضاء على بكتيريا هيليكوباكتر بيلوري (جرثومة المعدة) ومرض القرحة المعدية', 'about esomeprazole a proton pump inhibitor substituted benzimidazole antiulcer antisecretory drug. mechanism of action of esomeprazole esomeprazole is the s enantiomer of omeprazole and is a proton pump inhibitor. it is a prodrug. after administration it', '6224000908102', NULL, 5862, '2024-09-03', 'Esomeprazole is indicated for the treatment of acid-reflux disorders including healing and maintenance of erosive esophagitis, and symptomatic gastroesophageal reflux disease (GERD), peptic ulcer disease, H. pylori eradication, prevention of gastrointestinal bleeds with NSAID use, and for the long-term treatment of pathological hypersecretory conditions including Zollinger-Ellison Syndrome. ', 'Esomeprazole exerts its stomach acid-suppressing effects by preventing the final step in gastric acid production by covalently binding to sulfhydryl groups of cysteines found on the (H+, K+)-ATPase enzyme at the secretory surface of gastric parietal cells. This effect leads to inhibition of both basal and stimulated gastric acid secretion, irrespective of the stimulus. As the binding of esomeprazole to the (H+, K+)-ATPase enzyme is irreversible and new enzyme needs to be expressed in order to resume acid secretion, esomeprazole''s duration of antisecretory effect that persists longer than 24 hours.[FDA Label]

', 'Esomeprazole is a compound that inhibits gastric acid secretion and is indicated in the treatment of gastroesophageal reflux disease (GERD), the healing of erosive esophagitis, and <i>H. pylori</i> eradication to reduce the risk of duodenal ulcer recurrence. Esomeprazole belongs to a new class of antisecretory compounds, the substituted benzimidazoles, that do not exhibit anticholinergic or H2 histamine antagonistic properties, but that suppress gastric acid secretion by specific inhibition of the H<sup>+</sup>/K<sup>+</sup> ATPase at the secretory surface of the gastric parietal cell. By doing so, it inhibits acid secretion into the gsatric lumen. This effect is dose-related and leads to inhibition of both basal and stimulated acid secretion irrespective of the stimulus.



Esomeprazole is the s-isomer of [DB00338], which is a racemate of the S- and R-enantiomer. Esomeprazole has been shown to inhibit acid secretion to a similar extent as [DB00338], without any significant differences between the two compounds _in vitro_. 



PPIs such as esomeprazole have also been shown to inhibit the activity of dimethylarginine dimethylaminohydrolase (DDAH), an enzyme necessary for cardiovascular health. DDAH inhibition causes a consequent accumulation of the nitric oxide synthase inhibitor asymmetric dimethylarginie (ADMA), which is thought to cause the association of PPIs with increased risk of cardiovascular events in patients with unstable coronary syndromes.[A177577, A177580]



Due to their good safety profile and as several PPIs are available over the counter without a prescription, their current use in North America is widespread. Long term use of PPIs such as esomeprazole has been associated with possible adverse effects, however, including increased susceptibility to bacterial infections (including gastrointestinal _C. difficile_), reduced absorption of micronutrients including iron and B12, and an increased risk of developing hypomagnesemia and hypocalcemia which may contribute to osteoporosis and bone fractures later in life.[A177571]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30306, 'Charmalure hair spray 275 ml', 'شارمالور بخاخ سبراي للشعر 275 مل', '95', NULL, 'Hair care', 'Vitamin e+rosemary+coconut oil+olive oil+tea tree oil+peppermint oil+camphor oil+garlic oil', '> xcel pharma', 'Spray', 'بخاخ', '275 ml', '1', NULL, NULL, NULL, NULL, 1096, '2023-04-25', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19583, 'Gasmovac 5mg 20 tab. (n/a)', 'جاسموفاك 5مجم 20 قرص', '45', NULL, 'Selective 5ht4 agonist', 'Mosapride', 'Delta pharma > advocure', 'Tab', 'أقراص', '5mg', '2', NULL, NULL, '6224009460021', NULL, 1275, '2022-12-05', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (11914, 'Unistin 50mg vial for i.v. inf.', 'يونيستين 50مجم فيال وريد 50 مل', '316', '208', 'Antineoplastic', 'Cisplatin', 'Hikma specialized pharmaceuticals', 'Vial', 'فيال', '50mg', '1', 'مضاد للأورام والسرطان', 'description cisplatin cisplatinum or cis-diamminedichloroplatinum(ii) (cddp) is a platinum-based chemotherapy drug used to treat various types of cancers including sarcomas some carcinomas (e.g. small cell lung cancer and ovarian cancer) lymphomas and ger', '6223003840068', NULL, 1297, '2024-12-05', 'For the treatment of metastatic testicular tumors, metastatic ovarian tumors and advanced bladder cancer.', 'Alkylating agents work by three different mechanisms: 1) attachment of alkyl groups to DNA bases, resulting in the DNA being fragmented by repair enzymes in their attempts to replace the alkylated bases, preventing DNA synthesis and RNA transcription from the affected DNA, 2) DNA damage via the formation of cross-links (bonds between atoms in the DNA) which prevents DNA from being separated for synthesis or transcription, and 3) the induction of mispairing of the nucleotides leading to mutations.', 'Cisplatin is an antineoplastic in the class of alkylating agents and is used to treat various forms of cancer. Alkylating agents are so named because of their ability to add alkyl groups to many electronegative groups under conditions present in cells. They stop tumor growth by cross-linking guanine bases in DNA double-helix strands - directly attacking DNA. This makes the strands unable to uncoil and separate. As this is necessary in DNA replication, the cells can no longer divide. In addition, these drugs add methyl or other alkyl groups onto molecules where they do not belong which in turn inhibits their correct utilization by base pairing and causes a miscoding of DNA. Alkylating agents are cell cycle-nonspecific. Alkylating agents work by three different mechanisms all of which achieve the same end result - disruption of DNA function and cell death.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26325, 'Rotacarnite 1gm/5ml 5 i.v. amp.', 'روتاكارنايت 1جم/5مل 5 امبولات', '65', '40', 'Antioxidant', 'L-carnitine', 'Delta grand pharma > rotabiogen for pharmaceutical invest.', 'Amp', 'أمبول', '1gm', '5', NULL, 'about l-carnitine dietary supplement amino acid derivative antioxidant. mechanism of action of l-carnitine carnitine is an essential co-factor of fatty acid metabolism in the heart liver and skeletal muscle. it is normally synthesised in the liver brain a', NULL, NULL, 973, '2025-05-26', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3649, 'Ventospan 120 ml syrup', 'فينتوسبان 120 مل شراب', '65', NULL, 'Mucolytic expectorant bronchodilator', 'N-acetyle cysteine+ivy extract+propolis+msm+vit c', 'Gama pharma', 'Syrup', 'شراب', '120 ml', '1', NULL, NULL, NULL, NULL, 1386, '2025-04-28', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27876, 'Taminil-n 12mg/ml susp. 60 ml', 'تامينيل ان 12مجم/مل شراب معلق 60مل', '238', '183', 'Antiviral.release phase', 'Oseltamivir', 'El nile.', 'Susp', 'معلق', '12mg', '1', 'مضاد للفيروسات يستخدم لعلاج الانفلونزا الفيروسية', 'about oseltamivir antiviral neuraminidase inhibitor mechanism of action of oseltamivir oseltamivir is an ethyl ester prodrug requiring ester hydrolysis for conversion to active form oseltamivir carboxylate. oseltamivir is a neuraminidase inhibitor serving', '6221077041213', NULL, 2259, '2025-01-18', 'According to FDA prescribing information, oseltamivir is indicated for the treatment of acute, uncomplicated illness due to influenza A and B infection in patients 2 weeks of age and older who have been symptomatic for no more than 48 hours [F3094]. In particular, this agent is indicated in adults and children including full-term neonates who present with symptoms typical of influenza when influenza virus is circulating in the community. Efficacy has been demonstrated when treatment is initiated within two days of the first onset of symptoms.[F3097]



Oseltamivir is also indicated for the prophylaxis of influenza in patients one year and older [F3094]. Specifically, post-exposure prevention in individuals one year of age or older following contact with a clinically diagnosed influenza case when influenza virus is circulating in the community qualifies for such prophylactic therapy. Oseltamivir would only be indicated for post-exposure prevention of influenza in infants less than 1 year of age during a pandemic influenza outbreak. [F3097]', 'Oseltamivir phosphate is a pro-drug of the active metabolite (oseltamivir carboxylate) which is a potent and selective inhibitor of influenza virus neuraminidase enzymes, which are glycoproteins found on the virion surface. Viral neuraminidase enzyme activity is important for viral entry into uninfected cells, for the release of recently formed virus particles from infected cells, and for the further spread of the infectious virus in the body.[F3097, L5161, F3115] Oseltamivir activity reduces viral shedding and infectivity.



Oseltamivir is effective agaisnt viral neuraminidases of influenza A (including pandemic H1N1) and influenza B.[F3097, L5161, F3115] ', 'There have been postmarketing reports of delirium and abnormal behavior leading to injury, and in some cases resulting in fatal outcomes, in patients with influenza who were receiving oseltamivir. Because these events were reported voluntarily during clinical practice, estimates of frequency cannot be made but they appear to be uncommon based on oseltamivir. These events were reported primarily among pediatric patients and often had an abrupt onset and rapid resolution. The contribution of oseltamivir to these events has not been established. Influenza can be associated with a variety of neurologic and behavioral symptoms that can include events such as hallucinations, delirium, and abnormal behavior, in some cases resulting in fatal outcomes. These events may occur in the setting of encephalitis or encephalopathy but can occur without obvious severe disease.[F3097, A180586, A180589]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (32640, 'Cholelage oral drops 30 ml', 'كوليلاج نقط فم 30 مل', '65', '45', 'Vitamin d3 supplement', 'Vitamin d3', 'Masa group', 'Drops', 'نقط', '30 ml', '1', NULL, NULL, NULL, NULL, 930, '2025-07-20', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25795, 'Regenax 10 gm 10 sachets', 'ريجيناكس 10 جم 10 اكياس', '160', NULL, 'Anti-rheumatic.osteoarthritis.anabolic agents', 'Gelatin (collagen) hydrolysate+vitamin c', 'Pharmazad > biohealth company for medicines', 'Sachet', 'أكياس', '10 gm', '1', 'علاج الروماتيزم -- تقليل الالام المفاصل و العظام -- تقليل الالتهابات للمفاصل و العظام -- الحفاظ علي السائل الغضروفي', 'collagen 9 gm vitamin c 30 mg for health joints use: 1 sachet in a glass add 100 ml of water juice or milk stirr well till dissolve then drink daily for at least 3 months.', NULL, NULL, 4332, '2023-02-04', 'Gelatin is used for weight loss and for treating osteoarthritis, rheumatoid arthritis, and brittle bones (osteoporosis). Some people also use it for strengthening bones, joints, and fingernails. Gelatin is also used for improving hair condition and to shorten the recovery after exercise and sports-related injury [L2113]. Gelatin is used in preparations of foods, cosmetics, and medicine [L2113].



Plasma volume expander in hypovolaemic shock [L2115]. Haemostatic [L2115].



Gelatin-based hydrogels are being used in drug delivery and tissue engineering because they are able to promote cell adhesion and proliferation. In addition, these hydrogels can be used as wound dressings because of their attractive fluid absorbance properties. Manufacturing technologies such as ultraviolet stereolithography and two-photon polymerization can be used to prepare structures containing photosensitive gelatin-based hydrogels [L2111].', 'It works as a hemostatic by providing a physical framework within which clotting may occur [L2115].



As a volume expander, gelatin remains in the vascular space. When used in the treatment of hypovolaemia gelatin can produce a significant increase in blood volume, cardiac output, stroke volume, blood pressure, urinary output and oxygen delivery, increasing volume and pressure [L2117].



For intravascular volume expansion, the majority or gelatins produce an effect which is almost equivalent to of which are mild, although severe reactions albumin, with a duration of action of 3 to 4 hours to have been reported [L2118].



Gelatin or collagen chains suspended in solution can be covalently cross-linked to form matrices that are able to swell in the presence of aqueous solutions, forming what are called _gelatin hydrogels_. Hydrogels, characterized by their hydrophilicity and insolubility in water, have the capability of swelling into an equilibrium volume while maintaining their shape. The chemical cross-linkers used may be either small bifunctional molecules or polyfunctional macromolecules, for example, glutaraldehyde [L2122].', 'Gelatin contains collagen, which is one of the materials that make up cartilage and bone [L2113].



In addition to their well-established value as a nutritional protein source, collagen and collagen-derived products may exhibit various potential biological activities on cells and the extracellular matrix through the corresponding food-derived peptides post-ingestion.  This could justify their applications in dietary supplements and pharmaceutical agents [A32426].



Gelatin is a protein that is used as a hemostatic in surgical procedures.  It is also used as a plasma volume expander in hypovolemic shock. Gelatin rods structures may also be used to temporarily block tear outflow in cases of dry eye [L2115].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2083, 'Norkibones 30 tablet', 'نوركيبونز 30 قرص', '85', NULL, 'Calcium supplement', 'Calcium+vit d3+mg+zn', 'Nork pharma', 'Tablet', 'أقراص', NULL, '3', NULL, NULL, NULL, NULL, 1011, '2025-01-04', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (164, 'Adeno sed b forte 3 amp.', 'ادينو سيد ب فورت 3 امبولات', '8', NULL, 'Vitamins', 'Adenosine triphosphate+cocarboxylase+nicotinamide+vitamin b12', 'Sedico', 'Amp', 'أمبول', NULL, '3', NULL, NULL, '6221042416022', NULL, 1685, '2022-12-01', 'For nutritional supplementation, also for treating dietary shortage or imbalance', 'Nucleotides such as Adenosine-5''-Monophosphate affect a number of immune functions, including the reversal of malnutrition and starvation-induced immunosuppression, the enhancement of T-cell maturation and function, the enhancement of natural killer cell activity, the improvement of delayed cutaneous hypersensitivity, helping resistance to such infectious agents as Staphylococcus aureus and Candida albicans, and finally the modulation of T-cell responses toward type 1 CD4 helper lymphocytes or Th1 cells. Studies have shown that mice fed a nucleotide-free diet have both impaired humoral and cellular immune responses. The addition of dietary nucleotides normalizes both types of responses. RNA, a delivery form of nucleotides, and ribonucleotides were used in these studies. The mechanism of the immune-enhancing activity of nucleic acids/nucleotides is not clear.', 'Adenosine monophosphate, also known as 5''-adenylic acid and abbreviated AMP, is a nucleotide that is found in RNA. It is an ester of phosphoric acid with the nucleoside adenosine. AMP consists of the phosphate group, the pentose sugar ribose, and the nucleobase adenine. AMP is used as a dietary supplement to boost immune activity, and is also used as a substitute sweetener to aid in the maintenance of a low-calorie diet.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26945, 'Digest 365 daily use 30 caps. (adrien gagnon)', 'دايجست 365 مستخدم يومي 30 كبسولة', '690', '590', 'Digestive enzymes', 'Vegetable digestive enzyme complex (digezyme)', 'Egypharma', 'Cap', 'كبسولة', NULL, '3', NULL, NULL, NULL, NULL, 1501, '2024-07-25', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14147, 'Androcur 50mg 20 tab.', 'اندروكور 50مجم 20 قرص', '120', NULL, 'Anti-androgen', 'Cyproterone', 'Bayer schering > bayer healthcare', 'Tab', 'أقراص', '50mg', '2', NULL, 'about cyproterone a synthetic derivative of 17-hydroxyprogesterone androgen receptor antagonist antiandrogen. mechanism of action of cyproterone the drug exerts its action by acting as an anti-androgen. it blocks the normal activity of the androgen by com', NULL, NULL, 2977, '2022-06-26', 'For the palliative treatment of patients with advanced prostatic carcinoma.', 'The direct antiandrogenic effect of cyproterone is blockage of the binding of dihydrotestosterone to the specific receptors in the prostatic carcinoma cell. In addition, cyproterone exerts a negative feed-back on the hypothalamo-pituitary axis, by inhibiting the secretion of luteinizing hormone resulting in diminished production of testicular testosterone.', 'Cyproterone is an antiandrogen. It suppresses the actions of testosterone (and its metabolite dihydrotestosterone) on tissues. It acts by blocking androgen receptors which prevents androgens from binding to them and suppresses luteinizing hormone (which in turn reduces testosterone levels).', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29884, 'Zerit 15 mg 60 caps.', 'زريت 15مجم60 كبسولة', '1,250', NULL, 'Antiviral.nucleoside', 'Stavudine', 'Bristol-myers squibb', 'Cap', 'كبسولة', '15 mg', '6', NULL, 'about stavudine nucleoside reverse transcriptase inhibitor antiretroviral agent. mechanism of action of stavudine stavudine is a thymidine nucleoside analogue. it undergoes phosphorylation in the presence of kinase enzyme and form stavudine triphosphate.', NULL, NULL, 748, '2022-08-04', 'For the treatment of human immunovirus (HIV) infections.', 'Stavudine inhibits the activity of HIV-1 reverse transcriptase (RT) both by competing with the natural substrate dGTP and by its incorporation into viral DNA.', 'Stavudine is a nucleoside reverse transcriptase inhibitor (NRTI) with activity against Human Immunodeficiency Virus Type 1 (HIV-1). Stavudine is phosphorylated to active metabolites that compete for incorporation into viral DNA. They inhibit the HIV reverse transcriptase enzyme competitively and act as a chain terminator of DNA synthesis. The lack of a 3''-OH group in the incorporated nucleoside analogue prevents the formation of the 5'' to 3'' phosphodiester linkage essential for DNA chain elongation, and therefore, the viral DNA growth is terminated.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27312, 'Sotaloc 160mg tabs.', 'سوتالوك 160مجم أقراص', '10', NULL, 'Antiarrhythmias.class iii', 'Sotalol', 'Alfacure pharmaceuticals', 'Tab', 'أقراص', '160mg', '1', NULL, 'about sotalol beta (nonselective) adneregic blocking agent a potassium channel blocker anti-hypertensive class iii anti-arrhythmic. mechanism of action of sotalol sotalol is a non selective beta receptor antagonist. it has negative chronotropic effect on', NULL, NULL, 1393, '2022-07-09', 'Sotalol is indicated to treat life threatening ventricular arrhytmias and maintain normal sinus rhythm in patients with atrial fibrillation or flutter.[Label] There are also oral solutions and intravenous injections indicated for patients requiring sotalol, but for whom a tablet would not be appropriate.[Label,L6373,L6376]', 'Sotalol inhibits beta-1 adrenoceptors in the myocardium as well as rapid potassium channels to slow repolarization, lengthen the QT interval, and slow and shorten conduction of action potentials through the atria.[A178522,A178540,A178561,A34177] The action of sotalol on beta adrenergic receptors lengthens the sinus node cycle, conduction time through the atrioventricular node, refractory period, and duration of action potentials.[A178579]', 'Sotalol is a competitive inhibitor of the rapid potassium channel.[A178522] This inhibition lengthens the duration of action potentials and the refractory period in the atria and ventricles.[A178540,A178561] The inhibition of rapid potassium channels is increases as heart rate decreases, which is why adverse effects like torsades de points is more likely to be seen at lower heart rates.[A178579] L-sotalol also has beta adrenergic receptor blocking activity seen above plasma concentrations of 800ng/L.[A178579] The beta blocking ability of sotalol further prolongs action potentials.[A178579] D-sotalol does not have beta blocking activity but also reduces a patient''s heart rate while standing or exercising.[A178579] These actions combine to produce a negative inotropic effect that reduces the strength of contractility of muscle cells in the heart.[A178522] Extension of the QT interval is also adversely associated with the induction of arrhythmia in patients.[A178561]



Hyperglycemia is a greater risk for non insulin dependant diabetics than insulin dependant diabetics.[A33725] Beta blockers inhibit insulin secretion which may cause hyperglycemia in type II diabetes mellitus.[A33725] The risk of hypoglycemia is higher in insulin dependant diabetes than non insulin dependant diabetics.[A33725] Beta blockers decrease secretion of insulin, which may mask hypoglycemia in an insulin dependant patient.[A33725] Beta blockers also increase glucose uptake into cells which may prolong or potentiate hypoglycemia.[A33725]



Further information regarding adverse reactions can be found here.[L6379]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25781, 'Rednott 0.25mg/ml eye drops 5 ml', 'ريدنوت 0.25مجم/مل قطرة للعين 5مل', '12', NULL, 'Antihistamine.second-generation', 'Ketotifen', 'Rameda > pharopharma', 'Drops', 'نقط', '0.25mg', '1', NULL, 'about ketotifen second generation h1 antagonist mast cell stabilizer antihistamine. mechanism of action of ketotifen it is a mast cell stabilizer. this inhibit the development of airway hyper reactivity (associated with activation of platelets by paf (pla', '6221094102584', NULL, 1212, '2022-12-08', 'Administered orally, ketotifen is indicated as an add-on medication in the chronic treatment of mild atopic asthma in children.[L32283] It is also available as an over-the-counter ophthalmic solution which is indicated for the temporary prevention of itching of the eye due to allergic conjunctivitis.[L32278]', 'The precise mechanism(s) through which ketotifen exerts its therapeutic effects are unclear. Ketotifen is a potent and non-competitive antagonist of H1 histamine receptors, which is likely to be a significant contributor to its anti-allergic activity.[L32283] In addition, ketotifen stabilizes mast cells and has demonstrated _in vitro_ the ability to inhibit the release of allergic and inflammatory mediators such as histamine, leukotrienes C<sub>4</sub> and D<sub>4</sub> (i.e. SRS-A), and platelet-activating factor (PAF).[L32283]



Other _in vivo_ observations thought to contribute to ketotifen''s efficacy in asthma include the inhibition of various PAF-mediated processes (e.g. airway hyperreactivity, eosinophil and platelet accumulation in the airways), prevention of leukotriene-induced bronchoconstriction, and suppression of eosinophil priming.[L32283]', 'Ketotifen is a non-competitive histamine antagonist and mast cell stabilizer.[L32278] Administered orally, it functions as a non-bronchodilator antiasthmatic drug by inhibiting the effects of endogenous substances known to be inflammatory mediators.[L32283] While effects can take 6 to 12 weeks to become apparent,[A231214] the use of ketotifen has been demonstrated to reduce the frequency, severity, and duration of asthma symptoms, and may allow for a reduction in the use of other asthma therapies.[L32283]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7113, 'Larivex 500 mg 5 tab.', 'لاريفكس 500 مجم 5 اقراص', '66', '46', 'Antibiotic.quinolone', 'Levofloxacin', 'Amriya > european egyptian pharm. ind.', 'Tab', 'أقراص', '500 mg', '1', 'مضاد حيوي', NULL, '6221076010043', NULL, 2671, '2024-06-01', 'In oral and intravenous formulations, levofloxacin is indicated in adults for the treatment of various infections caused by susceptible bacteria, including infections of the upper respiratory tract, lower respiratory tract, skin, skin structures, urinary tract, and prostate.[L11638,L11692] The oral formulation is also indicated in both adults and children 6 months of age and older for the post-exposure management of inhalational anthrax caused by _Bacillus anthracis_ and for the treatment and/or prophylaxis of plague caused by _Yersinia pestis_.[L11638]



In its ophthalmic formulation, levofloxacin is indicated for the treatment of bacterial conjunctivitis caused by susceptible organisms.[L11641] An inhalational solution available in Canada is indicated for the management of cystic fibrosis patients aged 18 years or older with chronic pulmonary _Pseudomonas aeruginosa_ infections.[L11689]', 'Levofloxacin, like other fluoroquinolone antibiotics, exerts its antimicrobial activity via the inhibition of two key bacterial enzymes: DNA gyrase and topoisomerase IV.[L11638] Both targets are type II topoisomerases, but have unique functions within the bacterial cell. DNA gyrase is an enzyme found only in bacteria that introduces negative supercoils into DNA during replication - this helps to relieve torsional strain caused by the introduction of positive supercoils during replication, and these negative supercoils are essential for chromosome condensation and the promotion of transcription initiation.[A31453] It is comprised of four subunits (two A subunits and two B subunits) of which the A subunits appear to be the target of fluoroquinolone antibiotics.[A190663] Bacterial topoisomerase IV, in addition to contributing to the relaxation of positive supercoils, is essential at the terminal stages of DNA replication and functions to “unlink” newly replicated chromosomes to allow for the completion of cell division.[A31453]



Inhibition of these enzymes by levofloxacin likely occurs via complexation with the topoisomerase enzymes.[A31453] The end result is a blockade of DNA replication, thus inhibiting cell division and resulting in cell death.', 'Levofloxacin is bactericidal and exerts its antimicrobial effects via inhibition of bacterial DNA replication.[L11638] It has a relatively long duration of action in comparison with other antibiotics that allows for once or twice daily dosing. Levofloxacin is associated with QTc-interval prolongation and should be used with caution in patients with other risk factors for prolongation (e.g. hypokalemia, concomitant medications).[L11638]



Levofloxacin has demonstrated _in vitro_ activity against a number of aerobic gram-positive and gram-negative bacteria and may carry some activity against certain species of anaerobic bacteria[L11638] and other pathogens such as _Chlamydia_ and _Legionella_.[A190663] Resistance to levofloxacin may develop, and is generally due to mutations in DNA gyrase or topoisomerase IV, or via alterations to drug efflux.[L11638,A31453] Cross-resistance may occur between levofloxacin and other fluoroquinolones, but is unlikely to develop between levofloxacin and other antibiotic classes (e.g. macrolides) due to significant differences in chemical structure and mechanism of action.[L11638]



As antimicrobial susceptibility patterns are geographically distinct, local antibiograms should be consulted to ensure adequate coverage of relevant pathogens prior to use.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17499, 'Dextrose 5% & sodium chloride 0.9% (el nile) i.v. inf. 500 ml', 'دكستروز 5% و صوديوم كلورايد 0.9% النيل محلول وريدي 500 مل', '11', NULL, 'Sterile solution', 'Glucose (dextrose)+sodium chloride', 'El nile.', 'Unknown', 'غير محدد', '5%', '1', NULL, 'about glucose monosaccharide aldohexose nutritive sweetner. mechanism of action of glucose glucose is a simple carbohydrate and is used as energy source in man. it is readily converted to fat which provides a rich store of energy in concentrated form. glu', NULL, NULL, 873, '2022-07-30', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (8202, 'Minirin 0.2mg 30 tab. (n/a)', 'مينيرين 0.2مجم 30 قرص', '250', NULL, 'Urinary incotinence.antidiuretic', 'Desmopressin acetate', 'Rechon life science ab-sweden > ferring pharmaceuticals', 'Tab', 'أقراص', '0.2mg', '3', NULL, 'about desmopressin synthetic analogue of natural pituitary hormone 8-arginine vasopressin (adh) antidiuretic hormone in diabetes insipidus hemostatic agent antihemophilic agent. mechanism of action of desmopressin the drug exerts it`s antidiuretic action', NULL, NULL, 1480, '2022-08-14', '- Indicated for the treatment of nocturia due to nocturnal polyuria in adults who awaken at least 2 times per night to void (intranasal). 



- Indicated as antidiuretic replacement therapy in the management of central cranial diabetes insipidus and for management of the temporary polyuria and polydipsia following head trauma or surgery in the pituitary region (intranasal/parenteral).



- Indicated for patients with hemophilia A with factor VIII coagulant activity levels greater than 5% or mild to moderate classic von Willebrand''s Disease (Type I) with factor VIII levels greater than 5% during surgical procedures and postoperatively to maintain hemostasis (parenteral).', 'Upon binding of desmopressin to V2 receptors in the basolateral membrane of the cells of the distal tubule and collecting ducts of the nephron, adenylyl cyclase is stimulated. The resulting intracellular cascades in the collecting duct lead to increased rate of insertion of water channels, called aquaporins, into the lumenal membrane and enhanced the permeability of the membrane to water [T28]. ', 'By mimicking the actions of endogenous ADH, desmopressin acts as a selective agonist of V2 receptors expressed in the renal collecting duct (CD) to increase water re-absorption and reduce urine production. Desmopressin has been shown to be more potent than ADH in increasing plasma levels of factor VIII activity in patients with hemophilia and von Willebrand''s disease Type I [L1183]. Desmopressin demonstrates markedly diminished pressor activity. Desmopressin administered intranasally has an antidiuretic effect about one-tenth that of an equivalent dose administered by injection [L1182].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4825, 'Slowurge 5 mg 30 f.c.tabs', 'سلو ارج 5 مجم 30 قرص', '111', NULL, 'Urinary incontinence.antispasmodics.muscarinic antagonists', 'Solifenacin succinate', 'Biomed', 'Tab', 'أقراص', '5 mg', '3', NULL, NULL, NULL, NULL, 1008, '2025-06-21', 'Solifenacin tablets are indicated to treat an overactive bladder with urinary incontinence, urgency, and frequency.[L7511]', 'Solifenacin is a competitive muscarinic receptor antagonist.[L7511] It has the highest affinity for M3, M1, and M2 muscarinic receptors.[A181247] 80% of the muscarinic receptors in the bladder are M2, while 20% are M3.[A181262] Solifenacin''s antagonism of the M3 receptor prevents contraction of the detrusor muscle, while antagonism of the M2 receptor may prevent contraction of smooth muscle in the bladder.[A181262]', 'Solifenacin antagonizes the M2 and M3 muscarinic receptors in the bladder to treat an overactive bladder.[L7511,A181262] It has a long duration of action as it is usually taken once daily.[L7511] Patients taking solifenacin should be aware of the risks of angioedema and anaphylaxis.[L7511]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18694, 'Exelon 1.5mg 28 caps.', 'اكسيلون 1.5 مجم 28 كبسولة', '350', '210', 'Anticholinesterase', 'Rivastigmine', 'Novartis', 'Cap', 'كبسولة', '1.5mg', '2', NULL, 'about rivastigmine a central reversible non competitive anticholinesterase antidementia. mechanism of action of rivastigmine alzheimer`s disease may result from a deficiency in neurotransmitters used by nerves in the brain to communicate with one another.', NULL, NULL, 733, '2024-07-27', 'For the treatment of mild to moderate dementia associated with Parkinson''s disease or of the Alzheimer''s type.', 'Rivastigmine is a carbamate derivative that is structurally related to physostigmine, but not to donepezil and tacrine. The precise mechanism of rivastigmine has not been fully determined, but it is suggested that rivastigmine binds reversibly with and inactivates chlolinesterase (eg. acetylcholinesterase, butyrylcholinesterase), preventing the hydrolysis of acetycholine, and thus leading to an increased concentration of acetylcholine at cholinergic synapses. The anticholinesterase activity of rivastigmine is relatively specific for brain acetylcholinesterase and butyrylcholinesterase compared with those in peripheral tissues.', 'Rivastigmine is a parasympathomimetic and a reversible cholinesterase inhibitor. An early pathophysiological feature of Alzheimer''s disease that is associated with memory loss and cognitive deficits is a deficiency of acetylcholine as a result of selective loss of cholinergic neurons in the cerebral cortex, nucleus basalis, and hippocampus. Tacrine is postulated to exert its therapeutic effect by enhancing cholinergic function. While the precise mechanism of rivastigmine''s action is unknown, it is postulated to exert its therapeutic effect by enhancing cholinergic function. This is accomplished by increasing the concentration of acetylcholine through reversible inhibition of its hydrolysis by cholinesterase. If this proposed mechanism is correct, rivastigmine''s effect may lessen as the disease progresses and fewer cholinergic neurons remain functionally intact.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14100, 'Anandron 50mg 90 tab.', 'اناندرون 50مجم 90 قرص', '472', NULL, 'Anti-androgen', 'Nilutamide', 'Sanofi', 'Tab', 'أقراص', '50mg', '6', NULL, 'indication for use in combination with surgical castration for the treatment of metastatic prostate cancer involving distant lymph nodes bone or visceral organs (stage d2). mechanism of action nilutamide competes with androgen for the binding of androgen', NULL, NULL, 603, '2022-09-06', 'For use in combination with surgical castration for the treatment of metastatic prostate cancer involving distant lymph nodes, bone, or visceral organs (Stage D2).', 'Nilutamide competes with androgen for the binding of androgen receptors, consequently blocking the action of androgens of adrenal and testicular origin that stimulate the growth of normal and malignant prostatic tissue. This blockade of androgen receptors may result in growth arrest or transient tumor regression through inhibition of androgen-dependent DNA and protein synthesis.', 'Nilutamide is an antineoplastic hormonal agent primarily used in the treatment of prostate cancer. Nilutamide is a pure, nonsteroidal anti-androgen with affinity for androgen receptors (but not for progestogen, estrogen, or glucocorticoid receptors). Consequently, Nilutamide blocks the action of androgens of adrenal and testicular origin that stimulate the growth of normal and malignant prostatic tissue. Prostate cancer is mostly androgen-dependent and can be treated with surgical or chemical castration. To date, antiandrogen monotherapy has not consistently been shown to be equivalent to castration. The relative binding affinity of nilutamide at the androgen receptor is less than that of bicalutamide, but similar to that of hydroxuflutamide.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3625, 'Lictrilit 360ml solution', 'ليكتريليت 360 مل محلول', '55', NULL, 'Oral rehydration solution with zinc', 'Potassium citrate -sodium cloride-sodium citrate-zinc gluconate', 'Prosperity company', 'Solution', 'محلول', '360ml', '1', NULL, NULL, NULL, NULL, 886, '2025-04-26', 'For use as an electrolyte replenisher and in the treatment of hypokalemia.', 'Supplemental potassium in the form of high potassium food or potassium chloride may be able to restore normal potassium levels.', 'The potassium ion is in the principle intracellular cation of most body tissues. Potassium ions participate in a number of essential physiological processes including the maintenance of intracellular tonicity, the transmission of nerve impulses, the contraction of cardiac, skeletal and smooth muscle, and the maintenance of normal renal function. The intracellular concentration of potassium is approximately 150 to 160 mEq per liter. The normal adult plasma concentration is 3.5 to 5 mEq per liter. An active ion transport system maintains this gradient across the plasma membrane. Potassium is a normal dietary constituent and under steady-state conditions the amount of potassium absorbed from the gastrointestinal tract is equal to the amount excreted in the urine. The usual dietary intake of potassium is 50 to 100 mEq per day. Potassium depletion will occur whenever the rate of potassium loss through renal excretion and/or loss from the gastrointestinal tract exceeds the rate of potassium intake. Such depletion usually develops as a consequence of therapy with diuretics, primarily or secondary hyperaldosteronism, diabetic ketoacidosis, or inadequate replacement of potassium in patients on prolonged parenteral nutrition. Depletion can develop rapidly with severe diarrhea, especially if associated with vomiting. Potassium depletion due to these causes is usually accompanied by concomitant loss of chloride and is manifested by hypokalemia and metabolic alkalosis. Potassium depletion may produce weakness, fatigue, disturbances of cardiac rhythm (primarily ectopic beats), prominent U-waves in the electrocardiogram, and, in advanced cases, flaccid paralysis and/or impaired ability to concentrate urine. If potassium depletion associated with metabolic alkalosis cannot be managed by correcting the fundamental cause of the deficiency, e.g., where the patient requires long-term diuretic therapy, supplemental potassium in the form of high potassium food or potassium chloride may be able to restore normal potassium levels. In rare circumstances (e.g., patients with renal tubular acidosis) potassium depletion may be associated with metabolic acidosis and hyperchloremia. In such patients, potassium replacement should be accomplished with potassium salts other than the chloride, such as potassium bicarbonate, potassium citrate, potassium acetate, or potassium gluconate.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30113, 'Ameropem 500 mg i.v. vial', 'اميروبيم 500مجم فيال وريد', '252', '176', 'Carbapenem antibiotic', 'Meropenem', 'Pharco b', 'Vial', 'فيال', '500 mg', '1', NULL, 'about meropenem a synthetic broad-spectrum carbapenem (beta-lactam) antibiotic. mechanism of action of meropenem it binds to the penicillin binding protein in the cell wall of bacteria and inhibits the enzyme transpeptidase which is responsible for the tr', NULL, NULL, 1023, '2024-10-30', 'For use as single agent therapy for the treatment of the following infections when caused by susceptible isolates of the designated microorganisms: <b>complicated skin and skin structure infections</b> due to <i>Staphylococcus aureus</i> (b-lactamase and non-b-lactamase producing, methicillin-susceptible isolates only), <i>Streptococcus pyogenes</i>, <i>Streptococcus agalactiae</i>, viridans group streptococci, <i>Enterococcus faecalis</i> (excluding vancomycin-resistant isolates), <i>Pseudomonas aeruginosa</i>, <i>Escherichia coli</i>, <i>Proteus mirabilis</i>, <i>Bacteroides fragilis</i> and <i>Peptostreptococcus</i> species; <b>complicated appendicitis and peritonitis</b> caused by viridans group streptococci, <i>Escherichia coli</i>, <i>Klebsiella pneumoniae</i>, <i>Pseudomonas aeruginosa</i>, <i>Bacteroides fragilis</i>, <i>B. thetaiotaomicron</i>, and <i>Peptostreptococcus</i> species. Also for use in the treatment of bacterial meningitis caused by <i>Streptococcus pneumoniae</i>, <i>Haemophilus influenzae</i> (b-lactamase and non-b-lactamase-producing isolates), and <i>Neisseria meningitidis</i>.', 'The bactericidal activity of meropenem results from the inhibition of cell wall synthesis. Meropenem readily penetrates the cell wall of most Gram-positive and Gram-negative bacteria to reach penicillin-binding- protein (PBP) targets. Its strongest affinities are toward PBPs 2, 3 and 4 of <i>Escherichia coli</i> and <i>Pseudomonas aeruginosa</i>; and PBPs 1, 2 and 4 of <i>Staphylococcus aureus</i>.', 'Meropenem is a broad-spectrum carbapenem antibiotic. It is active against Gram-positive and Gram-negative bacteria. Meropenem exerts its action by penetrating bacterial cells readily and interfering with the synthesis of vital cell wall components, which leads to cell death.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20747, 'Immunomide 25 mg 21 caps', 'اميونومايد 25مجم 21 كبسولة', '8,404', NULL, 'Immunosuppressants', 'Lenalidomide', 'Hikma specialized pharmaceuticals', 'Cap', 'كبسولة', '25 mg', '3', 'مثبط للمناعة', NULL, NULL, NULL, 3273, '2022-05-22', 'Lenalidomide is indicated for the treatment of adult patients with multiple myeloma (MM) in combination with dexamethasone. It is also indicated as maintenance therapy in multiple myeloma following autologous hematopoietic stem cell transplantation (auto-HSCT).



It is indicated for the treatment of adult patients with transfusion-dependent anemia due to low- or intermediate-1-risk myelodysplastic syndromes (MDS) associated with a deletion 5q cytogenetic abnormality with or without additional cytogenetic abnormalities.



Lenalidomide is indicated for the treatment of adult patients with mantle cell lymphoma (MCL) whose disease has relapsed or progressed after two prior therapies, one of which included bortezomib.



In combination with a rituximab product, lenalidomide is indicated for the treatment of adult patients with previously treated follicular lymphoma (FL) or previously treated marginal zone lymphoma (MZL).[L16028]', 'Lenalidomide is a drug with multiple mechanisms of action. Lenalidomide exerts immunomodulating effects by altering cytokine production, regulating T cell co-stimulation, and enhancing the NK cell-mediated cytotoxicity.[A228543] Lenalidomide directly inhibits the cullin ring E3 ubiquitin ligase complex: upon binding to cereblon, a substrate adaptor of the complex, lenalidomide modulates substrate specificity of the complex to recruit substrate proteins of the ligase, including Ikaros (IKZF1), Aiolos (IKZF3), and CK1α.[L16028] These substrates are then tagged for ubiquitination and subsequent proteasomal degradation. IKZF1 and IKZF3 are B-cell transcription factors that are essential for B-cell differentiation and survival of malignant cells. IKZF3 also regulates the expression of interferon regulatory factor 4 (IRF4), which is a transcription factor that regulates the aberrant myeloma-specific gene. The immunomodulatory actions of lenalidomide can be partly explained by the degradation of IKZF3, since it is a repressor of the interleukin 2 gene (IL2): as lenalidomide decreases the level of IKZF3, the production of IL-2 increases, thereby increasing the proliferation of natural killer (NK), NKT cells, and CD4+ T cells.[A228703] Lenalidomide inhibits the production of pro-inflammatory cytokines TNF-α, IL-1, IL-6, and IL-12, while elevating the production of anti-inflammatory cytokine IL-10.[A228543] Lenalidomide acts as a T-cell co-stimulatory molecule that promotes CD3 T-cell proliferation and increases the production of IL-2 and IFN-γ in T lymphocytes, which enhances NK cell cytotoxicity and ADCC. It inhibits the expression and function of T-regulatory cells, which are often overabundant in some hematological malignancies.[A228708] 



Lenalidomide directly exerts antitumour effects by inhibiting the proliferation and inducing apoptosis of tumour cells. Lenalidomide triggers the activation of pro-apoptotic caspase-8, enhances tumour cell sensitivity to FAS-induced apoptosis, and downregulates NF-κB, an anti-apoptotic protein.[A228708] Independent of its immunomodulatory effects, lenalidomide mediates anti-angiogenic effects by inhibiting angiogenic growth factors released by tumour cells, such as vascular endothelial growth factor (VEGF), basic fibroblastic-growth factor (BFGF), and hepatocyte-growth factor. _In vitro_, lenalidomide inhibits cell adhesion molecules such as ICAM-1, LFA-1, β2 and β3 integrins, as well as gap-junction function, thereby preventing metastasis of malignant cells.[A228708] ', 'In hematological malignancies, the immune system is deregulated in the form of altered cytokine networks in the tumour microenvironment, defective T cell regulation of host-tumour immune interactions, and diminished NK cell activity.[A228543] Lenalidomide is an immunomodulatory agent with antineoplastic, antiangiogenic, and anti-inflammatory properties.[A228628] Lenalidomide exerts direct cytotoxicity by increasing apoptosis and inhibiting the proliferation of hematopoietic malignant cells.[A228708] It delays tumour growth in nonclinical hematopoietic tumour models _in vivo_, including multiple myeloma.[L16028] Lenalidomide also works to limit the invasion or metastasis of tumour cells and inhibits angiogenesis.[A228708]



Lenalidomide also mediates indirect antitumour effects via its immunomodulatory actions: it inhibits the production of pro-inflammatory cytokines, which are implicated in various hematologic malignancies. Lenalidomide enhances the host immunity by stimulating T cell proliferation and enhancing the activity of natural killer (NK) cells.[A713, A228703, A228708] Lenalidomide is about 100–1000 times more potent in stimulating T cell proliferation than [thalidomide].[A228543] _In vitro_, it enhances antibody-dependent cell-mediated cytotoxicity (ADCC), which is even more pronounced when used in combination with rituximab.[L16028] Due to its anti-inflammatory properties, lenalidomide has been investigated in the context of inflammatory and autoimmune diseases, such as amyotrophic lateral sclerosis.[A229418] ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (11925, 'Neesterol cream 30 gm', 'نيستيرول كريم 30 جم', '125', NULL, 'Healing topical', 'Sodium hyaluronate+beta-sitosterol+neem oil+propolis+beeswax+calendula+wheat germ oil+vit e+dexapanthenol+aloe barbadensis+olive oil+allantoin+zinc oxide', 'Era egypt pharma', 'Cream', 'كريم', '30 gm', '1', NULL, NULL, NULL, NULL, 1769, '2024-08-22', 'For the treatment of small uncomplicated varicose veins of the lower extremities that show simple dilation with competent valves.', 'Sodium tetradecyl sulfate is a potent toxin for endothelial cells in that brief exposure to even low concentrations are effective in stripping endothelium over a considerable distance and exposing highly thrombogenic endothelium in the process. Diluted sodium tetradecyl sulfate is also able to induce a hypercoagulable state, possibly by selective inhibition of protein C, and can also promote platelet aggregation.', 'Sodium tetradecyl sulfate is an anionic surfactant which occurs as a white, waxy solid. It is used as a sclerosing agent in sclerotherapy. Sclerotherapy is an injection of a sclerosing agent directly through the skin into a lesion and is used primarily for slow-flow vascular anomalies, particularly for venous malformation and lymphatic malformation. Intravenous injection causes intima inflammation and thrombus formation. This usually occludes the injected vein. Subsequent formation of fibrous tissue results in partial or complete vein obliteration that may or may not be permanent.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19888, 'Glucofer 300mg 60 sugar coated tab.', 'جلوكوفير 300مجم 60 قرص', '3', NULL, 'Iron supplement', 'Ferrous gluconate', 'Cid', 'Tab', 'أقراص', '300mg', '6', 'مكمل غذائي بالحديد -- لعلاح حالات الانيميا ونقص الحديد وفقر الدم', 'ferrous gluconate is effectively used in the treatment of hypochromic anemia. the use of this compound compared with other iron preparations results in satisfactory reticulocyte responses a high percentage utilization of iron and daily increase in hemoglo', NULL, NULL, 8672, '2023-05-14', 'Ferrous sulfate is used for the prevention and treatment of iron deficiency anemia in adults and children.[A190804,L2240,L11800]

', 'Iron is required to maintain optimal health, particularly for helping to form red blood cells (RBC) that carry oxygen around the body. A deficiency in iron indicates that the body cannot produce enough normal red blood cells.[A32514,L11800] Iron deficiency anemia occurs when body stores of iron decrease to very low levels, and the stored iron is insufficient to support normal red blood cell (RBC) production. Insufficient dietary iron, impaired iron absorption, bleeding, pregnancy, or loss of iron through the urine can lead to iron deficiency.[A32514,L11794]  Symptoms of iron deficiency anemia include fatigue, breathlessness, palpitations, dizziness, and headache.



Taking iron in supplement form, such as ferrous sulfate, allows for more rapid increases in iron levels when dietary supply and stores are not sufficient.[L2175] Iron is transported by the divalent metal transporter 1 (DMT1) across the endolysosomal membrane to enter the macrophage. It can then can be incorporated into ferritin and be stored in the macrophage or carried of the macrophage by ferroportin. This exported iron is  oxidized by the enzyme to ceruloplasmin to Fe3+, followed by sequestration by transferrin for transport in the serum to various sites, including the bone marrow for hemoglobin synthesis or into the liver.[A32524] Iron combines with porphyrin and globin chains to form hemoglobin, which is critical for oxygen delivery from the lungs to other tissues.[L2263]', 'Ferrous sulfate replenishes iron, an essential component in hemoglobin, myoglobin, and various enzymes. It replaces the iron that is usually found in hemoglobin and myoglobin. Iron participates in oxygen transport and storage, electron transport and energy metabolism, antioxidant and beneficial pro-oxidant functions, oxygen sensing, tissue proliferation and growth, as well as DNA replication and repair.[A32524,A32514]



', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (8222, 'Healthoval man 30 tabs', 'هيلثوفال مان 30 قرص', '150', NULL, 'Multivitamin', 'Vitamin a+vitamin c+vitamin d3+vitamin e+vitamin k+thiamine+riboflavin+niacin+vitamin b6+folic acid+vitamin b12+biotin+pantothenic acid+calcium+iron+phosphorus+iodine+magnesium+zinc+selenium+copper+manganese+chromium+molybdenum+chloride+potassium+lycopene', 'Tetracare limited', 'Tab', 'أقراص', NULL, '1', NULL, NULL, NULL, NULL, 485, '2025-12-07', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18465, 'Epitaxol 0.5% eye drops 5 ml', 'ابيتاكسول 0.5% قطرة 5 مل', '19', '13.2', 'Antihypertensive.beta blocker.selective b1', 'Betaxolol', 'Eipico', 'Drops', 'نقط', '0.5%', '1', NULL, 'about betaxolol cardioselective beta 1-adrenergic blocker anti glaucoma antihypertensive. mechanism of action of betaxolol betaxolol is a selective beta 1 (? 1) blocker. it inhibits the beta receptors present in the eye such as in the ciliary body epithel', '6221032331908', NULL, 1025, '2025-05-11', 'For the management of hypertension.', 'Betaxolol selectively blocks catecholamine stimulation of beta(1)-adrenergic receptors in the heart and vascular smooth muscle. This results in a reduction of heart rate, cardiac output, systolic and diastolic blood pressure, and possibly reflex orthostatic hypotension. Betaxolol can also competitively block beta(2)-adrenergic responses in the bronchial and vascular smooth muscles, causing bronchospasm.', 'Betaxolol is a competitive, beta(1)-selective (cardioselective) adrenergic antagonist. Betaxolol is used to treat hypertension, arrhythmias, coronary heart disease, glaucoma, and is also used to reduce non-fatal cardiac events in patients with heart failure. Activation of beta(1)-receptors (located mainly in the heart) by epinephrine increases the heart rate and the blood pressure, and the heart consumes more oxygen. Drugs such as betaxolol that block these receptors therefore have the reverse effect: they lower the heart rate and blood pressure and hence are used in conditions when the heart itself is deprived of oxygen. They are routinely prescribed in patients with ischemic heart disease. In addition, beta(1)-selective blockers prevent the release of renin, which is a hormone produced by the kidneys which leads to constriction of blood vessels. Betaxolol is lipophilic and exhibits no intrinsic sympathomimetic activity (ISA) or membrane stabilizing activity.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (11935, 'Universal femine wash 240 ml', 'يونيفرسال غسول للعناية الشخصية للسيدات 240 مل', '45', NULL, 'Vaginal wash', 'Glucosamine+chondroitin sulphate+msm+emu oil+menthol+vitamin c+camphor', 'Linkopharm', 'Unknown', 'غير محدد', '240 ml', '1', NULL, NULL, NULL, NULL, 811, '2023-06-19', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13793, 'Allerzone 0.1% cream 25 gm', 'الليرزون كريم 25 جرام', '40', '27.5', 'Anti-inflammatory.glucocorticoid.', 'Mometasone furoate', 'Egpi', 'Cream', 'كريم', '0.1%', '1', 'مضاد التهاب وحساسية', 'about mometasone a glucocorticoid steroid anti psoriatic anti asthma anti inflammatory. mechanism of action of mometasone mometasone is a topical medium potency glucocorticoid. the drug exerts its pharmacological action by penetrating and binding to cytop', '6223003930806', NULL, 2471, '2025-02-17', 'The inhaler is indicated for the maintenance treatment of asthma as prophylactic therapy. The nasal spray is indicated for the treatment of the nasal symptoms of seasonal allergic and perennial allergic rhinitis.', 'Unbound corticosteroids cross cell membranes and bind with high affinity to specific cytoplasmic receptors. Inflammation is decreased by diminishing the release of leukocytic acid hydrolases, prevention of macrophage accumulation at inflamed sites, interference with leukocyte adhesion to the capillary wall, reduction of capillary membrane permeability, reduction of complement components, inhibition of histamine and kinin release, and interference with the formation of scar tissue. The antiinflammatory actions of corticosteroids are thought to involve phospholipase A<sub>2</sub> inhibitory proteins, lipocortins, which control the biosynthesis of potent mediators of inflammation such as prostaglandins and leukotrienes. Mometasone furoate has been shown in vitro to exhibit a binding affinity for the human glucocorticoid receptor which is approximately 12 times that of dexamethasone, 7 times that of triamcinolone acetonide, 5 times that of budesonide, and 1.5 times that of fluticasone.', 'Mometasone is a medium-potency synthetic corticosteroid with antiinflammatory, antipruritic, and vasoconstrictive properties. Studies in asthmatic patients have demonstrated that mometasone provides a favorable ratio of topical to systemic activity due to its primary local effect along with the extensive hepatic metabolism and the lack of active metabolites. Though effective for the treatment of asthma, glucocorticoids do not affect asthma symptoms immediately. Maximum improvement in symptoms following inhaled administration of mometasone furoate may not be achieved for 1 to 2 weeks or longer after starting treatment. When glucocorticoids are discontinued, asthma stability may persist for several days or longer. Mometasone has been shown in vitro to exhibit a binding affinity for the human glucocorticoid receptor which is approximately 12 times that of dexamethasone, 7 times that of triamcinolone acetonide, 5 times that of budesonide, and 1.5 times that of fluticasone. The clinical significance of these findings is unknown.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16833, 'Cosantox 30 f.c. tabs.', 'كوزانتوكس 30 قرص', '225', '60', 'Antirheumatic.osteoarthritis.anabolic agents', 'Calcium+glucosamine+vitamin c', 'Multicare', 'Tab', 'أقراص', NULL, '3', '- مضاد للروماتيزم', NULL, '6224007326121', NULL, 28408, '2023-10-22', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28097, 'Thalix 100mg 30caps.(illegal import', 'ثاليكس 100مجم 30 كبسولة - مستورد', '0', NULL, 'Immunosuppressants', 'Thalidomide', 'Fresenius kabi >', 'Cap', 'كبسولة', '100mg', '3', NULL, NULL, NULL, NULL, 1758, '2022-07-05', 'Thalidomide is primarily used for the acute treatment and maintenance therapy to prevent and suppress the cutaneous manifestations of moderate to severe erythema nodosum leprosum (ENL).[L41235]  ', 'The mechanism of action of thalidomide is not fully understood. Previous research indicate that thalidomide binds to cerebron, a component of the E3 ubiquitin ligase complex, to selectively degrade the transcription factor IKZF3 and IKZF1. These 2 transcription factors are vital for the proliferation and survival of malignant myeloma cells. [A246125] 



Regarding TNF-alpha, thalidomide seems to block this mediator via a variety of mechanism. Thalidomide can inhibit the expression myeloid differentiating factor 88 (MyD88), an adaptor protein that is involved in the TNF-alpha production signalling pathway, at the protein and RNA level. Additionally, thalidomide prevents the activation of Nuclear Factor Kappa B (NF-kB), another upstream effector of the TNF-alpha production pathway. Finally, some evidences suggest that thalidomide can block alpha-1 acid glycoprotein (AGP), a known inducer of the NF-kB/MyD88 pathway, thus inhibiting the expression of TNF-alpha [A246195]. The down-regulation of NF-kB and MyD88 can also affect the cross talk between the NF-kB/MyD88 and VEGF pathway, resulting in thalidomide''s anti-angiogenic effect. [A246200]', 'Thalidomide, originally developed as a sedative, is an immunomodulatory and anti-inflammatory agent with a spectrum of activity that is not fully characterized. However, thalidomide is believed to exert its effect through inhibiting and modulating the level of various inflammatory mediators, particularly tumor necrosis factor-alpha (TNF-a) and IL-6. [A714] Additionally, thalidomide is also shown to inhibit basic fibroblast growth factor (bFGF) and vascular endothelial growth factor (VEGF), suggesting a potential anti-angiogenic application of thalidomide in cancer patients. [A246185]



Thalidomide is racemic &mdash; it contains both left and right handed isomers in equal amounts: the (+)R enantiomer is effective against morning sickness, and the (&minus;)S enantiomer is teratogenic. The enantiomers are interconverted to each other in vivo; hence, administering only one enantiomer will not prevent the teratogenic effect in humans [A246190].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17410, 'Dew antiseptic soap 100 gm', 'ديو صابون مطهر 100 جرام', '18', NULL, 'Antiseptic', 'Chlorhexidine+triclosan', 'Hi-care > bluebell pharmaceuticals', 'Soap', 'صابون', '100 gm', '1', NULL, NULL, NULL, NULL, 677, NULL, NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17955, 'Duloxeprin 60mg 28 capsules', 'دولوكسبرين 60مجم 28 كبسولة', '214', '162', 'Psychiatric.antidepressants', 'Duloxetine', 'Future pharmaceutical industries (fpi) > utopia pharmaceuticals company', 'Capsule', 'كبسولة', '60mg', '2', 'مضاد للاكتئاب', NULL, '6223012330130', NULL, 10265, '2024-07-18', '**Indicated** for:



1) Management of Major Depressive Disorder.[label]



2) Management of Generalized Anxiety Disorder.[label]



3) Management of diabetic peripheral neuropathy.[label]



4) Management of fibromyalgia.[label]



5) Management of chronic musculoskeletal pain.[label]



6) Management of osteoarthritis of the knee in adults.[L6364]



7) Management of chronic lower back pain in adults.[L6364]



8) Management of stress urinary incontinence in adult women.[L6367]



**Off-label** uses include:



1) Management of chemotherapy-induced peripheral neuropathy.[A178603]



2) Management of stress urinary incontinence in adult men after prostatectomy until recovery is complete.[L6370]', 'Duloxetine is a potent inhibitor of neuronal serotonin and norepinephrine reuptake and a less potent inhibitor of dopamine reuptake.[A178714] Duloxetine has no significant affinity for dopaminergic, adrenergic, cholinergic, histaminergic, opioid, glutamate, and GABA receptors.



Action on the external urinary sphincter is mediated via duloxetine''s CNS effects. Increased serotonin and norepinephrine concentrations in Onuf''s nucleus leads to increased activation of 5-HT<sub>2</sub>, 5-HT<sub>3</sub>, and α<sub>1</sub> adrenergic receptors.[A178663,A178666] 5-HT<sub>2</sub> and α<sub>1</sub> are both G<sub>q</sub> coupled and their activation increases the activity of the inositol trisphosphate/phospholipase C (IP<sub>3</sub>/PLC) pathway.[T116] This pathway leads to release of intracellular calcium stores, increasing intracellular calcium concentrations, and facilitating neuronal excitability. 5-HT<sub>3</sub> functions as a ligand-gated sodium channel which allows sodium to flow into the neuron when activated. Increased flow of sodium into the neuron contributes to depolarization and activation of voltage gated channels involved in action potential generation. The combined action of these three receptors contributes to increased excitability of the pudendal motor nerve in response to glutamate.



Also related to duloxetine''s action at the spinal cord is its modulation of pain. Increasing the concentration of serotonin and norepinephrine in the dorsal horn of the spinal cord increases descending inhibition of pain through activation of 5-HT<sub>1A</sub>, 5-HT<sub>1B</sub>, 5-HT<sub>1D</sub>, 5-HT<sub>2</sub>, 5-HT<sub>3</sub>, α<sub>1</sub>-adrenergic, and α<sub>2</sub>-adrenergic receptors.[A178705] 5-HT<sub>2</sub>, 5-HT<sub>3</sub>, and α<sub>1</sub>-adrenergic mediate neuronal activation as described above. The activated neuron in this case is the GABAergic inhibitory interneuron which synapses onto the nociceptive projection neuron to inhibit the transmission of painful stimuli to the brain. The 5-HT<sub>1</sub> and α<sub>2</sub> receptors are G<sub>i</sub>/G<sub>o</sub> coupled and their activation leads to increased potassium current through inward rectifier channels and decreased adenylyl cyclase/protein kinase A signaling which contributes to neuronal inhibition.[A178705,T116] These inhibitory receptors are present on the projection neuron itself as well as the dorsal root ganglion which precedes it and serves to directly suppress the transmission of painful stimuli.



The mechanisms involved in duloxetine''s benefits in depression and anxiety have not been fully elucidated. Dysfunctional serotonin and norepinephrine signaling are thought to be involved and increases in the availability of these neurotransmitters at the synaptic cleft thought to mediate a therapeutic effect.[A178666] It is postulated that the involvement of serotonin and norepinephrine in area responsible for emotional modulation such as the limbic system contributes to the effects in mood disorders specifically but this has yet to be confirmed.



Duloxetine''s hypertensive effect is related to its intended pharmacological effect. Increased availability of norepinephrine leads to activation of adrenergic receptors on the vascular endothelium. Since the action of α<sub>1</sub> receptors predominates, vasoconstriction results as the G<sub>q</sub> coupled receptor mediates calcium release from the sarcoplasmic reticulum to facilitate smooth muscle contraction.[T116]', 'Duloxetine, through increasing serotonin and norepinephrine concentrations in Onuf''s nucleus, enhances glutamatergic activation of the pudendal motor nerve which innervates the external urethral sphinter.[A178663,A178666] This enhanced signaling allows for stronger contraction. Increased contraction of this sphincter increases the pressure needed to produce an incontinence episode in stress urinary incontinence. Duloxetine has been shown to improve Patient Global Impression of Improvement and Incontinence Quality of Life scores.[A178669] It has also been shown to reduce the median incontinence episode frequency at doses of 40 and 80 mg.



Action at the dorsal horn of the spinal cord allows duloxetine to strengthen the the serotonergic and adrenergic pathways involved in descending inhibition of pain.[A178666,A178705] This results in an increased threshold of activation necessary to transmit painful stimuli to the brain and effective relief of pain, particularly in neuropathic pain. Pain relief has been noted in a variety of painful conditions including diabetic peripheral neuropathy, fibromyalgia, and osteoarthritis using a range of pain assessment surveys.[A178711]



While duloxetine has been shown to be effective in both animal models of mood disorders and in clinical trials for the treatment of these disorders in humans, the broad scope of its pharmacodynamic effects on mood regulation in the brain has yet to be explained.[A178714]



Increased blood pressure is a common side effect with duloxetine due to vasoconstriction mediated by the intended increase in norepinephrine signaling.[label,T116]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21576, 'Lamito iron 20 pieces', 'لاميتو ايرون 20 قطعة', '60', NULL, 'Iron supplement', 'Iron+vitamin c+folic acid+vitamin b-complex', 'Brand international fsc > optima pharma', 'Piece', 'قطعة', NULL, '2', NULL, NULL, NULL, NULL, 740, '2022-07-10', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20516, 'Hista-top 0.67mg/5ml syrup 60ml', 'هيستا توب 0.67 مجم/5 ملي شراب 60 ملي', '5', NULL, 'Antihistamine.antiallergy', 'Clemastine', 'Kahira > top pharm', 'Syrup', 'شراب', '0.67mg', '1', NULL, 'description an ethanolamine-derivative first generation histamine h1 antagonist used in hay fever rhinitis allergic skin conditions and pruritus. indication for the relief of symptoms associated with allergic rhinitis such as sneezing rhinorrhea pruritus', NULL, NULL, 793, '2022-07-24', 'For the relief of symptoms associated with allergic rhinitis such as sneezing, rhinorrhea, pruritus and acrimation. Also for the management of mild, uncomplicated allergic skin manifestations of urticaria and angioedema. Used as self-medication for temporary relief of symptoms associated with the common cold.', 'Clemastine is a selective histamine H1 antagonist and binds to the histamine H1 receptor. This blocks the action of endogenous histamine, which subsequently leads to temporary relief of the negative symptoms brought on by histamine.', 'Clemastine is an antihistamine that also induces anticholinergic and sedative effects. Antihistamines competitively antagonize various physiological effects of histamine including increased capillary permeability and dilatation, the formation of edema, the "flare" and "itch" response, and gastrointestinal and respiratory smooth muscle constriction. Within the vascular tree, H1- receptor antagonists inhibit both the vasoconstrictor and vasodilator effects of histamine. Depending on the dose, H1- receptor antagonists can produce CNS stimulation or depression. Most antihistamines exhibit central and/or peripheral anticholinergic activity. Antihistamines act by competitively blocking H1- receptor sites. Antihistamines do not pharmacologically antagonize or chemically inactivate histamine, nor do they prevent the release of histamine.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (8109, 'Mexicam 15mg/3ml i.m. 6 amp.', 'مكسيكام 15مجم/5مل 6 امبولات', '18', '16', 'Nsaid.oxicam', 'Meloxicam', 'Pharopharma > delta pharma', 'Amp', 'أمبول', '15mg', '6', NULL, 'about meloxicam nsaid thiazole derivative oxicam class analgesic anti-inflammatory. mechanism of action of meloxicam this drug reduces the prostaglandin synthesis peripherally by inhibiting cox1 & cox2 isoenzymes. this results its antinflammatory action.', '6221163005518', NULL, 1291, '2024-04-05', 'Meloxicam is indicated for the symptomatic treatment of arthritis and osteoarthritis.  In addition, it is indicated for the pauciarticular and polyarticular course of Juvenile Rheumatoid Arthritis (JRA) in patients aged 2 years old or above.[L11398] Off-label uses include the treatment of dental or post-surgical pain. In addition to the above, meloxicam has also been studied in the treatment of neuropathic pain. [A190189]



Meloxicam, in combination with [bupivacaine], is indicated for postsurgical analgesia in adult patients for up to 72 hours following soft tissue surgical procedures, foot and ankle procedures, and other orthopedic procedures in which direct exposure to articular cartilage is avoided.[L50427]', 'Meloxicam inhibits prostaglandin synthetase (cylooxygenase 1 and 2) enzymes leading to a decreased synthesis of prostaglandins, which normally mediate painful inflammatory symptoms.[A176366] As prostaglandins sensitize neuronal pain receptors, inhibition of their synthesis leads to analgesic and inflammatory effects. Meloxicam preferentially inhibits COX-2, but also exerts some activity against COX-1, causing gastrointestinal irritation.[A190189,L11398]', 'Meloxicam is an anti-inflammatory, analgesic analgesic with antipyretic effects in fever.[A190189] Prostaglandins are substances that contribute to inflammation.[A176366] This drug also exerts preferential actions against COX-2[A190195], which may reduce the possible gastrointestinal effects of this drug.  



In humans, meloxicam has demonstrated the ability to decrease erythrocyte sedimentation rate(ESR) in patients with rheumatoid arthritis, and to decrease ESR, C-reactive protein (CRP), as well as  aquaporin-1 expression.[A190189] As with other NSAIDS, prolonged use of meloxicum can result in renal or cardiovascular impairment or thrombotic cardiovascular events.[L11398]



A note on gastrointestinal effects



As meloxicam preferentially inhibits COX-2, it is thought to cause less gastrointestinal irritation compared to other NSAIDS. Despite this, it still carries a risk of gastric inflammation, bleeding and ulceration.[A190201,L11398]   In one study, patients on meloxicam suffered from gastrointestinal symptoms at a rate of 13% compared to 19% of those on [diclofenac]. GI events were found to be less severe in the meloxicam-treated patients.[A190189]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2971, 'Concerta 18mg 30 extended release tablets', 'كونسيرتا 18مجم 30 قرص', '257', NULL, 'Attention-deficit hyperactivity disorder', 'Methylphenidate', 'Alza corporation-usa > janssen cilag', 'Tablet', 'أقراص', '18mg', '3', 'اضطراب التشتت وضعف الانتباه وفرط الحركة', NULL, NULL, NULL, 21542, '2023-03-15', 'Methylphenidate is indicated for the treatment of Attention Deficit Hyperactivity Disorder (ADHD) in patients 6 years of age and older and for the treatment of narcolepsy.', 'While its exact mechanism is unclear, methylphenidate (MPH) has been shown to act as a norepinephrine and dopamine reuptake inhibitor (NDRI), thereby increasing the presence of these neurotransmitters in the extraneuronal space and prolonging their action.[A177541] There is a dose-related effect of psychostimulants on receptor stimulation, where higher doses are shown to increase norepinephrine (NE) and dopamine (DA) efflux throughout the brain which can result in impaired cognition and locomotor-activating effects. In contrast, low doses are found to selectively activate NE and DE neurotransmission within the prefrontal cortex which is an area of the brain thought to play a prominent role in ADHD pathophysiology, thereby improving clinical efficacy and preventing side effects.[A177547] The lower doses used to treat ADHD are not associated with the locomotor-activating effects associated with higher doses and instead reduce movement, impulsivity, and increase cognitive function including sustained attention and working memory.[A177541, A177544] Methylphenidate''s beneficial effects in sustaining attention have also been shown to be mediated by alpha-1 adrenergic receptor activity.[A177550]



Clinical findings have shown that children with ADHD have an abnormality in the dopamine transporter gene (DAT1), the D4 receptor gene (DRD-4), and/or the D2 receptor gene that may be at least partly overcome by the dopaminergic effects of methylphenidate, suggesting a possible mode of action.[F4474]', 'Methylphenidate is a racemic mixture comprised of the d- and l-isomers. The d-isomer is more pharmacologically active than the l-isomer. Radioligand binding studies demonstrate that binding of methylphenidate in the brain is localized to dopamine-rich areas, in particular in the prefrontal cortex which has been demonstrated to play a prominent role in ADHD pathophysiology.[A177547] In a number of animal models, methylphenidate enhances locomotor activity and induces stereotypic behaviours. ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19638, 'Gelatexin oral pwd. 10 sachets', 'جيلاتيكسين باودر 10 اكياس', '195', '110', 'Antirheumatic.osteoarthritis.anabolic agents', 'Gelatin (collagen) hydrolysate+vitamin c', 'Pharma zad > summit pharm trading', 'Gel', 'جل', NULL, '1', NULL, 'for health joints use: 1 sachet in a glass add 100 ml of water stirr well till dissolve then drink daily for 3 months', NULL, NULL, 1436, '2024-07-29', 'Gelatin is used for weight loss and for treating osteoarthritis, rheumatoid arthritis, and brittle bones (osteoporosis). Some people also use it for strengthening bones, joints, and fingernails. Gelatin is also used for improving hair condition and to shorten the recovery after exercise and sports-related injury [L2113]. Gelatin is used in preparations of foods, cosmetics, and medicine [L2113].



Plasma volume expander in hypovolaemic shock [L2115]. Haemostatic [L2115].



Gelatin-based hydrogels are being used in drug delivery and tissue engineering because they are able to promote cell adhesion and proliferation. In addition, these hydrogels can be used as wound dressings because of their attractive fluid absorbance properties. Manufacturing technologies such as ultraviolet stereolithography and two-photon polymerization can be used to prepare structures containing photosensitive gelatin-based hydrogels [L2111].', 'It works as a hemostatic by providing a physical framework within which clotting may occur [L2115].



As a volume expander, gelatin remains in the vascular space. When used in the treatment of hypovolaemia gelatin can produce a significant increase in blood volume, cardiac output, stroke volume, blood pressure, urinary output and oxygen delivery, increasing volume and pressure [L2117].



For intravascular volume expansion, the majority or gelatins produce an effect which is almost equivalent to of which are mild, although severe reactions albumin, with a duration of action of 3 to 4 hours to have been reported [L2118].



Gelatin or collagen chains suspended in solution can be covalently cross-linked to form matrices that are able to swell in the presence of aqueous solutions, forming what are called _gelatin hydrogels_. Hydrogels, characterized by their hydrophilicity and insolubility in water, have the capability of swelling into an equilibrium volume while maintaining their shape. The chemical cross-linkers used may be either small bifunctional molecules or polyfunctional macromolecules, for example, glutaraldehyde [L2122].', 'Gelatin contains collagen, which is one of the materials that make up cartilage and bone [L2113].



In addition to their well-established value as a nutritional protein source, collagen and collagen-derived products may exhibit various potential biological activities on cells and the extracellular matrix through the corresponding food-derived peptides post-ingestion.  This could justify their applications in dietary supplements and pharmaceutical agents [A32426].



Gelatin is a protein that is used as a hemostatic in surgical procedures.  It is also used as a plasma volume expander in hypovolemic shock. Gelatin rods structures may also be used to temporarily block tear outflow in cases of dry eye [L2115].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23122, 'Mustela cleansing milk 200ml', 'موستلا غسول 200 مل', '95', NULL, 'Baby care', 'Avocado perseose+sunflower oil+sweet almond oil+maize oil', 'Laboratoires expanscience-france > biotech egypt', 'Unknown', 'غير محدد', '200ml', '1', NULL, 'specially formulated for use right from day one * mustela bebe cleansing milk is ideal for your babyï-s daily hygiene: gently and thoroughly cleanses facial and diaper area without leaving it dry. hydrates the skin and preserves the hydrolipidic film than', NULL, NULL, 594, '2022-08-17', NULL, NULL, NULL, NULL, 0, 0, 0);
