INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15270, 'Biofreeze roll-on 59ml', 'بيوفريز رول اون 59 مل', '235', '148', 'Analgesic', 'Menthol', 'Performance health inc. > eva pharma', 'Unknown', 'غير محدد', '59ml', '1', NULL, 'penetrating long lasting pain relief from: arthritis - sore muscles & joints - back pain', '731124200051', NULL, 1165, '2023-09-05', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17285, 'Depiwhite.s spf 50 - 50 ml', 'ديبيوايت اس 50 ملي', '250', NULL, 'Whitening topical', NULL, 'Acm dermatological laboratories > biotech egypt', 'Unknown', 'غير محدد', '50 ml', '1', NULL, 'indications depiwhite.s spf 50+ is an spf 50+ / uva 20 anti-brown spot sun protection skincare product designed to: - reduce brown spots caused by sun exposure - limit their reappearance - provide effective sun protection spf 50+ / uva 20 thanks to its hi', NULL, NULL, 815, '2022-08-01', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13634, 'Albustix d 16/12.5mg 30 tab.', 'البوستيكس دي 16/ 12.5مجم 30 قرص', '156', '120', 'Antihypertensive. combined angiotensin blocker with diuretic', 'Candesartan cilexetil+hydrochlorothiazide', 'Sigma > concord pharmaceutical industries', 'Tab', 'أقراص', '12.5mg', '3', NULL, NULL, '6223005630100', NULL, 4554, '2024-08-02', 'May be used as a first line agent to treat uncomplicated hypertension, isolated systolic hypertension and left ventricular hypertrophy. May be used as a first line agent to delay progression of diabetic nephropathy. Candesartan may be also used as a second line agent in the treatment of congestive heart failure, systolic dysfunction, myocardial infarction and coronary artery disease in those intolerant of ACE inhibitors.', 'Candesartan selectively blocks the binding of angiotensin II to AT1 in many tissues including vascular smooth muscle and the adrenal glands. This inhibits the AT1-mediated vasoconstrictive and aldosterone-secreting effects of angiotensin II and results in an overall decrease in blood pressure. Candesartan is greater than 10,000 times more selective for AT1 than AT2. Inhibition of aldosterone secretion may increase sodium and water excretion while decreasing potassium excretion.', 'Candesartan cilexetil is an ARB prodrug that is rapidly converted to candesartan, its active metabolite, during absorption from the gastrointestinal tract. Candesartan confers blood pressure lowering effects by antagonizing the hypertensive effects of angiotensin II via the RAAS. RAAS is a homeostatic mechanism for regulating hemodynamics, water and electrolyte balance. During sympathetic stimulation or when renal blood pressure or blood flow is reduced, renin is released from granular cells of the juxtaglomerular apparatus in the kidneys. Renin cleaves circulating angiotensinogen to angiotensin I, which is cleaved by angiotensin converting enzyme (ACE) to angiotensin II. Angiotensin II increases blood pressure by increasing total peripheral resistance, increasing sodium and water reabsorption in the kidneys via aldosterone secretion, and altering cardiovascular structure. Angiotensin II binds to two receptors: type-1 angiotensin II receptor (AT1) and type-2 angiotensin II receptor (AT2). AT1 is a G-protein coupled receptor (GPCR) that mediates the vasoconstrictive and aldosterone-secreting effects of angiotensin II. Studies performed in recent years suggest that AT2 antagonizes AT1-mediated effects and directly affects long-term blood pressure control by inducing vasorelaxation and increasing urinary sodium excretion. Angiotensin receptor blockers (ARBs) are non-peptide competitive inhibitors of AT1. ARBs block the ability of angiotensin II to stimulate pressor and cell proliferative effects. Unlike ACE inhibitors, ARBs do not affect bradykinin-induced vasodilation. The overall effect of ARBs is a decrease in blood pressure.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28408, 'Travoprost queen 0.004% eye drops 2 ml', 'ترافوبروست كوين 0.004% قطرة للعين 2مل', '75', NULL, 'Antiglaucoma', 'Travoprost', 'Sigma tec > queen pharma international', 'Drops', 'نقط', '0.004%', '1', NULL, 'about travoprost a synthetic prostaglandin f2a analogue ophthalmic antiglaucoma. mechanism of action of travoprost travoprost is a prostaglandin f2-alpha analogue and it is a prostanoid selective fp receptor agonist which reduces the intraocular pressure', NULL, NULL, 721, '2022-07-23', 'Travoprost is indicated for the reduction of elevated intraocular pressure in patients with open-angle glaucoma or ocular hypertension.[L49434, L49429, L49515] It is also used in pediatric patients aged two months to less than 18 years.[L5146] ', 'Travoprost is a prodrug. Upon administration, travoprost is absorbed through the cornea and hydrolyzed to its active metabolite, travoprost free acid. The ester moiety of the free acid allows for enhanced penetration into the aqueous humour.[A262899] While the exact mechanism of travoprost is largely unknown, it is believed to be related to its full agonist activity for the prostaglandin FP receptor.[L49434] By binding to the FP receptor, travoprost free acid increases the outflow of aqueous humour via the trabecular meshwork and uveoscleral pathways, thereby reducing the intraocular pressure.[A262899, L5146, L49434]', 'Travoprost demonstrates preferential affinity and full agonist activity for the prostaglandin FP receptor in the nanomolar range.[A262914] Travoprost shows no significant affinity for other prostanoid or non-prostanoid receptors.[A262904]



Travoprost-induced reduction of intraocular pressure is observed about two hours after administration, and the maximum effect is reached after 12 hours. Significant lowering of intraocular pressure can be maintained for periods exceeding 24 hours with a single dose.[L5146]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24663, 'Penduline plus children hair oil 120 ml', 'بنديولين بلس زيت شعر للاطفال 120 مل', '220', '139', 'Hair care', 'Argan oil+jojoba oil+almond oil+olive oil+coconut oil+castor oil+rosemary oil+garlic oil+rocket oil', 'Cosmo pack > semak for cosmetics', 'Pen', 'قلم', '120 ml', '1', NULL, 'for frizz breakage & damage how to use: (recommended to be used for 3 months) .. for dry hair: put few drops on hair tips 3 times weekly + doing hot oil treatment twice weekly. .. for moderate hair: put few drops on hair tips 2 times weekly + doing hot oi', '6223011114366', NULL, 569, '2025-09-02', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18005, 'Easyrect 100 mg 4 tabs.', 'ايزيريكت 100 مجم 4 اقراص', '39', '26', 'Tonic for men', 'Avanafil', 'Egpi > zeta pharm', 'Tab', 'أقراص', '100 mg', '1', 'مقوي للرجال', 'avanafil is a pde5 inhibitor approved for erectile dysfunction by the fda on april 27 2012 and by ema on june 21 2013. avanafil is known by the trademark names stendra and spedra and was developed by vivus inc. avanafil acts by inhibiting a specific phosp', NULL, NULL, 1410, '2024-04-11', 'Avanafil is indicated for the treatment of erectile dysfunction.[L32058]', 'Avanafil inhibits the cGMP-specific phosphodiesterase type 5 (PDE5) which is responsible for the degradation of cGMP in the corpus cavernosum located around the penis. Sexual arousal results in the local release of nitric oxide, which in turn stimulates the enzyme guanylate cyclase to produce cGMP. Elevated levels of cGMP result in local smooth muscle relaxation and increased blood flow to the penis (i.e. an erection).[L32058,L32113]



As PDE5 inhibitors like avanafil require the endogenous release of nitric oxide in order to exert their pharmacologic effect, they have no effect on the user in the absence of sexual stimulation/arousal.[L32058,L32113]', 'Avanafil is a strong competitive inhibitor of phosphodiesterase 5 (PDE5) with a demonstrated _in vitro_ IC<sub>50</sub> of 5.2 nM.[L32113] Its inhibitory effects on PDE5 are 100-fold more potent than on PDE6 and >1000-fold more potent than on other PDE enzymes,[L32058] meaning it is less likely to cause visual disturbances and cardiovascular adverse effects when compared with less selective PDE5 inhibitors such as [sildenafil] and [vardenafil].[L32113] It has a relatively quick onset of action allowing for administration as early as 15 minutes prior to sexual activity.[L32058]



PDE5 inhibitors like avanafil can cause significant drug interactions when administered alongside certain antihypertensive agents (e.g. alpha blockers, substantial amounts of alcohol).[L32058] PDE5 inhibitors have also been associated with the development of non-arteritic anterior ischemic optic neuropathy (NAION), a rare condition that typically presents as sudden loss of vision in one or both eyes and appears to be more common in patients with a "crowded" optic disc. Patients presenting with any degree of vision loss should immediately discontinue use of all PDE5 inhibitors and seek medical attention.[L32058] In some jurisdictions, a history of NAION or other degenerative retinal disorders is considered a contraindication to avanafil therapy.[L32233]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6949, 'Klacid 125mg/5 ml susp. 70 ml', 'كلاسيد 125مجم/5مل معلق 70 مل', '98', NULL, 'Antibiotic.macrolide', 'Clarithromycin', 'Kahira > abbott laboratories', 'Susp', 'معلق', '125mg', '1', 'مضاد حيوي', NULL, '8002660030047', NULL, 2302, '2023-06-28', 'An alternative medication for the treatment of acute otitis media caused by <i>H. influenzae, M. catarrhalis, or S. pneumoniae</i> in patients with a history of type I penicillin hypersensitivity. Also for the treatment of pharyngitis and tonsillitis caused by susceptible <i>Streptococcus pyogenes</i>, as well as respiratory tract infections including acute maxillary sinusitis, acute bacterial exacerbations of chronic bronchitis, mild to moderate community-acquired pneuomia, Legionnaires'' disease, and pertussis. Other indications include treatment of uncomplicated skin or skin structure infections, helicobacter pylori infection, duodenal ulcer disease, bartonella infections, early Lyme disease, and encephalitis caused by <i>Toxoplasma gondii</i> (in HIV infected patients in conjunction with pyrimethamine). Clarithromycin may also decrease the incidence of cryptosporidiosis, prevent the occurence of α-hemolytic (viridans group) streptococcal endocarditis, as well as serve as a primary prevention for <i>Mycobacterium avium</i> complex (MAC) bacteremia or disseminated infections (in adults, adolescents, and children with advanced HIV infection).



Clarithromycin is indicated in combination with [vonoprazan] and [amoxicillin] as co-packaged triple therapy to treat _Helicobacter pylori_ (_H. pylori_) infection in adults.[L41695]', 'Clarithromycin is first metabolized to 14-OH clarithromycin, which is active and works synergistically with its parent compound. Like other macrolides, it then penetrates bacteria cell wall and reversibly binds to domain V of the 23S ribosomal RNA of the 50S subunit of the bacterial ribosome, blocking translocation of aminoacyl transfer-RNA and polypeptide synthesis. Clarithromycin also inhibits the hepatic microsomal CYP3A4 isoenzyme and P-glycoprotein, an energy-dependent drug efflux pump.', 'Clarithromycin is a macrolide antibiotic whose spectrum of activity includes many gram-positive (<i>Staphylococcus aureus, S. pneumoniae, and S. pyogenes</i>) and gram-negative aerobic bacteria (<i>Haemophilus influenzae, H. parainfluenzae, and Moraxella catarrhalis</i>), many anaerobic bacteria, some mycobacteria, and some other organisms including <i>Mycoplasma, Ureaplasma, Chlamydia, Toxoplasma</i>, and <i>Borrelia</i>. Other aerobic bacteria that clarithromycin has activity against include <i>C. pneumoniae and M. pneumoniae</i>. Clarithromycin has an in-vitro activity that is similar or greater than that of erythromycin against erythromycin-susceptible organisms. Clarithromycin is usually bacteriostatic, but may be bactericidal depending on the organism and the drug concentration.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19125, 'Flexeze massage gel 100 gm', 'فليكسيز مساج جل 100 جم', '125', '79', 'Massage', 'Glucosamin+chondroitin+msm+methyl salicylat+ginger+menthol+oils(eucalyptus+mint+thym+almond+camphor)', 'Italian cosmetics company > internovo company', 'Gel', 'جل', '100 gm', '1', 'جل مساج للعضلات -- تقليل الالام العضلات والعظام -- استرخاء العضلات', NULL, NULL, NULL, 2225, '2024-06-01', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5989, 'Helozin topical lotion 120 ml', 'هيلوزين لوسيون بخاخ 120 مل', '60', '50', 'Soothing topical', 'Zinc oxide+glycerin+panthenol+vit e+menthol', 'Hi care > roventis pharma', 'Lotion', 'لوشن', '120 ml', '1', 'حالات ترطيب الجلد -- تشقق الجلد -- استعادة رطوبة الجلد', '*** indications ï- pruritis and allergic skin conditions. ï- eczema. ï- insect bites. ï- mild sunburns. ï- protection against sunlight especially in case of excessive exposure. ï- skin dryness and cracks. ï- diaper rash sweat rash & napkin rash. ***direct', '6224000201234', NULL, 2194, '2025-01-02', 'Zinc can be used for the treatment and prevention of zinc deficiency/its consequences, including stunted growth and acute diarrhea in children, and slowed wound healing. It is also utilized for boosting the immune system, treating the common cold and recurrent ear infections, as well as preventing lower respiratory tract infections [L2172].  



', '**Zinc has three primary biological roles**: _catalytic_, _structural_, and _regulatory_. The catalytic and structural role of zinc is well established, and there are various noteworthy reviews on these functions. For example, zinc is a structural constituent in numerous proteins, inclusive of growth factors, cytokines, receptors, enzymes, and transcription factors for different cellular signaling pathways. It is implicated in numerous cellular processes as a cofactor for approximately 3000 human proteins including enzymes, nuclear factors, and hormones [L2096].



Zinc promotes resistance to epithelial apoptosis through cell protection (cytoprotection) against reactive oxygen species and bacterial toxins, likely through the antioxidant activity of the cysteine-rich metallothioneins [A32419].



In HL-60 cells (promyelocytic leukemia cell line), zinc enhances the up-regulation of A20 mRNA, which, via TRAF pathway, decreases NF-kappaB activation, leading to decreased gene expression and generation of tumor necrosis factor-alpha (TNF-alpha), IL-1beta, and IL-8 [A32418].



There are several mechanisms of action of zinc on acute diarrhea. Various mechanisms are specific to the gastrointestinal system: zinc restores mucosal barrier integrity and enterocyte brush-border enzyme activity, it promotes the production of antibodies and circulating lymphocytes against intestinal pathogens, and has a direct effect on ion channels, acting as a potassium channel blocker of adenosine 3-5-cyclic monophosphate-mediated chlorine secretion. Cochrane researchers examined the evidence available up to 30 September 2016 [L2106].



Zinc deficiency in humans decreases the activity of serum _thymulin_ (a hormone of the thymus), which is necessary for the maturation of T-helper cells. T-helper 1 (Th(1)) cytokines are decreased but T-helper 2 (Th(2)) cytokines are not affected by zinc deficiency in humans [A32417].



The change of _Th(1)_ to _Th(2)_ function leads to cell-mediated immune dysfunction. Because IL-2 production (Th(1) cytokine) is decreased, this causes decreased activity of natural-killer-cell (NK cell) and T cytolytic cells, normally involved in killing viruses, bacteria, and malignant cells [A32424]. 



In humans, zinc deficiency may lead to the generation of new CD4+ T cells, produced in the thymus. In cell culture studies (HUT-78, a Th(0) human malignant lymphoblastoid cell line), as a result of zinc deficiency, nuclear factor-kappaB (NF-kappaB) activation, phosphorylation of IkappaB, and binding of NF-kappaB to DNA are decreased and this results in decreased Th(1) cytokine production [A32417].



In another study, zinc supplementation in human subjects suppressed the gene expression and production of pro-inflammatory cytokines and decreased oxidative stress markers [A32424]. In HL-60 cells (a human pro-myelocytic leukemia cell line), zinc deficiency increased the levels of TNF-alpha, IL-1beta, and IL-8 cytokines and mRNA. In such cells, zinc was found to induce A20, a zinc finger protein that inhibited NF-kappaB activation by the tumor necrosis factor receptor-associated factor pathway. This process decreased gene expression of pro-inflammatory cytokines and oxidative stress markers [A32417].



The exact mechanism of zinc in acne treatment is poorly understood. However, zinc is considered to act directly on microbial inflammatory equilibrium and facilitate antibiotic absorption when used in combination with other agents. Topical zinc alone as well as in combination with other agents may be efficacious because of its anti-inflammatory activity and ability to reduce P. acnes bacteria by the inhibition of P. acnes lipases and free fatty acid levels [L2102].', 'Zinc is involved in various aspects of cellular metabolism. It has been estimated that approximately 10% of human proteins may bind zinc, in addition to hundreds of proteins that transport and traffic zinc. It is required for the catalytic activity of more than 200 enzymes, and it plays a role in immune function wound healing, protein synthesis, DNA synthesis, and cell division. Zinc is an essential element for a proper sense of taste and smell and supports normal growth and development during pregnancy, childhood, and adolescence. It is thought to have antioxidant properties, which may be protective against accelerated aging and helps to speed up the healing process after an injury; however, studies differ as to its effectiveness. Zinc ions are effective antimicrobial agents even if administered in low concentrations [L2211].



Studies on oral zinc for specific conditions shows the following evidence in various conditions [L2095]:



**Colds:** Evidence suggests that if zinc lozenges or syrup are taken within 24 hours after cold symptoms start, the supplement may shorten the length of colds. The use intranasal zinc has been associated with the loss of the sense of smell, in some cases long-term or permanently [L2095].



**Wound healing:** Patients with skin ulcers and decreased levels of zinc may benefit from oral zinc supplements [L2095]. 



**Diahrrea**: Oral zinc supplements can reduce the symptoms of diarrhea in children with low levels of zinc, especially in cases of malnutrition [L2095].

', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28090, 'Tetraspan 6% sol. for iv inf. 500 ml', 'تيتراسبان 6% محلول 500مل', '152', NULL, 'Plasma expander', 'Poly (0-2 hydroxyethyl) starch', 'B-braun medical ag-switerland > sunny medical', 'Sol', 'محلول', '6%', '1', NULL, 'active substance(s): calcium chloride dihydrate / magnesium chloride hexahydrate / malic acid / poly (0-2-hydroxyethyl) starch / potassium chloride / sodium acetate trihydrate / sodium chloride tetraspan 6% is an infusion solution which is administered vi', NULL, NULL, 696, '2022-08-04', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7782, 'Matujac 30mg 3 f.c. tabs.', 'ماتيوجاك 30مجم 3 قرص', '21', '21', 'Delay action', 'Dapoxetine', 'Hochster pharmaceutical industries > novell pharma', 'Tab', 'أقراص', '30mg', '3', 'للتأخير للرجال', NULL, NULL, NULL, 1759, '2022-09-28', 'For the treatment of premature ejaculation.', 'The drug''s mechanism of action is thought to be related to inhibition of neuronal reuptake of serotonin and subsequent potentiation of serotonin activity. The central ejaculatory neural circuit comprises spinal and cerebral areas that form a highly interconnected network. The sympathetic, parasympathetic, and somatic spinal centers, under the influence of sensory genital and cerebral stimuli integrated and processed at the spinal cord level, act in synergy to command physiologic events occurring during ejaculation. Experimental evidence indicates that serotonin (5-HT), throughout brain descending pathways, exerts an inhibitory role on ejaculation. To date, three 5-HT receptor subtypes (5-HT(1A), 5-HT(1B), and 5-HT(2C)) have been postulated to mediate 5-HT''s modulating activity on ejaculation.', 'Dapoxetine is a selective serotonin reuptake inhibitor currently undergoing trials through Alza (under license from GenuPro, a collaboration between Eli Lilly and PPD). Dapoxetine is a short-acting SSRI drug currently being considered for approval by the Food and Drug Administration (FDA) for the treatment of premature ejaculation in men, which would make it the first drug approved for such treatment. Despite two clinical trials finished in 2006, experts doubt it will be approved by the FDA soon because SSRIs come with undesirable side-effects after long-term use, such as psychiatric problems, dermatological reactions, increase in body weight, lower sex-drive, nausea, headache, upset stomach and weakness, thus not significantly outweighing the benefit of premature ejaculation medication versus the risks. By contrast with SSRIs approved for depression, which take 2 weeks or longer to reach steady-state concentration, dapoxetine has a unique pharmacokinetic profile, with a short time to maximum serum concentration (about 1 h) and rapid elimination (initial half-life of 1-2 h).', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4260, 'Acti 20 mg 20 tabs', 'اكتي 20مجم 20 قرص', '106', NULL, NULL, 'Vonoprazan', 'Mash premiere', 'Tab', 'أقراص', '20 mg', '2', NULL, NULL, NULL, NULL, 1562, '2025-06-16', 'Vonoprazan is indicated for the following conditions: 



- for healing of all grades of erosive esophagitis and relief of heartburn associated with erosive esophagitis in adults.[L51224]

- to maintain healing of all grades of erosive esophagitis and relief of heartburn associated with erosive esophagitis in adults.[L51224]

- for the relief of heartburn associated with non-erosive gastroesophageal reflux disease in adults.[L51224]

- in combination with [amoxicillin] and [clarithromycin] for the treatment of Helicobacter pylori (_H. pylori_) infection in adults.[L41695, L51224]

- in combination with amoxicillin for the treatment of H. pylori infection in adults.[L51224]', 'Vonoprazan is a potassium-competitive acid blocker (PCAB) that inhibits the H<sup>+</sup>, K<sup>+</sup>-ATPase enzyme system in a potassium-competitive manner. Through this mechanism, vonoprazan suppresses basal and stimulated gastric acid secretion at the secretory surface of gastric parietal cells.[L41695] 



Although both classes of drugs inhibit the H<sup>+</sup>, K<sup>+</sup>-ATPase, the mechanism of action of PCABs differs from that of proton-pump inhibitors (PPIs). PPIs form a covalent disulphide bond with a cysteine residue on the H<sup>+</sup>, K<sup>+</sup>-ATPase, which leads to the inactivation of the enzyme, while PCABs interfere with the binding of K<sup>+</sup> to the H<sup>+</sup>, K<sup>+</sup>-ATPase.[A253702]', 'The use of vonoprazan leads to an increase in intragastric pH. The inhibitory effect of vonoprazan on acid secretion increases with repeated daily dosing. Although the antisecretory effect of vonoprazan decreases after drug discontinuation, intragastric pH remains elevated for 24 to 48 hours. Vonoprazan does not have a clinically significant effect on QT prolongation.[L41695] 



Compared to other potassium-competitive acid blockers (PCABs), vonoprazan has a higher point-positive charge (pKa of 9.06). This allows vonoprazan to accumulate at higher concentrations in the canalicular space of the gastric parietal cells, where it binds H<sup>+</sup>, K<sup>+</sup>-ATPase in a  K<sup>+</sup>-competitive and reversible manner. Compared to other PCABs, such as SCH28080, or proton-pump inhibitors, such as lansoprazole, vonoprazan has a more potent H<sup>+</sup>, K<sup>+</sup>-ATPase inhibitory activity.[A253702]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25929, 'Resto-guide gel 50 ml', 'ريستو جايد جل 50 مل', '45', NULL, 'Orthopedic pain relief', 'Camphor+menthol+peppermint+eucalyptus', 'El helou for cosmetics > nutri', 'Gel', 'جل', '50 ml', '1', 'مسكن لآلام العضلات', NULL, NULL, NULL, 905, '2022-05-13', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16484, 'Clearaderm skin cleanser 120 ml', 'كليراديرم منظف ​​البشرة 120 مل', '165', '50', 'Skin care', NULL, 'Hi-care > pixel pharmaceutical company', 'Cleanser', 'منظف', '120 ml', '1', NULL, 'skin cleanser', '6224001661020', NULL, 981, '2024-02-04', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18459, 'Epirubicin mylan 200mg/100ml vial i.v. inf.', 'ايبريوبسين ميلان 200مجم/100مل مل فيال', '1,260', NULL, 'Antineoplastic', 'Epirubicin', 'Mylan s.a.s-france > ramco', 'Vial', 'فيال', '200mg', '1', NULL, 'about epirubicin anthracycline anibiotic antineoplastic. mechanism of action of epirubicin this anthracycline drug intercalates dna strands which results complex formation it will inhibit dna & rna synthesis. it triggers dna cleavage by inhibiting topoiso', NULL, NULL, 850, '2022-07-27', 'For use as a component of adjuvant therapy in patients with evidence of axillary node tumor involvement following resection of primary breast cancer.', 'Epirubicin has antimitotic and cytotoxic activity. It inhibits nucleic acid (DNA and RNA) and protein synthesis through a number of proposed mechanisms of action: Epirubicin forms complexes with DNA by intercalation between base pairs, and it inhibits topoisomerase II activity by stabilizing the DNA-topoisomerase II complex, preventing the religation portion of the ligation-religation reaction that topoisomerase II catalyzes. It also interferes with DNA replication and transcription by inhibiting DNA helicase activity.', 'Epirubicin is an antineoplastic in the anthracycline class. General properties of drugs in this class include: interaction with DNA in a variety of different ways including intercalation (squeezing between the base pairs), DNA strand breakage and inhibition with the enzyme topoisomerase II. Most of these compounds have been isolated from natural sources and antibiotics. However, they lack the specificity of the antimicrobial antibiotics and thus produce significant toxicity. The anthracyclines are among the most important antitumor drugs available. Doxorubicin is widely used for the treatment of several solid tumors while daunorubicin and idarubicin are used exclusively for the treatment of leukemia. Epirubicin may also inhibit polymerase activity, affect regulation of gene expression, and produce free radical damage to DNA. Epirubicin possesses an antitumor effect against a wide spectrum of tumors, either grafted or spontaneous. The anthracyclines are cell cycle-nonspecific.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16732, 'Conjyclear forte 0.2% eye drops 5 ml', 'كونجيكلير فورت 0.2% قطرة عين 5 مل', '40', NULL, 'Ophthalmic antihistaminic', 'Olopatadine', 'Orchidia pharmaceutical industries', 'Drops', 'نقط', '0.2%', '1', NULL, NULL, '6224000219758', NULL, 1162, '2022-07-27', 'Olopatadine is indicated for the symptomatic treatment of ocular itching associated with allergic conjunctivitis as ophthalmic solution.[L6781]



As a nasal spray, as a monotherapy or in combination with [mometasone furoate], olopatadine is indicated for the symptomatic relief of seasonal allergic rhinitis in patients 12 years of age and older.[L6784,L39845]', 'Histamine is a biogenic vasoactive amine that binds to its receptors, which are G-protein coupled receptors. Signaling through the histamine H1 receptor is thought to primarily promote the activation of inflammatory reactions, such as allergy, asthma, and autoimmune diseases.[A179731] H1 receptor signaling activates the intracellular transcription factors, such as IP3, PLC, PKC, DAG, and intracellular calcium ions, which all work to activate further downstream cascades. Activated downstream cascades lead to the production of cytokines, the release of mast cell inflammatory mediators, synthesis of prostacyclins, activation of platelet factor, as well as the synthesis of nitric oxide, arachidonic acid, and thromboxane, which all contribute to inflammatory reactions.[A179731] 



Olopatadine is an anti-allergic molecule that works via several mechanisms. As a mast cell stabilizer, it stabilizes rodent basophils and human conjunctival mast cells and inhibits the immunologically-stimulated release of histamine.[A179704] Olopatadine acts as an antagonist at the histamine H1 receptors with high selectivity, which is explained by a unique receptor binding pocket that consists of the aspartate residue in the third transmembrane helix and other sites in the H1 receptor.[A1170] Upon binding, olopatadine blocks the H1 receptor signaling pathway, inhibiting the release of inflammatory mediators, such as tryptase, prostaglandin D2, TNF-alpha, as well as pro-inflammatory cytokines.[L6790] It also decreases chemotaxis and inhibits eosinophil activation.[L6781] _In vitro_, olopatadine was shown to inhibit epithelial cell intercellular adhesion molecule-1 (ICAM-1), which promotes the recruitment of migrating pro-inflammatory mediators.[A179704]', 'Inflammatory reactions in response to various stimuli are mediated by endogenous mediators and other pro-inflammatory factors. Histamine receptor activation and mast cell degranulation are primary mechanisms that cause inflammatory reactions such as ocular itching, hyperemia, chemosis, eyelid swelling, and tearing of seasonal allergic conjunctivitis.[A179704] Olopatadine is an anti-allergenic molecule and mast cell stabilizer that inhibits the _in vivo_ type 1 immediate hypersensitivity reaction.[L6790] By blocking the effects of histamine, olopatadine works to reduce the symptoms of allergies and inflammation at various sites of administration, including the eyes and nose. It has shown to exert antihistaminic effects in isolated tissues, animal models, and humans.[L6784] Olopatadine also demonstrated dose-dependent inhibition of immunologically-stimulated release of histamine from rat basophilic leukemia cells and human conjunctival mast cells _in vitro_.[A179704] Olopatadine has a relatively rapid onset of action and prolonged duration, where it was shown to mediate anti-histaminic effects at 5 minutes to 24 hours post-administration.[A179704]



While olopatadine is a non-sedating antihistamine agent, there have been reports of somnolence in some patients taking nasal olopatadine during clinical trials.[L6784] Temporary blurred vision or other visual disturbances were observed following ophthalmic administration. Olopatadine has negligible effects on alpha-adrenergic, dopamine, muscarinic type 1 and 2, and serotonin receptors.[L6790] In clinical trials, there was no evidence of any effect of olopatadine on QT prolongation was observed following intranasal administration.[L6784]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24218, 'Orangedaily eye complex 15 ml', 'اورانجدايلي كومبليكس للعين 15مل', '389', NULL, 'Eye contour', NULL, 'Ampac usa inc. > spring', 'Unknown', 'غير محدد', '15 ml', '1', NULL, 'orangedaily has combined the powerful antioxidant vitamin c with cellular tissue nutrients to create a high performance anti-aging and firming eye therapy product.our specialized formula contains liposomes to maximize absorption of cellular tissue nutrien', NULL, NULL, 667, '2022-07-25', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26448, 'Safolica hair serum 120 ml', 'سافوليكا سيروم للشعر 120مل', '75', NULL, 'Hair care', 'Caffiec acid+keratin+jojoba oil+olive oil+almond oil+aloe vera+panthenol+vitamin e+salicylic acid+tea tree oil', 'Egyptian co. for cosmetics > docare pharm', 'Serum', 'سيروم', '120 ml', '1', NULL, 'indications : -hair damage breakage and split ends. -hair loss of various causes. -roughness of hair and scalp. -brittle and dry hair. -weak hair growth rate. -stressed hair due to environmental & hereditary factors colorants & chemicals. -hair dandruff &', NULL, NULL, 449, '2022-08-05', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24389, 'Jacomance 60 mg 3 f.c. tabs.', 'جاكو مانس 60 مجم 3 قرص', '53', NULL, 'Premature ejaculation', 'Dapoxetine', 'HP PHARMA', 'Tab', 'أقراص', '60 mg', '1', NULL, NULL, NULL, NULL, 940, '2022-10-30', 'For the treatment of premature ejaculation.', 'The drug''s mechanism of action is thought to be related to inhibition of neuronal reuptake of serotonin and subsequent potentiation of serotonin activity. The central ejaculatory neural circuit comprises spinal and cerebral areas that form a highly interconnected network. The sympathetic, parasympathetic, and somatic spinal centers, under the influence of sensory genital and cerebral stimuli integrated and processed at the spinal cord level, act in synergy to command physiologic events occurring during ejaculation. Experimental evidence indicates that serotonin (5-HT), throughout brain descending pathways, exerts an inhibitory role on ejaculation. To date, three 5-HT receptor subtypes (5-HT(1A), 5-HT(1B), and 5-HT(2C)) have been postulated to mediate 5-HT''s modulating activity on ejaculation.', 'Dapoxetine is a selective serotonin reuptake inhibitor currently undergoing trials through Alza (under license from GenuPro, a collaboration between Eli Lilly and PPD). Dapoxetine is a short-acting SSRI drug currently being considered for approval by the Food and Drug Administration (FDA) for the treatment of premature ejaculation in men, which would make it the first drug approved for such treatment. Despite two clinical trials finished in 2006, experts doubt it will be approved by the FDA soon because SSRIs come with undesirable side-effects after long-term use, such as psychiatric problems, dermatological reactions, increase in body weight, lower sex-drive, nausea, headache, upset stomach and weakness, thus not significantly outweighing the benefit of premature ejaculation medication versus the risks. By contrast with SSRIs approved for depression, which take 2 weeks or longer to reach steady-state concentration, dapoxetine has a unique pharmacokinetic profile, with a short time to maximum serum concentration (about 1 h) and rapid elimination (initial half-life of 1-2 h).', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20921, 'Invokana 300 mg 30 f.c. tabs.', 'انفوكانا 300 مجم 30 قرص', '505', NULL, 'Anti-diabetic', 'Canagliflozin', 'Janssen cilag > soficopharm', 'Tab', 'أقراص', '300 mg', '3', NULL, 'canagliflozin is an oral diabetes medicine that helps control blood sugar levels. canagliflozin works by helping the kidneys get rid of glucose from your bloodstream. canagliflozin is used together with diet and exercise to treat type 2 diabetes. canaglif', NULL, NULL, 1610, '2022-07-24', 'This drug is used in conjunction with diet and exercise to increase glycemic control in adults diagnosed with type 2 diabetes mellitus [FDA label].



Another indication for canagliflozin is the prevention of major cardiovascular events (myocardial infarction, stroke, or death due to a cardiovascular cause) in patients with type 2 diabetes, as well as hospitalization for heart failure in patients with type 2 diabetes[L5897,[L8917].



In addition to the above, canagliflozin can be used to lower the risk of end-stage kidney disease and major increases in serum creatinine and cardiovascular death for patients with a combination of type 2 diabetes mellitus, diabetic nephropathy, and albuminuria.[L8917]



It is important to note that this drug is **not** indicated for the treatment of type 1 diabetes mellitus or diabetic ketoacidosis [FDA label]. ', 'The sodium-glucose co-transporter2 (SGLT2), is found in the proximal tubules of the kidney, and reabsorbs filtered glucose from the renal tubular lumen. Canagliflozin inhibits the SGLT2 co-transporter. This inhibition leads to lower reabsorption of filtered glucose into the body and decreases the renal threshold for glucose (RTG), leading to increased glucose excretion in the urine [FDA label]. ', 'This drug increases urinary glucose excretion and decreases the renal threshold for glucose (RTG) in a dose-dependent manner [FDA label]. The renal threshold is defined as the lowest level of blood glucose associated with the appearance of detectable glucose in the urine [A176969, T545].  The end result of canagliflozin administration is increased urinary excretion of glucose and less renal absorption of glucose, decreasing glucose concentration in the blood and improving glycemic control. 



**A note on type 2 diabetes and cardiovascular disease**



The risk of cardiovascular events in diabetes type 2 is increased due to the damaging effects of diabetes on blood vessels and nerves in the cardiovascular system.  In particular, there is a tendency for hyperglycemia to create pro-atherogenic (plaque forming) lesions in blood vessels, leading to various fatal and non-fatal events including stroke and myocardial infarction [A177086, L5933].  Long-term glycemic control has been proven to be effective in the prevention of cardiovascular events such as myocardial infarction and stroke in patients with type 2 diabetes [A177095].

', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25194, 'Pressgoma 0.5% eye drops 5 ml', 'بريسجزما 0.5% قطرة للعين 5مل', '8', NULL, 'Antihypertensive.beta blocker.selective b1', 'Betaxolol', 'Alexandria', 'Drops', 'نقط', '0.5%', '1', NULL, 'about betaxolol cardioselective beta 1-adrenergic blocker anti glaucoma antihypertensive. mechanism of action of betaxolol betaxolol is a selective beta 1 (? 1) blocker. it inhibits the beta receptors present in the eye such as in the ciliary body epithel', NULL, NULL, 1046, '2022-07-20', 'For the management of hypertension.', 'Betaxolol selectively blocks catecholamine stimulation of beta(1)-adrenergic receptors in the heart and vascular smooth muscle. This results in a reduction of heart rate, cardiac output, systolic and diastolic blood pressure, and possibly reflex orthostatic hypotension. Betaxolol can also competitively block beta(2)-adrenergic responses in the bronchial and vascular smooth muscles, causing bronchospasm.', 'Betaxolol is a competitive, beta(1)-selective (cardioselective) adrenergic antagonist. Betaxolol is used to treat hypertension, arrhythmias, coronary heart disease, glaucoma, and is also used to reduce non-fatal cardiac events in patients with heart failure. Activation of beta(1)-receptors (located mainly in the heart) by epinephrine increases the heart rate and the blood pressure, and the heart consumes more oxygen. Drugs such as betaxolol that block these receptors therefore have the reverse effect: they lower the heart rate and blood pressure and hence are used in conditions when the heart itself is deprived of oxygen. They are routinely prescribed in patients with ischemic heart disease. In addition, beta(1)-selective blockers prevent the release of renin, which is a hormone produced by the kidneys which leads to constriction of blood vessels. Betaxolol is lipophilic and exhibits no intrinsic sympathomimetic activity (ISA) or membrane stabilizing activity.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2922, 'Colorelax 135mg 20 f.c.tab.', 'كلوريلاكس 135 مجم 10 اقراص', '14', NULL, 'Antimuscarinic.irritable bowel syndrome', 'Mebeverine', 'Hi-pharm', 'Tab', 'أقراص', '135mg', '2', NULL, 'about mebeverine reserpine derivative antimuscarinic a musculotropic antispasmodic motility stimulant. mechanism of action of mebeverine mebeverine has a direct relaxant effect on smooth muscle. this will inhibits gastrointestinal propulsive motility and', NULL, NULL, 1024, '2022-09-23', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31079, 'Parkimedine 50 mg 10 f.c. tabs.', 'باركيميدين 50مجم 10 قرص', '592', '369', 'Parkinsons disease.mao-b inhibitor', 'Safinamide', 'Eva pharma', 'Tab', 'أقراص', '50 mg', '1', NULL, 'mechanism of action the precise mechanism by which safinamide exerts its effect in pd is unknown inhibition of mao-b activity by blocking the catabolism of dopamine is thought to result in an increase in dopamine levels and a subsequent increase in dopami', NULL, NULL, 944, '2025-05-14', 'Safinamide is indicated as an add-on treatment to levodopa with or without other medicines for Parkinson’s disease', 'Safinamide is a unique molecule with multiple mechanisms of action and a very high therapeutic index. It combines potent, selective, and reversible inhibition of MAO-B with blockade of voltage-dependent Na+ and Ca2+ channels and inhibition of glutamate release. Safinamide has neuroprotective and neurorescuing effects in MPTP-treated mice, in the rat kainic acid, and in the gerbil ischemia model.', NULL, 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1544, 'Viridio treatment 120 ml hair lotion', 'فيريدو تريتمنت لوشن للشعر 120 مل', '255', NULL, 'Anti hair loss lotion', 'Procapil+trichogen veg ls 9922+caffeine+saw palmetto extract+hydrolyzed keratin+biotin', 'Yami pharmaceutical', 'Lotion', 'لوشن', '120 ml', '1', NULL, NULL, NULL, NULL, 874, '2022-09-07', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16443, 'Clarithro 250mg 14 f.c.tab', 'كلاريثرو 250مجم 14 قرص', '104', '72', 'Antibiotic.macrolide', 'Clarithromycin', 'Amriya > pharco b', 'Tab', 'أقراص', '250mg', '2', NULL, NULL, '6222012236107', NULL, 951, '2025-11-23', 'An alternative medication for the treatment of acute otitis media caused by <i>H. influenzae, M. catarrhalis, or S. pneumoniae</i> in patients with a history of type I penicillin hypersensitivity. Also for the treatment of pharyngitis and tonsillitis caused by susceptible <i>Streptococcus pyogenes</i>, as well as respiratory tract infections including acute maxillary sinusitis, acute bacterial exacerbations of chronic bronchitis, mild to moderate community-acquired pneuomia, Legionnaires'' disease, and pertussis. Other indications include treatment of uncomplicated skin or skin structure infections, helicobacter pylori infection, duodenal ulcer disease, bartonella infections, early Lyme disease, and encephalitis caused by <i>Toxoplasma gondii</i> (in HIV infected patients in conjunction with pyrimethamine). Clarithromycin may also decrease the incidence of cryptosporidiosis, prevent the occurence of α-hemolytic (viridans group) streptococcal endocarditis, as well as serve as a primary prevention for <i>Mycobacterium avium</i> complex (MAC) bacteremia or disseminated infections (in adults, adolescents, and children with advanced HIV infection).



Clarithromycin is indicated in combination with [vonoprazan] and [amoxicillin] as co-packaged triple therapy to treat _Helicobacter pylori_ (_H. pylori_) infection in adults.[L41695]', 'Clarithromycin is first metabolized to 14-OH clarithromycin, which is active and works synergistically with its parent compound. Like other macrolides, it then penetrates bacteria cell wall and reversibly binds to domain V of the 23S ribosomal RNA of the 50S subunit of the bacterial ribosome, blocking translocation of aminoacyl transfer-RNA and polypeptide synthesis. Clarithromycin also inhibits the hepatic microsomal CYP3A4 isoenzyme and P-glycoprotein, an energy-dependent drug efflux pump.', 'Clarithromycin is a macrolide antibiotic whose spectrum of activity includes many gram-positive (<i>Staphylococcus aureus, S. pneumoniae, and S. pyogenes</i>) and gram-negative aerobic bacteria (<i>Haemophilus influenzae, H. parainfluenzae, and Moraxella catarrhalis</i>), many anaerobic bacteria, some mycobacteria, and some other organisms including <i>Mycoplasma, Ureaplasma, Chlamydia, Toxoplasma</i>, and <i>Borrelia</i>. Other aerobic bacteria that clarithromycin has activity against include <i>C. pneumoniae and M. pneumoniae</i>. Clarithromycin has an in-vitro activity that is similar or greater than that of erythromycin against erythromycin-susceptible organisms. Clarithromycin is usually bacteriostatic, but may be bactericidal depending on the organism and the drug concentration.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18992, 'Ferroduonal 30 cap', 'فيروديونال 30 كبسولة', '135', '96', 'Anemia.iron supplement', 'Ferrous (ii)-glycine sulphate complex', 'Mina pharm', 'Cap', 'كبسولة', NULL, '3', 'مكمل غذائي بالحديد لعلاج الانيميا', NULL, '6222003702888', NULL, 38690, '2025-06-24', 'Ferrous sulfate is used for the prevention and treatment of iron deficiency anemia in adults and children.[A190804,L2240,L11800]

', 'Iron is required to maintain optimal health, particularly for helping to form red blood cells (RBC) that carry oxygen around the body. A deficiency in iron indicates that the body cannot produce enough normal red blood cells.[A32514,L11800] Iron deficiency anemia occurs when body stores of iron decrease to very low levels, and the stored iron is insufficient to support normal red blood cell (RBC) production. Insufficient dietary iron, impaired iron absorption, bleeding, pregnancy, or loss of iron through the urine can lead to iron deficiency.[A32514,L11794]  Symptoms of iron deficiency anemia include fatigue, breathlessness, palpitations, dizziness, and headache.



Taking iron in supplement form, such as ferrous sulfate, allows for more rapid increases in iron levels when dietary supply and stores are not sufficient.[L2175] Iron is transported by the divalent metal transporter 1 (DMT1) across the endolysosomal membrane to enter the macrophage. It can then can be incorporated into ferritin and be stored in the macrophage or carried of the macrophage by ferroportin. This exported iron is  oxidized by the enzyme to ceruloplasmin to Fe3+, followed by sequestration by transferrin for transport in the serum to various sites, including the bone marrow for hemoglobin synthesis or into the liver.[A32524] Iron combines with porphyrin and globin chains to form hemoglobin, which is critical for oxygen delivery from the lungs to other tissues.[L2263]', 'Ferrous sulfate replenishes iron, an essential component in hemoglobin, myoglobin, and various enzymes. It replaces the iron that is usually found in hemoglobin and myoglobin. Iron participates in oxygen transport and storage, electron transport and energy metabolism, antioxidant and beneficial pro-oxidant functions, oxygen sensing, tissue proliferation and growth, as well as DNA replication and repair.[A32524,A32514]



', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7777, 'Adwipalm tab', 'ادويبالم اقراص', '240', NULL, 'Anti inflammatory and analgesic', 'Palmitoylethanolamide', 'Adwitac pharma', 'Tab', 'أقراص', NULL, '1', NULL, NULL, NULL, NULL, 827, '2025-10-24', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2650, 'Cipromax 750mg 10 f.c.tab.', 'سيبروماكس 750 مجم 10 قرص', '60', NULL, 'Antibiotic.quinolone', 'Ciprofloxacin', 'Spimaco > eimc', 'Tab', 'أقراص', '750mg', '1', 'مضاد حيوي', NULL, '6281086011607', NULL, 1157, '2022-12-03', 'Ciprofloxacin is only indicated in infections caused by susceptible bacteria.[L6469,L6472,L6475,L6478,L6481,L6484,L6487,L6490,L6493]



Ciprofloxacin immediate release tablets, oral suspensions, and intravenous injections are indicated for the treatment of skin and skin structure infections, bone and joint infections, complicated intra-abdominal infections, nosocomial pneumonia, febrile neutropenia, adults who have inhaled anthrax, plague, chronic bacterial prostatitis, lower respiratory tract infections including acute exacerbations of chronic bronchitis, urinary tract infections, complicated urinary tract infections in pediatrics, complicated pyelonephritis in pediatrics, and acute sinusitis.[L6481,L6478]



A ciprofloxacin otic solution and otic suspension with hydrocortisone are indicated for acute otitis externa.[L6469,L6484] Ciprofloxacin suspension with dexamethasone is indicated for acute otitis media in pediatric patients with tympanostomy tubes or acute otitis externa.[L6490] A ciprofloxacin intratympanic injection is indicated for pediatric patients with bilateral otitis media with effusion who are having tympanostomy tubes placed or pediatric patients 6 months or older with acute otitis externa.[L6493]



A ciprofloxacin eye drop is indicated for bacterial corneal ulcers and conjunctivitis.[L6472] A ciprofloxacin eye ointment is indicated for bacterial conjunctivitis.[L6475]



A ciprofloxacin extended release tablet is indicated for uncomplicated urinary tract infections, complicated urinary tract infections, and acute uncomplicated pyelonephritis.[L6487]', 'Ciprofloxacin acts on bacterial topoisomerase II (DNA gyrase) and topoisomerase IV.[A178885] Ciprofloxacin''s targeting of the alpha subunits of DNA gyrase prevents it from supercoiling the bacterial DNA which prevents DNA replication.[A178876,A178882]', 'Ciprofloxacin is a second generation fluoroquinolone that is active against many Gram negative and Gram positive bacteria.[A178870,L6469,L6472,L6475,L6478,L6481,L6484,L6487,L6490,L6493] It produces its action through inhibition of bacterial DNA gyrase and topoisomerase IV.[A178885] Ciprofloxacin binds to bacterial DNA gyrase with 100 times the affinity of mammalian DNA gyrase.[A178894] There is no cross resistance between fluoroquinolones and other classes of antibiotics, so it may be of clinical value when other antibiotics are no longer effective.[A178894] Ciprofloxain and its derivatives are also being investigated for its action against malaria, cancers, and AIDS.[A178885]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6711, 'Femoston 1/10mg 28 f.c.tabs.', 'فيموستون 1/10مجم 28 قرص', '275', NULL, 'Female sex hormones', 'Estradiol+dydrogesterone', 'Abbott laboratories', 'Tab', 'أقراص', '10mg', '1', NULL, NULL, NULL, NULL, 899, '2025-08-12', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12907, 'Zocozet 10/20mg 14 f.c. tab.', 'زوكوزيت 10/20 مجم 14 قرص', '90', '63', 'Antihyperlipidemic.cholesterol absorption inhibitors.combined with statin', 'Ezetimibe+simvastatin', 'Marcyrl co. > geopharma-egypt', 'Tab', 'أقراص', '20mg', '2', 'علاج ارتفاع مستوى الكوليسترول في الدم', NULL, '6223003570910', NULL, 1622, '2024-10-01', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19844, 'Glimitoid 1 mg 30 tabs.', 'جليميتويد 1 مجم 30 قرص', '14', NULL, 'Anti-diabetic.secretagogues.sulfonylurea', 'Glimepiride', 'Eva pharma', 'Tab', 'أقراص', '1 mg', '3', NULL, 'indications : this medication is a sulfonylurea antidiabetic agent prescribed for type 2 diabetes. it works by causing the pancreas to produce insulin dosage : the recommended starting dose is 1-2 mg once daily with food.maximum: 2mg/day contraindications', '6223002453108', NULL, 807, '2022-07-30', 'Glimepiride is indicated for the management of type 2 diabetes in adults as an adjunct to diet and exercise to improve glycemic control as monotherapy. 



It may also be indicated for use in combination with metformin or insulin to lower blood glucose in patients with type 2 diabetes whose high blood sugar levels cannot be controlled by diet and exercise in conjunction with an oral hypoglycemic (a drug used to lower blood sugar levels) agent alone.[L10322]', 'ATP-sensitive potassium channels on pancreatic beta cells that are gated by intracellular ATP and ADP. The hetero-octomeric complex of the channel is composed of four pore-forming Kir6.2 subunits and four regulatory sulfonylurea receptor (SUR) subunits. Alternative splicing allows the formation of channels composed of varying subunit isoforms expressed at different concentrations in different tissues.[A177730] In pancreatic beta cells, ATP-sensitive potassium channels play a role as essential metabolic sensors and regulators that couple membrane excitability with glucose-stimulated insulin secretion (GSIS).[A177727] When there is a decrease in the ATP:ADP ratio, the channels are activated and open, leading to K+ efflux from the cell, membrane hyperpolarization, and suppression of insulin secretion.[A177727] In contrast, increased uptake of glucose into the cell leads to elevated intracellular ATP:ADP ratio, leading to the closure of channels and membrane depolarization. Depolarization leads to activation and opening of the voltage-dependent Ca2+ channels and consequently an influx of calcium ions into the cell.[A177727] Elevated intracellular calcium levels causes the contraction of the filaments of actomyosin responsible for the exocytosis of insulin granules stored in vesicles.[A177715] Glimepiride blocks the ATP-sensitive potassium channel by binding non-specifically to the B sites of both sulfonylurea receptor-1 (SUR1) and sulfonylurea receptor-2A (SUR2A) subunits as well as the A site of SUR1 subunit of the channel to promote insulin secretion from the beta cell.[A177715] ', 'Glimepiride stimulates the secretion of insulin granules from the pancreatic beta cells and improves the sensitivity of peripheral tissues to insulin to increase peripheral glucose uptake, thus reducing plasma blood glucose levels and glycated hemoglobin (HbA1C) levels. A multi-center, randomized, placebo-controlled clinical trial evaluated the efficacy of glimepiride (1–8 mg) as monotherapy titrated over 10 weeks compared with placebo in T2DM subjects who were not controlled by diet alone.[A177703] In this study, there was a reduction in fasting plasma glucose (FPG) by 46 mg/dL, post-prandial glucose (PPG) by 72 mg/dL, and HbA1c by 1.4% more than the placebo.[A177703] In another randomized study comprising of patients with T2DM receiving either placebo or one of the three doses (1, 4, or 8 mg) of glimepiride during a 14-week study period, all glimepiride regimens significantly reduced FPG, PPG, and HbA1c values (P < 0.001) compared to placebo by the end of the study period.[A177703] The 4- and 8-mg doses of glimepiride were more effective than the 1-mg dose; however, the 4-mg dose provided a nearly maximal antihyperglycemic effect.[A177703]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18701, 'Exelon 6mg 28 caps.', 'اكسيلون 6 مجم 28 كبسولة', '350', '210', 'Anticholinesterase', 'Rivastigmine', 'Novartis', 'Cap', 'كبسولة', '6mg', '2', NULL, 'about rivastigmine a central reversible non competitive anticholinesterase antidementia. mechanism of action of rivastigmine alzheimer`s disease may result from a deficiency in neurotransmitters used by nerves in the brain to communicate with one another.', NULL, NULL, 681, '2024-12-04', 'For the treatment of mild to moderate dementia associated with Parkinson''s disease or of the Alzheimer''s type.', 'Rivastigmine is a carbamate derivative that is structurally related to physostigmine, but not to donepezil and tacrine. The precise mechanism of rivastigmine has not been fully determined, but it is suggested that rivastigmine binds reversibly with and inactivates chlolinesterase (eg. acetylcholinesterase, butyrylcholinesterase), preventing the hydrolysis of acetycholine, and thus leading to an increased concentration of acetylcholine at cholinergic synapses. The anticholinesterase activity of rivastigmine is relatively specific for brain acetylcholinesterase and butyrylcholinesterase compared with those in peripheral tissues.', 'Rivastigmine is a parasympathomimetic and a reversible cholinesterase inhibitor. An early pathophysiological feature of Alzheimer''s disease that is associated with memory loss and cognitive deficits is a deficiency of acetylcholine as a result of selective loss of cholinergic neurons in the cerebral cortex, nucleus basalis, and hippocampus. Tacrine is postulated to exert its therapeutic effect by enhancing cholinergic function. While the precise mechanism of rivastigmine''s action is unknown, it is postulated to exert its therapeutic effect by enhancing cholinergic function. This is accomplished by increasing the concentration of acetylcholine through reversible inhibition of its hydrolysis by cholinesterase. If this proposed mechanism is correct, rivastigmine''s effect may lessen as the disease progresses and fewer cholinergic neurons remain functionally intact.', 'DrugBank', 0, 0, 0);
