INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26458, 'Salbovent 0.05% 5 amp.', 'سالبوفنت 0.05% 5 امبول', '6', NULL, 'Bronchodilator', 'Salbutamol', 'Alexandria', 'Amp', 'أمبول', '0.05%', '5', NULL, 'about salbutamol beta2-adrenergic agonist phenethylamine derivative bronchodilator(antiasthma) uterine relaxant. mechanism of action of salbutamol salbutamol is a short acting ?2 receptor agonist. it selectively stimulates ?2 receptors present in airway u', NULL, NULL, 1458, '2022-07-23', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (32660, 'Clinso lubricant gel 50 gm', 'كلينسو جل 50 جم', '66', '60', 'Lubricant', 'Tea tree oil+thyme+chamomile+oak ext.+ginko ext.+glycerin', 'Novex', 'Gel', 'جل', '50 gm', '1', NULL, 'indications : -vaginal dryness & dyspareunia. -stimulat female desire. -provide vaginal tightening. apply 15 minutes before intercourse.', NULL, NULL, 1091, '2024-02-08', 'Indicated for topical use to help protect against infection in minor cuts, scrapes, and burns. No FDA-approved therapeutic indications.', 'The components of tea tree oil, particularly terpinen-4-ol and α-terpineol, mediate antimicrobial actions by disrupting the structural and functional integrity of bacterial membrane. Hydrocarbons are capable of partitioning into the cell and cytoplasmic membrane of microorganisms and disrupt their vital functions, which may result in leakage of ions such as potassium, and the inhibition of respiration [A32439]. Eventually, cell lysis may occur due to weakening of the cell wall, and loss of turgor pressure and subsequent rupture of the cytoplasmic membrane [A32441]. The loss of 260-nm-absorbing material may be indicative of a damaged cytoplasmic membrane and loss of nucleic acids [A32441]. In _E. coli_, perturbed potassium homeostasis, glucose-dependent respiration, cell morphology, and ability to exclude propidium iodide was observed. 



Tea tree oil also mediates its antifungal actions in a similar way, where it alters the permeability of Candida albicans and inhibits its respiration in a dose-dependent manner [A32439]. Plasma and mitochondrial membranes of fungal species are also thought to be negatively affected by inhibition of glucose-induced medium acidification by tea tree oil, which involves inhibition of membrane ATPase responsible for the expulsion of protons [A32439]. Tea tree oil also inhibits the formation of germ tubes, or mycelial conversion, in _C. albicans_, thereby disrupting cell morphogenesis [A32439]. Water-soluble fraction of TTO, terpinen-4-ol, and α-terpineol, can inhibit the lipopolysaccharide-induced production of the inflammatory mediators such as TNF-α, IL-1β and IL-10 by human peripheral monocytes by approximately 50% and that of prostaglandin E2 by about 30% after 40 h [A32439]. These components of tea tree oil may also suppress superoxide production by agonist-stimulated monocytes and decrease the production of reactive oxygen species by both stimulated neutrophils and monocytes [A32439].', 'Tea tree oil exhibits antibacterial, antifungal, antiviral, and antiprotozoal activities [A32439]. It mostly mediates bactericidal actions at concentrations of 1.0% or less in most bacteria such as _Staphylococcus aureus_ and _Escherichia coli_, and causes bacteriostatic effects at lower concentrations [A32439]. Organisms such as commensal skin staphylococci and micrococci, _Enterococcus faecalis_, and Pseudomonas aeruginosa_emphasized text_ were susceptible to tea tree oil concentrations of 2% [A32439]. It is proposed that water-soluble components of tea tree oil are capable in inducing anti-inflammatory actions; terpinen-4-ol attenuates the vasodilation and plasma extravasation associated with histamine-induced inflammation in humans [A32438].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23377, 'Neomint i.v. infusion 250 ml', 'نيومينت وريد 250 مل', '10', NULL, 'Sterile solution', 'Glucose (dextrose)+potassium chloride+sodium chloride', 'Otsuka', 'Unknown', 'غير محدد', '250 ml', '1', 'يستخدم فى علاج نقص السكر في الدم وفقدان الدم والسوائل عن طريق التنقيط الوريدى', NULL, NULL, NULL, 1514, '2022-07-07', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30477, 'Empir g b12 30 chew.tab', 'امبيرجي ب12 - 30 قرص مضغ', '129', '109', 'Multivitamin', 'Methylcobalamin+folic acid', 'Pharma zad > elixir pharma', 'Tab', 'أقراص', NULL, '1', NULL, 'b12 1000 mg & folic acid 400 mg', '6225000411890', NULL, 1466, '2025-10-23', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5647, 'Imeglispire 500 mg 30 f.c. tabs.', 'ايميجلسباير 500مجم 30 قرص', '146', NULL, 'Anti-diabetic', 'Imeglimin', 'Inspire pharmaceutical company', 'Tab', 'أقراص', '500 mg', '3', NULL, NULL, '6224000866624', NULL, 2222, '2025-07-01', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16024, 'Cefobid 500mg vial', 'سيفوبيد 500مجم فيال', '60', '41.5', 'Antibiotic.cephalosporin.third-generation', 'Cefoperazone', 'Pfizer', 'Vial', 'فيال', '500mg', '1', NULL, NULL, '6221180000084', NULL, 854, '2025-01-14', 'Indicated for the treatment of following infections caused by susceptible bacteria:[label]



1) Respiratory tract infections caused by _S. pneumoniae_, _H. influenzae_, _S. aureus_ (penicillinase and non-penicillinase producing strains), _S. pyogenes_ (Group A beta-hemolytic streptococci), _P. aeruginosa_, _Klebsiella pneumoniae_, _E. coli_, _Proteus mirabilis_, and Enterobacter species.



2) Peritonitis and other intra-abdominal infections caused by _E. coli_, _P. aeruginosa_, and anaerobic gram-negative bacilli (including _Bacteroides fragilis_).



3) Bacterial septicemia caused by _S. pneumoniae_, _S. agalactiae_, _S. aureus_, _Pseudomonas aeruginosa_, _E. coli_, _Klebsiella_ spp., _Klebsiella pneumoniae_, Proteus species (indole-positive and indole-negative), _Clostridium_ spp. and anaerobic gram-positive cocci.



4) Infections of the skin and skin structures caused by S. aureus (penicillinase and non-penicillinase producing strains), S. pyogenes, and P. aeruginosa.



5) Pelvic Inflammatory Disease, Endometritis, and Other Infections of the Female Genital Tract caused by N. gonorrhoeae, S. epidermidis, S. agalactiae, E. coli, Clostridium spp., Bacteroides species (including Bacteroides fragilis), and anaerobic gram-positive cocci.



6) Urinary tract infections caused by Escherichia coli and Pseudomonas aeruginosa.



7) Enterococcal Infections. Although cefoperazone has been shown to be clinically effective in the treatment of infections caused by enterococci in cases of peritonitis and other intra-abdominal infections, infections of the skin and skin structures, pelvic inflammatory disease, endometritis and other infections of the female genital tract, and urinary tract infections, the majority of clinical isolates of enterococci tested are not susceptible to cefoperazone but fall just at or in the intermediate zone of susceptibility, and are moderately resistant to cefoperazone. However, _in vitro_ susceptibility testing may not correlate directly with _in vivo_ results. Despite this, cefoperazone therapy has resulted in clinical cures of enterococcal infections, chiefly in polymicrobial infections. Cefoperazone should be used in enterococcal infections with care and at doses that achieve satisfactory serum levels of cefoperazone.', 'Like all beta-lactam antibiotics, cefoperazone binds to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, causing the inhibition of the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins.', 'Cefoperazone is a third generation cephalosporin antibiotic. Cefoperazone exerts its bactericidal effect by inhibiting the bacterial cell wall synthesis', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15197, 'Bilogink 20 caps.', 'بيلوجينك 20 كبسولة', '85', '62', 'Multivitamins', 'Co enzyme q10+ginko biloba+ginseng+l-arginine+biotin+wheat germ oil+vitamin e+vitamin b complex+zinc+copper+selenium', 'El-obour > nin pharma', 'Cap', 'كبسولة', NULL, '1', 'فيتامينات متعددة -- يزيد تدفق الدم -- يحسن الذاكرة', 'indications: -loss of concentration & memory disorders. -stress and fatigue. -sexual tonic. -useful in hair loss. -chronic disease & post operative.', NULL, NULL, 1305, '2025-04-20', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5014, 'Flumox 500mg 16 caps', 'فلوموكس 500مجم 16 كبسولة', '71', '49', 'Penicillins penicillin with beta lactamase inhibitor', 'Amoxicillin+flucloxacillin', 'Eipico', 'Cap', 'كبسولة', '500mg', '2', 'مضاد حيوي', NULL, '6221032131522', NULL, 18151, '2024-06-13', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29814, 'Zaditen 1mg 30 tab', 'زاديتين 1مجم 30 قرص', '31', NULL, 'Antihistamine.second-generation', 'Ketotifen', 'Novartis > sandoz', 'Tab', 'أقراص', '1mg', '3', 'مضاد للحساسية لا يسبب نعاس', NULL, '6223002642069', NULL, 4214, '2022-12-15', 'Administered orally, ketotifen is indicated as an add-on medication in the chronic treatment of mild atopic asthma in children.[L32283] It is also available as an over-the-counter ophthalmic solution which is indicated for the temporary prevention of itching of the eye due to allergic conjunctivitis.[L32278]', 'The precise mechanism(s) through which ketotifen exerts its therapeutic effects are unclear. Ketotifen is a potent and non-competitive antagonist of H1 histamine receptors, which is likely to be a significant contributor to its anti-allergic activity.[L32283] In addition, ketotifen stabilizes mast cells and has demonstrated _in vitro_ the ability to inhibit the release of allergic and inflammatory mediators such as histamine, leukotrienes C<sub>4</sub> and D<sub>4</sub> (i.e. SRS-A), and platelet-activating factor (PAF).[L32283]



Other _in vivo_ observations thought to contribute to ketotifen''s efficacy in asthma include the inhibition of various PAF-mediated processes (e.g. airway hyperreactivity, eosinophil and platelet accumulation in the airways), prevention of leukotriene-induced bronchoconstriction, and suppression of eosinophil priming.[L32283]', 'Ketotifen is a non-competitive histamine antagonist and mast cell stabilizer.[L32278] Administered orally, it functions as a non-bronchodilator antiasthmatic drug by inhibiting the effects of endogenous substances known to be inflammatory mediators.[L32283] While effects can take 6 to 12 weeks to become apparent,[A231214] the use of ketotifen has been demonstrated to reduce the frequency, severity, and duration of asthma symptoms, and may allow for a reduction in the use of other asthma therapies.[L32283]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24298, 'Orovex-h mouth wash 150 ml', 'اوروفيكس-اتش غسول فم 250 مل', '150', '120', 'Oral care', 'Thymol+menthol+glycerin+sodium saccharine+sodium monofluorophosphate+chlorhexidine', 'Macro group pharmaceuticals', 'Mouth wash', 'غسول للفم', '150 ml', '1', NULL, '-contains hyaluronic acid which considered as a basic component in the product helps to maintain healthy gum & buccal cavity. -contains antiseptic & antimicrobial ingredients that helps in protection of gum & buccal cavity. -helps in rehydrating buccal ti', '6224007384480', NULL, 3066, '2025-03-04', NULL, NULL, NULL, NULL, 0, 0, 0);
