INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31663, 'Kelo-cote scar gel uv 6 gm', 'كيلو-كوت سكار جل 6 جم', '350', NULL, 'Skin care.scar therapy', 'Polysiloxanes+silicon dioxide', 'Alliance pharma > star international company', 'Gel', 'جل', '6 gm', '1', NULL, 'kelo-coteï- is an advanced formula silicone treatment which helps improve the appearance of scars. silicone gels are the first line treatment for scars by healthcare professionals including plastic surgeons.', NULL, NULL, 875, '2023-03-05', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (10698, 'Somazina supra 100mg/ml oral drops 30 ml', 'سومازينا سوبرا 100مجم/مل نقط للفم 30 مل', '250', '210', 'Cerebral circulatory inhancer', 'Citicholine', 'October pharma > ferrer international sa -spain', 'Drops', 'نقط', '100mg', '1', 'لتحفيز الدورة الدموية بالمخ', 'citicoline is a brain chemical that occurs naturally in the body. as a medicine it is taken by mouth as a supplement or given by iv or as a shot. citicoline is used for alzheimer s disease and other types of dementia head trauma cerebrovascular disease su', '6224009165285', NULL, 8201, '2025-11-09', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15650, 'Calcionate 6 amps.', 'كالسيونات 6 امبولات', '138', '96', 'Calcium supplement', 'Calcium gluconate+calcium levulinate', 'Memphis', 'Amp', 'أمبول', NULL, '6', NULL, 'properties: calcium is essential for the functional integrity of the nervous & muscular systems. it is necessary for normal cardiac function & is one of the factors involved in the coagulation of blood. it also functions as an enzyme cofactor & affects th', '6221050010465', NULL, 3069, '2024-08-22', 'Calcium acetate is one of a number of calcium salts used to treat hyperphosphatemia (too much phosphate in the blood) in patients with kidney disease.', 'Calcium acetate and other calcium salts are phosphate binders. They work by binding with the phosphate in the food you eat, so that it is eliminated from the body without being absorbed.', 'Patients with advanced renal insufficiency (creatinine clearance less than 30 ml/min) exhibit phosphate retention and some degree of hyperphosphatemia. The retention of phosphate plays a pivotal role in causing secondary hyperparathyroidism associated with osteodystrophy, and soft-tissue calcification. The mechanism by which phosphate retention leads to hyperparathyroidism is not clearly delineated. Therapeutic efforts directed toward the control of hyperphosphatemia include reduction in the dietary intake of phosphate, inhibition of absorption of phosphate in the intestine with phosphate binders, and removal of phosphate from the body by more efficient methods of dialysis. The rate of removal of phosphate by dietary manipulation or by dialysis is insufficient. Dialysis patients absorb 40% to 80% of dietary phosphorus. Therefore, the fraction of dietary phosphate absorbed from the diet needs to be reduced by using phosphate binders in most renal failure patients on maintenance dialysis. Calcium acetate when taken with meals combines with dietary phosphate to form insoluble calcium phosphate which is excreted in the feces. Maintenance of serum phosphorus below 6.0 mg/dl is generally considered as a clinically acceptable outcome of treatment with phosphate binders. Calcium acetate is highly soluble at neutral pH, making the calcium readily available for binding to phosphate in the proximal small intestine.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20975, 'Irovit 20 sachets', 'ايروفيت 20 اكياس', '44', NULL, 'Iron supplement', NULL, 'Alfacure pharmaceuticals', 'Sachet', 'أكياس', NULL, '1', 'مكمل غذائى لعلاج حالات الانيميا ودعم الجهاز العصبي والمناعى وامداد الجسم بالطاقة', '*rich in iron minerals and vitamins. *one sachet to be dissolved in a cup of milk.', '6225484158960', NULL, 2031, '2022-06-18', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27017, 'Simva-map 10/10mg 10 bilayer f.c. tab.', 'سيمفا ماب 10/10مجم 10 اقراص', '43', NULL, 'Antihyperlipidemic.cholesterol absorption inhibitors.combined with statin', 'Ezetimibe+simvastatin', 'Multi-apex', 'Tab', 'أقراص', '10mg', '1', NULL, 'about ezetimibe cholesterol absorption inhibitor 2-azetidinone derivative hypolipidemic agent. mechanism of action of ezetimibe it reduces the absorption of cholesterol from the intestine by localising at the brush border of the small intestine where it i', NULL, NULL, 754, '2022-07-15', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16255, 'Chlorpheniramine 4 mg 20 tab.', 'كلورفينيرامين 4 مجم 20 قرص', '1', NULL, 'Antihistamine.antiallergy', 'Chlorpheniramine', 'Hi-pharm > pyramids for trading pharmaceuticals-egypt', 'Tab', 'أقراص', '4 mg', '2', 'مضاد للهيستامين ويستخدم كمضاد مختلف الانواع من الحساسية', 'description a histamine h1 antagonist used in allergic reactions hay fever rhinitis urticaria and asthma. it has also been used in veterinary applications. one of the most widely used of the classical antihistaminics it generally causes less drowsiness an', NULL, NULL, 6549, '2022-07-13', 'For the treatment of rhinitis, urticaria, allergy, common cold, asthma and hay fever.', 'Chlorpheniramine binds to the histamine H1 receptor. This blocks the action of endogenous histamine, which subsequently leads to temporary relief of the negative symptoms brought on by histamine.', 'In allergic reactions an allergen interacts with and cross-links surface IgE antibodies on mast cells and basophils. Once the mast cell-antibody-antigen complex is formed, a complex series of events occurs that eventually leads to cell-degranulation and the release of histamine (and other chemical mediators) from the mast cell or basophil. Once released, histamine can react with local or widespread tissues through histamine receptors. Histamine, acting on H<sub>1</sub>-receptors, produces pruritis, vasodilatation, hypotension, flushing, headache, tachycardia, and bronchoconstriction. Histamine also increases vascular permeability and potentiates pain. Chlorpheniramine, is a histamine H1 antagonist (or more correctly, an inverse histamine agonist) of the alkylamine class. It competes with histamine for the normal H<sub>1</sub>-receptor sites on effector cells of the gastrointestinal tract, blood vessels and respiratory tract. It provides effective, temporary relief of sneezing, watery and itchy eyes, and runny nose due to hay fever and other upper respiratory allergies.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14082, 'Amthrost 125mg/5ml susp. 60ml', 'امثروست 125 مجم / 5 مل معلق 60 مل', '4', NULL, 'Antibiotic.cephalosporin.first-generation', 'Cefalexin', 'Sigma > sabaa', 'Susp', 'معلق', '125mg', '1', 'مضاد حيوي', 'about cefalexin a first-generation cephalosporin antibiotic. mechanism of action cephalexin is a 1st generation cephalosporin which exerts its bactericidal action against both gram positive & gram negative organisms by inhibiting bacterial cell wall synth', NULL, NULL, 925, '2022-05-19', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13931, 'Aminoplasmal e 10% i.v.infusion', 'امينوبلازمال اي 10% محلول وريدي', '50', NULL, 'Sterile solution', 'Amino acids+electrolytes', 'Sunny medical', 'Unknown', 'غير محدد', '10%', '1', NULL, 'about amino acids an amino acid is any molecule that contains both amine and carboxyl functional groups. these amino acids are the basic components of proteins. there are twenty standard amino acids used by cells in protein biosynthesis that are specified', NULL, NULL, 1290, '2023-01-20', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1829, 'Vera scar silicone gel 30 gm', 'فيرا سكار سيليكون جل 30 جم', '645', '595', 'Anti scar', 'Silicone gel', 'Avento pharmaceutical', 'Gel', 'جل', '30 gm', '1', NULL, NULL, NULL, NULL, 1139, '2025-07-15', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27859, 'Tadanerfi 20 mg 4 orodispersible films', 'تادانيرفي 20مجم 4 افلام ذائبة بالفم', '126', '90', 'Tonic for men', 'Tadalafil', 'Nerhadou international co.', 'Film', 'فيلم', '20 mg', '4', 'مقوي للرجال', 'about tadalafil a phosphodiesterase 5 enzyme inhibitor treating erectile dysfunction (ed). mechanism of action of tadalafil tadalafil is a phosphodiesterase enzyme (pde5) inhibitor. this enzyme is responsible for the degradation cyclic guanosine monophosp', '6224011153058', NULL, 21184, '2024-06-24', 'Tadalafil is indicated for the treatment of erectile dysfunction (ED) and either alone or in combination with [finasteride] for the treatment of benign prostatic hypertrophy (BPH).[L39095,L39439] It is also indicated for the treatment of pulmonary arterial hypertension (PAH) both alone and in combination with [macitentan] or other endothelin-1 antagonists.[L39100,L39105,L50622]', 'Tadalafil is a selective phosphodiesterase-5 (PDE5) inhibitor that produces several downstream effects with the most common therapeutic effect being smooth muscle relaxation. [L39095] Patients may experience ED due to a variety of causes including psychogenic, neurogenic, vasculogenic, iatrogenic, or endocrine. [A242392] These causes result in dysfunction of penile smooth muscle relaxation through either disrupted neuronal signaling or direct influence on smooth muscle cells. During sexual arousal, non-adrenergic non-cholinergic (NANC) neurons release nitric oxide (NO). Nitric oxide stimulates guanylate cyclase which converts guanosine triphosphate to cyclic guanosine monophosphate (cGMP).[A242287, A242377] cGMP activates the cGMP-dependent kinase (PKG) in a signal cascade which activates K+ channels leading to inhibition of Ca2+ channels, inhibits platelet activation, and inhibits smooth muscle cell proliferation while inducing apoptosis. This signal cascade is attenuated by PDE5 which breaks the phosphodiester bond of cGMP, converting it to GMP. Inhibition of PDE5 by tadalafil increases signaling via the PKG cascade which supports penile smooth muscle relaxation during sexual arousal by decreasing Ca2+ entry into smooth muscle cells. This smooth muscle relaxation allows blood to fill the corpus cavernosum thereby producing an erection.



In PAH, blood pressure in the pulmonary arteries is raised due to a variety of mechanisms stemming from endothelial dysfunction.[A242377] Decreased production of NO and prostacyclin reduce vasodilatory signaling while overproduction of endothelin-1 and thromboxane increase vasoconstriction. Inflammation, thromboses, and hypoxia later contribute to vascular remodeling which further reduces luminal size. The resultant increase in blood pressure reduces the capacity for gas exchange and increases afterload at the right ventricle, producing symptoms of dyspnea, fatigue, and dizziness as well as leading to right-sided heart failure. Tadalafil exerts its therapeutic effect in PAH through boosting NO-cGMP signaling to contribute to smooth muscle relaxation as with ED.



Lastly, tadalafil is used to treat BPH.[L39095] BPH produces urinary dysfunction through hyperproliferation of the epithelial and smooth muscle layers of the prostate.[A242382] The increased size of the prostate blocks urine flow through the urethra resulting in higher residual volumes due to incomplete emptying. Tadalafil does not appear to exert its benefit via smooth muscle relaxation of the prostate. It may instead exert its effect through a mix of increased oxygenation and decreased inflammation, which decreases tissue remodeling, and inhibition of cell proliferation through the cGMP cascade.



The decreased affinity for PDE6 compared to other PDE5 inhibitors may explain the decreased incidence of visual side effects as PDE6 is present in the eye and contributes to color vision.[L39100, L39105, A242287]', 'Tadalafil exerts a therapeutic effect in ED by increasing sexual stimulation-dependant smooth muscle relaxation in the penis, allowing the corpus cavernosum to fill with blood to produce an erection.[A242287, A242377] Smooth muscle relaxation in the pulmonary vasculature helps to produce vasodilation in PAH which reduces blood pressure in the pulmonary arteries.[A242377] In BPH, tadalafil may contribute to decreased smooth muscle cell proliferation which may reduce the size of the prostate and relieve the anatomical obstruction which produces urinary symptoms of BPH.[A242382] The decreased affinity of tadalafil for PDE6 compared to other PDE5 inhibitors may explain the reduced incidence of visual side effects.[L39100, L39105, A242287]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20514, 'Histarhine syrup 100ml', 'هيستارين شراب 100 مل', '5', NULL, 'Cold drugs', 'Pseudoephedrine+triprolidine', 'Amriya', 'Syrup', 'شراب', '100ml', '1', NULL, 'about pseudoephedrine alpha/beta adrenergic agonist a phenethylamine derivative a nasal decongestant. mechanism of action of pseudoephedrine it is a nasal decongestant with alpha-2 agonistic action. it produces local vasoconstriction reduces blood flow an', NULL, NULL, 1287, '2023-06-19', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7214, 'Dulopressive 60mg 30 capsule jar', 'دولوبريسيف 60مجم 30 كبسولة', '174', NULL, 'Psychiatric.antidepressants', 'Duloxetine', 'Ta pharma', 'Capsule', 'كبسولة', '60mg', '1', NULL, NULL, '6225000351158', NULL, 823, '2025-08-27', '**Indicated** for:



1) Management of Major Depressive Disorder.[label]



2) Management of Generalized Anxiety Disorder.[label]



3) Management of diabetic peripheral neuropathy.[label]



4) Management of fibromyalgia.[label]



5) Management of chronic musculoskeletal pain.[label]



6) Management of osteoarthritis of the knee in adults.[L6364]



7) Management of chronic lower back pain in adults.[L6364]



8) Management of stress urinary incontinence in adult women.[L6367]



**Off-label** uses include:



1) Management of chemotherapy-induced peripheral neuropathy.[A178603]



2) Management of stress urinary incontinence in adult men after prostatectomy until recovery is complete.[L6370]', 'Duloxetine is a potent inhibitor of neuronal serotonin and norepinephrine reuptake and a less potent inhibitor of dopamine reuptake.[A178714] Duloxetine has no significant affinity for dopaminergic, adrenergic, cholinergic, histaminergic, opioid, glutamate, and GABA receptors.



Action on the external urinary sphincter is mediated via duloxetine''s CNS effects. Increased serotonin and norepinephrine concentrations in Onuf''s nucleus leads to increased activation of 5-HT<sub>2</sub>, 5-HT<sub>3</sub>, and α<sub>1</sub> adrenergic receptors.[A178663,A178666] 5-HT<sub>2</sub> and α<sub>1</sub> are both G<sub>q</sub> coupled and their activation increases the activity of the inositol trisphosphate/phospholipase C (IP<sub>3</sub>/PLC) pathway.[T116] This pathway leads to release of intracellular calcium stores, increasing intracellular calcium concentrations, and facilitating neuronal excitability. 5-HT<sub>3</sub> functions as a ligand-gated sodium channel which allows sodium to flow into the neuron when activated. Increased flow of sodium into the neuron contributes to depolarization and activation of voltage gated channels involved in action potential generation. The combined action of these three receptors contributes to increased excitability of the pudendal motor nerve in response to glutamate.



Also related to duloxetine''s action at the spinal cord is its modulation of pain. Increasing the concentration of serotonin and norepinephrine in the dorsal horn of the spinal cord increases descending inhibition of pain through activation of 5-HT<sub>1A</sub>, 5-HT<sub>1B</sub>, 5-HT<sub>1D</sub>, 5-HT<sub>2</sub>, 5-HT<sub>3</sub>, α<sub>1</sub>-adrenergic, and α<sub>2</sub>-adrenergic receptors.[A178705] 5-HT<sub>2</sub>, 5-HT<sub>3</sub>, and α<sub>1</sub>-adrenergic mediate neuronal activation as described above. The activated neuron in this case is the GABAergic inhibitory interneuron which synapses onto the nociceptive projection neuron to inhibit the transmission of painful stimuli to the brain. The 5-HT<sub>1</sub> and α<sub>2</sub> receptors are G<sub>i</sub>/G<sub>o</sub> coupled and their activation leads to increased potassium current through inward rectifier channels and decreased adenylyl cyclase/protein kinase A signaling which contributes to neuronal inhibition.[A178705,T116] These inhibitory receptors are present on the projection neuron itself as well as the dorsal root ganglion which precedes it and serves to directly suppress the transmission of painful stimuli.



The mechanisms involved in duloxetine''s benefits in depression and anxiety have not been fully elucidated. Dysfunctional serotonin and norepinephrine signaling are thought to be involved and increases in the availability of these neurotransmitters at the synaptic cleft thought to mediate a therapeutic effect.[A178666] It is postulated that the involvement of serotonin and norepinephrine in area responsible for emotional modulation such as the limbic system contributes to the effects in mood disorders specifically but this has yet to be confirmed.



Duloxetine''s hypertensive effect is related to its intended pharmacological effect. Increased availability of norepinephrine leads to activation of adrenergic receptors on the vascular endothelium. Since the action of α<sub>1</sub> receptors predominates, vasoconstriction results as the G<sub>q</sub> coupled receptor mediates calcium release from the sarcoplasmic reticulum to facilitate smooth muscle contraction.[T116]', 'Duloxetine, through increasing serotonin and norepinephrine concentrations in Onuf''s nucleus, enhances glutamatergic activation of the pudendal motor nerve which innervates the external urethral sphinter.[A178663,A178666] This enhanced signaling allows for stronger contraction. Increased contraction of this sphincter increases the pressure needed to produce an incontinence episode in stress urinary incontinence. Duloxetine has been shown to improve Patient Global Impression of Improvement and Incontinence Quality of Life scores.[A178669] It has also been shown to reduce the median incontinence episode frequency at doses of 40 and 80 mg.



Action at the dorsal horn of the spinal cord allows duloxetine to strengthen the the serotonergic and adrenergic pathways involved in descending inhibition of pain.[A178666,A178705] This results in an increased threshold of activation necessary to transmit painful stimuli to the brain and effective relief of pain, particularly in neuropathic pain. Pain relief has been noted in a variety of painful conditions including diabetic peripheral neuropathy, fibromyalgia, and osteoarthritis using a range of pain assessment surveys.[A178711]



While duloxetine has been shown to be effective in both animal models of mood disorders and in clinical trials for the treatment of these disorders in humans, the broad scope of its pharmacodynamic effects on mood regulation in the brain has yet to be explained.[A178714]



Increased blood pressure is a common side effect with duloxetine due to vasoconstriction mediated by the intended increase in norepinephrine signaling.[label,T116]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22259, 'Mannitol 10% (el nile) i.v. inf. 500 ml (rubber cap)', 'مانيتول 10% النيل محلول وريدي 500 مل بغطاء مطاطي', '12', NULL, 'Osmotic diuretic', 'Mannitol', 'El nile.', 'Cap', 'كبسولة', '10%', '1', NULL, 'about mannitol 10% a polyol (sugar alcohol) low molecular weight non electrolyte osmotic diuretic. mechanism of action of mannitol 10% it is a non electrolyte of low molecular weight which limit the tubular water and electrolyte reabsorption in a variety', NULL, NULL, 629, '2022-08-05', 'Used for the promotion of diuresis before irreversible renal failure becomes established, the reduction of intracranial pressure, the treatment of cerebral edema, and the promotion of urinary excretion of toxic substances.



Mannitol is also indicated as add-on maintenance therapy for improving pulmonary function in cystic fibrosis patients aged 18 and over who have passed the BRONCHITOL tolerance test (BTT). It is recommended that patients take an orally inhaled short-acting bronchodilator 5-15 minutes prior to every inhaled mannitol dose.[L20024]', 'Mannitol is an osmotic diuretic that is metabolically inert in humans and occurs naturally, as a sugar or sugar alcohol, in fruits and vegetables. Mannitol elevates blood plasma osmolality, resulting in enhanced flow of water from tissues, including the brain and cerebrospinal fluid, into interstitial fluid and plasma. As a result, cerebral edema, elevated intracranial pressure, and cerebrospinal fluid volume and pressure may be reduced. As a diurectic mannitol induces diuresis because it is not reabsorbed in the renal tubule, thereby increasing the osmolality of the glomerular filtrate, facilitating excretion of water, and inhibiting the renal tubular reabsorption of sodium, chloride, and other solutes. Mannitol promotes the urinary excretion of toxic materials and protects against nephrotoxicity by preventing the concentration of toxic substances in the tubular fluid. As an Antiglaucoma agent mannitol levates blood plasma osmolarity, resulting in enhanced flow of water from the eye into plasma and a consequent reduction in intraocular pressure. As a renal function diagnostic aid mannitol is freely filtered by the glomeruli with less than 10% tubular reabsorption. Therefore, its urinary excretion rate may serve as a measurement of glomerular filtration rate (GFR).



The exact mechanism of action of inhaled mannitol in the symptomatic maintenance treatment of cystic fibrosis remains unclear.[A223199, L20024] It is hypothesized that mannitol produces an osmotic gradient across the airway epithelium that draws fluid into the extracellular space and alters the properties of the airway surface mucus layer, allowing easier mucociliary clearance.[A223199]', 'Chemically, mannitol is an alcohol and a sugar, or a polyol; it is similar to xylitol or sorbitol. However, mannitol has a tendency to lose a hydrogen ion in aqueous solutions, which causes the solution to become acidic. For this reason, it is not uncommon to add a substance to adjust its pH, such as sodium bicarbonate. Mannitol is commonly used to increase urine production (diuretic). It is also used to treat or prevent medical conditions that are caused by an increase in body fluids/water (e.g., cerebral edema, glaucoma, kidney failure). Mannitol is frequently given along with other diuretics (e.g., furosemide, chlorothiazide) and/or IV fluid replacement.



Inhaled mannitol has the possibility to cause bronchospasm and hemoptysis; the occurrence of either should lead to discontinuation of inhaled mannitol.[L20024]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28621, 'Twinia anti-acne cream 50 gm', 'تونيا كريم لعلاج حب الشباب 50جم', '40', NULL, 'Anti-acne', 'Salicylic acid+chamomile ext+jojoba oil+coconut oil+wheat germ oil+triethanolamine+white bees wax', 'Italian cosmetics company > alwadi pharma', 'Cream', 'كريم', '50 gm', '1', NULL, '** apply thin layer on skin at morning / or evening ** ingredients: salicylic acid + chamomile extract + jojoba oil + coconut oil + wheat germ oil + triethanolamine + white bees wax + stearic acid + cetyl alcohol + methylparaben + propylparaben', NULL, NULL, 863, '2022-07-15', 'Key additive in many skin-care products for the treatment of acne, psoriasis, callouses, corns, keratosis pilaris and warts.', 'Salicylic acid directly irreversibly inhibits COX-1 and COX-2 to decrease conversion of arachidonic acid to precursors of prostaglandins and thromboxanes. Salicylate''s use in rheumatic diseases is due to it''s analgesic and anti-inflammatory activity. Salicylic acid is a key ingredient in many skin-care products for the treatment of acne, psoriasis, calluses, corns, keratosis pilaris, and warts. Salicylic acid allows cells of the epidermis to more readily slough off. Because of its effect on skin cells, salicylic acid is used in several shampoos used to treat dandruff. Salicylic acid is also used as an active ingredient in gels which remove verrucas (plantar warts). Salicylic acid competitively inhibits oxidation of uridine-5-diphosphoglucose (UDPG) with nicotinamide adenosine dinucleotide (NAD) and noncompetitively with UDPG. It also competitively inhibits the transferring of the glucuronyl group of uridine-5-phosphoglucuronic acid (UDPGA) to a phenolic acceptor. Inhibition of mucopoly saccharide synthesis is likely responsible for the slowing of wound healing with salicylates.', 'Salicylic acid treats acne by causing skin cells to slough off more readily, preventing pores from clogging up. This effect on skin cells also makes salicylic acid an active ingredient in several shampoos meant to treat dandruff. Use of straight salicylic solution may cause hyperpigmentation on unpretreated skin for those with darker skin types (Fitzpatrick phototypes IV, V, VI), as well as with the lack of use of a broad spectrum sunblock. Subsalicylate in combination with bismuth form the popular stomach relief aid known commonly as Pepto-Bismol. When combined the two key ingredients help control diarrhea, nausea, heartburn, and even gas. It is also very mildly anti-biotic.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21619, 'Larynclave 400/57 pd. for oral susp. 70ml', 'لارينكلاف 400/57 مجم باودر لعمل معلق 70 مل', '13', NULL, 'Penicillins.penicillin with b-lactamase inhibitor', 'Amoxicillin+clavulanic acid', 'Misr > al rowad pharmaceutical industrial co.', 'Susp', 'معلق', '70ml', '1', NULL, 'amoxicillin is an analog of ampicillin derived from the basic penicillin nucleus 6-aminopenicillanic acid clavulanic acid is produced by the fermentation of streptomyces clavuligerus. it is a beta-lactam structurally related to the penicillins and possess', NULL, NULL, 1120, '2022-07-20', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1123, 'Audiclean ear cleansing wash spray 60ml', 'اوديكلين سبراي للاذن 60 مل', '110', NULL, 'Earwax remover', 'Isotonic sea water+bicarbonates', 'Le laboratoire de la mar- goemar > star company', 'Spray', 'بخاخ', '60ml', '1', NULL, '- keeps ears refreshingly clean & clear. - gently washes away excess wax and dirt. - prevents wax build-up. - safe and natural for the entire family (from 6 months old) directions for use: use 2 to 3 times a week or daily if needed. when using audiclean f', NULL, NULL, 872, '2022-09-20', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2814, 'Orogeng 125 ml mouth wash', 'اوروجينج 125 مل غسول للفم', '45', NULL, 'Anti bactrial', 'Chlorhexidine diacetate+peppermint oil+menthol+tea tree ext+eugenia+clove ext+thyme ext+calcium phosphate', 'Bio nl pharma', 'Mouth wash', 'غسول للفم', '125 ml', '1', NULL, NULL, NULL, NULL, 804, '2025-02-06', 'Chlorhexidine is available over-the-counter in various formulations (e.g. solution, sponge, cloth, swab) as a topical antiseptic to sanitize prior to surgeries and/or medical procedures.[L11518,L11521,L11527,L11533] Dental formulations, available by prescription only, include an oral rinse indicated for the treatment of gingivitis[L11512] and a slow-release "chip" which is inserted into periodontal pockets and is indicated for the reduction of pocket depth in adult patients with periodontitis as an adjunct therapy to dental scaling and root planing procedures.[L11536]', 'Chlorhexidine’s broad-spectrum antimicrobial effects are due to its ability to disrupt microbial cell membranes. The positively charged chlorhexidine molecule reacts with negatively charged phosphate groups on microbial cell surfaces - this reaction both destroys the integrity of the cell, allowing leakage of intracellular material, and allows chlorhexidine to enter the cell, causing precipitation of cytoplasmic components and ultimately cell death.[L11536,A190453] The specific means of cell death is dependent on the concentration of chlorhexidine - lower concentrations are bacteriostatic and result in leakage of intracellular substances such as potassium and phosphorous, whereas higher concentrations are bactericidal and cause cytoplasmic precipitation.[A190453]', 'Chlorhexidine is a broad-spectrum antimicrobial with demonstrated activity against both gram-positive and gram-negative bacteria, yeasts, and viruses.[A190417] Antimicrobial activity is dose-dependent - chlorhexidine is bacteriostatic at lower concentrations (0.02%-0.06%) and bactericidal at higher concentrations (>0.12%).[A190417]  Pharmacokinetic studies of oral chlorhexidine rinses indicate that approximately 30% of the active ingredient is retained in the mouth following rinsing, which is subsequently slowly released into oral fluids.[L11512] This ability to adsorb to dentine, shared with tetracycline antibiotics such as [doxycycline], is known as "substantivity" and is the result of chlorhexidine''s positive charge - it is likely that this substantivity plays at least some role in chlorhexidine''s antimicrobial activity, as its persistence on surfaces such as dentine prevent microbial colonization.[A190453]



Dental chlorhexidine rinses may result in staining of oral surfaces, such as teeth. This effect is not ubiquitous and appears to be more significant with extended therapy (i.e. up to 6 months) - nevertheless, patients for whom oral staining is unacceptable should use chlorhexidine rinse with caution and for the shortest effective interval.[L11512] Allergic reactions to chlorhexidine have been associated with the development of anaphylaxis.[A190468]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (248, 'Albothyl 1.8% vag. jel 40 gm', 'البوثيل 1.8% جل مهبلي 40 جرام', '46', '30.5', 'Antiseptic.vaganial infection', 'Policresulen', 'Marcyrl co. > aug pharma', 'Unknown', 'غير محدد', '1.8%', '1', 'جل مهبلى مطهر للقضاء على الالتهابات والروائح والافرازات الكريهة', 'about policresulen topical antiseptic condensation product of metacresolsulfonic acid & methanol. pharmacokinets of policresulen absorption: absorbed after topical administration. side effects of policresulen mild local discomfort contra-indications of po', NULL, NULL, 2167, '2024-12-24', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18696, 'Exelon 3mg 14 caps.', 'اكسيلون 3 مجم 14 كبسولة', '105', NULL, 'Anticholinesterase', 'Rivastigmine', 'Novartis', 'Cap', 'كبسولة', '3mg', '1', NULL, 'about rivastigmine a central reversible non competitive anticholinesterase antidementia. mechanism of action of rivastigmine alzheimer`s disease may result from a deficiency in neurotransmitters used by nerves in the brain to communicate with one another.', NULL, NULL, 983, '2022-07-17', 'For the treatment of mild to moderate dementia associated with Parkinson''s disease or of the Alzheimer''s type.', 'Rivastigmine is a carbamate derivative that is structurally related to physostigmine, but not to donepezil and tacrine. The precise mechanism of rivastigmine has not been fully determined, but it is suggested that rivastigmine binds reversibly with and inactivates chlolinesterase (eg. acetylcholinesterase, butyrylcholinesterase), preventing the hydrolysis of acetycholine, and thus leading to an increased concentration of acetylcholine at cholinergic synapses. The anticholinesterase activity of rivastigmine is relatively specific for brain acetylcholinesterase and butyrylcholinesterase compared with those in peripheral tissues.', 'Rivastigmine is a parasympathomimetic and a reversible cholinesterase inhibitor. An early pathophysiological feature of Alzheimer''s disease that is associated with memory loss and cognitive deficits is a deficiency of acetylcholine as a result of selective loss of cholinergic neurons in the cerebral cortex, nucleus basalis, and hippocampus. Tacrine is postulated to exert its therapeutic effect by enhancing cholinergic function. While the precise mechanism of rivastigmine''s action is unknown, it is postulated to exert its therapeutic effect by enhancing cholinergic function. This is accomplished by increasing the concentration of acetylcholine through reversible inhibition of its hydrolysis by cholinesterase. If this proposed mechanism is correct, rivastigmine''s effect may lessen as the disease progresses and fewer cholinergic neurons remain functionally intact.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (10172, 'Saline sodium chloride 2.7% 500 ml p.infusion', 'محلول ملح كلوريد صوديوم وريدي 0.9% زجاجة 500 مل', '27', NULL, 'Sterile solution', 'Sodium chloride', 'Company', 'Unknown', 'غير محدد', '2.7%', '1', NULL, NULL, '6221067010830', NULL, 1597, '2022-12-08', 'This intravenous solution is indicated for use in adults and pediatric patients as a source of electrolytes and water for hydration. Also, designed for use as a diluent and delivery system for intermittent intravenous administration of compatible drug additives. ', 'Sodium and chloride — major electrolytes of the fluid compartment outside of cells (i.e., extracellular) — work together to control extracellular volume and blood pressure. Disturbances in sodium concentrations in the extracellular fluid are associated with disorders of water balance.', 'Sodium, the major cation of the extracellular fluid, functions primarily in the control of water distribution, fluid balance, and osmotic pressure of body fluids. Sodium is also associated with chloride and bicarbonate in the regulation of the acid-base equilibrium of body fluid.

Chloride, the major extracellular anion, closely follows the metabolism of sodium, and changes in the acid-base balance of the body are reflected by changes in the chloride concentration. ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22561, 'Mepacovent 4mg s.r. tab.', 'ميباكوفنت 4مجم اس ار اقراص', '10', NULL, 'Bronchodilator', 'Salbutamol', 'Mepaco', 'Tab', 'أقراص', '4mg', '1', NULL, 'about salbutamol beta2-adrenergic agonist phenethylamine derivative bronchodilator(antiasthma) uterine relaxant. mechanism of action of salbutamol salbutamol is a short acting beta-2 receptor agonist. it selectively stimulates beta-2 receptors present in', NULL, NULL, 1168, '2023-04-19', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (11758, 'Tussilar-m 10mg/ml drops 15 ml', 'توسيلار-ام 10مجم/مل نقط 15 مل', '11', '3.5', 'Antitussive', 'Dextromethorphan', 'Kahira', 'Drops', 'نقط', '10mg', '1', 'مضاد للسعال والكحة', 'about dextromethorphan a synthetic morphine (((dextrorotatory methyl ether of levorphanol))) derivative a synthetic narcotic antitussive. mechanism of action of dextromethorphan dextromethorphan hydrobromide is a selective antitussive which raises thresho', '6221068503171', NULL, 1142, '2024-11-09', 'Dextromethorphan is indicated in combination with [brompheniramine] and [pseudoephedrine] in the treatment of coughs and upper respiratory symptoms associated with allergies or the common cold.[L14366] Dextromethorphan is also used in combination with [guaifenesin] as an over-the-counter product to relieve a cough.[L14369] Dextromethorphan in combination with [quinidine] is indicated in the treatment of pseudobulbar affect.[L14363]', 'Dextromethorphan is a low-affinity uncompetitive NMDA antagonist and sigma-1 receptor agonist.[L14363] It is also an antagonist of α3/β4 nicotinic receptors.[A10589] However, the mechanism by which dextromethorphan''s receptor agonism and antagonism translate to a clinical effect is not well understood.[A215397]', 'Dextromethorphan is an opioid-like molecule indicated in combination with other medication in the treatment of coughs and pseudobulbar affect.[L14366,L14369,L14363] It has a moderate therapeutic window, as intoxication can occur at higher doses.[A215402] Dextromethorphan has a moderate duration of action.[L14363] Patients should be counselled regarding the risk of intoxication.[A215402]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (33292, 'Novita 20 tab', 'نوفيتا 20 قرص', '75', '55.5', 'Multivitamins', 'Calcium+magnesium+vit d+vit c+zinc', '(u. s. o) united scientific office', 'Tab', 'أقراص', NULL, '1', NULL, NULL, '5128103685353', NULL, 1068, '2025-08-03', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20301, 'Hedraledox syrup 120 ml', 'هيدراليدوكس شراب 120 مل', '29', '18', 'Anti-cough.antitussive and expectorant', 'Ivy leaves', 'Organo pharma', 'Syrup', 'شراب', '120 ml', '1', 'علاج الكحة -- التخلص من البلغم المسبب للكحة -- تحسين وظائف التنفس', 'due to their broncholytic and secretolic properties ivy leaf dried extract has long provided its worth in practice. their efficacy has been demonstrated in numerous scientific studies. according to a pharmacological investigation its constituent appears t', '6224000908560', NULL, 1509, '2024-01-13', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27677, 'Sunnydarone 150mg/3ml 5 i.v. amp', 'صانيدارون 150مجم/3مل 5 امبولات', '50', '30', 'Antiarrhythmias.class iii', 'Amiodarone', 'Sunny medical', 'Amp', 'أمبول', '150mg', '5', 'لعلاج عدم انتظام ضربات القلب', NULL, '6221155043498', NULL, 2686, '2024-12-21', 'The FDA approved indications for amiodarone are recurrent ventricular fibrillation (VF) and recurrent hemodynamically unstable ventricular tachycardia (VT). The FDA emphasizes that this drug should only be given in these conditions when they are clinically documented and have not responded to normal therapeutic doses of other antiarrhythmic agents, or when other drugs are not tolerated by the patient.[L3561]



Off-label indications include atrial fibrillation and supraventricular tachycardia.[A189666,A189720,A189723,L11286]', 'Amiodarone is considered a class III anti-arrhythmic drug. It blocks potassium currents that cause repolarization of the heart muscle during the third phase of the cardiac action potential. As a result amiodarone increases the duration of the action potential as well as the effective refractory period for cardiac cells (myocytes). Therefore, cardiac muscle cell excitability is reduced, preventing and treating abnormal heart rhythms.[A183137,A189747]



Unique from other members of the class III anti-arrhythmic drug class, amiodarone also interferes with the functioning of beta-adrenergic receptors, sodium channels, and calcium channels channels. These actions, at times, can lead to undesirable effects, such as hypotension, bradycardia, and Torsades de pointes (TdP).[L11265] In addition to the above, amiodarone may increase activity of peroxisome proliferator-activated receptors, leading to steatogenic changes in the liver or other organs.[A20227,A20650] Finally, amiodarone has been found to bind to the thyroid receptor due to its iodine content, potentially leading to amiodarone induced hypothyroidism or thyrotoxicosis.[A182168]', 'After intravenous administration, amiodarone acts to relax smooth muscles that line vascular walls, decreases peripheral vascular resistance (afterload), and increases the cardiac index by a small amount. Administration by this route also decreases cardiac conduction, preventing and treating arrhythmias.[A1901,A189666,L3561] When it is given orally, however, amiodarone does not lead to significant changes in the left ventricular ejection fraction. Similar to other anti-arrhythmic agents, controlled clinical trials do not confirm that oral amiodarone increases survival.[L3561] 



Amiodarone prolongs the QRS duration and QT interval.  In addition, a decreased SA (sinoatrial) node automaticity occurs with a decrease in AV node conduction velocity. Ectopic pacemaker automaticity is also inhibited.[L11265] Thyrotoxicosis or hypothyroidism may also result from the administration of amiodarone, which contains high levels of iodine, and interferes with normal thyroid function.[A182168]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22010, 'Longavit b12 1mg/ml 3 amp. for im inj', 'لونجافيت ب12 1مجم/مل 3 امبولات', '16', NULL, 'Vitamin b', 'Hydroxocobalamin', 'Memphis', 'Inj', 'حقن', '1mg', '3', 'آلام العمود الفقري.', NULL, '6221050010953', NULL, 2558, '2022-04-19', 'For treatment of pernicious anemia and the prevention and treatment of vitamin B12 deficiency arising from alcoholism, malabsorption, tapeworm infestation, celiac, hyperthyroidism, hepatic-biliary tract disease, persistent diarrhea, ileal resection, pancreatic cancer, renal disease, prolonged stress, vegan diets, macrobiotic diets or other restrictive diets. Also for the treatment of known or suspected cyanide poisoning.', 'Vitamin B12 exists in four major forms referred to collectively as cobalamins; deoxyadenosylcobalamin, methylcobalamin, hydroxocobalamin, and cyanocobalamin. Two of these, methylcobalamin and 5-deoxyadenosyl cobalamin, are primarily used by the body. Methionine synthase needs methylcobalamin as a cofactor. This enzyme is involved in the conversion of the amino acid homocysteine into methionine. Methionine in turn is required for DNA methylation. 5-Deoxyadenosyl cobalamin is a cofactor needed by the enzyme that converts L-methylmalonyl-CoA to succinyl-CoA. This conversion is an important step in the extraction of energy from proteins and fats. Furthermore, succinyl CoA is necessary for the production of hemoglobin, the substances that carries oxygen in red blood cells.', 'Hydroxocobalamin is a synthetic, injectable form of Vitamin B12. Hydroxocobalamin is actually a precursor of two cofactors or vitamins (Vitamin B12 and Methylcobalamin) which are involved in various biological systems in man. Vitamin B12 is required for the conversion of methylmalonate to succinate. Deficiency of this enzyme could therefore interfere with the production of lipoprotein in myelin sheath tissue and so give rise to neurological lesions. The second cofactor, Methylcobalamin, is necessary for the conversion of homocysteine to methionine which is essential for the metabolism of folic acid. Deficiency of tetrahydrafolate leads to reduced synthesis of thymidylate resulting in reduced synthesis of DNA which is essential for cell maturation. Vitamin B12 is also concerned in the maintenance of sulphydryl groups in reduced form, deficiency leading to decreased amounts of reduced SH content of erythrocytes and liver cells. Overall, vitamin B12 acts as a coenzyme for various metabolic functions, including fat and carbohydrate metabolism and protein synthesis. It is necessary for growth, cell replication, hematopoiesis, and nucleoprotein as well as myelin synthesis. This is largely due to its effects on metabolism of methionine folic acid, and malonic acid.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12208, 'Ventophyl 100mg s.r. 16 caps.', 'فنتوفيل 100مجم 16 كبسولة', '5', NULL, 'Bronchodilator.phosphodiesterase inhibitors', 'Theophylline', 'Pharco', 'Cap', 'كبسولة', '100mg', '2', NULL, 'about theophylline bronchodilator a methylxanthine derivative anti asthma in copd. mechanism of action of theophylline it inhibit phosphodiesterase enzyme which degrades cyclic nucleotides intracellularly and it results the cyclic amp accumulation in the', '6223000013663', NULL, 1007, '2022-12-14', 'For the treatment of the symptoms and reversible airflow obstruction associated with chronic asthma and other chronic lung diseases, such as emphysema and chronic bronchitis.', 'Theophylline relaxes the smooth muscle of the bronchial airways and pulmonary blood vessels and reduces airway responsiveness to histamine, methacholine, adenosine, and allergen. Theophylline competitively inhibits type III and type IV phosphodiesterase (PDE), the enzyme responsible for breaking down cyclic AMP in smooth muscle cells, possibly resulting in bronchodilation. Theophylline also binds to the adenosine A2B receptor and blocks adenosine mediated bronchoconstriction. In inflammatory states, theophylline activates histone deacetylase to prevent transcription of inflammatory genes that require the acetylation of histones for transcription to begin.', 'Theophylline, an xanthine derivative chemically similar to caffeine and theobromine, is used to treat asthma and bronchospasm. Theophylline has two distinct actions in the airways of patients with reversible (asthmatic) obstruction; smooth muscle relaxation (i.e., bronchodilation) and suppression of the response of the airways to stimuli (i.e., non-bronchodilator prophylactic effects).', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3634, 'Dicloferaz 50mg 30 dispersible tab.', 'ديكلوفيراز 50 مجم 20 قرص', '63', '31', 'Nsaid.acetic acid derivatives', 'Diclofenac sodium', 'Jedco int. co. for pharmaceuticals > elrazy pharmaceuticals-egypt', 'Tab', 'أقراص', '50mg', '2', 'مسكن عام للالام -- تقليل الالتهابات و الالام', 'description a non-steroidal anti-inflammatory agent (nsaid) with antipyretic and analgesic actions. it is primarily available as the sodium salt. indication : for the acute and chronic treatment of signs and symptoms of osteoarthritis and rheumatoid arthr', '6224000738518', NULL, 1352, '2024-12-18', 'Diclofenac is indicated for use in the treatment of pain and inflammation from varying sources including inflammatory conditions such as osteoarthritis, rheumatoid arthritis, and ankylosing spondylitis, as well as injury-related inflammation due to surgery and physical trauma. It is often used in combination with [misoprostol] as a gastro-protective agent in patients with high risk of developing NSAID-induced ulcers.', 'Diclofenac inhibits cyclooxygenase-1 and -2, the enzymes responsible for production of prostaglandin (PG) G<sub>2</sub> which is the precursor to other PGs.[label,T116] These molecules have broad activity in pain and inflammation and the inhibition of their production is the common mechanism linking each effect of diclofenac.



PGE<sub>2</sub> is the primary PG involved in modulation of nociception.[A179023] It mediates peripheral sensitization through a variety of effects.[T116,A179023] PGE<sub>2</sub> activates the G<sub>q</sub>-coupled EP<sub>1</sub> receptor leading to increased activity of the inositol trisphosphate/phospholipase C pathway. Activation of this pathway releases intracellular stores of calcium which directly reduces action potential threshold and activates protein kinase C (PKC) which contributes to several indirect mechanisms. PGE<sub>2</sub> also activates the EP<sub>4</sub> receptor, coupled to G<sub>s</sub>, which activates the adenylyl cyclase/protein kinase A (AC/PKA) signaling pathway. PKA and PKC both contribute to the potentiation of transient receptor potential cation channel subfamily V member 1 (TRPV1) potentiation, which increases sensitivity to heat stimuli. They also activate tetrodotoxin-resistant sodium channels and inhibit inward potassium currents. PKA further contributes to the activation of the P2X3 purine receptor and sensitization of T-type calcium channels. The activation and sensitization of depolarizing ion channels and inhibition of inward potassium currents serve to reduce the intensity of stimulus necessary to generate action potentials in nociceptive sensory afferents. PGE<sub>2</sub> act via EP<sub>3</sub> to increase sensitivity to bradykinin and via EP<sub>2</sub> to further increase heat sensitivity. Central sensitization occurs in the dorsal horn of the spinal cord and is mediated by the EP<sub>2</sub> receptor which couples to G<sub>s</sub>. Pre-synaptically, this receptor increases the release of pro-nociceptive neurotransmitters glutamate, CGRP, and substance P. Post-synaptically it increases the activity of AMPA and NMDA receptors and produces inhibition of inhibitory glycinergic neurons. Together these lead to a reduced threshold of activating, allowing low intensity stimuli to generate pain signals. PGI<sub>2</sub> is known to play a role via its G<sub>s</sub>-coupled IP receptor although the magnitude of its contribution varies. It has been proposed to be of greater importance in painful inflammatory conditions such as arthritis. By limiting sensitization, both peripheral and central, via these pathways NSAIDs can effectively reduce inflammatory pain.



PGI<sub>2</sub> and PGE<sub>2</sub> contribute to acute inflammation via their IP and EP<sub>2</sub> receptors.[T116,A179044] Similarly to β adrenergic receptors these are G<sub>s</sub>-coupled and mediate vasodilation through the AC/PKA pathway. PGE<sub>2</sub> also contributes by increasing leukocyte adhesion to the endothelium and attracts the cells to the site of injury.[T116] PGD<sub>2</sub> plays a role in the activation of endothelial cell release of cytokines through its DP<sub>1</sub> receptor.[A179044] PGI<sub>2</sub> and PGE<sub>2</sub> modulate T-helper cell activation and differentiation through IP, EP<sub>2</sub>, and EP<sub>4</sub> receptors which is believed to be an important activity in the pathology of arthritic conditions. By limiting the production of these PGs at the site of injury, NSAIDs can reduce inflammation.



PGE<sub>2</sub> can cross the blood-brain barrier and act on excitatory G<sub>q</sub> EP<sub>3</sub> receptors on thermoregulatory neurons in the hypothalamus.[T116] This activation triggers an increase in heat-generation and a reduction in heat-loss to produce a fever. NSAIDs prevent the generation of PGE<sub>2</sub> thereby reducing the activity of these neurons.

', 'Diclofenac reduces inflammation and by extension reduces nociceptive pain and combats fever.[label,T116] It also increases the risk of developing a gastrointestinal ulcer by inhibiting the production of protective mucus in the stomach.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27884, 'Tamoxifen-acdima 10mg 30 tab', 'تاموكسيفين اكديما 10مجم 30 اقراص', '19', NULL, 'Estrogen antagonist', 'Tamoxifen', 'Y.r.p.g. co. ltd. > acdima international trading', 'Tab', 'أقراص', '10mg', '3', NULL, NULL, '6224008302407', NULL, 832, '2022-07-18', 'Tamoxifen is indicated to treat estrogen receptor positive metastatic breast cancer in adults, as an adjuvant in the treatment of early stage estrogen receptor positive breast cancer in adults, to reduce the risk of invasive breast cancer after surgery and radiation in adult women with ductal carcinoma in situ.[L7802]', 'Tamoxifen competitively inhibits estrogen binding to its receptor, which is critical for it''s activity in breast cancer cells.[A1025] Tamoxifen leads to a decrease in tumor growth factor α and insulin-like growth factor 1, and an increase in sex hormone binding globulin.[A1025] The increase in sex hormon binding globulin limits the amount of freely available estradiol.[A1025] These changes reduce levels of factors that stimulate tumor growth.[A1025]



Tamoxifen has also been shown to induce apoptosis in estrogen receptor positive cells.[A182195] This action is thought to be the result of inhibition of protein kinase C, which prevents DNA synthesis.[A182195] Alternate theories for the apoptotic effect of tamoxifen comes from the approximately 3 fold increase in intracellular and mitochondrial calcium ion levels after administration or the induction of tumor growth factor β.[A182195]', 'Tamoxifen is a selective estrogen receptor modulator that inhibits growth and promotes apoptosis in estrogen receptor positive tumors.[A1025,A182195] It has a long duration of action as the active metabolite N-desmethyltamoxifen has a half life of approximately 2 weeks.[L7799,L7802] It has a narrow therapeutic index as higher doses can lead to breathing difficulty or convulsions.[L7799,L7802] Tamoxifen administration is also associated with an increased incidence of uterine malignancies.[L7799,L7802]', 'DrugBank', 0, 0, 0);
