INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28556, 'Trivera hair shampoo 120 ml', 'تراي فيرا شامبو 120 مل', '30', NULL, 'Hair care', 'Olive oil+almond oil+soy bean oil+seasame oil+menthol oil+eruca oil+aloe vera extract+wheat germ oil', 'Hi-care > elrazy pharmaceuticals-egypt', 'Amp', 'أمبول', '120 ml', '1', NULL, NULL, NULL, NULL, 1524, '2022-07-07', 'In a clinical context, olive oil is used as a source of calories and fatty acids in patients requiring parenteral nutrition. A formulation called Clinolipid, comprising olive oil and [soybean oil], is indicated in all age groups, including term and preterm neonates, as a source of calories and essential fatty acids for parenteral nutrition (PN) when oral or enteral nutrition is not possible, insufficient, or contraindicated.[L51013]', 'Fatty acids act as a substrate in energy production through beta-oxidation as well as important components of cell membrane structures and prescursors for bioactive molecules like prostaglandins.', 'Provides a source of calories and fatty acids.[L51013]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23164, 'Loxy probiotic drinkable amoules 10 x 12 ml', 'لوكسي بروبيوتيك امبولات للشرب 10 * 12 مل', '750', '520', 'Immunity booster', 'Bacillus coagulans spores+curcumin+niacin', 'Loxymore pharma', 'Unknown', 'غير محدد', '12 ml', '10', NULL, NULL, NULL, NULL, 1061, '2025-10-31', 'Indicated for intravesical use in the treatment and prophylaxis of carcinoma in situ (CIS) of the urinary bladder and for the prophylaxis of primary or recurrent stage Ta and/or T1 papillary tumors following transurethral resection (TUR). ', NULL, NULL, 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25473, 'Psychopram 20 mg 20 f.c. tabs.', 'سيكوبرام 20مجم 20 قرص', '140', '105', 'Psychiatric.antidepressants', 'Escitalopram', 'Multi-apex > multicare', 'Tab', 'أقراص', '20 mg', '2', NULL, NULL, '6224007326114', NULL, 2003, '2025-01-16', 'Escitalopram is indicated for the acute and maintenance treatment of major depressive disorder (MDD) in adults and pediatric patients 12 years old and older and for the acute treatment of generalized anxiety disorder (GAD) in adults and pediatric patients 7 years old and older.[L8513,L8513] It is additionally indicated for symptomatic relief of obsessive-compulsive disorder (OCD) in Canada.[L8516]', 'Escitalopram, like other selective serotonin re-uptake inhibitors, enhances serotonergic activity by binding to the orthosteric (i.e. primary) binding site on the serotonin transporter (SERT), the same site to which endogenous 5-HT binds, and thus prevents the re-uptake of serotonin into the presynaptic neuron.[A185420,A39738,A185726] Escitalopram, along with [paroxetine], is also considered an allosteric serotonin re-uptake inhibitor - it binds to a secondary allosteric site on the SERT molecule to more strongly inhibit 5-HT re-uptake. Its combination of orthosteric and allosteric activity on SERT allows for greater extracellular 5-HT levels, a faster onset of action, and greater efficacy as compared to other SSRIs. [A185825,A185726] The sustained elevation of synaptic 5-HT eventually causes desensitization of 5-HT<sub>1A</sub> auto-receptors, which normally shut down endogenous 5-HT release in the presence of excess 5-HT - this desensitization may be necessary for the full clinical effect of SSRIs and may be responsible for their typically prolonged onset of action.[A185828,A185726]



Escitalopram has shown little-to-no binding affinity at a number of other receptors, such as histamine and muscarinic receptors, and minor activity at these off-targets may explain some of its adverse effects.[L8513,L8516,L8522,A185726]', 'Escitalopram belongs to a class of medications called selective serotonin re-uptake inhibitors (SSRIs). These agents cause an increase in serotonin levels in neuronal synapses by preventing the re-uptake of serotonin (5-HT) into the presynaptic terminals of serotonergic neurons.[A185420,A39738,L8513,L8516] As compared to other SSRIs, it appears to have a relatively quick onset of effect due to its potency.[A185819,A185822]



SSRIs as a class have been associated with abnormal bleeding, particularly in patients receiving concomitant therapy with other medications affecting hemostasis, and with the development of serotonin syndrome. Use escitalopram with caution in patients with a higher-than-baseline risk of bleeding and in patients receiving concomitant therapy with other serotonergic drugs. Escitalopram may also cause a discontinuation syndrome with abrupt removal of the drug, and should be slowly tapered if discontinuation of therapy is warranted.[L8513,L8516,L8522]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30599, 'Gliflozamet xr 12.5/1000 mg 30 f.c.tabs.', 'جليفلوزاميت اكس 12.5/1000مجم 30 قرص', '288', '216', 'Anti-diabetic.secretagogues.sglt2 inhibitors', 'Empagliflozin+metformin', 'Marcyrl co.', 'Tab', 'أقراص', '1000 mg', '3', NULL, 'it is a prescription medicine used along with diet and exercise to lower blood sugar in adults with type 2 diabetes alone or along with other antidiabetic medicines and also to reduce the risk of cardiovascular death in adults with type 2 diabetes who hav', '6223003572303', NULL, 2006, '2024-10-14', NULL, NULL, NULL, NULL, 0, 0, 0);
