INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18171, 'Eligard 45mg 6-months depot pd. for sc inj.', 'اليجارد 45مجم 6 شهور مخزن للحقن تحت الجلد', '3,917', NULL, 'Gnrh analogue', 'Leuprorelin', 'Astellas pharma inc > multipharma', 'Inj', 'حقن', '45mg', '1', NULL, NULL, NULL, NULL, 755, '2022-08-04', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15880, 'Max hylocare cream', 'ماكس هايلوكير كريم', '260', '175', NULL, 'Dimethicone+hyaluronic acid', 'Arz pharma', 'Cream', 'كريم', NULL, '1', NULL, NULL, NULL, NULL, 816, '2025-10-18', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (3613, 'Diarol 1 mg 20 tab.', 'ديارول 1مجم 20قرص', '18', NULL, 'Anti-diabetic.secretagogues.glinides', 'Repaglinide', 'Amoun', 'Tab', 'أقراص', '1 mg', '2', NULL, 'about repaglinide meglitinide derivative insulin secretagogue oral anti-diabetic drug. mechanism of action of repaglinide it is a quick and short acting antidiabetic drug which exerts it`s action by stimulating insulin release from ?-cells of pancreas. it', '6221025017475', NULL, 953, '2022-12-04', 'As an adjunct to diet and exercise to improve glycemic control in adults with type 2 diabetes mellitus. ', 'Repaglinide activity is dependent on the presence functioning &beta; cells and glucose. In contrast to sulfonylurea insulin secretatogogues, repaglinide has no effect on insulin release in the absence of glucose. Rather, it potentiates the effect of extracellular glucose on ATP-sensitive potassium channel and has little effect on insulin levels between meals and overnight. As such, repaglinide is more effective at reducing postprandial blood glucose levels than fasting blood glucose levels and requires a longer duration of therapy (approximately one month) before decreases in fasting blood glucose are observed. The insulinotropic effects of repaglinide are highest at intermediate glucose levels (3 to 10 mmol/L) and it does not increase insulin release already stimulated by high glucose concentrations (greater than 15 mmol/L). Repaglinide appears to be selective for pancreatic &beta; cells and does not appear to affect skeletal or cardiac muscle or thyroid tissue. ', 'Insulin secretion by pancreatic &beta; cells is partly controlled by cellular membrane potential. Membrane potential is regulated through an inverse relationship between the activity of cell membrane ATP-sensitive potassium channels (ABCC8) and extracellular glucose concentrations. Extracellular glucose enters the cell via GLUT2 (SLC2A2) transporters. Once inside the cell, glucose is metabolized to produce ATP. High concentrations of ATP inhibit ATP-sensitive potassium channels causing membrane depolarization. When extracellular glucose concentrations are low, ATP-sensitive potassium channels open causing membrane repolarization. High glucose concentrations cause ATP-sensitive potassium channels to close resulting in membrane depolarization and opening of L-type calcium channels. The influx of calcium ions stimulates calcium-dependent exocytosis of insulin granules. Repaglinide increases insulin release by inhibiting ATP-sensitive potassium channels in a glucose-dependent manner. ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15608, 'Cafa ds hair cream 120 gm', 'كافا دي اس كريم الشعر 120 جم', '199', '169', 'Hair care', 'Caffeine+olive oil+panthenol+aloe vera+vitamin a+vitamin e', 'El-helou perfumes & cosmetics > beauty & beyond international', 'Cream', 'كريم', '120 gm', '1', NULL, 'advanced formula for accelerating hair growth deeply moisturizing hair and protecting hair from damage', '6224007424452', NULL, 1459, '2024-11-04', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27315, 'Sovelpak 400/100mg 28 tablets', 'سوفيلباك 400/100مجم 28 قرص', '1,270', NULL, 'Antiviral', 'Sofosbuvir+velpatasvir', 'Marcyrl', 'Tablet', 'أقراص', '100mg', '4', 'مضاد فيروسات', NULL, NULL, NULL, 5515, '2022-04-07', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15969, 'Cefacet plus 10 caps.', 'سيفاسيت بلس 10 كبسولات', '5', NULL, 'Nsaid.analgesic', 'Acetylsalicylic acid+caffeine+paracetamol(acetaminophen)', 'Jedco int. co. for pharmaceuticals', 'Cap', 'كبسولة', NULL, '1', NULL, 'description: it contain three active ingredients aspirin paracetamol and caffeine. aspirin: belongs to a group of medicines called non-steroidal anti-inflammatory drugs (nsaids). it works by blocking the action of a substance in the body called cyclo-oxyg', NULL, NULL, 889, '2022-07-17', '**Pain, fever, and inflammation**



Acetylsalicylic acid (ASA), in the regular tablet form (immediate-release), is indicated to relieve pain, fever, and inflammation associated with many conditions, including the flu, the common cold, neck and back pain, dysmenorrhea, headache, tooth pain, sprains, fractures, myositis, neuralgia, synovitis, arthritis, bursitis, burns, and various injuries. It is also used for symptomatic pain relief after surgical and dental procedures [FDA label]. 



The _extra strength_ formulation of acetylsalicylic acid is also indicated for the management migraine pain with photophobia (sensitivity to light) and phonophobia (sensitivity to sound)[FDA label].



**Other indications**



ASA is also indicated for various other purposes, due to its ability to inhibit platelet aggregation. These include: 



Reducing the risk of cardiovascular death in suspected cases of myocardial infarction (MI) [FDA label]. 



Reducing the risk of a first non-fatal myocardial infarction in patients, and for reducing the risk of morbidity and mortality in cases of unstable angina and in those who have had a prior myocardial infarction [FDA label].



For reducing the risk of transient ischemic attacks (TIA) and to prevent atherothrombotic cerebral infarction (in conjunction with other treatments) [FDA label].



For the prevention of thromboembolism after hip replacement surgery [FDA label]. 



For decreasing platelet to platelet adhesion following carotid endarterectomy, aiding in the prevention of transient ischemic attacks (TIA) [FDA label].



Used for patients undergoing hemodialysis with a silicone rubber arteriovenous cannula inserted to prevent thrombosis at the insertion site [FDA Label]. 



**Important note regarding use of the extended-release formulation [F4405]**



In the setting of acute myocardial infarction, or before percutaneous interventions, the extended-release form of acetylsalicylic acid should not be used. Use immediate-release formulations in scenarios requiring rapid onset of action [Label, F4405]. The extended-release form is taken to decrease the incidence of mortality and myocardial infarction (MI) for individuals diagnosed with chronic coronary artery disease (CAD), including patients with previous myocardial infarction (MI) or unstable angina or with chronic stable angina. Additionally, the extended-release form is used to decrease the risk of death and recurrent episodes of stroke in patients with a history of stroke or TIA [F4405].





 ', 'Acetylsalicylic acid (ASA) blocks prostaglandin synthesis. It is non-selective for COX-1 and COX-2 enzymes [A177241, A10989, A32682]. Inhibition of COX-1 results in the inhibition of platelet aggregation for about 7-10 days (average platelet lifespan). The acetyl group of acetylsalicylic acid binds with a serine residue of the cyclooxygenase-1 (COX-1) enzyme, leading to irreversible inhibition. This prevents the production of pain-causing prostaglandins. This process also stops the conversion of arachidonic acid to thromboxane A2 (TXA2), which is a potent inducer of platelet aggregation [FDA label].  Platelet aggregation can result in clots and harmful venous and arterial thromboembolism, leading to conditions such as pulmonary embolism and stroke. 



It is important to note that there is 60% homology between the protein structures of COX-1 and COX-2. ASA binds to serine 516 residue on the active site of COX-2 in the same fashion as its binding to the serine 530 residue located on the active site of COX-1. The active site of COX-2 is, however, slightly larger than the active site of COX-1, so that arachidonic acid (which later becomes prostaglandins) manages to bypass the aspirin molecule inactivating COX-2 [A32682, A177256].  ASA, therefore, exerts more action on the COX-1 receptor rather than on the COX-2 receptor [A177268]. A higher dose of acetylsalicylic acid is required for COX-2 inhibition [A177325]. ', '**Effects on pain and fever**



Acetylsalicylic acid disrupts the production of prostaglandins throughout the body by targeting cyclooxygenase-1 (COX-1) and cyclooxygenase-2 (COX-2) [A177241, A10989, A32682]. Prostaglandins are potent, irritating substances that have been shown to cause headaches and pain upon injection into humans.  Prostaglandins increase the sensitivity of pain receptors and substances such as histamine and bradykinin. Through the disruption of the production and prevention of release of prostaglandins in inflammation, this drug may stop their action at pain receptors, preventing symptoms of pain. Acetylsalicylic acid is considered an antipyretic agent because of its ability to interfere with the production of brain prostaglandin E1. Prostaglandin E1 is known to be an extremely powerful fever-inducing agent [FDA label]. 



**Effects on platelet aggregation**



The inhibition of platelet aggregation by ASA occurs because of its interference with thromboxane A2 in platelets, caused by COX-1 inhibition. Thromboxane A2 is an important lipid responsible for platelet aggregation, which can lead to clot formation and future risk of heart attack or stroke  [FDA label]. 



**A note on cancer prevention**



ASA has been studied in recent years to determine its effect on the prevention of various malignancies [A177325]. In general, acetylsalicylic acid is involved in the interference of various cancer signaling pathways, sometimes inducing or upregulating tumor suppressor genes [A177325, A177403]. Results of various studies suggest that there are beneficial effects of long-term ASA use in the prevention of several types of cancer, including stomach, colorectal, pancreatic, and liver cancers [A177400].   Research is ongoing.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2089, 'Care by care baby soap 100 gm', 'كير باي كير بيبي صابونة 100 جرام', '60', '50', 'Soothing topical', 'Zinc oxide+lanolin+olive oil+vaseline+triclosan+bees wax', 'El-helou perfumes & cosmetics > macro group pharmaceuticals', 'Soap', 'صابون', '100 gm', '1', NULL, NULL, '6224010643239', NULL, 1083, '2025-03-04', 'Zinc can be used for the treatment and prevention of zinc deficiency/its consequences, including stunted growth and acute diarrhea in children, and slowed wound healing. It is also utilized for boosting the immune system, treating the common cold and recurrent ear infections, as well as preventing lower respiratory tract infections [L2172].  



', '**Zinc has three primary biological roles**: _catalytic_, _structural_, and _regulatory_. The catalytic and structural role of zinc is well established, and there are various noteworthy reviews on these functions. For example, zinc is a structural constituent in numerous proteins, inclusive of growth factors, cytokines, receptors, enzymes, and transcription factors for different cellular signaling pathways. It is implicated in numerous cellular processes as a cofactor for approximately 3000 human proteins including enzymes, nuclear factors, and hormones [L2096].



Zinc promotes resistance to epithelial apoptosis through cell protection (cytoprotection) against reactive oxygen species and bacterial toxins, likely through the antioxidant activity of the cysteine-rich metallothioneins [A32419].



In HL-60 cells (promyelocytic leukemia cell line), zinc enhances the up-regulation of A20 mRNA, which, via TRAF pathway, decreases NF-kappaB activation, leading to decreased gene expression and generation of tumor necrosis factor-alpha (TNF-alpha), IL-1beta, and IL-8 [A32418].



There are several mechanisms of action of zinc on acute diarrhea. Various mechanisms are specific to the gastrointestinal system: zinc restores mucosal barrier integrity and enterocyte brush-border enzyme activity, it promotes the production of antibodies and circulating lymphocytes against intestinal pathogens, and has a direct effect on ion channels, acting as a potassium channel blocker of adenosine 3-5-cyclic monophosphate-mediated chlorine secretion. Cochrane researchers examined the evidence available up to 30 September 2016 [L2106].



Zinc deficiency in humans decreases the activity of serum _thymulin_ (a hormone of the thymus), which is necessary for the maturation of T-helper cells. T-helper 1 (Th(1)) cytokines are decreased but T-helper 2 (Th(2)) cytokines are not affected by zinc deficiency in humans [A32417].



The change of _Th(1)_ to _Th(2)_ function leads to cell-mediated immune dysfunction. Because IL-2 production (Th(1) cytokine) is decreased, this causes decreased activity of natural-killer-cell (NK cell) and T cytolytic cells, normally involved in killing viruses, bacteria, and malignant cells [A32424]. 



In humans, zinc deficiency may lead to the generation of new CD4+ T cells, produced in the thymus. In cell culture studies (HUT-78, a Th(0) human malignant lymphoblastoid cell line), as a result of zinc deficiency, nuclear factor-kappaB (NF-kappaB) activation, phosphorylation of IkappaB, and binding of NF-kappaB to DNA are decreased and this results in decreased Th(1) cytokine production [A32417].



In another study, zinc supplementation in human subjects suppressed the gene expression and production of pro-inflammatory cytokines and decreased oxidative stress markers [A32424]. In HL-60 cells (a human pro-myelocytic leukemia cell line), zinc deficiency increased the levels of TNF-alpha, IL-1beta, and IL-8 cytokines and mRNA. In such cells, zinc was found to induce A20, a zinc finger protein that inhibited NF-kappaB activation by the tumor necrosis factor receptor-associated factor pathway. This process decreased gene expression of pro-inflammatory cytokines and oxidative stress markers [A32417].



The exact mechanism of zinc in acne treatment is poorly understood. However, zinc is considered to act directly on microbial inflammatory equilibrium and facilitate antibiotic absorption when used in combination with other agents. Topical zinc alone as well as in combination with other agents may be efficacious because of its anti-inflammatory activity and ability to reduce P. acnes bacteria by the inhibition of P. acnes lipases and free fatty acid levels [L2102].', 'Zinc is involved in various aspects of cellular metabolism. It has been estimated that approximately 10% of human proteins may bind zinc, in addition to hundreds of proteins that transport and traffic zinc. It is required for the catalytic activity of more than 200 enzymes, and it plays a role in immune function wound healing, protein synthesis, DNA synthesis, and cell division. Zinc is an essential element for a proper sense of taste and smell and supports normal growth and development during pregnancy, childhood, and adolescence. It is thought to have antioxidant properties, which may be protective against accelerated aging and helps to speed up the healing process after an injury; however, studies differ as to its effectiveness. Zinc ions are effective antimicrobial agents even if administered in low concentrations [L2211].



Studies on oral zinc for specific conditions shows the following evidence in various conditions [L2095]:



**Colds:** Evidence suggests that if zinc lozenges or syrup are taken within 24 hours after cold symptoms start, the supplement may shorten the length of colds. The use intranasal zinc has been associated with the loss of the sense of smell, in some cases long-term or permanently [L2095].



**Wound healing:** Patients with skin ulcers and decreased levels of zinc may benefit from oral zinc supplements [L2095]. 



**Diahrrea**: Oral zinc supplements can reduce the symptoms of diarrhea in children with low levels of zinc, especially in cases of malnutrition [L2095].

', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (8373, 'Moxen 7.5 mg 20 tabs', 'موكسين 7.5مجم 20 قرص', '28', '22.5', 'Nsaid.oxicam', 'Meloxicam', 'Egpi', 'Tab', 'أقراص', '7.5 mg', '2', NULL, NULL, '6223003930264', NULL, 2398, '2024-08-10', 'Meloxicam is indicated for the symptomatic treatment of arthritis and osteoarthritis.  In addition, it is indicated for the pauciarticular and polyarticular course of Juvenile Rheumatoid Arthritis (JRA) in patients aged 2 years old or above.[L11398] Off-label uses include the treatment of dental or post-surgical pain. In addition to the above, meloxicam has also been studied in the treatment of neuropathic pain. [A190189]



Meloxicam, in combination with [bupivacaine], is indicated for postsurgical analgesia in adult patients for up to 72 hours following soft tissue surgical procedures, foot and ankle procedures, and other orthopedic procedures in which direct exposure to articular cartilage is avoided.[L50427]', 'Meloxicam inhibits prostaglandin synthetase (cylooxygenase 1 and 2) enzymes leading to a decreased synthesis of prostaglandins, which normally mediate painful inflammatory symptoms.[A176366] As prostaglandins sensitize neuronal pain receptors, inhibition of their synthesis leads to analgesic and inflammatory effects. Meloxicam preferentially inhibits COX-2, but also exerts some activity against COX-1, causing gastrointestinal irritation.[A190189,L11398]', 'Meloxicam is an anti-inflammatory, analgesic analgesic with antipyretic effects in fever.[A190189] Prostaglandins are substances that contribute to inflammation.[A176366] This drug also exerts preferential actions against COX-2[A190195], which may reduce the possible gastrointestinal effects of this drug.  



In humans, meloxicam has demonstrated the ability to decrease erythrocyte sedimentation rate(ESR) in patients with rheumatoid arthritis, and to decrease ESR, C-reactive protein (CRP), as well as  aquaporin-1 expression.[A190189] As with other NSAIDS, prolonged use of meloxicum can result in renal or cardiovascular impairment or thrombotic cardiovascular events.[L11398]



A note on gastrointestinal effects



As meloxicam preferentially inhibits COX-2, it is thought to cause less gastrointestinal irritation compared to other NSAIDS. Despite this, it still carries a risk of gastric inflammation, bleeding and ulceration.[A190201,L11398]   In one study, patients on meloxicam suffered from gastrointestinal symptoms at a rate of 13% compared to 19% of those on [diclofenac]. GI events were found to be less severe in the meloxicam-treated patients.[A190189]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24233, 'Orchamethasone 0.1% eye drops sdu 20 doses', 'اوركاميثازون 0.1% قطرة عين 20 وحدة مفردة', '72', '40', 'Antibiotic anti-inflamatory eye drops', 'Dexamethasone', 'Orchidia pharmaceutical industries', 'Drops', 'نقط', '0.1%', '1', NULL, NULL, '6223005444486', NULL, 2677, '2024-12-26', 'Dexamethasone and [ciprofloxacin] otic suspension is indicated for bacterial infections with inflammation in acute otitis media and acute otitis externa.[L10698] Intramuscular and intravenous injections are indicated for a number of endocrine, rheumatic, collagen, dermatologic, allergic, ophthalmic, gastrointestinal, respiratory, hematologic, neoplastic, edematous, and other conditions.[L10701] Oral tablets are indicated for the treatment of multiple myeloma.[L10710] An intravitreal implant is indicated for some forms of macular edema and non-infectious posterior uveitis affecting the posterior of the eye.[L10719] Various ophthalmic formulations are indicated for inflammatory conditions of the eye.[L10704,L10707,L10713,L10716,L10722,L10725]', 'The short term effects of corticosteroids are decreased vasodilation and permeability of capillaries, as well as decreased leukocyte migration to sites of inflammation.[A187463] Corticosteroids binding to the glucocorticoid receptor mediates changes in gene expression that lead to multiple downstream effects over hours to days.[A187463]



Glucocorticoids inhibit neutrophil apoptosis and demargination; they inhibit phospholipase A2, which decreases the formation of arachidonic acid derivatives; they inhibit NF-Kappa B and other inflammatory transcription factors; they promote anti-inflammatory genes like interleukin-10.[A187463]



Lower doses of corticosteroids provide an anti-inflammatory effect, while higher doses are immunosuppressive.[A187463] High doses of glucocorticoids for an extended period bind to the mineralocorticoid receptor, raising sodium levels and decreasing potassium levels.[A187463]', 'Corticosteroids bind to the glucocorticoid receptor, inhibiting pro-inflammatory signals, and promoting anti-inflammatory signals.[A187463] Dexamethasone''s duration of action varies depending on the route.[L10698,L10701,L10704,L10707,L10710,L10713,L10716,L10719,L10722,L10725] Corticosteroids have a wide therapeutic window as patients may require doses that are multiples of what the body naturally produces.[A187463] Patients taking corticosteroids should be counselled regarding the risk of hypothalamic-pituitary-adrenal axis suppression and increased susceptibility to infections.[A187463]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14715, 'Avene thermal spring water 150 ml', 'افين مياه الينابيع الحرارية المضادة للتهيج 150 مل', '230', '230', 'Skin care', NULL, 'Pierre - fabre france > biotech egypt', 'Unknown', 'غير محدد', '150 ml', '1', NULL, 'avene thermal spring water soothes softens and calms skin. achieve maximum hydration with our large size bottle. ï¿½ soothes redness associated with inflammation ï¿½ calms itching sensations ï¿½ rich in silicate and trace elements ï¿½ low salt mineral con', NULL, NULL, 821, '2022-07-21', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26940, 'Sigmalip 20 mg 10 f.c.tab.', 'سيجماليب 20مجم 10 اقراص', '24', NULL, 'Antihyperlipidemic.statins', 'Atorvastatin', 'Sigma', 'Tab', 'أقراص', '20 mg', '1', NULL, 'description : is a member of the drug class known as statins. dose: adult po- initial- the recommended dose is 10 mg daily. maximum: 80 mg/day. indication : may be used as primary prevention in individuals with multiple risk factors for coronary heart dis', '6221051013458', NULL, 1003, '2022-12-08', 'Atorvastatin is indicated for the treatment of several types of dyslipidemias, including primary hyperlipidemia and mixed dyslipidemia in adults, hypertriglyceridemia, primary dysbetalipoproteinemia, homozygous familial hypercholesterolemia, and heterozygous familial hypercholesterolemia in adolescent patients with failed dietary modifications.[A177397]



Dyslipidemia describes an elevation of plasma cholesterol, triglycerides or both as well as to the presence of low levels of high-density lipoprotein. This condition represents an increased risk for the development of atherosclerosis.[L6025] 



Atorvastatin is indicated, in combination with dietary modifications, to prevent cardiovascular events in patients with cardiac risk factors and/or abnormal lipid profiles.[A177397]



Atorvastatin can be used as a preventive agent for myocardial infarction, stroke, revascularization, and angina, in patients without coronary heart disease but with multiple risk factors and in patients with type 2 diabetes without coronary heart disease but multiple risk factors.[A177397]



Atorvastatin may be used as a preventive agent for non-fatal myocardial infarction, fatal and non-fatal stroke, revascularization procedures, hospitalization for congestive heart failure and angina in patients with coronary heart disease.[A177397]



Prescribing of statin medications is considered standard practice following any cardiovascular events and for people with a moderate to high risk of development of CVD. Statin-indicated conditions include diabetes mellitus, clinical atherosclerosis (including myocardial infarction, acute coronary syndromes, stable angina, documented coronary artery disease, stroke, trans ischemic attack (TIA), documented carotid disease, peripheral artery disease, and claudication), abdominal aortic aneurysm, chronic kidney disease, and severely elevated LDL-C levels.[A181087, A181406]', 'Atorvastatin is a statin medication and a competitive inhibitor of the enzyme HMG-CoA (3-hydroxy-3-methylglutaryl coenzyme A) reductase, which catalyzes the conversion of HMG-CoA to mevalonate, an early rate-limiting step in cholesterol biosynthesis.[A177388,A181421] Atorvastatin acts primarily in the liver, where decreased hepatic cholesterol concentrations stimulate the upregulation of hepatic low-density lipoprotein (LDL) receptors, which increases hepatic uptake of LDL. Atorvastatin also reduces Very-Low-Density Lipoprotein-Cholesterol (VLDL-C), serum triglycerides (TG) and Intermediate Density Lipoproteins (IDL), as well as the number of apolipoprotein B (apo B) containing particles, but increases High-Density Lipoprotein Cholesterol (HDL-C). 



_In vitro_ and _in vivo_ animal studies also demonstrate that atorvastatin exerts vasculoprotective effects independent of its lipid-lowering properties, also known as the pleiotropic effects of statins.[A181424] These effects include improvement in endothelial function, enhanced stability of atherosclerotic plaques, reduced oxidative stress and inflammation, and inhibition of the thrombogenic response. Statins were also found to bind allosterically to β2 integrin function-associated antigen-1 (LFA-1), which plays an essential role in leukocyte trafficking and T cell activation.[A181559]', 'Atorvastatin is an oral antilipemic agent that reversibly inhibits HMG-CoA reductase. It lowers total cholesterol, low-density lipoprotein-cholesterol (LDL-C), apolipoprotein B (apo B), non-high density lipoprotein-cholesterol (non-HDL-C), and triglyceride (TG) plasma concentrations while increasing HDL-C concentrations. High LDL-C, low HDL-C and high TG concentrations in the plasma are associated with increased risk of atherosclerosis and cardiovascular disease. The total cholesterol to HDL-C ratio is a strong predictor of coronary artery disease, and high ratios are associated with a higher risk of disease. Increased levels of HDL-C are associated with lower cardiovascular risk. By decreasing LDL-C and TG and increasing HDL-C, atorvastatin reduces the risk of cardiovascular morbidity and mortality.[A174580,A181087,A181406,A181925] 



Elevated cholesterol levels (and high low-density lipoprotein (LDL) levels in particular) are an important risk factor for the development of CVD.[A181087] Clinical studies have shown that atorvastatin reduces LDL-C and total cholesterol by 36-53%.[A177415] In patients with dysbetalipoproteinemia, atorvastatin reduced the levels of intermediate-density lipoprotein cholesterol.[A177397] It has also been suggested that atorvastatin can limit the extent of angiogenesis, which can be useful in the treatment of chronic subdural hematoma.[A177388]



**Myopathy/Rhabdomyolysis**



Atorvastatin, like other HMG-CoA reductase inhibitors, is associated with a risk of drug-induced myopathy characterized by muscle pain, tenderness, or weakness in conjunction with elevated levels of creatine kinase (CK). Myopathy often manifests as rhabdomyolysis with or without acute renal failure secondary to myoglobinuria. The risk of statin-induced myopathy is dose-related, and the symptoms of myopathy are typically resolved upon drug discontinuation. Results from observational studies suggest that 10-15% of people taking statins may experience muscle aches at some point during treatment.[A182258]



**Liver Dysfunction**



Statins, like some other lipid-lowering therapies, have been associated with biochemical abnormalities of liver function. Persistent elevations (> 3 times the upper limit of normal [ULN] occurring on two or more occasions) in serum transaminases occurred in 0.7% of patients who received atorvastatin in clinical trials. This effect appears to be dose-related.[F4670,F4673]



**Endocrine Effects**



Statins are associated with a risk of increased serum HbA1c and glucose levels. An _in vitro_ study demonstrated a dose-dependent cytotoxic effect on human pancreatic islet β cells following treatment with atorvastatin. Moreover, insulin secretion rates decreased relative to control.[A182012]



HMG-CoA reductase inhibitors interfere with cholesterol synthesis and may theoretically interfere with the production of adrenal and/or gonadal steroids. Clinical studies with atorvastatin and other HMG-CoA reductase inhibitors have suggested that these agents do not affect plasma cortisol concentrations, basal plasma testosterone concentration, or adrenal reserve. However, the effect of statins on male fertility has not been fully investigated. The effects of statins on the pituitary-gonadal axis in premenopausal women are unknown.[F4673]



**Cardiovascular**



Significant decreases in circulating ubiquinone levels in patients treated with atorvastatin and other statins have been observed. The clinical significance of a potential long-term statin-induced deficiency of ubiquinone has not been established. It has been reported that a decrease in myocardial ubiquinone levels could lead to impaired cardiac function in patients with borderline congestive heart failure.[F4673]



**Lipoprotein A**



In some patients, the beneficial effect of lowered total cholesterol and LDL-C levels may be partly blunted by the concomitant increase in Lp(a) lipoprotein concentrations. Present knowledge suggests the importance of high Lp(a) levels as an emerging risk factor for coronary heart disease.[F4673] Further studies have demonstrated statins affect Lp(a) levels differently in patients with dyslipidemia depending on their apo(a) phenotype; statins increase Lp(a) levels exclusively in patients with the low molecular weight apo(a) phenotype.[A181418]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5522, 'Ginso max 30 cap', 'جنسو ماكس 30 كبسولة', '84', NULL, 'Multivitamin', 'Ginseng', 'China', 'Cap', 'كبسولة', NULL, '3', 'لزيادة الوزن -- علاج النحافة الشديدة -- فيتامينات متعددة', NULL, NULL, NULL, 9076, '2023-01-28', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13627, 'Rumaximap 60mg 30 f.c. tab', 'روماكسيماب 60مجم 30 قرص', '147', NULL, 'Nsaid.selective cox-2 inhibitors', 'Etoricoxib', 'Apex pharma', 'Tab', 'أقراص', '60mg', '3', NULL, NULL, '6223003206628', NULL, 1738, '2022-08-31', 'For the treatment of rheumatoid arthritis, osteoarthritis, ankylosing spondylitis, chronic low back pain, acute pain and gout.', 'Like any other COX-2 selective inhibitor Etoricoxib selectively inhibits isoform 2 of cyclo-oxigenase enzyme (COX-2), preventing production of prostaglandins (PGs) from arachidonic acid.', 'Etoricoxib is a COX-2 selective inhibitor (approximately 106 times more selective for COX-2 inhibition over COX-1).', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30437, 'Dupixent 200 mg 2 pref. syringe s.c.', 'دوبيكسنت 200مجم 2 سرنجة معبأة تحت الجلد', '54,192', '24633', NULL, 'Dupilumab', 'Sanofi > sanofi scientific office', 'Syringe', 'حقنة', '200 mg', '2', NULL, 'dupilumab is a fully human monoclonal antibody produced in chinese hamster ovary (cho) cells by recombinant dna technology. indications: - atopic dermatitis: indicated for the treatment of moderate-to-severe atopic dermatitis in adults and adolescents 12', NULL, NULL, 979, '2024-12-15', 'In the US, dupilumab is indicated for the treatment of patients aged six months and older with moderate-to-severe atopic dermatitis whose disease is not adequately controlled with topical prescription therapies or when those therapies are not advisable.[L42005] In Europe and Canada, the drug for this indication is approved for patients aged six years and older.[L7192,L41439] In Europe, patients six to 11 years of age should have severe atopic dermatitis and be candidates for systemic therapy.[L7192] Dupilumab can be used with or without topical corticosteroids for this condition.[L7186,L41439]



Dupilumab is indicated as an add-on maintenance treatment of patients aged six years and older with moderate-to-severe asthma characterized by an eosinophilic phenotype or with oral corticosteroid dependent asthma. However, the drug is not indicated for relief of acute bronchospasm or status asthmaticus.[L7186,L7192,L41439]



Dupilumab is indicated as an add-on maintenance treatment in adult patients with inadequately controlled chronic rhinosinusitis with nasal polyposis.[L7186] In Canada and Europe, it is used with intranasal corticosteroids.[L7192,L41439]



In the US and Europe, dupilumab is also indicated for the treatment of adults and children aged 12 years and older weighing at least 40 kg with eosinophilic esophagitis (EoE), and adults with prurigo nodularis.[L7192,L41900,L43297]', 'Type 2 inflammatory processes in various allergic and atopic conditions, such as asthma and atopic diseases, involve the type 2 helper T-cell (Th2) immunity.[A180709] Upregulation of this Type 2/Th2 pathway is commonly observed in other inflammatory conditions [A180712] and the activation of Th2 cells is linked to the production of Th2-associated cytokines, such as interleukin (IL) 4, IL-5, IL-9, and IL-13.[A180709] IL-4 and IL-13 play a central role in inducing inflammatory conditions such as allergic rhinitis, asthma, and atopic dermatitis,[L7225] by regulating Type 2 inflammation and immune function. These inflammatory cytokines work by modulating gene expression downstream of receptor signalling,[A180712] regulating Th2 cell differentiation, and activating inflammatory cells such as mast cells and macrophages.[A181274] 



There are two types of receptors for IL-4: the type 1 receptor, which is composed of the IL-4 chain (IL-4Rα) and a γ chain (γC), and the type 2 receptor, which is composed of the IL-4Rα chain and the α1 chain of the IL-13 receptor (IL-13Rα1).[A180709] Essentially, IL‐4Rα is a component shared by the IL‐4 and IL-13 receptor complexes [A180478] and is ubiquitously expressed on both innate and adaptive immune cells to promote the signaling of IL-4 and IL-13.[A181274] The type I receptor is primarily expressed on lymphocytes and controls Th2-cell differentiation, whereas the type II receptor is mostly found across resident and myeloid cells.[A18874] Dupilumab is a fully human monoclonal antibody directed against IL‐4Rα to inhibit the signalling of IL‐4 and IL‐13.[A180712] Dupilumab inhibits IL-4 signalling via the Type I receptor (IL-4Rα/γc), and both IL-4 and IL-13 signaling through the Type II receptor (IL-4Rα/IL-13Rα).[L7192] It ultimately downregulates type-2 immunity.[A180709]', 'Dupilumab is an recombinant human IgG4 antibody to the IL-4 receptor that works by inhibiting the activation of certain pro-inflammatory cytokines that are implicated in the pathophysiology of several allergic and atopic conditions, including asthma, chronic rhinosinusitis with nasal polyps, and food and environmental allergies. _In vivo_, dupilumab was shown to reduce the levels of type 2 inflammatory biomarkers associated with atopic dermatitis, such as thymus and activation-regulated chemokine (TARC/CCL17), total serum IgE, allergen-specific IgE, and lactate dehydrogenase (LDH). A decrease in the levels of biomarkers of asthma, such as FeNO, eotaxin-3, IgE, periostin, and eotaxin-3 (CCL26) was also observed.[L7192,L7225] Since dupilumab works to suppress the immune response, it is proposed that it may influence the immune response against some infections, such as helminth infections, by inhibiting IL-4/IL-13 signaling. It is advised that infections are appropriately treated until resolved before initiating dupilumab therapy.[L7192] 



While findings of some _in vitro_ and _in vivo_ studies suggest that some cytokine modulators may influence the expression and activity of specific cytochrome P450 (CYP450) enzymes, an open-label drug-drug interaction study demonstrated that dupilumab displays no significant effect on the activity of CYP450 enzymes studied (CYP3A, CYP2C19, CYP2C9, CYP1A2, and CYP2D6).[A180712]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26494, 'Sandimmun neoral 50mg 50 soft gelatin caps.', 'سانديميون نيورال 50مجم 50 كبسولة', '828', '460', 'Immunosuppressants', 'Cyclosporin', 'Novartis', 'Cap', 'كبسولة', '50mg', '1', NULL, NULL, NULL, NULL, 12688, '2024-10-16', NULL, NULL, NULL, NULL, 0, 0, 0);
