INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4694, 'Fareston 60mg 100 tab', 'فاريستون 60مجم 100 قرص', '1,213', NULL, NULL, 'Toremifene', 'Company', 'Tab', 'أقراص', '60mg', '1', 'يعالج سرطان الثدي. وهو يعمل عن طريق منع هرمون الاستروجين في أنسجة الثدي، مما يمنع خلايا سرطان الثدي من الانتشار أو النمو.', NULL, NULL, NULL, 1430, '2023-05-30', 0, 'For the treatment of metastatic breast cancer in postmenopausal women with estrogen receptor-positive or receptor-unknown tumors. Toremifene is currently under investigation as a preventative agent for prostate cancer in men with high-grade prostatic intraepithelial neoplasia and no evidence of prostate cancer.', 'Toremifene is a nonsteroidal triphenylethylene derivative. Toremifene binds to estrogen receptors and may exert estrogenic, antiestrogenic, or both activities, depending upon the duration of treatment, animal species, gender, target organ, or endpoint selected. The antitumor effect of toremifene in breast cancer is believed to be mainly due to its antiestrogenic effects, in other words, its ability to compete with estrogen for binding sites in the cancer, blocking the growth-stimulating effects of estrogen in the tumor. Toremifene may also inhibit tumor growth through other mechanisms, such as induction of apoptosis, regulation of oncogene expression, and growth factors.', 'Toremifene is an antineoplastic hormonal agent primarily used in the treatment of advanced breast cancer. Toremifene is a nonsteroidal agent that has demonstrated potent antiestrogenic properties in animal test systems. The antiestrogenic effects may be related to its ability to compete with estrogen for binding sites in target tissues such as breast. Toremifene inhibits the induction of rat mammary carcinoma induced by dimethylbenzanthracene (DMBA) and causes the regression of already established DMBA-induced tumors. In this rat model, Toremifene appears to exert its antitumor effects by binding the estrogen receptors. In cytosols derived from human breast adenocarcinomas, Toremifene competes with estradiol for estrogen receptor protein.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4696, 'Rotalabtal 5mg/ml i.v./inf. vial 20 ml', 'روتالابتال 5مجم 20 مل فيال', '63', NULL, 'Anti-hypertensive.beta blocker.unselective', 'Labetalol', 'Rotabiogen', 'Vial', 'فيال', '5mg', '1', NULL, NULL, NULL, NULL, 709, '2025-06-19', 0, 'Labetalol injections are indicated to control blood pressure in severe hypertension.[L7727] Labetalol tablets are indicated alone or in combination with antihypertensives like thiazides and loop diuretics to manage hypertension.[L7730]', 'Labetalol non-selectively antagonizes beta-adrenergic receptors, and selectively antagonizes alpha-1-adrenergic receptors.[A181901] Following oral administration, labetalol has 3 times the beta-blocking ability than alpha-blocking ability.[A181901] This increases to 6.9 times following intravenous administration.[A181901] Antagonism of alpha-1-adrenergic receptors leads to vasodilation and decreased vascular resistance.[A181907] This leads to a decrease in blood pressure that is most pronounced while standing.[A181910] Antagonism of beta-1-adrenergic receptors leads to a slight decrease in heart rate.[L7727] Antagonism of beta-2-adrenergic receptors leads to some of the side effects of labetalol such as bronchospasms, however this may be slightly attenuated by alpha-1-adrenergic antagonism.[A181910] Labetalol leads to sustained vasodilation over the long term without a significant decrease in cardiac output or stroke volume, and a minimal decrease in heart rate.[A181907,A181910]', 'Labetalol antagonizes various adrenergic receptors to decrease blood pressure.[A181901,A181907,A181910,L7727] The duration of action is long as it is generally given twice daily, and the therapeutic window is wide as patients usually take 200-400mg twice daily.[L7730] Patients susceptible to bronchospasms should not use labetalol unless they are unresponsive to or intolerant of other antihypertensives.[L7730]', 'DrugBank', 0, 0, 0);
