INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4802, 'Fentanyl- hameln 0.1mg/2ml 1 amp. (net price)', 'فنتانيل - هاملن 0.1مجم/2مل 1 أمبول', '16', '10.5', 'Analgesics.opioid', 'Fentanyl', 'Sunny pharmaceuticals', 'Amp', 'أمبول', '0.1mg', '1', 'مسكن قوي للألم', 'about fentanyl citrate synthetic primary opioid agonist anilidopiperidine derivative narcotic analgesic general anesthetic. mechanism of action of fentanyl citrate this drug primarily acts on the cns. fentanyl is a pure opioid agonist and primarily acts o', NULL, NULL, 11547, '2023-09-14', 0, 'Fentanyl intravenous or intramuscular injections are indicated for short term analgesia during induction, maintenance, and recovery from general or regional anesthesia.[Label] These injections are also used with a neuroleptic for premedication, induction, and as an adjunct to maintenance of anesthesia.[Label] Finally, fentanyl intravenous or intramuscular injections are used with oxygen for anesthesia in high risk patients.[Label]



Fentanyl sublingual tablets, transmucosal lozenges, buccal tablets, sublingual sprays, transdermal systems, and nasal sprays are indicated for the management of breakthrough pain in opioid tolerant cancer patients who require around the clock pain management.[L6598,L6601,L6604,L6607,L922,L6610]', 'Fentanyl binds to opioid receptors, especially the mu opioid receptor, which are coupled to G-proteins.[A179533] Activation of opioid receptors causes GTP to be exchanged for GDP on the G-proteins which in turn down regulates adenylate cyclase, reducing concentrations of cAMP.[A179533] Reduced cAMP decreases cAMP dependant influx of calcium ions into the cell.[A179533] The exchange of GTP for GDP results in hyperpolarization of the cell and inhibition of nerve activity.[A179533]', 'Fentanyl produces strong analgesia through its activation of opioid receptors.[Label,A179533] It has a duration of action of several hours and a wider therapeutic index as patients develop tolerance to opioids.[Label] Fentanyl is associated with a risk of addiction and abuse and should not be mixed with alcohol or benzodiazepines.[Label,L6598,L6601,L6604,L6607,L922,L6610,L6613]', 'DrugBank', 0, 0, 0);
