INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4818, 'Ferriprox 100mg/ml (250ml) oral solution', 'فريبروكس 100مجم/مل 250 مل محلول بالفم', '385', NULL, 'Iron chelating agent', 'Deferiprone', 'Apotex inc. canada > egyptian pharmex', 'Solution', 'محلول', '100mg', '1', NULL, 'description deferiprone is an oral iron chelator used as a second line agent in thalassemia syndromes when iron overload from blood transfusions occurs. thalassemias are a type of hereditary anaemia due a defect in the production of hemoglobin. as a resul', NULL, NULL, 926, '2022-04-10', 0, 'Deferiprone is indicated in thalassemia syndromes when first line chelation agents are not adequate to treat transfusional iron overload.', 'Deferiprone is an iron chelator that binds to ferric ions (iron III) and forms a 3:1 (deferiprone:iron) stable complex and is then eliminated in the urine. Deferiprone is more selective for iron in which other metals such as zinc, copper, and aluminum have a lower affinity for deferiprone.', NULL, 'DrugBank', 0, 0, 0);
